// sql modifications from Contrexx v1.0.1

CREATE TABLE `contrexx_module_calendar` (  `id` int(11) NOT NULL auto_increment,  `catid` int(11) NOT NULL default '0',  `date` varchar(10) NOT NULL default '',  `time` varchar(4) NOT NULL default '',  `end_date` varchar(10) NOT NULL default '',  `end_time` varchar(4) NOT NULL default '',  `priority` int(1) NOT NULL default '3',  `name` varchar(100) NOT NULL default '',  `comment` text NOT NULL,  `sort` varchar(10) NOT NULL default '',  `place` varchar(25) NOT NULL default '',  `info` varchar(255) NOT NULL default 'http://',  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=1 ;
CREATE TABLE `contrexx_module_calendar_categories` (  `id` int(5) NOT NULL auto_increment,  `name` varchar(150) NOT NULL default '',  `status` int(1) NOT NULL default '0',  `lang` int(1) NOT NULL default '0',  `pos` int(5) NOT NULL default '0',  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=2 ;
CREATE TABLE `contrexx_module_calendar_style` (  `id` int(11) NOT NULL auto_increment,  `tableWidth` varchar(4) NOT NULL default '141',  `tableHeight` varchar(4) NOT NULL default '92',  `tableColor` varchar(7) NOT NULL default '',  `tableBorder` int(11) NOT NULL default '0',  `tableBorderColor` varchar(7) NOT NULL default '',  `tableSpacing` int(11) NOT NULL default '0',  `fontSize` int(11) NOT NULL default '10',  `fontColor` varchar(7) NOT NULL default '',  `numColor` varchar(7) NOT NULL default '',  `normalDayColor` varchar(7) NOT NULL default '',  `normalDayRollOverColor` varchar(7) NOT NULL default '',  `curDayColor` varchar(7) NOT NULL default '',  `curDayRollOverColor` varchar(7) NOT NULL default '',  `eventDayColor` varchar(7) NOT NULL default '',  `eventDayRollOverColor` varchar(7) NOT NULL default '',  `shownEvents` int(4) NOT NULL default '10',  `periodTime` varchar(5) NOT NULL default '00 23',  `stdCat` text NOT NULL,  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=3 ;
CREATE TABLE `contrexx_module_docsys` (  `id` smallint(6) unsigned NOT NULL auto_increment,  `date` int(14) default NULL,  `title` varchar(250) NOT NULL default '',  `author` varchar(150) NOT NULL default '',  `text` mediumtext NOT NULL,  `source` varchar(250) NOT NULL default '',  `url1` varchar(250) NOT NULL default '',  `url2` varchar(250) NOT NULL default '',  `catid` tinyint(2) NOT NULL default '0',  `lang` tinyint(2) NOT NULL default '0',  `userid` smallint(6) NOT NULL default '0',  `startdate` date NOT NULL default '0000-00-00',  `enddate` date NOT NULL default '0000-00-00',  `status` tinyint(4) NOT NULL default '1',  `changelog` int(14) NOT NULL default '0',  KEY `ID` (`id`),  FULLTEXT KEY `newsindex` (`title`,`text`)) TYPE=MyISAM AUTO_INCREMENT=2 ;
CREATE TABLE `contrexx_module_docsys_categories` (  `catid` tinyint(2) unsigned NOT NULL auto_increment,  `name` varchar(100) NOT NULL default '',  `lang` tinyint(2) unsigned NOT NULL default '1',  PRIMARY KEY  (`catid`)) TYPE=MyISAM AUTO_INCREMENT=2 ;
CREATE TABLE `contrexx_module_feed_category` (  `id` int(11) NOT NULL auto_increment,  `name` varchar(150) NOT NULL default '',  `status` int(1) NOT NULL default '1',  `time` int(100) NOT NULL default '0',  `lang` int(1) NOT NULL default '0',  `pos` int(3) NOT NULL default '0',  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=8 ;
CREATE TABLE `contrexx_module_feed_news` (  `id` int(11) NOT NULL auto_increment,  `subid` int(11) NOT NULL default '0',  `name` varchar(150) NOT NULL default '',  `link` varchar(150) NOT NULL default '',  `filename` varchar(150) NOT NULL default '',  `articles` int(2) NOT NULL default '0',  `cache` int(4) NOT NULL default '3600',  `time` int(100) NOT NULL default '0',  `image` int(1) NOT NULL default '1',  `status` int(1) NOT NULL default '1',  `pos` int(3) NOT NULL default '0',  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=28 ;
CREATE TABLE `contrexx_module_gallery_categories` (  `id` int(11) NOT NULL auto_increment,  `pid` int(11) NOT NULL default '0',  `name` varchar(100) NOT NULL default '',  `description` text NOT NULL,  `sorting` tinyint(3) NOT NULL default '0',  `status` set('0','1') NOT NULL default '1',  PRIMARY KEY  (`id`),  FULLTEXT KEY `galleryCategoryIndex` (`name`,`description`)) TYPE=MyISAM AUTO_INCREMENT=2 ;
CREATE TABLE `contrexx_module_gallery_pictures` (  `id` int(11) NOT NULL auto_increment,  `catid` int(11) NOT NULL default '0',  `validated` set('0','1') NOT NULL default '0',  `status` set('0','1') NOT NULL default '1',  `size_show` set('0','1') NOT NULL default '1',  `name` varchar(25) NOT NULL default '',  `path` text NOT NULL,  `linkname` text NOT NULL,  `link` text NOT NULL,  `lastedit` int(14) NOT NULL default '0',  `size_type` set('abs','proz') NOT NULL default 'proz',  `size_proz` int(3) NOT NULL default '0',  `size_abs_h` int(11) NOT NULL default '0',  `size_abs_w` int(11) NOT NULL default '0',  `quality` tinyint(3) NOT NULL default '0',  PRIMARY KEY  (`id`),  FULLTEXT KEY `galleryPicturesIndex` (`name`,`path`)) TYPE=MyISAM AUTO_INCREMENT=3 ;
CREATE TABLE `contrexx_module_gallery_settings` (  `id` int(11) NOT NULL auto_increment,  `name` varchar(30) NOT NULL default '',  `value` text NOT NULL,  `description` varchar(50) NOT NULL default '',  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=12 ;
CREATE TABLE `contrexx_newsletter_archiv` (  `archivid` smallint(5) unsigned NOT NULL auto_increment,  `archivlistid` tinyint(2) unsigned NOT NULL default '1',  `archivfromemail` varchar(250) NOT NULL default '',  `archivfromname` varchar(40) NOT NULL default '',  `archivsubject` varchar(100) NOT NULL default '',  `archivbody` longtext NOT NULL,  `archivdate` date NOT NULL default '0000-00-00',  `archivpriority` tinyint(2) NOT NULL default '3',  `archivhtml` tinyint(1) NOT NULL default '0',  UNIQUE KEY `ID` (`archivid`),  KEY `archivlistid` (`archivlistid`),  KEY `archivid` (`archivid`),  KEY `archivdate` (`archivdate`)) TYPE=MyISAM AUTO_INCREMENT=2 ;
CREATE TABLE `contrexx_newsletter_emails` (  `emailid` smallint(6) unsigned NOT NULL auto_increment,  `emailadress` varchar(250) NOT NULL default '',  `emailvalidate` tinyint(1) NOT NULL default '0',  `emaillistid` tinyint(3) NOT NULL default '0',  `emaildate` date NOT NULL default '0000-00-00',  `emaillastsend` date NOT NULL default '0000-00-00',  PRIMARY KEY  (`emailid`),  KEY `emailadress` (`emailadress`)) TYPE=MyISAM AUTO_INCREMENT=1 ;
CREATE TABLE `contrexx_newsletter_lists` (  `listid` tinyint(2) unsigned NOT NULL auto_increment,  `listname` varchar(100) NOT NULL default '',  `listemailfrom` varchar(250) NOT NULL default '',  `listheader` text,  `listfooter` text,  `listwelcomemsg` text,  `listvalidmsg` text,  `listwelcomemsgtitle` varchar(100) default NULL,  `listvalidmsgtitle` varchar(100) default NULL,  PRIMARY KEY  (`listid`),  KEY `ID` (`listid`),  KEY `listid` (`listid`)) TYPE=MyISAM AUTO_INCREMENT=2 ;
CREATE TABLE `contrexx_newsletter_settings` (  `setid` smallint(6) NOT NULL auto_increment,  `setname` varchar(250) NOT NULL default '',  `setvalue` text NOT NULL,  `setdescription` varchar(50) NOT NULL default '',  PRIMARY KEY  (`setid`),  KEY `setname` (`setname`)) TYPE=MyISAM AUTO_INCREMENT=9 ;
CREATE TABLE `contrexx_voting_results` (  `id` int(11) NOT NULL auto_increment,  `voting_system_id` int(11) default NULL,  `question` char(200) default NULL,  `votes` int(11) default '0',  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=21 ;
CREATE TABLE `contrexx_voting_system` (  `id` int(11) NOT NULL auto_increment,  `date` timestamp(14) NOT NULL,  `title` varchar(60) NOT NULL default '',  `question` text,  `status` tinyint(1) default '1',  `votes` int(11) default '0',  PRIMARY KEY  (`id`)) TYPE=MyISAM AUTO_INCREMENT=6 ;

