INSERT INTO `contrexx_module_topics_categories` (`id`, `parent_id`, `active`, `name`, `slug`, `description`, `created`, `updated`) VALUES
(1, NULL, 1, 'Personenversicherungen', 'personenversicherungen', NULL, '2016-08-29 10:22:01', '2016-08-29 10:22:07'),
(2, NULL, 1, 'Sachversicherungen', 'sachversicherungen', NULL, '2016-08-29 10:22:01', '2016-08-29 10:22:08'),
(3, NULL, 1, 'Haftpflichtversicherungen', 'haftpflichtversicherungen', NULL, '2016-08-29 10:22:01', '2016-08-29 10:22:08'),
(4, NULL, 1, 'Motorfahrzeugversicherungen', 'motorfahrzeugversicherungen', NULL, '2016-08-29 10:22:01', '2016-08-29 10:22:08'),
(5, NULL, 1, 'Fonds / Finanz', 'fonds-finanz', NULL, '2016-08-29 10:22:01', '2016-08-29 10:22:08'),
(6, NULL, 1, 'Versicherungsrecht', 'versicherungsrecht', NULL, '2016-08-29 10:22:01', '2016-08-29 10:22:09');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(1, 1, 'Erschwerungen', 'erschwerungen', NULL, 'Mit Erschwerungen können auch Versicherungen abgeschlossen werden, die ein erhöhtes oder anomales <a href="risiko">Risiko</a> beinhalten. Die erschwerten Bedingungen können in einer abgekürzten <a href="versicherungsdauer">Versicherungsdauer</a> bestehen (bei jüngerem Endalter ist das Risiko im allgemeinen kleiner und auch statistisch besser berechenbar), oder in einer gegenüber der Tarifprämie erhöhten <a href="pramie">Prämie</a>. Der Prämienzuschlag kann für die ganze <a href="vertragsdauer">Vertragsdauer</a> oder nur für eine bestimmte Zeit vereinbart werden. Räumt die Gesellschaft eine Revisionsmöglichkeit ein, kann die <a href="versicherte-person">versicherte Person</a> später aufgrund einer neuen <a href="gesundheitsprufung">Gesundheitsprüfung</a> den Wegfall oder die Reduktion des Zuschlages beantragen. Eine weitere Möglichkeit besteht in der Staffelung der Todesfallsumme, indem zum Beispiel bei Tod im ersten Versicherungsjahr nur ein Teil und in jedem weiteren Versicherungsjahr weitere Teile der Versicherungssumme ausbezahlt werden. Die Art des Risikos kann auch eine Kombination der verschiedenen Erschwerungen bedingen. Eine weitere Möglichkeit besteht darin, besondere Risiken oder die Folgen einer durchgemachten Erkrankung oder eines erlittenen Unfalls von der Deckung auszuschliessen (<a href="vorbehalt">Vorbehalt</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(2, 1, 'Missbrauchschutz', 'missbrauchschutz', NULL, 'Schutz der Allgemeinheit vor Vertrauensmissbrauch.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(3, 1, 'ZB', 'zb', NULL, 'Abkürzung für "Zusatzbedingungen" (zu den <a href="avb">AVB</a>)', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(4, 1, 'Annahme', 'annahme', NULL, 'Mit der Annahme des Lebensversicherungsantrages durch die entsprechende Gesellschaft ist diese an die im <a href="antrag">Antrag</a> festgehaltenen Leistungen und <a href="pramie">Prämien</a> gebunden. Sie hält diese in der Police fest.<br />Spätere Änderungen des <a href="risiko">Risikos</a>, z.B. des Gesundheitszustandes, des Berufes oder des Wohnortes spielen keine Rolle.<br />Der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> bestätigt durch seine Unterschrift auf dem Antrag, dass er mit den darin vereinbarten Leistungen und Prämien einverstanden ist, und dass er die damit verbundenen Bedingungen annimmt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(5, 1, 'Bindungsfrist', 'bindungsfrist', NULL, 'Gesetzlich geregelte Dauer der Bindung des Antragsstellers (<a href="versicherungsnehmer-in">Versicherungsnehmers</a>) an seinen <a href="antrag">Antrag</a>. Im Normalfall beträgt die Bindungsfrist 14 Tage; ist eine ärztliche Untersuchung notwendig, beträgt die Bindungsfrist vier Wochen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(6, 1, 'Unfall', 'unfall', NULL, 'Als Unfall gilt jede Körperschädigung, die der <a href="versicherter">Versicherte</a> durch plötzlich auf ihn einwirkende äussere Gewalt unfreiwillig erleidet. Unfällen gleichgestellt sind u.a. Vergiftungen sowie Ertrinken.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(7, 1, 'Unfallversicherung', 'unfallversicherung', NULL, '<a href="personenversicherung">Personenversicherung</a>, die das Ereignis <a href="unfall">Unfall</a> versichert. Man unterscheidet die <a href="obligatorische-unfallversicherung">obligatorische</a> und die private Unfallversicherung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(8, 1, 'Wohneigentum, selbst genutzt', 'wohneigentum-selbst-genutzt', NULL, 'Am Wohnsitz des Versicherten durch den <a href="versicherter">Versicherten</a> genutztes Wohneigentum (Stockwerkeigentum, Liegenschaft im Allein- oder Miteigentum, selbständiges und dauerndes Wohnrecht).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(9, 1, 'Gewohnte Lebensführung', 'gewohnte-lebensfuhrung', NULL, 'Innerhalb des <a href="sozialversicherung">Sozialversicherung</a>ssystems übergeordnetes Ziel der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> bzw. der 2. Säule: Fortführung der gewohnten Lebensführung in angemessener Weise.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(10, 1, 'Errungenschaft', 'errungenschaft', NULL, 'Vermögenswerte, die ein Ehegatte während der Ehedauer entgeltlich erwirbt (Art. 197 <a href="zgb">ZGB</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(11, 1, 'Adäquater Kausalzusammenhang', 'adaquater-kausalzusammenhang', NULL, 'Ein adäquater Kausalzusammenhang besteht, wenn zwischen Ursache und Wirkung ein Zusammenhang besteht, der nach dem normalen Lauf der Dinge und der allgemeinen Lebenserfahrung zu erwarten ist; ein direkter rechtserheblicher Zusammenhang.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(12, 1, 'Umlageverfahren', 'umlageverfahren', NULL, 'Die Leistungen für die Rentner werden jedes Jahr unmittelbar durch die Beiträge der aktiven <a href="versicherter">Versicherten</a> finanziert, d.h. es sammelt sich kein <a href="deckungskapital">Deckungskapital</a> an, da die Einnahmen gerade den jeweiligen Ausgaben zu entsprechen haben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(13, 1, 'Ausgleichung', 'ausgleichung', NULL, 'Anrechnung von Zuwendungen des Erblassers zu Lebzeiten an die Erben im Rahmen der Erbteilung (Art. 626 ff. <a href="zgb">ZGB</a>)', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(14, 1, 'Verwaltungskosten', 'verwaltungskosten', NULL, 'I <a href="costi-d-amministrazione">costi d''amministrazione</a> comprendono i costi d''acquisizione, i costi generali d''amministrazione e i costi d''<a href="incasso">incasso.</a> Tutti questi costi sono inclusi nel <a href="premio-lordo">premio lordo</a>, ma non nel <a href="premio-netto">premio netto</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(15, 1, 'Affidavit', 'affidavit', NULL, 'Siehe <a href="bankenerklarung-affidavit">Bankenerklärung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(16, 1, 'Anschlussvertrag, berufliche Vorsorge', 'anschlussvertrag-berufliche-vorsorge', NULL, 'Hat ein Arbeitgeber keine eigene <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> errichtet, so schliesst er sich mit einem Anschlussvertrag einer Sammel- oder Gemeinschaftseinrichtung zur Durchführung der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> an. Der Anschlussvertrag umschreibt die gegenseitigen Rechte und Pflichten und regelt unter anderem die Dauer, die Kündigung und die Auflösungsmodalitäten des Vertrages.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(17, 1, 'Eintrittsalter', 'eintrittsalter', NULL, 'Das Eintrittsalter ist massgebend für die Ermittlung der <a href="pramie">Prämie</a> beim Abschluss der Lebensversicherung. Es entspricht der Differenz zwischen Versicherungsbeginn und Geburtstag der <a href="versicherte-person">versicherten Person</a>. Das Eintrittsalter wird in ganzen Jahren ausgedrückt; Bruchteile von mehr als sechs Monaten werden auf das volle nächste Jahr aufgerundet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(18, 1, 'Endalter', 'endalter', NULL, 'Alter des <a href="versicherter">Versicherten</a> bei Ablauf des <a href="versicherungsvertrag">Versicherungsvertrages.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(19, 1, 'Agentur', 'agentur', NULL, 'Verkaufsstelle als Bestandteil des direkten <a href="absatzkanal">Absatzkanals</a>. Der <a href="versicherer">Versicherer</a> betreibt in seinem Tätigkeitsgebiet ein Netz von Agenturen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(20, 1, 'AHV-Ausweis', 'ahv-ausweis', NULL, 'Persönlicher Ausweis, auf welchem die Nummern aller <a href="ausgleichskasse">Ausgleichskassen</a> aufgedruckt sind, denen ein <a href="versicherter">Versicherter</a> angehört hat.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(21, 1, 'AHV-Ausgleichskasse', 'ahv-ausgleichskasse', NULL, 'Siehe <a href="ausgleichskasse">Ausgleichskasse</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(22, 1, 'AHV-Lohn', 'ahv-lohn', NULL, 'Bruttolohn der Lohnabrechnung, der "AHV-pflichtig" ist. Ausgangspunkt für die Ermittlung des koordinierten <a href="lohn">Lohns</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(23, 1, 'AHV', 'ahv-1', NULL, 'Abkürzung für <a href="alters-und-hinterlassenenversicherung">"Alters- und Hinterlassenenversicherung"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(24, 1, 'AIV', 'aiv', NULL, 'Abkürzung für  "<a href="arbeitslosen-und-insolvenzversicherung">Arbeitslosen- und Insolvenzversicherung</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(25, 1, 'Umhüllende Vorsorgelösung', 'umhullende-vorsorgelosung', NULL, 'Organisationsform der überobligatorischen Versicherung in der <a href="berufliche-vorsorge">beruflichen Vorsorge</a>, bei der die BVG-Minimalleistungen und die <a href="uberobligatorium-bv">überobligatorischen</a> Leistungen als Einheit zusammengefasst werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(26, 1, 'All-in-Fee', 'all-in-fee-1', NULL, 'Die All-in-Fee deckt die im Zusammenhang mit der Fondsleitung und der Verwaltung der Anlagen anfallenden Kosten und wird dem <a href="fondsvermogen">Fondsvermögen</a> belastet. Darin eingeschlossen sind insbesondere Aufwendungen für Kauf, Verkauf und Verwahrung der Wertpapiere sowie alle sonstigen Kosten (Druck der <a href="prospekt-fondsreglement">Prospekte</a>, <a href="jahresbericht-halbjahresbericht">Jahres- und Halbjahresberichte</a>, Kosten der <a href="revisionsstelle">Revisionsstelle</a> und Kurspublikationen, Gebühren der <a href="aufsichtsbehorde-fonds">Aufsichtsbehörde</a> etc.). Welche Kosten in der All-in-Fee enthalten sind, wird von den Fondsgesellschaften verschiedentlich definiert. Die genauen Angaben sind im jeweiligen <a href="prospekt-fondsreglement">Fondsreglement</a> ersichtlich.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(27, 1, 'All-Risk-Versicherung', 'all-risk-versicherung', NULL, 'Wörtlich: Versicherung aller Risiken. Bei dieser Versicherung besteht der <a href="versicherungsschutz">Versicherungsschutz</a> einer Sache unabhängig davon, durch welche <a href="gefahr">Gefahr</a> ein versichertes Schadenereignis eintritt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(28, 1, 'Anamnese', 'anamnese', NULL, 'Krankenvorgeschichte der zu versichernden Person für die gesundheitliche <a href="risikoprufung">Risikoprüfung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(29, 1, 'Tierschäden', 'tierschaden', NULL, 'Bei der <a href="motorfahrzeug-kaskoversicherung">Motorfahrzeug-Kaskosversicherung</a>: Kollision des <a href="motorfahrzeug">Motorfahrzeugs</a> mit einem Tier auf öffentlicher Strasse.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(30, 1, 'Tierhalterhaftpflicht', 'tierhalterhaftpflicht', NULL, '<a href="kausalhaftung">Kausalhaftung</a> des Tierhalters für den <a href="schaden">Schaden</a>, der durch das Tier bei einer Drittperson verursacht wurde. Es handelt sich um einen Fall der <a href="ausservertragliche-haftpflicht">ausservertraglichen Haftpflicht</a> (<a href="milde-kausalhaftung">milde Kausalhaftung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(31, 1, 'Annexvertrieb', 'annexvertrieb', NULL, 'Zusätzlicher ("angehängter") Vertrieb und Bestandteil des indirekten <a href="absatzkanal">Absatzkanals</a>. Hier werden bestimmte <a href="versicherung">Versicherungen</a> durch branchenfremde Partner angeboten und verkauft (z.B. Reiseannullationsversicherungen durch Reisebüros, <a href="lebensversicherung">Lebensversicherungen</a> durch die Post, Motorfahrzeugversicherungen durch Garagen, etc.).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(32, 1, 'Jahresbericht / Halbjahresbericht', 'jahresbericht-halbjahresbericht', NULL, 'Die <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> veröffentlicht für jeden von ihr verwalteten <a href="anlagefonds">Anlagefonds</a> innerhalb von vier Monaten nach Abschluss des <a href="rechnungsjahr">Rechnungsjahres</a> einen geprüften Jahresbericht und innerhalb von zwei Monaten nach Ablauf der ersten Hälfte des Rechnungsjahres einen ungeprüften Halbjahresbericht. Der Jahresbericht enthält u.a. Angaben zur Jahresrechnung, zu den Mittelzu- und -abflüssen und zum Inventar des Vermögens sowie den Bericht der <a href="revisionsstelle">Revisionsstelle</a>. Der Halbjahresbericht enthält die wichtigsten Halbjahresergebnisse.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(33, 1, 'Geldwäschereigesetz (GWG)', 'geldwaschereigesetz-gwg', NULL, 'Das GWG schreibt vor, dass in der <a href="lebensversicherung">Lebensversicherung</a> bei der Antragsprüfung und der Entgegennahme von Geldern für vermögensbildende Versicherungen im Rahmen der <a href="saule-3b">Säule 3b</a> bestimmte Sorgfaltspflichten eingehalten werden müssen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(34, 1, 'Aufwertungsfaktor', 'aufwertungsfaktor', NULL, 'Zahl, die im Rahmen der <a href="alters-und-hinterlassenenversicherung">AHV</a>-<a href="rente">Rente</a>nberechnung verwendet wird und die Teuerung während der Dauer der Beitragspflicht berücksichtigt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(35, 1, 'Bewilligung', 'bewilligung', NULL, 'Ein <a href="versicherer">Versicherer</a> darf erst tätig werden, wenn er die erforderliche Bewilligung durch das <a href="efd">EFD</a> erhalten hat.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(36, 1, 'Arbitrage', 'arbitrage-1', NULL, 'Ausnützung örtlicher oder internationaler Bewertungsunterschiede gleicher Wertpapiere oder verwandter Werte, wobei diese auf dem Markt mit dem niedrigeren Preis gekauft und am Markt mit dem höheren Preis verkauft werden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(37, 1, 'Architekt', 'architekt', NULL, 'Planer und Zeichner eines entstehenden Bauwerks (meist im Hochbau).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(38, 1, 'Verzug', 'verzug', NULL, 'Der Zustand, in welchem sich der säumige Schuldner nach erfolgter <a href="mahnung">Mahnung</a> und unbenutzter Nachfrist befindet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(39, 1, 'Asset Allocation', 'asset-allocation-1', NULL, 'Je nach Fonds die Verteilung der Anlagen auf die verschiedenen <a href="anlageinstrumente">Anlageinstrumente</a> (Geldmarktpapiere, Obligationen, Aktien und Immobilien), Branchen, Märkte und Währungen.<br />\r<br />Strukturierung eines <a href="portefeuille">Portfolios</a> nach Kriterien wie Währung, Anlageinstrumente oder Strategie.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(40, 1, 'Anlagestrategiefonds', 'anlagestrategiefonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, welche die offiziellen Anlagestrategien der jeweiligen Anbieter für die verschiedenen Risikoklassen nachbilden. Anlagestrategiefonds investieren weltweit in verschiedenen <a href="anlageinstrumente">Anlageinstrumenten</a>, je nach Risikoklasse werden <a href="aktie">Aktien</a> beziehungsweise <a href="obligationen">Obligationen</a> übergewichtet. Anlagestrategiefonds werden auch Asset Allocation Funds, Strategy Funds, Portfoliofonds, Anlagezielfonds, Vermögensverwaltungsfonds oder gemischte Fonds genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(41, 1, 'Inventarwert', 'inventarwert', NULL, 'Das <a href="nettovermogen-immobilienfonds">Nettovermögen</a> eines Fonds geteilt durch die Anzahl der sich im Umlauf befindlichen <a href="anteil">Anteile</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(42, 1, 'Aktiven', 'aktiven', NULL, 'In der <a href="bilanz">Bilanz</a> auf der linken Seite aufgeführte Guthaben oder Vermögenswerte einer Unternehmung. Gegenstück <a href="passiven">Passiven</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(43, 1, 'Anrechnungswert', 'anrechnungswert', NULL, '¶"Innerer Wert" einer Police an einem bestimmten Stichtag nach Abzug der für das <a href="risiko">Risiko</a> und Verwaltung getragenen <a href="kosten">Kosten.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(44, 1, 'Abtretung', 'abtretung', NULL, 'Auch Zession genannt. Die Abtretung ist ein (schriftlicher) Vertrag zwischen altem und neuem <a href="glaubiger">Gläubiger</a>, die, wenn sie wirksam ist, zum Gläubigerwechsel führt. An Stelle des bisherigen Gläubigers tritt ein anderer. Es kann auch von einem Wechsel der Versicherungsnehmerschaft gesprochen werden. Bei einer Abtretung gehen die Rechte und Pflichten an den neuen Versicherungsnehmer (Dritten) über.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(45, 1, 'Kontrollstelle', 'kontrollstelle', NULL, 'Organ, welches die jährliche Prüfung der Geschäftsführung, des Rechnungswesens und der Vermögensanlage einer Vorsorgeeinrichtung prüft; sie muss unabhängig von der <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> und dem Arbeitgeber sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(46, 1, 'Revisionsstelle', 'revisionsstelle', NULL, 'Von der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> und der <a href="depotbank">Depotbank</a> unabhängige Instanz. Sie überprüft regelmässig die Einhaltung der gesetzlichen Vorschriften. Revisionsstellen müssen durch die <a href="aufsichtsbehorde-fonds">Aufsichtsbehörde</a>, in der Schweiz die <a href="#invalid-reference-533">Eidgenössische Bankenkommission</a>, anerkannt sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(47, 1, 'Anerkannter Experte', 'anerkannter-experte', NULL, 'Person, die das Eidg. Diplom als Pensionsversicherungsexperte besitzt oder durch das <a href="bundesamt-fur-sozialversicherung">Bundesamt für Sozialversicherung</a> (BSV) als solcher anerkannt worden ist sowie eine juristische Person, die solche Experten beschäftigt. Der Experte prüft die Sicherheit der <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> insbesondere bezüglich der Leistungen und deren Finanzierung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(48, 1, 'Automatische Summenanpassung', 'automatische-summenanpassung', NULL, 'Die automatische Summenanpassung bezieht die Preissteigerung in die <a href="versicherungssumme">Versicherungssumme</a> und die <a href="pramie">Prämie</a> mit ein. Sie orientiert sich an der  Entwicklung des <a href="hausratindex">Hausratindexes</a> und passt sich jährlich an die Preisentwicklung an. Die automatische Summenanpassung wird dem Kunden als Schutz gegen <a href="unterversicherung-uv">Unterversicherung</a> empfohlen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(49, 1, 'Hilfspersonenhaftung', 'hilfspersonenhaftung', NULL, '<a href="kausalhaftung">Kausalhaftung</a> des Unternehmers / des Unternehmens für den <a href="schaden">Schaden</a>, die Hilfspersonen (Mitarbeitende) bei der Erfüllung vertraglicher Pflichten beim Vertragspartner anrichten. Es handelt sich um eine <a href="vertragliche-haftpflicht">vertragliche Haftpflicht</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(50, 1, 'AVIG', 'avig', NULL, 'Abkürzung für "Bundesgesetz über die obligatorische <a href="arbeitslosen-und-insolvenzversicherung">Arbeitslosenversicherung und die Insolvenzentschädigung"</a>vom 25. Juni 1982 (SR 837.1).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(51, 1, 'Innendienst', 'innendienst', NULL, 'Aufgaben im Leistungsprozess, die nicht vom <a href="aussendienst">Aussendienst</a> wahrgenommen werden (z.B. Produktentwicklung, Underwriting / <a href="riskmanagement-rm">Riskmanagement</a>, Bestandsverwaltung, Zahlungsverkehr, <a href="schadendienst">Schadendienst</a>), sowie die Unterstützungsprozesse (z.B. Rechnungswesen / Controlling, Personalwesen, Ausbildung, IT-Services)', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(52, 1, 'Schlechtwetterentschädigung', 'schlechtwetterentschadigung', NULL, 'Leistung der Arbeitslosenversicherung (<a href="arbeitslosen-und-insolvenzversicherung">ALV</a>) bei Reduktion des Erwerbseinkommens wegen schlechten Wetters für bestimmte Branchen (z. B. für das Baugewerbe).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(53, 1, 'Entreissdiebstahl', 'entreissdiebstahl', NULL, 'Entreissdiebstahl liegt vor, wenn der Täter dem Opfer Sachen, die dieses auf sich trägt oder in der Hand hält, gewaltsam entreisst. Entreissdiebstahl kann entweder als <a href="beraubung">Beraubung</a> (Anwendung von Gewalt), als <a href="einfacher-diebstahl">einfacher Diebstahl</a>oder als separates <a href="risiko">Risiko</a> definiert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(54, 1, 'Bilanz', 'bilanz', NULL, 'Periodische Gegenüberstellung sämtlicher <a href="aktiven">Aktiven</a> und <a href="passiven">Passiven</a> auf ein bestimmtes Datum hin (meist der 31.12.). Die Bilanz ist stets ausgeglichen, d.h. die Summe der Aktiven entspricht immer der Summe der Passiven. Die Erstellung einer Jahresbilanz ist für gewisse Gesellschaftsformen im Schweizerischen Obligationenrecht (OR) zwingend vorgeschrieben. Für Banken bestehen darüber hinaus noch besonders weitgehende Vorschriften, die im Bankengesetz geregelt sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(55, 1, 'Bankenerklärung (Affidavit)', 'bankenerklarung-affidavit', NULL, 'Bei <a href="anlagefonds">Anlagefonds</a> schweizerischen Rechts wird bei Ertragsausschüttungen (Zinsen/<a href="dividende">Dividenden</a>) grundsätzlich die eidgenössische <a href="verrechnungssteuer-fonds">Verrechnungssteuer</a> von 35% abgezogen. Für <a href="anleger">Anleger</a> mit Domizil im Ausland werden die Ertragssausschüttungen ohne Verrechnungssteuerabzug vorgenommen, sofern die ausgeschütteten Erträge zu mindestens 80% aus dem Ausland stammen und eine Bank bestätigt (Bankenerklärung), dass die <a href="anteil">Anteile</a> bei ihr im Depot liegen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(56, 1, 'Kassenobligation', 'kassenobligation', NULL, 'Je nach Bedarf laufend zur Ausgabe gelangende mittelfristige Schuldverschreibungen der Banken, in runden Beträgen, meistens mit einer Laufzeit von zwei bist acht Jahren.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(57, 1, 'Konkurs', 'konkurs', NULL, 'Zwangsmässige Totalliquidation des Vermögens des Schuldners zur Befriedigung der Forderungen der <a href="glaubiger">Gläubiger</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(58, 1, 'Inhaberaktie', 'inhaberaktie', NULL, 'Aktie, die auf den (nicht namentlich bezeichneten) Inhaber lautet. Gegensatz: <a href="namenaktie">Namenaktie</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(59, 1, 'Benchmark', 'benchmark', NULL, '<a href="index-fonds">Index</a>, der einem <a href="anlagefonds">Anlagefonds</a> als Vergleichsbasis für die <a href="performance">Performance</a> dient. Auch Referenzindex oder Vergleichsindex genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(60, 1, 'Begünstigte/r', 'begunstigte-r', NULL, 'Der Begünstigte, der sowohl eine <a href="naturliche-person">natürliche</a> als auch eine <a href="juristische-person">juristische Person</a> sein kann, hat einen anwartschaftlichen Anspruch auf die <a href="versicherungsleistungen">Versicherungsleistung</a>. Er wird vom <a href="versicherungsnehmer-in">Versicherungsnehmer</a> bestimmt und kann entweder für die ganze oder anteilige <a href="versicherungssumme">Versicherungssumme</a> begünstigt werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(61, 1, 'Begünstigung', 'begunstigung', NULL, 'Der Versicherungsnehmer kann in einer sogenannter Begünstigung bestimmen, wer nach Eintritt des versicherten Ereignisses in den Genuss der ganzen oder teilweisen Versicherungsleistung gelangen soll. Eine Begünstigung im Versicherungsvertrag ist also eine Verfügung über die Versicherungsleistung. Eine Begünstigung ist nur in der <a href="personenversicherung">Personenversicherung</a> möglich. Die Begünstigung ist grundsätzlich unabhängig vom Erbrecht. Der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> ist befugt, einen <a href="dritte">Dritten</a> als Begünstigten zu bezeichnen (Art. 76 Abs. 1 <a href="vvg-1">VVG</a>). Der Begünstigte wird dadurch aber keineswegs Vertragspartei. Er schuldet deshalb auch keine <a href="pramie">Prämien</a>, hat aber im <a href="versicherungsfall">Versicherungsfall</a> verschiedene Obliegenheiten zu erfüllen, da er Anspruchsberechtigter ist. Der Versicherungsnehmer kann die Begünstigung, die er ausgesprochen hat, jederzeit widerrufen, sofern er auf das Widerrufsrecht nicht verzichtet hat (Art. 77 Abs. 2 VVG). Begünstigt werden können sowohl <a href="naturliche-person">natürliche</a> wie <a href="juristische-person">juristische Personen</a> oder beliebige Institutionen. Die Begünstigung wird normalerweise bereits im <a href="antrag">Antrag</a> festgelegt und von dort in die <a href="police">Police</a> übernommen. Oft enthalten auch die <a href="allgemeine-versicherungsbedingungen">Allgemeinen Versicherungsbedingungen</a> eine Begünstigungsregel, die gilt, falls nichts anderes bestimmt wird. Besteht keine Begünstigung, so fällt beim Tod des Versicherten die <a href="versicherungsleistungen">Versicherungsleistung</a> in die Erbmasse. Da die Begünstigung entscheidend ist für die Leistung im Ereignisfall und zu den besonderen rechtlichen Privilegien der Lebensversicherung im Bereich der Familien-<a href="vorsorge">Vorsorge</a> gehört, lohnt es sich, diese mit dem Lebensversicherungsfachmann offen und gründlich zu besprechen und von Zeit zu Zeit zu überprüfen, ob sie noch den Vorstellungen und Bedürfnissen im gegenwärtigen Zeitpunkt entspricht. In der beruflichen Vorsorge und in der gebundenen Selbstvorsorge wie auch bei <a href="freizugigkeitspolice">Freizügigkeitspolicen</a> ist die Begünstigung gesetzlich nur beschränkt möglich.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(62, 1, 'Erbvorteile', 'erbvorteile', NULL, 'Bei Tod einer<a href="versicherte-person">versicherten Person</a> fallen die <a href="versicherungsleistungen">Versicherungsleistungen</a>nicht in die Erbmasse, sondern grundsätzlich dem oder den <a href="begunstigte-r">Begünstigten</a> zu. Vorbehalten bleibt die Herabsetzungsklage bei Pflichtteilsverletzung. In diesem Fall wird der im Zeitpunkt des Todes bestehende <a href="ruckkaufswert">Rückkaufswert</a>zur Berechnung der <a href="pflichtteil">Pflichtteile</a> herangezogen. Sind erbberechtigte Nachkommen, ein Ehegatte, Eltern, Grosseltern oder Geschwister die Begünstigten, haben sie Anspruch auf die <a href="versicherungsleistungen">Versicherungsleistungen</a>, auch wenn sie die Erbschaft, z.B. weil diese überschuldet ist, ausschlagen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(63, 1, 'Best-of-breed', 'best-of-breed', NULL, 'Fachlich ausgewiesene Investoren im <a href="private-equity-1">Private-Equity</a> und <a href="hedge-funds-1">Hedge-Fund</a>-Bereich.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(64, 1, 'Beta', 'beta-1', NULL, 'Ein Risikomass, das angibt, wie sensitiv eine Anlage, z.B. ein Fonds, auf Marktbewegungen reagiert, die durch die jeweilige <a href="benchmark">Benchmark</a> repräsentiert werden. Ein Beta von 1,2 besagt beispielsweise, dass man beim Fonds eine Wertveränderung von 12% erwarten kann, wenn man für den Markt eine Veränderung von 10% prognostiziert. Der Zusammenhang beruht auf historischen Beobachtungen und gilt nur approximativ. Er ist umso enger, je höher die <a href="korrelation">Korrelation</a> zwischen der Benchmark und dem <a href="anlagefonds">Anlagefonds</a> ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(65, 1, 'Sperrtage', 'sperrtage', NULL, 'Die Versicherungsleistung der <a href="arbeitslosenentschadigung">Arbeitslosenentschädigung</a> besteht aus Taggeldern. Der Anspruch auf Arbeitslosentaggeld kann bei einem Verschulden des Versicherten sistiert werden. Je nach Schwere des Verschuldens können die Behörden des Arbeitsamts bis zu 60 Sperrtage aussprechen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(66, 1, 'Prämiensperrdepot', 'pramiensperrdepot', NULL, 'siehe <a href="pramiendepot-oder-pramienkonto">Prämiendepot</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(67, 1, 'Blue Chips', 'blue-chips-1', NULL, 'Bezeichnung für <a href="aktie">Aktien</a> führender Unternehmen mit erstklassiger <a href="bonitat-fonds">Bonität</a>, hoher <a href="borsenkapitalisierung">Börsenkapitalisierung</a>, hoher Ertragskraft und solider Finanzstruktur.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(68, 1, 'Stiftungsrat', 'stiftungsrat', NULL, 'Oberstes Verwaltungsorgan der Stiftung. Im Rahmen von Gesetz und Stiftungsurkunde entscheidet er frei und endgültig. Er trägt auch die Verantwortung für die Stiftung, insbesondere für die Anlage des Stiftungsvermögens.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(69, 1, 'Unfallähnliche Körperschädigung (UV)', 'unfallahnliche-korperschadigung-uv', NULL, 'Körperlicher Schaden, der nicht auf einen <a href="unfall">Unfall</a> (im Sinne von: ungewöhnliche Einwirkung eines äusseren Faktors) zurückzuführen ist, aber durch die <a href="obligatorische-unfallversicherung">obligatorische Unfallversicherung</a>nach UVG versichert ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(70, 1, 'Obligationenfonds', 'obligationenfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in <a href="obligationen">Obligationen</a> und anderen fest- oder variabel verzinslichen Papiere investieren. Obligationenfonds sind in der Regel auf eine bestimmte Referenz- und Anlagewährung ausgerichtet. Auch Bond Funds genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(71, 1, 'Obligationen', 'obligationen', NULL, 'Schuldverpflichtungen mit festem, gelegentlich auch variablem Zinssatz und in der Regel festem Fälligkeits- und Rückzahlungstermin. Synonym für Anleihen, Renten, Bonds und Schuldverschreibungen. Häufigste <a href="emittent">Emittenten</a> sind grosse Unternehmen, staatliche Organisationen wie Bund und Kantone, öffentlich-rechtliche Institutionen sowie internationale Organisationen wie die Weltbank oder der Internationale Währungsfonds.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(72, 1, 'Not leidende Obligationen', 'not-leidende-obligationen', NULL, '<a href="obligationen">Obligationen</a>, deren Zinszahlungen oder Rückzahlungen nicht fristgerecht erfolgen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(73, 1, 'Bonus', 'bonus', NULL, 'Bonus ist eine Form von <a href="uberschussbeteiligung">Überschussbeteiligung</a>; die Überschussanteile werden beim Bonussystem zur Erhöhung der <a href="versicherungsleistungen">Versicherungsleistung</a> verwendet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(74, 1, 'Rentenbonus', 'rentenbonus', NULL, 'Bei aufgeschobenen Renten sammelt sich während der Aufschubszeit nach dem ersten Versicherungsjahr der sogenannte Rentenbonus an, der dann (nach der Aufschubszeit) in Form einer Bonusrente (zusammen mit der Grundrente und der Überschussrente) ausbezahlt wird.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(75, 1, 'Bonussystem', 'bonussystem', NULL, 'Das Bonussystem ist vor allem in der <a href="motorfahrzeug-haftpflichtversicherung">Motorfahrzeug-Haftpflichtversicherung</a> sowie in der <a href="motorfahrzeug-kaskoversicherung">Motorfahrzeug-Vollkaskoversicherung</a> bekannt. <br />Dieses System erlaubt es, die <a href="pramie">Prämie</a> zu reduzieren. Wird die <a href="police">Police</a> durch keinen Schadenfall belastet, so wird dem Halter ein <a href="bonus">Bonus</a> (Rabatt) gewährt. <br />Schon nach einem unfallfreien Jahr setzt das Bonussystem ein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(76, 1, 'Buchgewinn / Buchverlust', 'buchgewinn-buchverlust', NULL, 'Guadagni o perdite teoriche di un investimento di capitale sulla base dell''aumento o della diminuzione del suo valore finché l''investimento non viene venduto.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(77, 1, 'Fremdfinanzierung', 'fremdfinanzierung', NULL, 'Entspricht den Verbindlichkeiten des <a href="anlagefonds">Fonds</a> vor Liquidationssteuern in Prozent des Gesamtfondsvermögens.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(78, 1, 'Anleihen', 'anleihen', NULL, 'Siehe <a href="obligationen">Obligationen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(79, 1, 'Branche', 'branche', NULL, 'Als Branche werden die Versicherungszweige bezeichnet, die je ein spezielles <a href="risiko">Risiko</a> einschliessen, wie z. B. die Unfallversicherung oder in der Hausratversicherung die Branchen <a href="feuer">Feuer</a>, <a href="wasser-schaden">Wasser</a>, <a href="diebstahl">Diebstahl</a>, <a href="glasbruchversicherung">Glasbruch.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(80, 1, 'Makler', 'makler', NULL, 'Unabhängiger Partner und Bestandteil des indirekten <a href="absatzkanal">Absatzkanals</a>. Er ist tätig im Auftrag von Kunden und erhält als Gegenleistung vom <a href="versicherer">Versicherer</a> eine Provision auf Abschlüsse und/oder die Dauer des <a href="versicherung">Versicherung</a>svertrags. Einzelne Makler arbeiten auf Honorarbasis, d.h. sie werden vom Kunden direkt entschädigt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(81, 1, 'Courtage', 'courtage', NULL, 'Entschädigung der Börsenagenten für den Kauf oder Verkauf von Wertpapieren an der Börse. Die Courtagen sind nicht mehr einheitlich geregelt; jede Bank ist grundsätzlich bei deren Festlegung frei.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(82, 1, 'BSV', 'bsv', NULL, 'Abkürzung für "<a href="bundesamt-fur-sozialversicherung">Bundesamt für Sozialversicherung</a>".', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(83, 1, 'Gebäude', 'gebaude', NULL, 'Unbewegliches Gebilde mit Dach und benutzbarem Raum, das einer Bautätigkeit entspringt und dauerhaft eingerichtet wird.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(84, 1, 'Bauliche Einrichtung', 'bauliche-einrichtung', NULL, 'Bauelemente, die mit einem <a href="gebaude">Gebäude</a> weniger eng verbunden sind als die <a href="bestandteil">Bestandteile</a> und deshalb ohne Beschädigung des Gebäudes entfernt werden können (z. B. Fenster, Türen, Toilette).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(85, 1, 'Gebäudeversicherungsanstalt', 'gebaudeversicherungsanstalt', NULL, 'Siehe <a href="kantonale-gebaudeversicherung">Kantonale Gebäudeversicherung</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(86, 1, 'Gebäudehaftpflichtversicherung', 'gebaudehaftpflichtversicherung', NULL, '<a href="vermogensversicherung">Vermögensversicherung</a>. Schützt den Eigentümer eines Gebäudes vor berechtigten Schadenersatzansprüchen von Dritten, die durch das Gebäude geschädigt werden (v.a. <a href="werkeigentumerhaftpflicht">Werkeigentümerhaftpflicht</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(87, 1, 'Gebäudesachversicherung', 'gebaudesachversicherung', NULL, 'Siehe <a href="gebaudeversicherung">Gebäudeversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(88, 1, 'Beweislast', 'beweislast', NULL, 'Wo das Gesetz es nicht anders bestimmt, hat derjenige das Vorhandensein einer behaupteten Tatsache zu beweisen, der aus ihr Rechte ableitet; er hat die Beweislast.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(89, 1, 'Betriebsunterbrechungsversicherung', 'betriebsunterbrechungsversicherung', NULL, 'Die Betriebsunterbrechungsversicherung deckt Ertragsausfälle eines Betriebes, welche durch einen versicherten Schaden (Feuer, Wasser, Einbruch) entstehen. Es handelt sich eigentlich um eine <a href="vermogensversicherung">Vermögensversicherung</a>, sie wird aber in der Regel mit einer Geschäfts-Sachversicherung kombiniert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(90, 1, 'Geschäftherrenhaftpflicht', 'geschaftherrenhaftpflicht', NULL, '<a href="kausalhaftung">Kausalhaftung</a> des Unternehmers / des Unternehmens für den <a href="schaden">Schaden</a>, den die Arbeitnehmenden oder andere Hilfspersonen in Ausübung ihrer Tätigkeit bei Drittpersonen anrichten. Es handelt sich um eine <a href="ausservertragliche-haftpflicht">ausservertragliche Haftpflicht</a> (<a href="milde-kausalhaftung">milde</a> <a href="kausalhaftung">Kausalhaftung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(91, 1, 'Geschäftsplan', 'geschaftsplan', NULL, 'Gibt Auskunft über die geplanten Aktivitäten des <a href="versicherer">Versicherers</a>. Muss als Bestandteil des Verfahrens zur Erteilung der <a href="bewilligung">Bewilligung</a> beim <a href="efd">EFD</a> eingereicht werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(92, 1, 'Geschäftsprozess', 'geschaftsprozess', NULL, 'Geschäftsprozesse sind Arbeitsabläufe oder Abfolgen von Tätigkeiten, die notwendig sind, um ein bestimmtes Resultat zu erreichen. Man unterscheidet <a href="leistungsprozess">Leistungs</a>- und <a href="unterstutzungsprozess">Unterstützungsprozesse</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(93, 1, 'Betriebshaftpflichtversicherung', 'betriebshaftpflichtversicherung', NULL, 'Versichert ist die <a href="haftpflicht">Haftpflicht</a>, die sich aus dem Betrieb eines Unternehmens aufgrund rechtlicher Bestimmungen ergibt (Anlagenrisiko, Betriebsrisiko, Produktrisiko).<br />Versichert sind Betriebsinhaber und deren Mitarbeiter.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(94, 1, 'Geschäftsjahr', 'geschaftsjahr', NULL, 'Siehe <a href="rechnungsjahr">Rechnungsjahr</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(95, 1, 'Buy-out', 'buy-out', NULL, 'Damit werden Übernahmen von Unternehmen bezeichnet. Die bekannten Formen sind das Management Buy-Out (MBO), die Firmen- oder Bereichsübernahme durch das Management und das Leveraged Buy-Out (LBO), bei dem neue Investoren die Firmenmehrheit unter Verwendung von Fremdkapital übernehmen. Buy-Outs werden oft auch für Nachfolgeregelungen in Familienunternehmen eingesetzt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(96, 1, 'BV', 'bv-1', NULL, 'Abkürzung für die Bundesverfassung.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(97, 1, 'BVG', 'bvg-1', NULL, 'Abkürzung für  "Bundesgesetz über die berufliche Alters-, Hinterlassenen- und Invalidenvorsorge" vom 25. Juni 1982). Siehe auch <a href="berufliche-vorsorge">berufliche Vorsorge.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(98, 1, 'Call-Optionen', 'call-optionen', NULL, 'Siehe <a href="optionen">Optionen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(99, 1, 'Annullierungskosten', 'annullierungskosten', NULL, 'Gesetzliche oder vertraglich festgelegte <a href="kosten">Kosten</a>, die bei Rücktritt von einem Vertrag entstehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(100, 1, 'Kantonalbank', 'kantonalbank', NULL, 'Das sind Banken, die durch kantonalen gesetzlichen Erlass gegründet wurden und für deren Verbindlichkeiten in der Regel der Kanton haftet. Die meisten Kantonalbanken sind öffentlich-rechtliche Institute mit eigener Rechtspersönlichkeit, deren Grundkapital (Dotationskapital genannt) häufig allein vom Kanton zur Verfügung gestellt wird.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(101, 1, 'Kantonale Gebäudeversicherung', 'kantonale-gebaudeversicherung', NULL, 'Versicherungseinrichtung, bei der - teilweise obligatorisch, teilweise freiwillig - Gebäude (z.T. auch <a href="fahrhabe">Fahrhabe</a>) versichert werden müssen oder können. Sie hat, im Gegensatz zu den privaten <a href="versicherer">Versicherern</a>, keinen werbenden Aussendienst.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(102, 1, 'Absicherungsfonds', 'absicherungsfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die den Anlegern die Möglichkeit geben, an der positiven Entwicklung der Finanzmärkte teilzuhaben, aber gegen nachteilige Marktschwankungen, die über eine bestimmte Prozentzahl hinausgehen, weitgehend abgesichert zu sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(103, 1, 'Kapitalversicherung', 'kapitalversicherung', NULL, 'Form der Versicherung, bei welcher die versicherte Leistung in der Auszahlung eines bestimmten, im <a href="versicherungsvertrag">Versicherungsvertrag</a> festgelegten Kapitals (= einmaliger Beitrag) besteht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(104, 1, 'Kapitaloption', 'kapitaloption', NULL, 'Die reglementarischen Bestimmungen in der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> können vorsehen, dass die Anspruchsberechtigten anstelle einer Alters-, Witwen- oder Invalidenrente eine Kapitalabfindung verlangen können. Für die Altersleistung hat der <a href="versicherter">Versicherte</a> mindestens drei Jahre vor Entstehung des <a href="anspruch">Anspruchs</a> eine Erklärung abzugeben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(105, 1, 'Kapitalisierungssatz, statische Ertragswertmeth.', 'kapitalisierungssatz-statische-ertragswertmeth', NULL, 'Dieser setzt sich aus der Lastenrechnung einer Liegenschaft zusammen (Anlagekapitalzins, Abgaben, Betriebskosten, Versicherungsprämien, Unterhalts- und Instandhaltungskosten, Gebäudeamortisation/Abschreibung, Mietzinsausfallrisiko, Verwaltungskosten) und wird als Prozentsatz des Ertragswertes ausgedrückt. Als Faktor zur Ermittlung des Ertragswertes wird der Kapitalisierungssatz auf Grund der zu erwartenden Liegenschaftsaufwendungen von einem unabhängigen Schätzungsexperten festgelegt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(106, 1, 'Ertragswert', 'ertragswert', NULL, 'Dieser wird aus den aktuellen Mieteinnahmen und einem liegenschaftsspezifischen Zinssatz (sog. Kapitalisierungssatz) berechnet. Die Mietzinsen müssen angemessen und auf Dauer erhältlich erscheinen, um vollständig in die Berechnung einzufliessen. Das Hauptgewicht bei der Bewertung von <a href="renditeliegenschaft">Renditeliegenschaften</a> liegt beim Ertragswert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(107, 1, 'Kapitaldeckungsverfahren', 'kapitaldeckungsverfahren', NULL, 'Das Kapitaldeckungsverfahren besteht darin, dass Ausgaben, die aus bestimmten Rechtsverhältnissen künftig zu erwarten sind, durch Bereitstellung entsprechender Kapitalien voraus finanziert werden. Jeder spart für sich selber. Es führt zur Bildung von Fonds, im Gegensatz zu <a href="umlageverfahren">Umlageverfahren</a>, wo die Ausgaben gleichsam Zug um Zug durch die Einnahmen gedeckt werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(108, 1, 'Rentenwert', 'rentenwert', NULL, '<a href="einmaleinlage-einmalpramie">Einmaleinlage</a> für eine sofort beginnende Rente.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(109, 1, 'Pflegeleistungen (KV)', 'pflegeleistungen-kv', NULL, 'Leistungen der <a href="obligatorische-krankenpflegeversicherung">obligatorischen Krankenpflegeversicherung</a>, die die Kosten für die Behandlung und Heilung eines Versicherten bei Krankheit, Unfall nach KVG, Geburtsgebrechen und Mutterschaft umfassen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(110, 1, 'Pflegeleistungen (UV)', 'pflegeleistungen-uv', NULL, 'Leistungen der <a href="obligatorische-unfallversicherung">obligatorischen Unfallversicherung</a>, die die Kosten für die Behandlung der Unfallfolgen und die Pflege des Verunfallten umfassen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(111, 1, 'Betreuungsgutschrift', 'betreuungsgutschrift', NULL, 'Betrag, der im Rahmen der <a href="alters-und-hinterlassenenversicherung">AHV</a>-<a href="rente">Rente</a>nberechnung gut geschrieben wird und die Pflege von nahestehenden, hilfsbedürftigen Personen bei sich  zu Hause während der Dauer der Beitragspflicht berücksichtigt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(112, 1, 'Cashflow', 'cashflow', NULL, 'Der Cashflow stellt den in einem Geschäftsjahr erzielten Nettoertrag vor Abschreibungen und Rückstellungen dar.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(113, 1, 'Cashflow-Rendite', 'cashflow-rendite', NULL, 'Nettoertrag vor Abschreibungen und Rückstellungen im Verhältnis zum aktuellen Börsenkurs, abzüglich darin enthaltener aufgelaufener Erträge.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(114, 1, 'Barauszahlung (BV)', 'barauszahlung-bv', NULL, 'In determinati casi della <a href="previdenza-professionale">previdenza professionale</a> l''assicurato può pretendere il pagamento in contanti della sua <a href="prestazione-di-libero-passaggio-pp">prestazione di libero passaggio</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(115, 1, 'Kausalhaftung', 'kausalhaftung', NULL, 'Kausalhaftung heisst, dass eine <a href="haftung">Haftung</a> auch ohne <a href="verschulden">Verschulden</a> besteht.<br />Das Haftpflichtrecht unterscheidet die milde (einfache) Kausalhaftung von der scharfen Kausalhaftung (Gefährdungshaftung).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(116, 1, 'Kausalzusammenhang', 'kausalzusammenhang', NULL, 'Siehe <a href="adaquater-kausalzusammenhang">adäquater Kausalzusammenhang</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(117, 1, 'ZGB', 'zgb', NULL, 'Abkürzung für "Zivilgesetzbuch".', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(118, 1, 'Zentrale Ausgleichsstelle AHV', 'zentrale-ausgleichsstelle-ahv', NULL, 'Stelle, die das zentrale Versichertenregister führt und die letzte <a href="ausgleichskasse">Ausgleichskasse</a> im Versicherungsfall bei der Zusammenstellung der <a href="individuelles-konto-ik">individuellen Konten</a> eines <a href="versicherter">Versicherten</a> unterstützt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(119, 1, 'Zertifikat', 'zertifikat', NULL, 'Sammeltitel für einen oder mehrere Anlagefonds-Anteile.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(120, 1, 'Kursveränderung / Kapitalveränderung', 'kursveranderung-kapitalveranderung', NULL, '<a href="gewinn">Gewinne</a> oder Verluste (real oder buchmässig) werden wie folgt berechnet: Tageskurs oder Verkaufspreis minus investiertes Kapital.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(121, 1, 'Handänderung', 'handanderung', NULL, '<p>Wechselt der Gegenstand des Vertrages den Eigentümer, so gehen die Rechte und Pflichten aus dem <a href="versicherungsvertrag">Versicherungsvertrag</a> auf den neuen Eigentümer über. Der neue Eigentümer kann den Übergang des Vertrages durch eine schriftliche Erklärung bis spätestens 30 Tage nach der Handänderung ablehnen. Das Versicherungsunternehmen kann den Vertrag innert 14 Tagen nach Kenntnis des neuen Eigentümers kündigen. Der Vertrag endet frühstens 30 Tage nach der Kündigung.</p>\n<p>Für die <a href="motorfahrzeug-haftpflichtversicherung">Motorfahrzeug-Haftpflichtversicherungen</a> gilt als Spezialgesetz Art. 67 SVG, welcher den Halterwechsel regelt. Die <a href="motorfahrzeug-haftpflichtversicherung">Motorfahrzeug-Haftpflichtversicherung</a> geht mit sämtlichen Rechten und Pflichten auf den neuen Halter übergeht. Der neue Halter kann den Vertrag beenden, wenn er belegt, dass er einen <a href="versicherungsvertrag">Versicherungsvertrag</a> mit einer anderen Gesellschaft abgeschlossen hat. Die Prämie bleibt bei der Handänderung nur bis zur Vertragsauflösung geschuldet.<br />\nKauf, gesellschaftsrechtliche Vorgänge, Erbschaft, Tausch, Schenkung, Versteigerung, Enteignung etc. sind Rechtsgründe für den Eigentümerwechsel.</p>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(122, 1, 'Halterwechsel', 'halterwechsel', NULL, 'Von Halterwechsel spricht man, wenn das <a href="motorfahrzeug">Motorfahrzeug</a> auf einen neuen <a href="halter">Halter</a> übergeht (Handänderung des Motorfahrzeuges).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(123, 1, 'Erziehungsgutschrift', 'erziehungsgutschrift', NULL, 'Betrag, der im Rahmen der <a href="alters-und-hinterlassenenversicherung">AHV</a>-<a href="rente">Rente</a>nberechnung gut geschrieben wird und das Aufziehen von Kindern während der Dauer der Beitragspflicht berücksichtigt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(124, 1, 'Anspruch', 'anspruch', NULL, 'Von Anspruch auf Schadenersatz wird gesprochen, wenn ein <a href="geschadigte">Geschädigter</a> den Ersatz eines <a href="schaden">Schadens</a> einfordern kann.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(125, 1, 'Schadenanzeige', 'schadenanzeige', NULL, 'Benachrichtigung des <a href="versicherer">Versicherers</a> durch den <a href="versicherungsnehmer-in">Versicherungsnehmer</a> bzw. den Versicherten im <a href="schaden">Schaden</a>fall.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(126, 1, 'Schadenabwicklung', 'schadenabwicklung', NULL, 'Serviceleistung, die eine kompetente, unbürokratische Beratung und Unterstützung der versicherten Kunden im Schadenfall umfasst. Als Kernleistung werden die finanziellen Folgen der <a href="schaden">Schäden</a> übernommen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(127, 1, 'Schadensatz', 'schadensatz', NULL, 'Kennzahl, die den Schadenaufwand eines Versicherers im Verhältnis zu den <a href="pramie">Prämien</a> bemisst (brutto oder netto).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(128, 1, 'Schadendienst', 'schadendienst', NULL, 'Aufgaben, die anfallen, wenn Kunden <a href="schaden">Schäden</a> melden. Dazu gehören insbesondere: die Überprüfung von <a href="schadenmeldung-schadenanzeige">Schadenmeldungen</a>, die Ermittlung der Schadenhöhe und das Erbringen der <a href="versicherungsleistungen">Versicherungsleistung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(129, 1, 'Sicherungsteam', 'sicherungsteam', NULL, 'Umsetzung des Servicegedankens in der <a href="privatversicherung">Privatversicherung</a>sbranche. Kundenberater und Kunde bilden ein Team mit dem Ziel, jederzeit den optimal auf die jeweiligen Bedürfnisse des Kunden abgestimmten <a href="versicherungsschutz">Versicherungsschutz</a> sicherzustellen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(130, 1, 'Geschlossener Fonds', 'geschlossener-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a> in Gesellschaftsform (normalerweise als Aktiengesellschaft) mit fixem Kapital. Ein geschlossener Fonds ist nicht verpflichtet, ausgegebene <a href="anteil">Anteile</a> auf Verlangen des Anteilinhabers zurückzunehmen. Nach schweizerischem Recht ist diese Anlagefondsform zum öffentlichen Vertrieb nicht zugelassen. Gegenteil: <a href="offener-fonds">Offener Fonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(131, 1, 'OR', 'or-1', NULL, 'Abkürzung für "schweizerisches Obligationenrecht"', '2016-08-29 10:21:09', '2016-08-29 10:21:55');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(132, 1, 'Inkasso', 'inkasso', NULL, 'Einziehung von Geldforderungen', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(133, 1, 'Kollektivanlagevertrag', 'kollektivanlagevertrag', NULL, 'Der Kollektivanlagevertrag stellt die Rechtsgrundlage für das Anlagefondsgeschäft in der Schweiz dar und wird zwischen der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a>, der <a href="depotbank">Depotbank</a> und dem <a href="anleger">Anleger</a> abgeschlossen. Er bildet die Rechtsgrundlage für die Verwaltung des <a href="anlagefonds">Anlagefonds</a> durch die Fondsleitung einerseits und für die Beteiligung des Anlegers am Vermögen des Anlagefonds andererseits. Der Kollektivanlagevertrag wird durch das <a href="prospekt-fondsreglement">Fondsreglement</a> verkörpert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(134, 1, 'Combined Ratio (CR)', 'combined-ratio-cr-1', NULL, 'Kombinierter Satz. Kennzahl, die sich aus dem <a href="schadensatz">Schadensatz</a> und dem <a href="kostensatz">Kostensatz</a> zusammensetzt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(135, 1, 'Vertragsbeginn', 'vertragsbeginn', NULL, 'Der Zeitpunkt, in welchem der Vertragsschluss durch Annahme eines verbindlichen <a href="antrag">Antrages</a> herbeigeführt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(136, 1, 'Geschäftsversicherung', 'geschaftsversicherung', NULL, 'Zur Geschäftsversicherung werden all jene <a href="versicherungsvertrag">Versicherungsverträge</a> gezählt, die <a href="schaden">Schäden</a> durch <a href="feuer">Feuer</a>, <a href="wasser-schaden">Wasser</a>, Glasbruch und Einbruch sowie <a href="haftpflicht">Haftpflicht</a> und Betriebsunterbruch in Industrie-, Handels- und Gewerbebetrieben versichern.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(137, 1, 'Betriebsrechtsschutz', 'betriebsrechtsschutz', NULL, 'Bereich der <a href="rechtsschutzversicherung">Rechtsschutzversicherung</a>, der Rechtsstreitigkeiten versichert, mit denen Unternehmen oder selbstständig erwerbende Personen konfrontiert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(138, 1, 'Geschäftssachversicherung', 'geschaftssachversicherung', NULL, 'siehe <a href="geschaftsversicherung">Geschäftsversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(139, 1, 'Commission de Surveillance du Sect. Financier', 'commission-de-surveillance-du-sect-financier', NULL, 'Die Commission de Surveillance du Secteur Financier (CSSF) ist die staatliche <a href="aufsichtsbehorde-fonds">Aufsichtsbehörde</a> zur Überwachung des Anlagefondsgeschäfts in Luxemburg.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(140, 1, 'Kommissionen', 'kommissionen', NULL, '<a href="ausgabekommission">Ausgabekommission</a> und <a href="rucknahmekommission">Rücknahmekommission</a>. Gebühr, die bei der <a href="zeichnung">Zeichnung</a> oder <a href="rucknahmen">Rücknahme</a> von Fondsanteilen erhoben wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(141, 1, 'Ausschuss', 'ausschuss', NULL, 'Für besondere Aufgaben (z.B. Vermögensanlage) ausgewählte Personen eines bestimmten Gremiums oder Organs (z.B. <a href="stiftungsrat">Stiftungsrat</a>}', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(142, 1, 'Warenfonds', 'warenfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die ihr Vermögen hauptsächlich in handelbaren Rohwaren anlegen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(143, 1, 'Commodity Traded Advisor (CTA)', 'commodity-traded-advisor-cta', NULL, 'CTAs investieren ausschliesslich in <a href="futures">Futures</a> und anderen <a href="derivate">Derivaten</a>. Der Grossteil der CTAs verfolgt eine "Trend Following"-Strategie. Dieser liegt die Erkenntnis zu Grunde, dass es in den Finanzmärkten temporäre Trends gibt. Trend Followers replizieren einen Trend und profitieren sowohl von fallenden als auch von steigenden Märkten. Eine weitere Strategie ist, genau diese Trendbrüche zu erkennen und davon zu profitieren.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(144, 1, 'Versichertengemeinschaft', 'versichertengemeinschaft', NULL, 'Organisation einer <a href="gefahrengemeinschaft">Gefahrengemeinschaft</a> durch einen Versicherer.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(145, 1, 'Genugtuung', 'genugtuung', NULL, 'Entschädigung für erlittenen immateriellen <a href="schaden">Schaden</a> (Schmerzensgeld).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(146, 1, 'Schadenersatz', 'schadenersatz', NULL, 'Ersatzleistung bedeutet, dass jemand einem anderen den <a href="schaden">Schaden</a> ausgleicht, der diesem entstanden ist. Eine Schadenersatzpflicht kann sich aus einem Vertrag oder aus unerlaubter Handlung ergeben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(147, 1, 'Ausgleichsfonds', 'ausgleichsfonds', NULL, 'Geldreserve, um Schwankungen der Mittelzuflüsse zur Deckung der AHV-, IV- und ALV-Leistungen abzufangen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(148, 1, 'Anspruchskonkurrenz', 'anspruchskonkurrenz', NULL, 'Ist eine Vertragsverletzung zugleich ein Verstoss gegen ein allgemeines Gebot der Rechtsordnung kann nebeneinander ein Schadenersatzanspruch des Geschädigten aus Vertrag oder ein Schadenersatzanspruch aus unerlaubter Handlung entstehen. Beide <a href="anspruch">Ansprüche</a> können geltend gemacht werden, jedoch nicht kumulativ, d.h. ein Anspruch geht mit der Erfüllung des anderen ebenfalls unter.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(149, 1, 'Kulanz', 'kulanz', NULL, 'Entgegenkommende Behandlung eines Geschäftspartners aus kaufmännischen Erwägungen z.B. Regulierung von <a href="schaden">Schäden</a> (z.B. volle Entschädigung auch bei <a href="unterversicherung-uv">Unterversicherung</a>). Die Kulanzleistung stellt rechtlich keine Schenkung dar, sondern erfolgt nach der Grundlage des <a href="versicherungsvertrag">Versicherungsvertrages.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(150, 1, 'Komplementärrente (UV)', 'komplementarrente-uv', NULL, 'Leistung der <a href="obligatorische-unfallversicherung">obligatorischen Unfallversicherung</a>, welche die Leistungen der <a href="invaliditat">IV</a>-<a href="rente">Rente</a> auf 90% des versicherten Jahresverdiensts ergänzt (max. 90 % des UVG-Maximums).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(151, 1, 'Abschlusskosten', 'abschlusskosten', NULL, 'Kosten, welche durch das Zustandekommen eines <a href="versicherungsvertrag">Versicherungsvertrages</a> entstehen, wie z.B.<br />- Beratung<br />- ärztliche Untersuchungen<br />- Ausfertigung der Police<br />- Spesen<br />- Provisionen usw.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(152, 1, 'Bestandteil', 'bestandteil', NULL, 'Teile einer Sache, der so eng mit dieser verbunden ist,  dass er ohne Beschädigung der Sache nicht getrennt werden kann. Bei einem <a href="gebaude">Gebäude</a> alle Bauelemente, aus denen es zwingend besteht (z. B. Fundament, Wände, Dach).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(153, 1, 'Familienversicherung', 'familienversicherung', NULL, 'Versicherungsform der Haushaltversicherung (<a href="hausratversicherung">Hausratversicherung</a>, <a href="privathaftpflichtversicherung">Privathaftpflichtversicherung</a> und <a href="privatrechtsschutz">Rechtsschutzversicherung</a>), bei der sich der Versicherungsschutz über mehrere Personen erstreckt, die in einer dauerhaften Hausgemeinschaft leben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(154, 1, 'Kaskoversicherung', 'kaskoversicherung', NULL, 'Bei der Kaskoversicherung unterscheidet man zwischen Teil- und Vollkaskoversicherungen. Die <a href="motorfahrzeug-kaskoversicherung">Motorfahrzeug-Kaskoversicherung</a> deckt <a href="schaden">Schäden</a> am <a href="fahrzeug">Fahrzeug</a> des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> infolge eines versicherten Ereignisses. Bei Teilkasko sind u. a. folgende Schadenursachen gedeckt:<a href="brand">Brand</a>, Blitz, <a href="explosion-1">Explosion</a>, Kurzschluss, <a href="elementarereignis">Elementarereignisse</a>, <a href="diebstahl">Diebstahl</a>, Glasbruch, Tiere. Bei Vollkasko kommt die Kollision hinzu.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(155, 1, 'Motorfahrzeug-Kaskoversicherung', 'motorfahrzeug-kaskoversicherung', NULL, 'Diese Versicherung übernimmt <a href="schaden">Schäden</a> am <a href="fahrzeug">Fahrzeug</a> des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> infolge eines versicherten Ereignisses.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(156, 1, 'Obligatorische Krankenpflegeversicherung', 'obligatorische-krankenpflegeversicherung', NULL, 'Teil der <a href="sozialversicherung">Sozialversicherung</a>, der Behandlungs- und Heilungskosten bei <a href="krankheit">Krankheiten</a>, <a href="unfall">Unfällen</a> (nicht dem <a href="uvg">UVG</a> unterstellte Personen) und Mutterschaft versichert. Teil der <a href="soziale-krankenversicherung">sozialen Krankenversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(157, 1, 'Abschluss', 'abschluss', NULL, 'Zustandekommen des <a href="versicherungsvertrag">Versicherungsvertrages</a> durch Annahme des <a href="antrag">Antrages</a> durch den <a href="versicherer">Versicherer</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(158, 1, 'Geburtsgebrechen', 'geburtsgebrechen', NULL, '<a href="krankheit">Krankheit</a>, die bei vollendeter Geburt besteht. Versichertes Risiko der <a href="obligatorische-krankenpflegeversicherung">obligatorischen Krankenpflegeversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(159, 1, 'Rückwirkungsschäden', 'ruckwirkungsschaden', NULL, 'Ein Begriff aus der <a href="betriebsunterbrechungsversicherung">Betriebsunterbrechungsversicherung</a>. Rückwirkungsschäden sind Ertragsausfälle, die bei einem versicherten Betrieb dadurch entstehen, dass ein Zulieferer oder Abnehmer einen versicherten <a href="schaden">Schaden</a> erleidet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(160, 1, 'Gleich bleibende Versicherungssumme', 'gleich-bleibende-versicherungssumme', NULL, 'Die Versicherungssumme einer <a href="lebensversicherung">Lebensversicherung</a> bleibt während der vereinbarten <a href="versicherungsdauer">Versicherungsdauer</a> konstant und entspricht dem Todesfallkapital im Todesfall des Versicherten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(161, 1, 'Bauingenieur', 'bauingenieur', NULL, 'Technischer Ingenieur, der ein entstehendes Bauwerk im Hoch- und Tiefbau konzipiert, plant, berechnet, herstellt und ggf. betreibt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(162, 1, 'Rohbauversicherung', 'rohbauversicherung', NULL, '<a href="sachversicherung">Sachversicherung</a>, die ein entstehendes Bauwerk vor Feuer und <a href="elementarereignis">Elementarereignissen</a> schützt. Im <a href="schaden">Schaden</a>fall wird der <a href="vermogensbedarf">Vermögensbedarf</a>ersetzt, um den Rohbau wieder auf den Stand unmittelbar vor dem versicherten Ereignis zu bringen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(163, 1, 'Bauhandwerker', 'bauhandwerker', NULL, 'Impresa che fornisce prestazioni edili o esegue progetti di costruzione su ordine o mandato (contratto d''appalto) del <a href="committente">committente.</a> L''imprenditore è tenuto a fornire le prestazioni in anticipo, ossia fornisce dapprima le sue prestazioni (materiale, lavoro e salario) ma viene rimunerato solo alla consegna delle prestazioni fornite.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(164, 1, 'Vertragstypus', 'vertragstypus', NULL, '<a href="anlagefonds">Anlagefonds</a> ohne eigene Rechtspersönlichkeit. Der <a href="anleger">Anleger</a> schliesst durch den Kauf von <a href="anteil">Anteilen</a> einen <a href="kollektivanlagevertrag">Kollektivanlagevertrag</a> mit der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> und der <a href="depotbank">Depotbank</a> ab. Der Anteilinhaber hat keine Eigentumsrechte am Fondsvermögen, sondern Anspruch auf Beteiligung an Vermögen und Ertrag des Fonds. Gegenteil: <a href="gesellschaftstypus">Gesellschaftstypus</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(165, 1, 'Vertragsprämie', 'vertragspramie', NULL, 'Die Vertrags<a href="pramie">prämie</a> ist der Prämienbetrag, den der Versicherte unter Ausserachtlassung eines Bonus/Malus für den Versicherungsschutz zu bezahlen hat. Sie ergibt sich aus der <a href="bruttopramie">Bruttoprämie</a> abzüglich oder zuzüglich kundenseitiger Besonderheiten (wie z.B. halbjährliche Zahlung oder Rabatte).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(166, 1, 'Bauunternehmer', 'bauunternehmer', NULL, 'Firma, die im Auftrag (Werkvertrag) des <a href="bauherr">Bauherrn</a> Bauleistungen erbringt bzw. Baupläne ausführt. Ein Bauunternehmer ist vorleistungspflichtig, d.h. er erbringt seine Leistungen (Material, Arbeitsleistung, Arbeitslohn) im Voraus, erhält die entsprechende Vergütung aber erst nach der Abnahme der Bauleistung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(167, 1, 'Bauwesenversicherung', 'bauwesenversicherung', NULL, '<a href="technische-versicherungen">Technische Versicherung</a>, die ein entstehendes Bauwerk vor bauspezifischen Risiken schützt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(168, 1, 'Vertragliche Haftpflicht', 'vertragliche-haftpflicht', NULL, 'Die <a href="haftung">Haftung</a> der <a href="haftpflicht">haftpflicht</a>igen Person für einen <a href="schaden">Schaden</a> beim Vertragspartner besteht aufgrund einer vertraglichen Pflicht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(169, 1, 'Beitragslücke', 'beitragslucke', NULL, 'Differenz zwischen geschuldeten und geleisteten Beitragsjahren für Beiträge der<a href="alters-und-hinterlassenenversicherung">Alters- und Hinterlassenenversicherung</a>. Personen mit Beitragslücken erhal-ten nur eine Teil<a href="rente">rente</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(170, 1, 'Umwandlung', 'umwandlung', NULL, 'Bei der Umwandlung eines bestehenden Vertrages wird dessen <a href="deckungskapital">Deckungskapital</a> als Teil-<a href="einmaleinlage-einmalpramie">Einmaleinlage</a>in eine neue Versicherung eingebaut. Bei diesem Vorgang erlischt der alte <a href="versicherungsvertrag">Versicherungsvertrag</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(171, 1, 'Umwandlungswert', 'umwandlungswert', NULL, 'Wurden die <a href="pramie">Prämien</a> einer Lebensversicherung für mindestens 3 Jahre bezahlt und stellt der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> die Prämienzahlung ein oder verlangt er die Umwandlung, wird die <a href="versicherungssumme">Versicherungssumme</a> nach versicherungsmathematischen Grundsätzen auf die Höhe des Umwandlungssatzes herabgesetzt. Die Versicherung wird prämienfrei weitergeführt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(172, 1, 'Wandelanleihen', 'wandelanleihen', NULL, '<a href="obligationen">Obligationen</a>, die mit einem Wandelrecht ausgestattet sind, das die Inhaber berechtigt, die Obligation zu einem bestimmten Zeitpunkt und zu einem im Voraus bestimmten Verhältnis in Aktien des betreffenden Unternehmens umzuwandeln. Nach der Wandlung geht die Obligation unter.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(173, 1, 'Koordinierter Lohn', 'koordinierter-lohn', NULL, 'Dies ist der Lohnteil, der gemäss <a href="bvg-1">BVG</a> zu versichern ist. Er liegt zwischen 87.5% (7/8) der maximalen <a href="ahv-1">AHV</a>-Rente (<a href="koordinationsabzug">Koordinationsabzug</a>) und 300% der maximalen AHV-Rente (<a href="bvg-lohnmaximum">BVG-Lohnmaximum</a>). <br />Zum koordinierten Lohn gelangt man, indem man bei Löhnen über dem BVG-Lohnmaximum den Koordinationsabzug vom BVG-Lohnmaximum abzieht, bei Löhnen unter dem BVG-Lohnmaximum den Koordinationslohn vom Bruttolohn abzieht. <br />Ist der koordinierte Lohn kleiner als 1/8 der maximalen AHV-Rente (bei Löhnen zwischen der maximalen AHV-Rente und der <a href="eintrittsschwelle-bv">Eintrittsschwelle</a>), so wird er auf den Minimalbetrag aufgerundet. <br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(174, 1, 'Koordinationsabzug', 'koordinationsabzug', NULL, 'Lohnteil, der im <a href="bvg-1">BVG</a>-Obligatorium nicht zu versichern ist. Er entspricht 7/8 der maximalen <a href="ahv-1">AHV</a>-Rente.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(175, 1, 'Core Portfolio', 'core-portfolio-1', NULL, 'Das Core Portfolio ist der Teil des <a href="portefeuille">Portefeuilles</a> eines (dynamischen) <a href="absicherungsfonds">Absicherungsfonds</a>, mit dem der Kapitalschutz sichergestellt werden soll. Die Anlagen erfolgen in der Regel in Geldmarktanlagen resp. <a href="obligationen">Obligationen</a> in der <a href="referenzwahrung">Referenzwährung</a> des Fonds.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(176, 1, 'Gesellschaftstypus', 'gesellschaftstypus', NULL, 'Dieser <a href="anlagefonds">Anlagefonds</a> hat eine eigene Rechtspersönlichkeit, i.d.R. die einer Aktiengesellschaft. Die <a href="anteil">Anteile</a> werden in Form von <a href="aktie">Aktien</a> ausgestellt. Der Anleger wird Aktionär und besitzt sowohl Vermögens- als auch Mitgliedschaftsrechte. Das schweizerische <a href="#invalid-reference-469">Anlagefondsgesetz</a> kennt die körperschaftliche Form nicht, in anderen Teilen Europas und den USA ist sie aber weit verbreitet. Gegenteil: <a href="vertragstypus">Vertragstypus</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(177, 1, 'Corporate Governance', 'corporate-governance-1', NULL, 'Uno studio dell''OCSE la definisce come la struttura di rapporti e di responsabilità reciproche tra la direzione dell''azienda e gli azionisti.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(178, 1, 'Corporate Social Responsibillity', 'corporate-social-responsibillity-1', NULL, 'Il concetto di responsabilità sociale d''impresa (Corporate Social Responsibility - CSR) definisce un comportamento aziendale trasparente basato su valori etici e sul rispetto dei collaboratori, della società e dell''ambiente. Questo comportamento mira alla creazione di un valore sostenibile per la società e per gli azionisti. (Definizione: Prince of Wales Business Leader Forum).', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(179, 1, 'Korrelation', 'korrelation', NULL, 'Mass für die Parallelität der Preisentwicklung verschiedener Anlagekategorien oder -instrumente. Die Korrelation quantifiziert die Stärke des Zusammenhanges und kann Werte zwischen -1 und +1 aufweisen. Je näher die Zahl bei 1 liegt, desto stärker ist der Zusammenhang. Liegt der Wert bei -1, bewegen sich die Anlagen und die <a href="benchmark">Benchmark</a> gegenläufig. Bei einer Korrelation von 0 besteht scheinbar kein Zusammenhang zwischen der Entwicklung der beiden Anlagen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(180, 1, 'Durchschnittskosten-Methode', 'durchschnittskosten-methode', NULL, 'Mise à profit judicieuse des fluctuations de cours. Cette méthode mise sur l''avantage des versements réguliers: si les versements mensuels sont identiques, l''investisseur acquiert davantage de parts quand les prix d''émission sont bas et moins de parts quand ils sont plus élevés. Sur le long terme, l''investisseur obtient ainsi un prix d''achat plus intéressant qu''en achetant régulièrement un nombre défini de parts sur la même période.', '2016-08-29 10:21:09', '2016-08-29 10:22:12'),
(181, 1, 'Kostenbeteiligung', 'kostenbeteiligung', NULL, 'Versicherte der <a href="obligatorische-krankenpflegeversicherung">obligatorischen Krankenpflegeversicherung</a> müssen einen Teil der Behandlungskosten selbst übernehmen. Die Kostenbeteiligung leistet einen wichtigen Beitrag zur Kostendämpfung im Gesundheitswesen und besteht aus einer <a href="franchise">Franchise</a> und einem <a href="selbstbehalt">Selbstbehalt</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(182, 1, 'Kostenprämie', 'kostenpramie', NULL, 'Bestandteil der <a href="pramie">Prämie</a> für eine Lebensversicherung. Beinhaltet die anteilmässigen Abschluss-, Inkasso- und Verwaltungskosten des Versicherers.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(183, 1, 'Teuerungszulagen', 'teuerungszulagen', NULL, 'Anpassung laufender <a href="rente">Renten</a> an die Teuerung. Für Hinterlassenen- und Invalidenrenten vom <a href="bvg-1">BVG</a> vorgeschrieben. Über Teuerungszulagen an <a href="altersrenten">Altersrenten</a> hat der <a href="stiftungsrat">Stiftungsrat</a> zu beschliessen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(184, 1, 'Schlossänderungskosten', 'schlossanderungskosten', NULL, '<a href="mittelbarer-schaden">Mittelbarer Schaden</a> (Folgekosten) eines <a href="schaden">Schaden</a>ereignisses in der <a href="hausratversicherung">Hausratversicherung</a>, der dadurch entsteht, dass der Versicherte Schlösser (z.B. an der Wohnung) auswechseln muss (z. B. nach einem Einbruch). Die Hausratversicherung übernimmt diese Kosten bis zum vorgesehenen Betrag.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(185, 1, 'Kosten', 'kosten', NULL, 'Unter Kosten versteht man die effektiven Ausgaben, die einem <a href="versicherungsnehmer-in">Versicherungsnehmer</a> im Schadenfall durch zusätzliche Lebenshaltung, Notverglasung, Nottüre oder -schlösser, sowie Schlossänderungs- und Räumungsarbeiten entstehen. Die Erstattung der Kosten ist in der Regel summenmässig limitiert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(186, 1, 'Länderfonds', 'landerfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die ihr Vermögen hauptsächlich in <a href="aktie">Aktien</a> eines bestimmten Landes anlegen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(187, 1, 'Coupon', 'coupon-1', NULL, 'Der einem Wertpapier beigegebene Zins- oder Dividendenschein. Dieser Schein berechtigt den Besitzer, die fällig werdenden Erträge und verbrieften Anrechte geltend zu machen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(188, 1, 'Gericht', 'gericht', NULL, 'Staatliches Organ der Rechtsprechung, das über Konflikte oder Streitigkeiten zwischen zwei Vertragspartnern verbindlich entscheidet.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(189, 1, 'Deckung', 'deckung', NULL, 'Anspruch auf  <a href="versicherungsleistungen">Versicherungsleistung</a> besteht nur dann, wenn das <a href="schaden">Schaden</a>ereignis gedeckt bzw. die entsprechende <a href="gefahr">Gefahr</a> versichert ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(190, 1, 'Gefährdungshaftung', 'gefahrdungshaftung', NULL, 'Siehe <a href="kausalhaftung">Kausalhaftung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(191, 1, 'Bonität (Fonds)', 'bonitat-fonds', NULL, 'Die Qualität eines Schuldners insbesondere bezüglich seiner Zahlungsfähigkeit und seines Zahlungswillens. Die Bonität erlaubt Rückschlüsse auf die Qualität von <a href="obligationen">Obligationen</a> und die Wahrscheinlichkeit, dass die Zinsen regelmässig bezahlt und der Nominalbetrag bei Fälligkeit zurückbezahlt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(192, 1, 'Bonitätsrisiko', 'bonitatsrisiko', NULL, 'Risiko, dass der <a href="emittent">Emittent</a> von Wertschriften zahlungsunfähig wird. Siehe auch <a href="bonitat-fonds">Bonität</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(193, 1, 'Gläubiger', 'glaubiger', NULL, 'Als Gläubiger wird die Person bezeichnet, die eine Forderung gegenüber einer anderen Person (Schuldner) hat.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(194, 1, 'Bonität', 'bonitat', NULL, 'Sammelbegriff für verschiedene positiv zu wertende Eigenschaften (wie Zahlungswille, Moral, Charakter etc.) eines Bankkunden oder Anleihensschuldners, die auf seine Kreditwürdigkeit hinweisen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(195, 1, 'CSSF', 'cssf', NULL, 'Siehe <a href="commission-de-surveillance-du-sect-financier">Commission de Surveillance du Secteur Financier</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(196, 1, 'Kumulation', 'kumulation', NULL, 'Der <a href="anspruch">Anspruch</a> aus einer Personenversicherung kann mit Ansprüchen aus anderen Versicherungen, aus Haftpflicht oder sonstigen Rechtsverhältnissen nebeneinander geltend gemacht werden. Ein <a href="regress">Regress</a> des <a href="versicherer">Versicherers</a> gegen den haftpflichtigen Dritten ist ausgeschlossen. Ein Teil der Personenversicherung (Heilungskosten, schadenabhängiger Erwerbsersatz) wird allerdings der Schadenversicherung zugewiesen, wo das Entschädigungsprinzip herrscht (<a href="anspruchskonkurrenz">Anspruchskonkurrenz</a> und Regress des Versicherers).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(197, 1, 'Rechnungswährung', 'rechnungswahrung', NULL, 'Währung, in welcher die Buchhaltung des Fonds geführt wird und in welcher der <a href="nettoinventarwert">Nettoinventarwert</a> sowie die <a href="ausgabepreis">Ausgabe-</a> und <a href="rucknahmepreis">Rücknahmepreise</a> berechnet werden. Nicht zu verwechseln mit der <a href="anlagewahrung">Anlagewährung</a> und der <a href="referenzwahrung">Referenzwährung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(198, 1, 'Barwert', 'barwert', NULL, 'Der bei Vertragsabschluss mittels des <a href="technischer-zins">Technischen Zinssatzes</a> diskontierte Betrag sämtlicher künftiger Zahlungen des Kunden bzw. Leistungen des Versicherers.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(199, 1, 'Depotbank', 'depotbank', NULL, 'Bank, die das Vermögen eines Fonds aufbewahrt und für die Ausgabe und Rücknahme der Fondsanteile verantwortlich ist. Sie überwacht die Einhaltung der Bestimmungen des <a href="#invalid-reference-470">Anlagefondsgesetzes</a> und des Fondsprospektes durch die <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(200, 1, 'Depotführungspreis', 'depotfuhrungspreis', NULL, 'Droits prélevés annuellement pour la conservation et l''administration des valeurs mobilières.', '2016-08-29 10:21:09', '2016-08-29 10:22:12'),
(201, 1, 'Taggeld', 'taggeld', NULL, '<a href="versicherungsleistungen">Versicherungsleistung</a>, die im Falle einer <a href="arbeitsunfahigkeit">Arbeits-</a> oder <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a> erbracht wird. Man unterscheidet Unfalltaggeld und Krankentaggeld.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(202, 1, 'Taggeld (Private Unfallversicherung)', 'taggeld-private-unfallversicherung', NULL, 'Baustein der privaten Unfallversicherung, der den Ausfall des Erwerbseinkommens während einer unfallbedingten Arbeitsunfähigkeit kompensiert.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(203, 1, 'Spitaltaggeld (Private Unfallversicherung)', 'spitaltaggeld-private-unfallversicherung', NULL, 'Baustein der privaten Unfallversicherung, der Kosten für Mehrauslagen im Spital (z. B. Telefongebühren, Fernsehmiete) übernimmt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(204, 1, 'Krankentaggeldversicherung (nach KVG)', 'krankentaggeldversicherung-nach-kvg', NULL, 'Teil der Sozialversicherung, der mögliche Erwerbsausfälle während einer <a href="krankheit">krankheit</a>sbedingten <a href="arbeitsunfahigkeit">Arbeitsunfähigkeit</a> versichert. Die Versicherung ist freiwillig, ihre Ausgestaltung ist aber im KVG geregelt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(205, 1, 'Krankentaggeldversicherung (nach VVG)', 'krankentaggeldversicherung-nach-vvg', NULL, 'Freiwillige private <a href="personenversicherung">Personenversicherung</a>, die Erwerbsausfälle wegen <a href="krankheit">krankheit</a>sbedingter <a href="arbeitsunfahigkeit">Arbeitsunfähigkeit</a> versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(206, 1, 'Sengschaden', 'sengschaden', NULL, 'Zum Beispiel durch Funkenwurf des Cheminées oder glühende Zigarettenasche hervorgerufene Schäden (immer ohne Flamme).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(207, 1, 'Stromwirkungsschaden', 'stromwirkungsschaden', NULL, 'Ein Stromwirkungsschaden kann an elektrischen Apparaten durch Überspannung oder Kurzschluss entstehen. Er wird wie ein <a href="schaden">Schaden</a> durch ein <a href="nutzfeuer">Nutzfeuer</a> behandelt und ist in der Regel nicht versichert. Eine Ausnahme bildet der Blitzschlag.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(208, 1, 'Obhutsschäden', 'obhutsschaden', NULL, '<a href="schaden">Schäden</a> an fremden Sachen, die in der  Obhut des <a href="versicherter">Versicherten</a>sind, d.h. von ihm benutzt, aufbewahrt oder transportiert werden. Die <a href="privathaftpflichtversicherung">Privathaftpflichtversicherung</a> ersetzt den <a href="vermogensbedarf">Vermögensbedarf</a> des Geschädigten. In der  Betriebshaftpflichtversicherung besteht Deckung aufgrund besonderer Vereinbarung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(209, 1, 'Schaden', 'schaden', NULL, 'Schaden ist eine Vermögensverminderung, und zwar die Differenz zwischen dem gegenwärtigen Stand des Vermögens des <a href="geschadigte">Geschädigten</a> und dem Stand, den das Vermögen ohne das schädigende Ereignis aufwiese. <br />Grundsätzlich wird jeder Nachteil berücksichtigt, auch der entgangene Gewinn, also eine wegen des Schadens nicht eingetretene Vermögensvermehrung. Man unterscheidet zwischen Personen-, Sach- und Vermögensschaden.<br />Achtung das Strassenverkehrsgesetz unterscheidet nicht zwischen <a href="personenschaden">Personenschäden</a> und <a href="sachschaden">Sachschäden.</a> Im <a href="svg">SVG</a> werden Vermögensschäden, die als Folge von Personenschäden oder Sachschäden entstanden sind, zu den Personen- bzw. Sachschäden gezählt und dementsprechend entschädigt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(210, 1, 'Todesfallkapital (Private Unfallversicherung)', 'todesfallkapital-private-unfallversicherung', NULL, 'Baustein der privaten Unfallversicherung (<a href="summenversicherung">Summenversicherung</a>),  der Hinterbliebene im Todesfall finanziell entschädigt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(211, 1, 'Todesfallrisiko-Versicherung', 'todesfallrisiko-versicherung', NULL, 'Die Versicherungsgesellschaft zahlt das vereinbarte Kapital aus, falls der <a href="versicherter">Versicherte</a> vor dem vereinbarten <a href="endtermin">Endtermin</a> stirbt. Erlebt der Versicherte den Vertragsablauf, so werden keine Leistungen fällig. Da bei dieser Versicherung kein Sparprozess wie bei einer <a href="gemischte-versicherung">gemischten Versicherung</a> eingeschlossen ist, liegt die <a href="pramie">Prämie</a> entsprechend tiefer. Sie dient vor allem der Deckung der Todesfallzahlungen. Wenn das versicherte Kapital während der <a href="vertragsdauer">Vertragsdauer</a> gleich hoch bleibt, handelt es sich um eine Todesfall<a href="risikoversicherung">risiko-Versicherung</a>mit gleichbleibenden Kapital. Es ist dies die übliche Risiko-Versicherung; sie dient vorab dem Schutz der Familie beim Tod des Versicherten. Sie kann aber auch zur Sicherstellung einer Schuld eingesetzt werden. Wird die Schuld jährlich amortisiert, kann vereinbart werden, dass die Risikoabdeckung jährlich um einen gleichbleibenden Teil abnimmt. Bei der Todesfallrisiko-Versicherung mit abnehmenden Kapital vermindert sich das im ersten Versicherungsjahr vereinbarte Kapital um einen Bruchteil der Vertragsdauer. Bei Tod des Versicherten vor dem Endtermin wird das im Zeitpunkt des Todes noch versicherte Kapital an die <a href="begunstigte-r">Begünstigten</a> ausbezahlt. Erlebt der Versicherte den Ablauf der Versicherung, werden auch hier keine Leistungen fällig. Diese Art Risiko-Versicherung wird oft auch zur Abdeckung einer amortisierbaren zweiten Hypothek eingesetzt. Durch die jährliche Abnahme der versicherten Leistungen und des geringeren Risikos für die Gesellschaft ist die Prämie äusserst günstig. Die Todesfallrisiko-Versicherung mit abnehmenden Kapital eignet sich daher auch besonders gut für Personen, die kurzfristig einen sehr hohen Vorsorgeschutz brauchen, aber nur wenig Prämien aufwenden können. In Kombination mit einer <a href="gemischte-versicherung">gemischten Versicherung</a> kann ein bedarfsgerechter Vorsorgeschutz zu tragbaren Prämien und einer Erlebensfall-Leistung, die  die gesamten aufgewendeten Prämien zurückbringt, erreicht werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(212, 1, 'Obligation', 'obligation-1', NULL, 'Wertpapier, das eine Geldleistung zum Inhalt hat. Obligationen werden in der Regel zum Zweck der mittel- bis langfristigen Aufnahme von Kapital ausgegeben. Die Verzinsung ist während der Laufzeit gleich (es gibt aber einige wenige Ausnahmen) und richtet sich nach der Höhe des aktuellen Zinsniveaus auf dem Kapitalmarkt, der Laufzeit der Obligation und der <a href="bonitat">Bonität</a> des Schuldners. Das Wertpapier selbst wird Obligation genannt und verkörpert einen Teil der gesamten Kapitalaufnahme (Anleihe). Im generellen Sprachgebrauch versteht man unter Obligation auch eine Verpflichtung zwischen zwei oder mehreren Personen, wobei die eine Seite den Schuldner und die andere Seite den <a href="glaubiger">Gläubiger</a> darstellt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(213, 1, 'Räumungs- und Entsorgungskosten', 'raumungs-und-entsorgungskosten', NULL, '<a href="mittelbarer-schaden">Mittelbarer Schaden</a> (Folgekosten) eines <a href="schaden">Schaden</a>ereignisses, der durch Aufräum- und Entsorgungsarbeiten entsteht (z. B. nach einem Feuer, Einbruch oder Sturm). Die <a href="hausratversicherung">Hausratversicherung</a> / <a href="geschaftsversicherung">Geschäftsversicherung</a> deckt diese Kosten bis zu einem bestimmten Maximalbetrag.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(214, 1, 'Betreibungsrechtliches Privileg', 'betreibungsrechtliches-privileg', NULL, 'Die Vermögenswerte einer Lebensversicherung können in einem Betreibungsverfahren verpfändet bzw. im <a href="konkurs">Konkurs</a>fall zur Konkursmasse geschlagen werden. Hat der Versicherte aber seinen Ehegatten und/oder seine Kinder an erster Stelle begünstigt, kann die Lebensversicherung nicht betrieben werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(215, 1, 'Schuldverschreibungen', 'schuldverschreibungen', NULL, 'Siehe <a href="obligationen">Obligationen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(216, 1, 'Schuldnerrisiko', 'schuldnerrisiko', NULL, 'Siehe <a href="bonitatsrisiko">Bonitätsrisiko</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(217, 1, 'Abnehmende Versicherungssumme', 'abnehmende-versicherungssumme', NULL, 'Die <a href="versicherungssumme">Versicherungssumme</a> wird während der vereinbarten <a href="versicherungsdauer">Versicherungsdauer</a>schrittweise reduziert (jedes Jahr um einen gleich hohen Betrag). Entsprechend nimmt das Todesfallkapital ab, das dem Begünstigten im Todesfall des Versicherten ausbezahlt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(218, 1, 'Franchise', 'franchise', NULL, 'Jährlicher Betrag, den der Versicherte der <a href="obligatorische-krankenpflegeversicherung">obligatorischen Krankenpflegeversicherung</a> selbst an die Behandlungskosten bezahlen muss. Wer die gesetzliche Möglichkeit nutzt, die Franchise zu erhöhen, profitiert von tieferen Prämien. Siehe <a href="kostenbeteiligung">Kostenbeteiligung</a>, <a href="selbstbehalt">Selbstbehalt</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(219, 1, 'Aufschubszeit', 'aufschubszeit', NULL, 'Zeit vor der Fälligkeit einer anwartschaftlichen Leistung (z.B. Altersrente).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(220, 1, 'Aufgeschobene Rente', 'aufgeschobene-rente', NULL, 'Bei einer privaten <a href="altersrenten">Altersrente</a>nversicherung mit aufgeschobenem Beginn wird ein bestimmter (späterer) Zeitpunkt vereinbart, zu dem die Rentenleistung des Versicherers einsetzt. Der Versicherungsnehmer baut in der Zwischenzeit das notwendige <a href="rente">Rente</a>nkapital auf (in Form von Jahresprämien) oder er macht eine <a href="einmaleinlage-einmalpramie">Einmaleinlage</a> oder er kombiniert beide Möglichkeiten miteinander.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(221, 1, 'Beitragsprimat (BV)', 'beitragsprimat-bv', NULL, 'Prinzip der Leistungserbringung im Rahmen der <a href="berufliche-vorsorge">beruflichen Vorsorge</a>. Die Versicherungsleistungen werden aufgrund des individuell erreichten <a href="altersguthaben">Altersguthabens</a> (= verzinste Beträge) erbracht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(222, 1, 'Prämienprimat', 'pramienprimat', NULL, 'Beim Prämienprimat werden die versicherten Leistungen auf Grund der geleisteten Beiträge anhand der <a href="tarif">Tarife</a> errechnet. Die Beiträge müssen klar definiert sein, zum Beispiel in Prozenten des versicherten Lohnes oder seltener in absoluten Frankenbeträgen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(223, 1, 'Leistungsprimat', 'leistungsprimat', NULL, 'Bei <a href="vorsorgeeinrichtung">Vorsorgeeinrichtungen</a>, die nach Leistungsprimat aufgebaut sind, richtet sich die Höhe der einzelnen Beiträge nach den vorgesehenen Leistungen. Die Leistungen werden dabei in Prozenten des versicherten Lohnes festgesetzt oder seltener nach Versichertenkategorien in festen Beträgen. Die zu erhebenden Beiträge werden für die Leistungen hierauf individuell aufgrund der Tarife unter Berücksichtigung des Alters des Versicherten berechnet. Leistungsprimatkassen kennen Einkaufsgelder, Nachzahlungen bei Lohnerhöhungen oder höhere Durchschnittsprämien, da sich die Leistung meistens auf den letzten Lohn bemisst.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(224, 1, 'Invaliditätsgrad', 'invaliditatsgrad', NULL, 'Prozentzahl, die ausdrückt, um wie viel die wirtschaftliche Leistungsfähigkeit durch die <a href="invaliditat">Invalidität</a> eingeschränkt ist. Der Invaliditätsgrad entscheidet über die Bemessung der IV-<a href="rente">Rente</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(225, 1, 'Delta einer Option', 'delta-einer-option', NULL, 'Das Delta einer <a href="optionen">Option</a> ist eine Sensitivitätszahl, die aussagt, in welchem Ausmass eine Option an einer Bewegung des zu Grunde liegenden Basiswertes (z.B. SMI) teilnimmt. Hat eine Option auf eine Schweizer <a href="aktie">Aktie</a> ein Delta von 0,4 (oder 40%), so gewinnt sie 0,4 Franken für jeden Franken, den die Aktie steigt, und verliert 0,4 Franken für jeden Franken, den die Aktie fällt. Das Delta einer Option bleibt nur für sehr kleine Bewegungen des zu Grunde liegenden Basiswertes konstant und kann in jedem Falle nur indikativ sein, da es in seinem Wert vor allem vom zu Grunde liegenden Basiswert und der <a href="restlaufzeit">Restlaufzeit</a> der Option abhängt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(226, 1, 'Delta adjustiertes Exposure', 'delta-adjustiertes-exposure-1', NULL, 'Das Delta adjustierte Exposure in einem Basiswert (z.B. SMI) entspricht der Summe der gewichteten Deltas (siehe «<a href="delta-einer-option">Delta einer Option</a>») von allen einzelnen <a href="optionen">Optionen</a>, die in einem gesamten <a href="portefeuille">Portefeuille</a> sind, zuzüglich aller Aktienengagements. Diese Zahl sagt dann auch aus, um wie viel ein gesamtes Portefeuille an den zukünftigen Aktienmarktbewegungen teilnehmen wird. Hat ein Portefeuille momentan zum Beispiel ein Delta adjustiertes Aktienexposure von 30%, so partizipiert das ganze Portefeuille im Umfang von 30% an den Bewegungen des Aktienmarktes. Steigt der Aktienmarkt um 2%, so steigt das gesamte Portefeuille um 0,6% (30%*0,2% = 0,6%). Wie das Delta bleibt auch das Delta adjustierte Exposure nur für sehr kleine Bewegungen des zu Grunde liegenden Basiswertes konstant und kann in jedem Falle nur indikativ sein, da es in seinem Wert vor allem vom zu Grunde liegenden Basiswert und der <a href="restlaufzeit">Restlaufzeit</a> der Option abhängt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(227, 1, 'Abbruchwert', 'abbruchwert', NULL, 'Ersatzwert für ein Abbruchobjekt (besonderer Fall des Verkehrswerts).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(228, 1, 'Mutationsgewinne', 'mutationsgewinne', NULL, 'Unter Mutationsgewinnen versteht man die in der <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> bei Austritt von <a href="versicherter">Versicherten</a> frei werdenden <a href="deckungskapital">Deckungskapitalien.</a> Bei Vorsorgeeinrichtungen mit Kollektivversicherungen entspricht der Mutationsgewinn der Differenz zwischen dem von der Versicherungsgesellschaft vergüteten <a href="ruckkaufswert">Rückkaufswert</a> und der <a href="freizugigkeitsleistung">Freizügigkeitsleistung.</a> In einer reinen <a href="bvg-1">BVG</a>-Minimal-Vorsorgeeinrichtung fallen keine Mutationsgewinne an. Jeder Austretende nimmt sein gesamtes <a href="altersguthaben">Altersguthaben</a> mit.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(229, 1, 'Depot', 'depot', NULL, 'Bezeichnung für die bei einer Bank zur Verwahrung und Verwaltung eingereichten Wertpapiere. Beim geschlossenen Depot werden die Wertgegenstände verpackt und gegebenenfalls versiegelt zur Verwahrung übergeben. Diese Art eignet sich für Gegenstände, die keiner Verwaltung bedürfen (Schmuck, Dokumente, Münzen etc.). Das offene Depot eignet sich dort, wo Wertpapiere auch verwaltet sein müssen, wie Aktien, <a href="obligation-1">Obligationen</a> etc.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(230, 1, 'Passivgeschäft', 'passivgeschaft', NULL, 'Mit Passivgeschäft bezeichnet man die Geschäfte, mit denen sich die Banken Gelder beschaffen, um sie auf der Aktivseite in Form von Krediten wieder ausleihen zu können. Die wichtigsten Passivposten sind die Spareinlagen, <a href="festgeld">Festgelder</a>, <a href="kassenobligation">Kassenobligationen</a>, Pfandbriefe und Anleihensobligationen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(231, 1, 'Abschreibungsbetrag', 'abschreibungsbetrag', NULL, 'Betrag, um welchen der <a href="neuwert">Neuwert</a> eines Gegenstandes infolge Abnützung oder Alterung gekürzt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(232, 1, 'Wertminderung', 'wertminderung', NULL, 'Mit zunehmendem Alter und durch Abnützung vermindert sich der Wert eines versicherten Gegenstandes. Je nach Vertragsbestimmungen wird im Schadenfall der <a href="neuwert">Neuwert</a> um die Wertminderung gekürzt und somit nur noch der <a href="zeitwert">Zeitwert</a> entschädigt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(233, 1, 'Derivate', 'derivate', NULL, 'Finanzinstrumente, z.B. <a href="optionen">Optionen</a> und <a href="futures">Futures</a>, die von zu Grunde liegenden Basiswerten, häufig <a href="aktie">Aktien</a> oder Devisen, abgeleitet werden. Im Portfoliomanagement können Derivate u. a. dazu eingesetzt werden, das Risiko von Vermögenseinbussen zu verringern.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(234, 1, 'Begünstigungsklausel', 'begunstigungsklausel', NULL, 'Verfügung des <a href="versicherungsnehmer-in">Versicherungsnehmers</a>, an wen Todesfall- und allenfalls auch Erlebensfallleistungen auszurichten sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(235, 1, 'Schadenfeuer', 'schadenfeuer', NULL, 'Ein Schadenfeuer hat, im Gegensatz zum <a href="nutzfeuer">Nutzfeuer</a> oder <a href="sengschaden">Sengschaden</a>, seinen bestimmungsmässigen Herd verlassen, sich aus eigener Kraft weiterverbreitet und dadurch <a href="schaden">Schaden</a> angerichtet. (z.B. ein auf den Teppich vor dem Cheminée übergreifendes Feuer, eine den Vorhang in Brand setzende Kerzenflamme, etc.)', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(236, 1, 'Direkter Schaden', 'direkter-schaden', NULL, 'Unmittelbarer <a href="schaden">Schaden</a> an einer versicherten Sache.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(237, 1, 'Direct-Mail', 'direct-mail-1', NULL, 'Bestandteil des direkten <a href="absatzkanal">Absatzkanals</a>. Beinhaltet die Kontaktaufnahme mit Kunden und den Verkauf von <a href="versicherung">Versicherungen</a> per Brief.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(238, 1, 'Direkte Rendite', 'direkte-rendite', NULL, 'Ausschüttung pro Anteil in Prozent des Börsenkurses.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(239, 1, 'Erwerbsausfallversicherung', 'erwerbsausfallversicherung', NULL, 'Die Erwerbsausfallversicherung erbringt Leistungen bei <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a> zufolge Krankheit, Unfall oder Gebrechen der <a href="versicherte-person">versicherten Person</a> in Form von <a href="pramienbefreiung">Prämienbefreiung</a>, <a href="rente">Renten</a> oder Kapitalien. Die Prämienbefreiung bei Erwerbsunfähigkeit bewirkt je nach dem Grad der Erwerbsunfähigkeit die volle oder teilweise Befreiung von der Prämienzahlungspflicht. Die versicherten Vorsorgeleistungen bleiben dabei in vollem Umfang erhalten. Die Leistungen beginnen nach Ablauf der vertraglichen <a href="wartefrist">Wartefrist</a> und dauern solange die Erwerbsunfähigkeit besteht, längstens bis zum Vertragsende. Da sich die Zeit der Erwerbstätigkeit am <a href="ahv-1">AHV</a>-Alter misst, wird die Prämienbefreiung in der Regel längstens bis zum <a href="tarifalter">Tarifalter</a>65 bei Männern und 62 bei Frauen gewährt. Die Erwerbsausfall-Renten sichern bei Erwerbsunfähigkeit der versicherten Person ein Ersatzeinkommen. Auch hier ist die Leistungshöhe abhängig vom Grad der Erwerbsunfähigkeit. Die Wartefrist, ab welcher die Versicherungsgesellschaft leistungspflichtig wird, kann frei gewählt werden, z.B. zwischen 30 Tagen und 2 Jahren, damit der persönlichen Situation des Versicherten Rechnung getragen werden kann. Die Rente wird bezahlt, solange die versicherte Person erwerbsunfähig ist, längstens bis zum vereinbarten Endtermin. Das normalerweise maximale Tarifalter beträgt auch hier 65 bzw. 62 Jahre. Beim Erwerbsausfall-Kapital besteht die Leistung in Form einer einmaligen Kapitalzahlung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(240, 1, 'Invaliditätskapital', 'invaliditatskapital', NULL, 'Versicherungsleistung in Form einer einmaligen Kapitalabfindung bei Erwerbsunfähigkeit einer Person.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(241, 1, 'Invaliditätskapital (Private Unfallversicherung)', 'invaliditatskapital-private-unfallversicherung', NULL, 'Baustein der privaten <a href="unfallversicherung">Unfallversicherung</a> (<a href="summenversicherung">Summenversicherung</a>), der eine bleibende Beeinträchtigung der Gesundheit finanziell absichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(242, 1, 'Invalidität', 'invaliditat', NULL, 'Invalidität wird vielfach gleichgesetzt mit Erwerbsunfähigkeit. Im engeren Sinne bedeutet Invalidität jedoch eine dauernde Beeinträchtigung der Gesundheit (teilweise oder gänzlich) zufolge Krankheit oder <a href="unfall">Unfall.</a> Ein Erwerbsausfall muss nicht unbedingt damit verbunden sein. In der Lebensversicherung unterscheidet man zwischen dauernder und vorübergehender <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit.</a> Eine Leistungspflicht der Gesellschaft liegt nur dann vor, wenn zufolge der Krankheit oder des Unfalls tatsächlich eine Einkommensminderung resultiert und die <a href="versicherte-person">versicherte Person</a> ihren Beruf oder eine andere zumutbare Tätigkeit nicht mehr ausüben kann.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(243, 1, 'Erwerbsunfähigkeit', 'erwerbsunfahigkeit', NULL, 'Erwerbsunfähigkeit nach den Regeln der Lebensversicherungsgesellschaften liegt vor, wenn die<a href="versicherte-person">versicherte Person</a> wegen Krankheit, Unfall oder eines Gebrechens vorübergehend oder dauernd nicht mehr imstande ist, ihren angestammten Beruf oder eine andere ihr zumutbare Tätigkeit auszuüben. Als zumutbar ist dabei eine Tätigkeit anzusehen, die den Kenntnissen und Fähigkeiten sowie der bisherigen Lebensstellung des <a href="versicherter">Versicherten</a> entspricht. Bei den <a href="erwerbsausfallversicherung">Erwerbsausfallversicherungen</a> wird ein Leistungsanspruch davon abhängig gemacht, dass tatsächlich ein Einkommensausfall aus Erwerbsunfähigkeit eintritt. Der Grad der Erwerbsunfähigkeit wird bei Erwerbstätigen anhand des effektiven Verdienstausfalls berechnet. Dabei wird das Erwerbseinkommen der versicherten Person vor Eintritt der Erwerbsunfähigkeit mit demjenigen Einkommen verglichen, das diese nach Eintritt der Erwerbsunfähigkeit in ihrem Beruf oder einer anderen zumutbaren Erwerbstätigkeit noch erzielt oder allenfalls erzielen könnte. Die Differenz, in Prozenten des Einkommens ausgedrückt, ergibt den Grad der Erwerbsunfähigkeit. Für Nichterwerbstätige (z.B. Hausfrauen und Jugendliche) wird das Ausmass der Einschränkung im Aufgaben- und Tätigkeitsbereich der versicherten Person herangezogen. Insbesondere unselbständig Erwerbende müssen darauf achten, dass durch entsprechende <a href="wartefrist">Wartefristen</a> bei Erwerbsausfall-Renten Lohnzahlungspflicht (gesetzliche oder vertragliche) und Beginn der Rentenzahlung koordiniert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(244, 1, 'Disagio', 'disagio', NULL, 'Bei <a href="immobilienfonds-liegenschaftsfonds">Immobilienfonds</a> die prozentuale Differenz zwischen dem Börsenkurs und dem (höheren) <a href="nettoinventarwert">Nettoinventarwert</a> der Anteile. Gegenteil: <a href="agio-1">Agio</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(245, 1, 'Diskontpolitik', 'diskontpolitik', NULL, 'Die Diskontpolitik ist eines der klassischen geldpolitischen Instrumente. Eine Bank, die Geld benötigt, kann der <a href="nationalbank">Nationalbank</a> erstklassige Wechsel verkaufen, d.h. diskontieren lassen. Für die Zeit zwischen dem Diskont- und dem Fälligkeitstag verrechnet die Notenbank einen Zins, den sogenannten Diskontsatz. (Genau genommen handelt es sich dabei um ein Rediskontgeschäft, da die Banken ihre Wechsel bereits Privaten gegenüber diskontiert haben.)<br />Durch Anhebung des Diskontsatzes verteuert sich für die Banken der Wechselkredit, was tendenziell die Nachfrage nach Wechseln und mithin die Geldmenge verringert. In umgekehrter Richtung wirkt eine Senkung des Diskontsatzes.<br />Zur Zeit der Gründung der Schweizerischen Nationalbank und in den folgenden Jahrzehnten spielte die Diskontpolitik eine relativ wichtige Rolle. Als Folge der schwindenden Bedeutung des Wechsels als Finanzierungsmittel ist auch die Diskontpolitik in neuerer Zeit stark in den Hintergrund gerückt. Diskontsatzänderungen haben aber oft so etwas wie eine Signalwirkung; die Zentralbank (Nationalbank) zeigt z.B. an, dass sie eine restriktivere Geldpolitik zu führen gedenkt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(246, 1, 'Pfändbarkeit', 'pfandbarkeit', NULL, 'Lebensversicherungspolicen der <a href="freie-vorsorge">Säule 3b</a> unterliegen grundsätzlich der Zwangsvollstreckung. Sie können somit gepfändet oder im Konkurs zur Konkursmasse gezählt werden. Sind Ehegatte oder Kinder an erster Stelle begünstigt, ist die Zwangsvollstreckung des Versicherungsanspruches nicht möglich (<a href="konkursprivileg">Konkursprivileg</a>).<br />\r<br />Vorsorgeleistungen der <a href="gebundene-vorsorge">Säule 3a</a> und der 2. Säule (<a href="bvg-1">BVG</a>) sind vor ihrer Fälligkeit nicht pfändbar, ausser es liegt ein Barauszahlungsbegehren und ein Barauszahlungsgrund vor. Nach Eintritt der Fälligkeit ist eine beschränkte Pfändbarkeit möglich, und zwar nach Ermessen des Betreibungbeamten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(247, 1, 'Pfändung', 'pfandung', NULL, 'Amtliche Beschlagnahme einzelner Vermögenswerte des Schuldners aufgrund eines rechtskräftigen Zahlungsbefehls, um einen Anspruch des <a href="glaubiger">Gläubigers</a> zu erfüllen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(248, 1, 'Zwangsverwertung', 'zwangsverwertung', NULL, 'Bei Konkurs/Pfändung, nicht Verpfändung. Es handelt sich dabei um eine Verwertung, die nicht mehr im Machtbereich des Eigentümers der zu verwertenden Sache, sondern im Machtbereich des Staates liegt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(249, 1, 'Ausschüttung', 'ausschuttung', NULL, 'Die jährliche Auszahlung der vom Fonds erzielten <a href="ertrag">Erträge</a> an die Anteilseigner. Gegenteil: <a href="thesaurierung">Thesaurierung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(250, 1, 'Ausschüttender Fonds', 'ausschuttender-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, welcher die erwirtschafteten <a href="ertrag">Erträge</a> an seine Anteilinhaber ausschüttet. Gegenteil: <a href="thesaurierender-fonds">Thesaurierender Fonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(251, 1, 'Diversifikation (Risikostreuung)', 'diversifikation-risikostreuung', NULL, 'Die Verteilung oder Streuung der Anlagen auf eine Vielzahl verschiedener Einzeltitel, Branchen, Länder und Währungen. Die Diversifikation oder Risikostreuung gilt in der modernen <a href="portfoliotheorie">Portfoliotheorie</a> als entscheidender Faktor der Risikoreduktion: Durch die systematische Verteilung der Anlagen auf eine Vielzahl von Titeln streut man das <a href="risiko-fonds">Risiko</a>, sodass das Gesamtrisiko eines <a href="portefeuille">Portefeuilles</a> deutlich unter demjenigen der einzelnen Titel zu liegen kommt. Eine breite Diversifikation mit dutzenden oder hunderten von Einzeltiteln ist nur mit einem substanziellen Anlagevermögen oder mit <a href="anlagefonds">Anlagefonds</a> möglich.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(252, 1, 'Dividende', 'dividende', NULL, 'Auf <a href="aktie">Aktien</a>, Partizipationsscheinen, Genossenschaftsanteilen oder Genussscheinen ausgeschütteter Anteil am Reingewinn einer Gesellschaft. Diese Art von Wertpapieren wird daher oft auch als "Dividendenpapier" bezeichnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(253, 1, 'Inlandanleihen', 'inlandanleihen', NULL, '<a href="obligationen">Obligationen</a> einheimischer Schuldner in der eigenen Währung auf dem Heimmarkt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(254, 1, 'Doppelversicherung', 'doppelversicherung', NULL, 'Doppelversicherung besteht, wenn dieselbe Sache gegen ein bestimmtes <a href="risiko">Risiko</a> gleichzeitig bei mehr als einer Versicherungsgesellschaft versichert ist und die <a href="versicherungssumme">Versicherungssumme</a> den effektiven Wert (<a href="ersatzwert">Ersatzwert</a>) übersteigt. Häufig verspricht sich der <a href="versicherter">Versicherte</a> davon eine mehrfache Entschädigung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(255, 1, 'Doppelbesteuerungsabkommen (DBA)', 'doppelbesteuerungsabkommen-dba', NULL, 'Trattati internazionali stipulati dalla Svizzera con altri stati al fine di evitare o mitigare eventuali doppie imposizioni fiscali. Si parla di doppia imposizione quando un contribuente è tassato da due differenti stati per il medesimo oggetto fiscale risp. per il medesimo fatto di rilevanza fiscale. Una CDI può p.e. avere come conseguenza che una trattenuta effettuata in Svizzera a titolo di imposta preventiva sia computata in parte o interamente a favore dell''investitore domiciliato all''estero (domicilio fiscale) con la sua dichiarazione d''imposta.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(256, 1, 'Dow Jones Index', 'dow-jones-index-1', NULL, 'Ältester und bekanntester <a href="aktienindex">Aktienindex</a> der New Yorker Effektenbörse, der den Durchschnittspreis einer Anzahl Aktien wiedergibt. Er wird gesondert für Aktien der Industrie (30 Unternehmungen), der Transportwerte (20 Unternehmungen) und der Versorgungswerte (15 Unternehmungen) berechnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(257, 1, 'Lenker', 'lenker', NULL, 'Lenker ist, wer ein <a href="motorfahrzeug">Motorfahrzeug</a> führt. Er kann gleichzeitig <a href="halter">Halter</a> sein oder auch nicht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(258, 1, 'Fälligkeit', 'falligkeit', NULL, 'Fällig wird eine Forderung mit dem Zeitpunkt, von dem an der <a href="glaubiger">Gläubiger</a> die Leistung fordern und einklagen kann.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(259, 1, 'Duration', 'duration', NULL, 'Die Duration bezeichnet die durchschnittliche Zeitdauer der Kapitalbindung einer <a href="obligationen">Obligation</a>. Im Gegensatz zur <a href="restlaufzeit">Restlaufzeit</a> wird beim Konzept der Duration auch die zeitliche Struktur der Kapitalrückflüsse (z.B. Couponsrückzahlungen) berücksichtigt. Die durchschnittliche Duration des <a href="portefeuille">Portefeuilles</a> ergibt sich aus dem gewichteten Mittel der Duration der einzelnen Titel. Die «modifizierte Duration» wird von der Duration abgeleitet und stellt ein Risikomass dar, mit dem die Sensitivität von Obligationen und Obligationenportefeuilles auf Zinssatzveränderungen geschätzt werden kann. So führt die Erhöhung (Senkung) des Zinsniveaus um 1% etwa zu einem prozentualen Rückgang (Anstieg) des Kurses im Ausmass der modifizierten Duration. Beispiel: Die modifizierte Duration eines <a href="obligationenfonds">Obligationenfonds</a> beträgt 4,5 Jahre, die theoretische Verfallrendite 5,3%. Fällt die Rendite um 1 auf 4,3%, so steigt der Fondskurs um rund 4,5%. Bei Obligationen- und <a href="performance">Strategiefonds</a> wird die Duration auf allen Festverzinslichen angegeben. In der Fachliteratur findet man die Angabe der «modifizierten Duration» sowohl als Faktor wie auch in Jahren.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(260, 1, 'Vertragsdauer', 'vertragsdauer', NULL, 'Période qui s''étend de la conclusion à la cessation du contrat.', '2016-08-29 10:21:09', '2016-08-29 10:22:12'),
(261, 1, 'Versicherungsdauer', 'versicherungsdauer', NULL, 'Die Zeitspanne, während welcher der <a href="versicherungsschutz">Versicherungsschutz</a> wirksam ist. Sie wird in der Regel in mehrere Versicherungsperioden unterteilt. Die Versicherungsdauer kann mit der <a href="vertragsdauer">Vertragsdauer</a> zusammenfallen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(262, 1, 'Mitwirkungspflicht', 'mitwirkungspflicht', NULL, 'Gesetzliche Obliegenheit des <a href="anspruchsberechtigter">Anspruchsberechtigten</a>, dem <a href="versicherer">Versicherer</a> auf Begehren jede Auskunft über solche ihm bekannte Tatsachen zu erteilen, die zur Ermittlung der Umstände des Schadenereignisses oder zur Feststellung der Folgen dienlich sind. Vertraglich können bestimmte Handlungen zur Pflicht erklärt werden (<a href="vvg-1">VVG</a> 39).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(263, 1, 'Anzeigepflicht', 'anzeigepflicht', NULL, 'Offenlegungspflicht von <a href="gefahrstatsachen">Gefahrstatsachen</a> des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> bei Vertragsabschluß; unverzügliche Meldepflicht des <a href="anspruchsberechtigter">Anspruchsberechtigten</a> bei Eintreten des versicherten Ereignisses.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(264, 1, 'Rettungspflicht', 'rettungspflicht', NULL, 'Nach Eintritt des befürchteten Ereignisses hat der <a href="anspruchsberechtigter">Anspruchsberechtigte</a> gemäss <a href="vvg-1">VVG</a> 61 dafür zu sorgen, dass sich der <a href="schaden">Schaden</a> mindert oder nicht vergrössert. Er hat dabei die Weisungen des <a href="versicherer">Versicherers</a> einzuholen und zu befolgen. Hat der Anspruchsberechtigte diese Pflichten schuldhaft verletzt, darf der Versicherer die Entschädigung um den Betrag kürzen, als sich der Schaden wegen Missachtung der Rettungspflicht nicht vermindert oder vergrössert hat.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(265, 1, 'Sorgfaltspflicht', 'sorgfaltspflicht', NULL, 'Unser Zivilrecht geht davon aus, dass jeder von seinen Mitmenschen ein bestimmtes Mass von Sorgsamkeit und Aufmerksamkeit erwarten darf. So ist jeder <a href="versicherungsnehmer-in">Versicherungsnehmer</a> zur Sorgfalt verpflichtet. Die schuldhafte Verletzung von selbstverständlichen Schadenverhütungsregeln kann zu einer Herabsetzung der <a href="versicherungsleistungen">Versicherungsleistung</a> führen. Richtet jemand einen <a href="schaden">Schaden</a> an, weil er die durchschnittliche Sorgfalt nicht aufgewendet hat, so spricht man von einer Sorgfaltspflichtverletzung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(266, 1, 'Informationspflicht', 'informationspflicht', NULL, 'Pflicht des <a href="versicherer">Versicherers</a> zur Übergabe von <a href="avb">AVB</a> und weiterer Informationen ("Verbraucherinformationen") bei Vertragsabschluss.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(267, 1, 'Dynamic Floor Funds', 'dynamic-floor-funds-1', NULL, 'Siehe <a href="absicherungsfonds">Absicherungsfonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(268, 1, 'SB', 'sb', NULL, 'Abkürzung für "<a href="selbstbehalt">Selbstbehalt</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(269, 1, 'Vermögensschäden', 'vermogensschaden', NULL, 'Ein Vermögensschaden ist weder <a href="sachschaden">Sachschaden</a> noch <a href="personenschaden">Personenschaden</a>, sondern ein <a href="schaden">Schaden</a> der durch die Einbusse von Einkommen oder Vermögen entsteht (Lohnausfall, entgangener Gewinn, Einkommenseinbusse, Umsatzverlust). Er kann auch als Folge eines Personen- oder Sachschadens auftreten. Gemäss <a href="svg">SVG</a> ist er nur dann versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(270, 1, 'ECU', 'ecu', NULL, 'Abkürzung für "European Currency Unit" (Europäische Währungseinheit). Mit der Gründung des Europäischen Währungssystems wurde der ECU zur offiziellen Rechnungseinheit erklärt. Er entspricht einem Wert, der sich aus der Mischrechnung aller Währungen, die in der EU vertreten sind, zusammensetzt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(271, 1, 'Effizienzgrenze', 'effizienzgrenze', NULL, 'Die moderne Finanztheorie beurteilt den Zusammenhang zwischen <a href="ertrag">Ertrag</a> und <a href="risiko-fonds">Risiko</a> als zweidimensionale Betrachtung von <a href="performance">Rendite</a> und <a href="volatilitat">Volatilität</a>. Die Rendite eines <a href="portefeuille">Portfolios</a> entspricht dem gewichteten Durchschnitt der Renditen der einzelnen Wertpapiere. Die Volatilität des Portfolios jedoch errechnet sich nicht als gewichteter Durchschnitt der Einzelvolatilitäten, da die Bewertungsverläufe der einzelnen Wertpapiere nicht perfekt korrelieren. Durch <a href="diversifikation-risikostreuung">Diversifikation</a> kann der <a href="anleger">Anleger</a> also Risiko reduzieren, ohne dafür Rendite aufgeben zu müssen. Wegen der tiefen <a href="korrelation">Korrelation</a> von alternativen und traditionellen Anlagen kann ein bestehendes Portfolio durch Beimischung alternativer Anlagen ein günstigeres Risiko-Rendite-Profil erzielen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(272, 1, 'Elektron. Datenverabeitungsanlagen-Vers. (EDVA)', 'elektron-datenverabeitungsanlagen-vers-edva', NULL, '<a href="technische-versicherungen">Technische Versicherung</a> für EDV-Anlagen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(273, 1, 'ELG', 'elg', NULL, 'Abkürzung für "Bundesgesetz über Ergänzungsleistungen zur <a href="ahv-1">AHV</a> / <a href="iv">IV</a>" vom 19. März 1965.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(274, 1, 'Auswärts', 'auswarts', NULL, 'Auswärts bedeutet, dass sich ein Schaden nicht an dem in der <a href="police">Police</a> aufgeführten Ort (Grundstück, Gebäude) ereignet. Das kann direkt vor dem Eingang, an einem anderen Ort innerhalb der Schweiz oder im Ausland sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(275, 1, 'Notverglasung', 'notverglasung', NULL, '<a href="mittelbarer-schaden">Mittelbarer Schaden</a> (Folgekosten) eines <a href="schaden">Schaden</a>ereignisses in der <a href="hausratversicherung">Hausratversicherung</a> und Geschäftsversicherung, der dadurch entsteht, dass eine versicherte Gebäudeverglasung notfallmässig repariert werden muss. Die Hausratversicherung / <a href="geschaftsversicherung">Geschäftsversicherung</a> übernimmt diese Kosten bis zum vorgesehenen Betrag.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(276, 1, 'Emerging Economies Bonds', 'emerging-economies-bonds-1', NULL, '<a href="obligationen">Obligationen</a> aus Lateinamerika, Osteuropa und Asien, die hohe <a href="performance">Renditen</a> in Aussicht stellen, auf Grund der politischen oder wirtschaftlichen Unsicherheit in diesen Ländern jedoch mit einem grösseren <a href="risiko-fonds">Risiko</a> behaftet sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(277, 1, 'Emerging Markets', 'emerging-markets-1', NULL, 'Gli emerging markets o mercati emergenti sono Paesi  soprattutto di Asia, Europa orientale e America Latina  in rapido sviluppo, ma con un''economia e una piazza finanziaria ancora al di sotto degli standard occidentali.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(278, 1, 'Emerging-Market-Fonds', 'emerging-market-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in Schwellenländern investieren, wie z.B. in Asien oder Lateinamerika. Siehe auch <a href="emerging-markets-1">Emerging Markets</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(279, 1, 'Vermittlungsfähigkeit', 'vermittlungsfahigkeit', NULL, 'Eine Voraussetzung für eine <a href="arbeitslosenentschadigung">Arbeitslosenentschädigung</a>. Arbeitslos ist nur jemand, der in der Lage und bereit ist, eine zumutbare Stelle anzutreten. Vermittlungsfähigkeit bedeutet also Arbeitsfähigkeit und Arbeitswilligkeit.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(280, 1, 'Arbeitgeber', 'arbeitgeber', NULL, 'Schweizer Arbeitgeber sind gesetzlich verpflichtet, Versicherungsbeiträge für die <a href="erste-saule">Erste</a> und <a href="zweite-saule">Zweite Säule</a> zu entrichten. Die auf einen Arbeitnehmer entfallenden AHV/IV/EO-Beiträge müssen zur Hälfte vom Arbeitgeber getragen werden. Das BVG verlangt, dass der Arbeitgeber mindestens gleich viele Beiträge leistet wie alle seine Mitarbeiter zusammen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(281, 1, 'Erwerbseinkommen', 'erwerbseinkommen', NULL, 'Einkommen aus selbstständiger oder unselbstständiger Arbeit (in Form von Geld, Natural- oder Dienstleistungen).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(282, 1, 'Lastenrechnung', 'lastenrechnung', NULL, 'Gli oneri di un immobile imputabili al tasso del capitale investito, alle imposte, alle spese d''esercizio, ai premi assicurativi, ai costi di manutenzione, all''ammortamento dello stabile, all''assicurazione sulla perdita delle pigioni e ai costi amministrativi. L''onerosità dei singoli oggetti può presentare anche forti divergenze, ad esempio a causa dell''età della struttura e i relativi costi di manutenzione, del tipo di edificio (stabile ad uso abitativo o ufficio) e della pressione fiscale nel luogo in cui è ubicato l''immobile.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(283, 1, 'Indossament', 'indossament', NULL, 'Schriftliche und unterzeichnete Erklärung auf einem <a href="orderpapier">Orderpapier</a>, womit der Inhaber alle Eigentumsrechte an dieser Urkunde überträgt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(284, 1, 'Gemischte Versicherung', 'gemischte-versicherung', NULL, 'Die gemischte Versicherung kann als die klassischste Form der Lebensversicherung angesehen werden. Im Gegensatz zur reinen <a href="todesfallrisiko-versicherung">Todesfallrisiko-Versicherung</a>wird bei der gemischten Versicherung nicht nur bei vorzeitigem Tod, sondern auch bei Erleben des <a href="endtermin">Endtermins</a> eine Leistung erbracht. Die <a href="pramie">Prämie</a> für eine gemischte Versicherung enthält neben dem Risiko- und Kostenteil im wesentlichen einen Sparteil. Der Sparteil der Prämie wird zum <a href="technischer-zins">technischen Zins</a>angelegt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(285, 1, 'Unternehmerisches Risiko', 'unternehmerisches-risiko', NULL, 'Nicht versicherbares Risiko für den Misserfolg der Geschäftsaktivitäten. Das Unternehmen muss es selbst tragen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(286, 1, 'Anvertraute Sache (geleaste oder gemietete)', 'anvertraute-sache-geleaste-oder-gemietete', NULL, 'Sachen, die nicht im Eigentum einer Person stehen, sondern die sie bloss besitzt, indem sie ihr von anderen Personen zur Verfügung gestellt werden. Die <a href="hausratversicherung">Hausratversicherung</a> versichert diese Gegenstände.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(287, 1, 'Eintrittsgeneration', 'eintrittsgeneration', NULL, 'Der Eintrittsgeneration gehören die Personen an, die bei Inkrafttreten des <a href="bvg-1">BVG</a> (1. Januar 1985) das 25. Altersjahr vollendet und das Rentenalter noch nicht erreicht hatten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(288, 1, 'Eintrittsschwelle (BV)', 'eintrittsschwelle-bv', NULL, 'Untere Einkommensgrenze, die massgeblich dafür ist, ob ein Arbeitnehmer im Rahmen der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> obligatorisch versichert werden muss oder nicht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(289, 1, 'Ökoeffizienz', 'okoeffizienz', NULL, 'I principali indicatori dell''eco-efficienza sono il consumo di energia e di risorse. Le attività eco-efficienti producono non solo vantaggi ambientali, ma anche benefici economici grazie a risparmi mirati.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(290, 1, 'Ökofonds', 'okofonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die beim Investitionsentscheid ökologische Kriterien miteinbeziehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(291, 1, 'EO', 'eo', NULL, 'Abkürzung für<a href="erwerbsersatzordnung">"Erwerbsersatzordnung"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(292, 1, 'EOG', 'eog', NULL, 'Abkürzung für "Bundesgesetz über die Erwerbsersatzordnung" für Wehr- und Zivilschutzpflichtige vom 3.Oktober.1975.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(293, 1, 'Epidemieversicherung', 'epidemieversicherung', NULL, '<a href="vermogensversicherung">Vermögensversicherung</a>, die die finanziellen Folgen (Betriebsunterbrechung, Ertragsausfälle, zusätzlicher Vermögensbedarf) beim Ausbruch einer Epidemie (Seuche) versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(294, 1, 'Betriebseinrichtung', 'betriebseinrichtung', NULL, 'Gegenstände, die für die Erstellung von Produkten oder Dienstleistungen benötigt werden. Sie gehören zu den versicherten Gegenständen der <a href="geschaftsversicherung">Geschäftsversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(295, 1, 'Aktie', 'aktie', NULL, 'Titolo che rappresenta una quota di capitale di una società anonima. In qualità di partecipante, l''azionista possiede diritti societari (di voto e d''informazione) e patrimoniali (diritto a una quota degli utili e all''opzione di acquisto di azioni).', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(296, 1, 'Aktienfonds', 'aktienfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die ihr Vermögen hauptsächlich in <a href="aktie">Aktien</a> investieren. Wichtigste Kategorien: <a href="landerfonds">Länder-</a> und <a href="regionenfonds">Regionenfonds</a>, <a href="emerging-market-fonds">Emerging-Market-Fonds</a>, <a href="nebenwertefonds">Nebenwertefonds</a>, <a href="branchenfonds-sektorenfonds">Branchen-</a> und <a href="themenfonds">Themenfonds</a>, <a href="indexfonds">Indexfonds</a>. Auch Equity Funds genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(297, 1, 'Equity Hedged', 'equity-hedged-1', NULL, 'Auf <a href="aktie">Aktien</a> beruhende Strategien mit <a href="long-short-1">Long- und Shortpositionen</a> (Gewichtverteilung zwischen Long- und Shortpositionen kann von Strategie zu Strategie erheblich variieren). Absicherung durch <a href="leerverkauf">Leerverkäufe</a>, <a href="optionen">Optionen</a> und <a href="futures">Futures</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(298, 1, 'Montageversicherung', 'montageversicherung', NULL, '<a href="technische-versicherungen">Technische Versicherung</a>, die Maschinen, Anlagen, Installationen oder Apparate eines Unternehmens für die besonderen <a href="gefahr">Gefahren</a> während der Montage / Demontage und Probebetrieb versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(299, 1, 'Eurobonds', 'eurobonds-1', NULL, 'Auf dem <a href="euromarkt">Euromarkt</a> ausgegebene, quellensteuerfreie <a href="obligationen">Obligationen</a>. Zentrum des Eurobond-Handels ist aus steuerlichen Gründen London. Siehe auch <a href="auslandsanleihen">Auslandsanleihen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(300, 1, 'Euromarkt', 'euromarkt', NULL, 'Désignations des marchés monétaire et  obligataire où sont négociés des devises et des titres en dehors de leurs pays d''origine respectifs.', '2016-08-29 10:21:09', '2016-08-29 10:22:12'),
(301, 1, 'Event-Driven', 'event-driven-1', NULL, 'Bei Event-Driven-Strategien werden Informationen über angekündigte Ereignisse schnell und vor allem Gewinn bringend eingesetzt. Bei dieser Strategie geht man davon aus, dass die Märkte Informationen zu optimistisch oder zu pessimistisch bewerten. Profundes Wissen und Erfahrung bei der Bewertung von Wertpapieren und der Zugang zu aktuellen Informationen sind bei Event-Driven-Strategien der Erfolgsfaktor Nummer Eins.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(302, 1, 'Versicherungsbescheinigung', 'versicherungsbescheinigung', NULL, 'siehe <a href="versicherungsnachweis">Versicherungsnachweis.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(303, 1, 'Selbstbehalt', 'selbstbehalt', NULL, 'Bei verschiedenen Versicherungen trägt der <a href="versicherter">Versicherte</a> einen Teil des <a href="schaden">Schadens</a>  selbst, sei es in Form eines Prozentsatzes oder eines fixen Betrages. Mit einem Selbstbehalt (SB) wird vermieden, dass viele Kleinschäden angemeldet werden.<br />Andere Ausdrücke für Selbstbehalt sind die Begriffe "Selbstbeteiligung" und "Franchise".', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(304, 1, 'Schadenexzedenten-Rückversicherung', 'schadenexzedenten-ruckversicherung', NULL, 'Schadenexzedenten-Rückversicherung ist nicht-proportionale <a href="ruckversicherung">Rückversicherung</a>. <br />\r<br />Die Schadenexzedenten-Rückversicherung pro Risiko stellt auf das einzelne versicherte Risiko (im Sinne des Versicherungsgegenstands) ab. Der Rückversicherer übernimmt pro versichertem Risiko bis maximal zur Deckungsobergrenze die Teile eines <a href="schaden">Schadens</a>, die die Priorität übersteigen.<br />\r<br />Die Schadenexzedenten-Rückversicherung pro Ereignis stellt auf das versicherte Ereignis ab. Der Rückversicherer übernimmt pro eingetretenem Ereignis bis maximal zur Deckungsobergrenze die Teile eines Schadens, die die Priorität übersteigen.<br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(305, 1, 'Wechselkursrisiko', 'wechselkursrisiko', NULL, 'Risiko von Kursschwankungen zwischen <a href="rechnungswahrung">Rechnungswährung</a> und <a href="referenzwahrung">Referenzwährung</a> des <a href="anleger">Anlegers</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(306, 1, 'Ausschluss', 'ausschluss', NULL, 'Nach dem Grundsatz "Was nicht ausdrücklich ausgeschlossen ist, gilt in der <a href="versicherung">Versicherung</a> als eingeschlossen" werden Ausschlüsse im <a href="versicherungsvertrag">Versicherungsvertrag</a> möglichst detailliert aufgeführt, um im Versicherungsfall jeden Zweifel an der Leistungspflicht der <a href="versicherer">Versicherer</a> zu vermeiden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(307, 1, 'Kostensatz', 'kostensatz', NULL, 'Kennzahl in %, die die Kosten des Versicherungsbetriebs im Verhältnis zu den <a href="pramie">Prämien</a> bemisst (brutto oder netto).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(308, 1, 'Explosion', 'explosion-1', NULL, 'Eine Explosion ist eine plötzliche, sehr schnell und heftig ablaufende chemische oder physikalische Reaktion, an der meist Gase oder Dämpfe beteiligt sind.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(309, 1, 'Prolongationsklausel', 'prolongationsklausel', NULL, 'Abrede, wonach sich der <a href="versicherungsvertrag">Versicherungsvertrag</a> nach der vereinbarten ersten Dauer mangels <a href="kundigung">Kündigung</a> verlängert (<a href="vvg-1">VVG</a> 47, stillschweigende Vertragserneuerung). Die Verlängerung kann längstens ein Jahr betragen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(310, 1, 'Externe Kosten', 'externe-kosten', NULL, 'Kosten, die nicht in den Marktpreisen enthalten sind, da sie nicht vom eigentlichen Verursacher getragen werden, werden als externe Kosten bezeichnet. Hierzu gehören beispielsweise die Kosten für das Waldsterben, Gesundheitsschäden, Bau- und Materialschäden, Klimaveränderung etc.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(311, 1, 'Überobligatorium (BV)', 'uberobligatorium-bv', NULL, 'Teil des Versicherungsschutzes der <a href="berufliche-vorsorge">beruflichen Vorsorge</a>, der über den gesetzlich vorgeschriebenen Minimalschutz hinausgeht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(312, 1, 'Werkeigentümerhaftpflicht', 'werkeigentumerhaftpflicht', NULL, '<a href="kausalhaftung">Kausalhaftung</a> des Haus- oder Werkeigentümers für den <a href="schaden">Schaden</a>, den Drittpersonen wegen mangelhafter Konstruktion oder mangelhaften Unterhalts durch das Haus / Werk erleiden. Es handelt sich um einen Fall der <a href="ausservertragliche-haftpflicht">ausservertraglichen Haftpflicht</a> (<a href="milde-kausalhaftung">milde Kausalhaftung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(313, 1, 'Fair Trade', 'fair-trade-1', NULL, 'Commercio equo, o «fair trade», è sinonimo di un''attività commerciale con cooperative agricole e piantagioni all''insegna della correttezza, il che vuol dire, tra l''altro, prezzi in grado di coprire le spese, garanzia di salari minimi per i lavoratori nonché relazioni commerciali a lungo termine. In cambio, le aziende producono rispettando severe direttive e assicurando così un''alta qualità. Varie organizzazioni, come ad es. Max Havelaar definiscono gli standard e controllano il rispetto dei criteri.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(314, 1, 'Hausarzt-Modell', 'hausarzt-modell', NULL, 'Kostensparendes <a href="versicherungsmodell">Versicherungsmodell</a>, bei dem der Versicherte einen Hausarzt auswählt und sich verpflichtet, im Krankheitsfall immer zuerst diesen aufzusuchen. Im Unterschied zum <a href="hmo-modell-1">HMO-Modell</a> stellt der Hausarzt für jede Behandlung eine Rechnung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(315, 1, 'Verschulden', 'verschulden', NULL, 'Als Verschulden wird ein rechtlich vorwerfbares, menschliches Verhalten (Tun oder Unterlassen) bezeichnet, das Ursache eines <a href="schaden">Schadens</a> ist. Es werden Absicht und Fahrlässigkeit unterschieden. Absichtlich handelt, wer mit Wissen und Willen einen Schaden verursacht, fahrlässig handelt, wer diejenige Sorgfalt nicht beachtet, zu der er nach den Umständen und seinen persönlichen Verhältnissen verpflichtet ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(316, 1, 'FCP', 'fcp', NULL, 'Siehe <a href="fonds-commun-de-placement">Fonds commun de placement</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(317, 1, 'EFD', 'efd', NULL, 'Abkürzung für Eidgenössisches Finanzdepartement.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(318, 1, 'EDI', 'edi', NULL, 'Abkürzung für "Eidgenössisches Departement des Innern".', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(319, 1, 'EJPD', 'ejpd', NULL, 'Abkürzung für "Eidgenössisches Justiz- und Polizeidepartement".', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(320, 1, 'Bundesamt für Sozialversicherung', 'bundesamt-fur-sozialversicherung', NULL, 'Das Bundesamt für Sozialversicherung (BSV) ist analog dem Bundesamt für Privatversicherungen (BPV) vom Bundesrat als oberste Aufsichtsbehörde für die Sozialversicherungen eingesetzt. Es untersteht dem Eidg. Departement des Innern (EDI).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(321, 1, 'Treuepflicht', 'treuepflicht', NULL, 'Die gesetzlich verankerte Pflicht von <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> und <a href="depotbank">Depotbank</a> sowei ihrer Beauftragten, ausschliesslich die Interessen der <a href="anleger">Anleger</a> wahrzunehmen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(322, 1, 'Treuhandgeschäft', 'treuhandgeschaft', NULL, 'Transaktion, die der Treuhänder, oft eine Bank, in seinem eigenen Namen, aber im Auftrag und für Rechnung und Gefahr des Treugebers, abwickelt. Der Treuhänder nimmt z.B. vom Treugeber Gelder entgegen und legt sie in seinem Namen, aber im Auftrag und für Rechnung und Gefahr des Treugebers, bei <a href="dritte">Dritten</a> an.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(323, 1, 'Aussendienst', 'aussendienst', NULL, 'Aussenstelle der Absatzorganisation und Bestandteil des direkten <a href="absatzkanal">Absatzkanals</a>. Umfasst alle Mitarbeiter, die Kunden vor Ort beraten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(324, 1, 'Endwert', 'endwert', NULL, 'Im Gegensatz zum <a href="barwert">Barwert</a> werden die Prämienzahlungen und Versicherungsleistungen auf den Vertragsablauf mit dem <a href="technischer-zinssatz">technischen Zinssatz</a> aufgezinst.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(325, 1, 'Projiziertes Altersguthaben', 'projiziertes-altersguthaben', NULL, 'Darunter wird das hypothetische Altersguthaben (<a href="bvg-1">BVG</a>) im reglementarisch vorgesehenen Schlussalter verstanden.<br />\r<br />Der Gesamtbetrag bestehend aus Altersgutschriften und Zins wird auch projiziertes Altersguthaben mit Zins genannt. Anhand dieses Werts wird mit einem Umwandlungssatz die BVG-Altersrente berechnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(326, 1, 'Finanzgeschäft', 'finanzgeschaft', NULL, 'Teil der Erfolgsrechnung, in dem die Erträge aus den Kapitalanlagen erfasst sind, die sich aus der Investition der Mittel aus dem <a href="versicherungsgeschaft">Versicherungsgeschäft</a> ergeben. Davon abgezogen werden der Aufwand für Zinsen und die Abschreibungen auf Finanzanlagen. Anstatt vom Finanzgeschäft spricht man auch von der nicht-versicherungstechnischen Erfolgsrechnung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(327, 1, 'Vermögensbedarf', 'vermogensbedarf', NULL, 'Wenn ein versichertes Ereignis eintritt, hat der <a href="versicherter">Versicherte</a> eine unfreiwillige Vermögensminderung. Diese Vermögensminderung soll die <a href="versicherung">Versicherung</a> ausgleichen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(328, 1, 'Rechnungsjahr', 'rechnungsjahr', NULL, 'Periode zwischen zwei Jahresabschlüssen eines <a href="anlagefonds">Anlagefonds</a>. Das Rechnungsjahr muss nicht zwangsläufig dem Kalenderjahr entsprechen. Auch Geschäftsjahr genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(329, 1, 'FINMA', 'finma', NULL, 'Mit dem Finanzmarktgesetz FINMAG werden die drei Behörden Bundesamt für Privatversicherungen BPV, Eidgenössische Bankenkommission EBK und Kontrollstelle für die Bekämpfung der Geldwäscherei Kst GwG in der Eidgenössischen Finanzmarktaufsicht FINMA zusammengeführt. Die Finanzmarktaufsicht bezweckt den Schutz der Gläubiger, der Anleger, der Versicherten sowie den Schutz der Funktionsfähigkeit der Finanzmärkte.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(330, 1, 'Brand', 'brand', NULL, 'Ein Brand ist ein <a href="schadenfeuer">Schadenfeuer</a>, das ohne einen bestimmungsgemässen Herd entsteht oder diesen verlässt, sich aus eigener Kraft weiterverbreitet und dadurch Schaden anrichtet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(331, 1, 'Feuer', 'feuer', NULL, '¶"Feuer" bezeichnet eines der vier <a href="risiko">Risiken</a>, die in der <a href="hausratversicherung">Hausratversicherung</a> eingeschlossen sind (siehe auch <a href="feuerversicherung">Feuerversicherung</a>). <br />Der Begriff "Feuer" wird auch als Abgrenzung zum <a href="brand">Brand</a> verwendet. Als Feuer wird dann ein <a href="nutzfeuer">Nutzfeuer</a> bezeichnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(332, 1, 'Feuerversicherung', 'feuerversicherung', NULL, 'Die Feuerversicherung ist Teil der <a href="hausratversicherung">Hausratversicherung.</a> Sie versichert <a href="schaden">Schäden</a> durch <a href="brand">Brand</a>, Rauch, Blitzschlag, <a href="explosion-1">Explosion</a>, <a href="elementarereignis">Elementarereignisse</a> und abstürzende Luft- und Raumfahrzeuge. Ebenfalls versichert ist das Abhandenkommen infolge von <a href="feuer">Feuer.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(333, 1, 'Erstrisikoversicherung', 'erstrisikoversicherung', NULL, 'Alternative zur <a href="vollwertversicherung">Vollwertversicherung</a> bei einer <a href="sachversicherung">Sachversicherung</a>, um eine Leistungsreduktion wegen <a href="unterversicherung-uv">Unterversicherung</a> auszuschliessen. Dabei wird die <a href="versicherungssumme">Versicherungssumme</a> auf einen bestimmten Maximalbetrag (erstes Risiko) festgesetzt, der im <a href="schaden">Schaden</a>fall ausbezahlt wird. Eine <a href="erstes-risiko">Erstrisiko</a>versicherung wird vereinbart, wenn der <a href="versicherungswert">Versicherungswert</a> nicht im Voraus bestimmt werden kann.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(334, 1, 'Erste Säule', 'erste-saule', NULL, 'Staatliche <a href="vorsorge">Vorsorge</a> als erster Pfeiler des schweizerischen <a href="sozialversicherung">Sozialversicherung</a>ssystems. Die erste Säule ist für die gesamte Bevölkerung <a href="obligatorium">obligatorisch</a> und umfasst die<a href="alters-und-hinterlassenenversicherung">Alters- und Hinterlassenenversicherung</a> (AHV), die <a href="invalidenversicherung">Invalidenversicherung</a> (IV) sowie die <a href="erganzungsleistungen-el">Ergänzungsleistungen</a> (EL).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(335, 1, 'Eintrittsprämie', 'eintrittspramie', NULL, 'Erste zu entrichtende periodische <a href="pramie">Prämie</a> (Leben).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(336, 1, 'Erstes Risiko', 'erstes-risiko', NULL, 'Erstes Risiko (ER) ist eine vom Kunden selbst festgelegte Versicherungssumme, die als Höchstwert der Entschädigung für eine bestimmte Gegenstandsgruppe, <a href="kosten">Kosten</a> oder eine bestimmte <a href="gefahr">Gefahr</a> gelten soll (z.B.: <a href="geldwerte">Geldwerte</a>, bei <a href="diebstahl">Diebstahl</a> etc.). Im Gegensatz zur <a href="vollwertversicherung">Vollwertversicherung</a> gibt es beim Ersten Risiko keine <a href="unterversicherung-uv">Unterversicherung.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(337, 1, 'Fixprämie', 'fixpramie', NULL, 'Versicherungs<a href="pramie">prämie</a>, deren Höhe für eine bestimmte <a href="vertragsdauer">Vertragsdauer</a> festgelegt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(338, 1, 'Summenversicherung', 'summenversicherung', NULL, 'Summenversicherung liegt vor, wenn die <a href="versicherungsleistungen">Versicherungsleistung</a> unabhängig davon geschuldet ist, ob das versicherte Ereignis eine Vermögenseinbusse  bewirkt hat und wie hoch diese effektiv ausgefallen ist. Die Lebensversicherung ist grundsätzlich eine Summenversicherung. Die übrigen Personenversicherungen können sowohl als Summenversicherung (feste Taggelder, Invaliditätskapitalien) als auch als <a href="schadenversicherung">Schadenversicherung</a> (Heilungskosten, konkreter Erwerbsausfall) ausgestaltet sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(339, 1, 'Stichtagsversicherung', 'stichtagsversicherung', NULL, '<a href="geschaftsversicherung">Geschäftsversicherung</a>, bei der zu Beginn eines <a href="versicherungsjahr">Versicherungsjahres</a> eine provisorische <a href="versicherungssumme">Versicherungssumme</a> festgelegt wird (= Bezugsgrösse für provisorische <a href="pramie">Prämie</a>). An vereinbarten Stichtagen (z. B. am Ende jeden Monats) meldet der Versicherte seinen tatsächlichen Warenbestand. So kann die effektive Versicherungssumme pro Periode festgestellt und am Ende des Versicherungsjahres die definitive Prämie abgerechnet werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(340, 1, 'Festverzinsliche Wertpapiere', 'festverzinsliche-wertpapiere', NULL, 'Zusammen mit den Geldmarktpapieren werden <a href="obligationen">Obligationen</a> als festverzinsliche Wertpapiere bezeichnet, weil sie regelmässige, feste Zinszahlungen leisten und am Ende der <a href="laufzeit">Laufzeit</a> den Nominalbetrag vollumfänglich zurückbezahlen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(341, 1, 'Festhypothek', 'festhypothek', NULL, 'Hypothek mit festem, unveränderlichen Zinssatz während der gesamten zwischen der Bank und dem Kreditnehmer vereinbarten Laufzeit. Diese beträgt in der Regel drei bis acht Jahre. Die Hypothek ist während der Laufzeit von beiden Seiten unkündbar. Für den Kreditnehmer hat dies den Vorteil, dass er während der Laufzeit seine Zinsbelastung im voraus kalkulieren und budgetieren kann.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(342, 1, 'Terminkonto / Termingeldkonto', 'terminkonto-termingeldkonto', NULL, 'Wie <a href="kassenobligation">Kassenobligation</a>, aber ohne Stückelung (runde Beträge) und Wahl der genauen Laufzeit zwischen ein und zehn Jahren.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(343, 1, 'Termingelder', 'termingelder', NULL, 'Bankeinlagen, die nicht jederzeit, sondern an einem bestimmten vereinbarten Zeitpunkt fällig sind und entsprechend eine höhere Verzinsung abwerfen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(344, 1, 'Flat Fee', 'flat-fee-1', NULL, 'Bei <a href="immobilienfonds-liegenschaftsfonds">Immobilienfonds</a> die Kommission zu Gunsten der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> zur Deckung ihrer Aufwendungen (wie zum Beispiel) für die Verwaltung des <a href="fondsvermogen">Fondsvermögens</a>, die Bewirtschaftung der Liegenschaften, den Überwachungsaufwand der <a href="depotbank">Depotbank</a>, die Kosten für die Verwahrung von Wertschriften und Schuldbriefen, die Ausübung des Zahlungsverkehrs, die Couponsauszahlungskommissionen, die Honorare der Schätzungsexperten und der <a href="revisionsstelle">Revisionsstelle</a> usw.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(345, 1, 'Flottenrabatt', 'flottenrabatt', NULL, '<a href="pramie">Prämie</a>n<a href="pramienvergunstigung">vergünstigung</a> für Kunden, die beim gleichen Versicherer mehrere <a href="motorfahrzeug">Motorfahrzeug</a>versicherungen abschliessen (z. B. für die Geschäftsfahrzeuge eines Unternehmens).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(346, 1, 'Pauschalpolice', 'pauschalpolice', NULL, 'Vertragsart der <a href="transportversicherung">Transportversicherung</a>, die für zahlreiche Transporte von Gütern unterschiedlichster Art gilt. Dabei muss der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> die einzelnen Warensendungen nicht anmelden. Massgebend für die Berechnung der <a href="pramie">Prämien</a> sind die Art der Güter und die Anzahl der Transporte. Der <a href="versicherer">Versicherer</a> fordert die Prämie zu Beginn der Versicherungsperiode ein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(347, 1, 'Floating Rate Notes', 'floating-rate-notes-1', NULL, 'Variabel verzinsliche <a href="notes-1">Notes</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(348, 1, 'Überschwemmung', 'uberschwemmung', NULL, '<a href="elementargefahr">Elementargefahr</a>, bei der Schäden durch Wasser entstehen als Folge von übermässigen Niederschlägen in unverhältnismässigen Mengen;  die von der Natur oder vom Menschen gezogenen Grenzen werden überbordet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(349, 1, 'Floor', 'floor-1', NULL, 'Wertuntergrenze, unter die das investierte Kapital an einem bestimmten Stichtag nicht sinken soll.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(350, 1, 'Floor Funds', 'floor-funds-2', NULL, 'Siehe <a href="absicherungsfonds">Absicherungsfonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(351, 1, 'Fonds commun de placement', 'fonds-commun-de-placement', NULL, 'Französische Bezeichnung für <a href="anlagefonds">Anlagefonds</a> in Vertragsform.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(352, 1, 'Höhere Gewalt', 'hohere-gewalt', NULL, 'Höhere Gewalt ist ein unvorhergesehenes, unvorhersehbares, aussergewöhnliches und vom menschlichen Verhalten unabhängiges Ereignis, das mit unabwendbarer Gewalt von aussen hereinbricht.<br />Höhere Gewalt sind z.B. extreme Sturmwinde, Felsstürze oder Überschwemmungen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(353, 1, 'Auslandsanleihen', 'auslandsanleihen', NULL, '<a href="obligationen">Obligationen</a>, die ein Schuldner ausserhalb seines Heimatlandes in der Währung des Emissionsmarktes ausgibt. Siehe auch <a href="eurobonds-1">Eurobonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(354, 1, 'Devisen', 'devisen', NULL, 'Auf ausländische Währung lautende und im Ausland zahlbare Geldforderungen (z.B. Checks und Guthaben bei ausländischen Banken).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(355, 1, 'Mahnung', 'mahnung', NULL, 'Aufforderung des <a href="glaubiger">Gläubigers</a> an den Schuldner zur Bezahlung einer fälligen Leistung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(356, 1, 'Forward Pricing', 'forward-pricing-1', NULL, 'Nach diesem System wird das <a href="fondsvermogen">Fondsvermögen</a> jeweils anhand der Börsenschlusskurse vom Vortag bewertet. Gegenteil: <a href="historic-pricing-1">Historic Pricing</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(357, 1, 'Verkehrshaftungsversicherung', 'verkehrshaftungsversicherung', NULL, '<a href="transportversicherung">Transportversicherung</a>, die die <a href="haftpflicht">Haftpflicht</a> des Transportgewerbes für transportierte Güter versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(358, 1, 'ER', 'er-1', NULL, 'Abkürzung für<a href="erstes-risiko">"Erstes Risiko"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(359, 1, 'Fraktion', 'fraktion', NULL, 'Quota frazionaria di un fondo d''investimento.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(360, 1, 'Bruchteil', 'bruchteil', NULL, 'Siehe <a href="fraktion">Fraktion</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(361, 1, 'Verfügung', 'verfugung', NULL, 'Unter Verfügung kann ein Testament oder ein Erbvertrag verstanden werden. Mittel einer Person, noch zu Lebzeiten und im Rahmen der Dispositionsfreiheit selbst das Schicksal des künftigen Nachlasses zu regeln. Diese Verfügungen werden erst mit dem Tod der verfügenden Person wirksam.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(362, 1, 'Vollwert', 'vollwert', NULL, 'Bei einer <a href="vollwertversicherung">Vollwertversicherung</a> entspricht die <a href="versicherungssumme">Versicherungssumme</a> dem <a href="versicherungswert">Versicherungswert</a> der versicherten Objekte.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(363, 1, 'Universalbank', 'universalbank', NULL, 'Bank, die grundsätzlich alle Sparten des Bankgeschäftes betreibt. In der Schweiz haben die meisten Banken einen Universalbankcharakter. Ausnahmen: spezialisierte Banken wie Kleinkreditbanken, <a href="raiffeisenbank">Raiffeisenkassen</a>, <a href="privatbankier">Privatbankiers.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(364, 1, 'Vollwertversicherung', 'vollwertversicherung', NULL, 'Die <a href="sachversicherung">Sachversicherung</a> ist in der Regel eine Vollwertversicherung. Das bedeutet, dass das gesamte vorhandene Inventar zu seinem vollen Wert versichert werden muss (z.B. <a href="hausratversicherung">Hausratversicherung</a>, <a href="geschaftsversicherung">Geschäftsversicherung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(365, 1, 'Fondsvermögen', 'fondsvermogen', NULL, 'Mit dem Begriff «Fondsvermögen»  (auch «Nettofondsvermögen» genannt) wird das zu Verkehrswerten berechnete Fondsvermögen abzüglich allfälliger Verbindlichkeiten bezeichnet. Bei <a href="verkehrswert-immobilienfonds">Immobilienfonds</a> sind ausser den Hypothekar- und anderen Schulden auch noch die bei der Liquidation voraussichtlich anfallenden Steuern abzuziehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(366, 1, 'Fondsdomizil', 'fondsdomizil', NULL, 'Der Fonds hat sein Domizil in dem Land, in welchem er errichtet bzw. der dortigen <a href="aufsichtsbehorde-fonds">Aufsichtsbehörde</a> zur Registrierung gemeldet wurde.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(367, 1, 'Fondsleitung (Verwaltungsgesellschaft)', 'fondsleitung-verwaltungsgesellschaft', NULL, 'Die Fondsleitung verwaltet den <a href="anlagefonds">Anlagefonds</a> für Rechnung der <a href="anleger">Anleger</a>. Sie entscheidet insbesondere über die Anlagen und macht alle zum Anlagefonds gehörigen Rechte geltend. Sie kann die Anlageentscheide und weitere Teilaufgaben delegieren, haftet aber für Handlungen der von ihr Beauftragten wie für eigenes Handeln.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(368, 1, 'Fund of Funds', 'fund-of-funds-1', NULL, '<a href="anlagefonds">Anlagefonds</a>, die ihre Anlagen auf <a href="anteil">Anteile</a> anderer Anlagefonds beschränken. Nicht zu verwechseln mit <a href="umbrella-fonds">Umbrella-Fonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(369, 1, 'Fondsreglement', 'fondsreglement', NULL, 'Siehe <a href="prospekt-fondsreglement">Prospekt</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(370, 1, 'Fondsgebundene Lebensversicherung', 'fondsgebundene-lebensversicherung', NULL, 'Bei der fondsgebundenen Lebensversicherung wird der Anteil der Versicherungsbeiträge, welcher normalerweise im Deckungsstock angelegt wird (Sparanteil), für den Erwerb von Fondsanteilen verwendet. Es gibt sowohl Lebensversicherungspolicen, bei denen der Kunde unter einer Reihe von <a href="anlagefonds">Fonds</a> auswählen kann, als auch Policen, bei denen nur der Anlageschwerpunkt gewählt werden kann (<a href="obligationen">Obligationen</a>, <a href="aktie">Aktien</a>, Immobilien etc.).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(371, 1, 'Mobiliarverglasung', 'mobiliarverglasung', NULL, 'Zur Mobiliarverglasung zählen alle Glasflächen an Möbeln sowie Wandspiegel.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(372, 1, 'Umzugsversicherung', 'umzugsversicherung', NULL, 'In der <a href="hausratversicherung">Hausratversicherung</a> schützt die Umzugsversicherung die versicherten Sachen beim Umzug von einem Wohnort zum anderen. Der Schutz besteht innerhalb des <a href="versichertes-gebiet-bei-umzug">versicherten Gebiets</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(373, 1, 'Futures', 'futures', NULL, 'Futures verpflichten die Vertragspartner, eine standardisierte Menge eines Basiswertes zu einem vereinbarten Preis an einem bestimmten Zeitpunkt zu übernehmen (Käufer) oder zu liefern (Verkäufer). Siehe auch <a href="derivate">Derivate</a>, <a href="optionen">Optionen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(374, 1, 'AVB', 'avb', NULL, 'Abkürzung für <a href="allgemeine-versicherungsbedingungen">"Allgemeine Versicherungs-Bedingungen"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(375, 1, 'Havarie-Grosse', 'havarie-grosse', NULL, 'Alte Regel der Schifffahrt.<br />Eine Havarie-Grosse liegt vor, wenn ein Schaden dadurch entsteht, dass zur Rettung von Schiff und Ladung aus einer unmittelbar drohenden Gefahr Kosten aufgewendet oder andere Opfer gebracht werden. Die Havarie-Grosse beruht auf der Idee der <a href="gefahrengemeinschaft">Gefahrengemeinschaft</a> und bestimmt, dass der Schiffseigentümer (Reeder) und die Eigentümer der transportierten Güter gemeinsam für die entstandenen Kosten aufkommen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(376, 1, 'Allgemeine Versicherungsbedingungen', 'allgemeine-versicherungsbedingungen', NULL, 'Die Allgemeinen Versicherungsbedingungen (AVB) regeln die für alle Vertragsparteien in gleicher Weise gültigen Bestimmungen. Sie sind ein integrierter Bestandteil des <a href="versicherungsvertrag">Versicherungsvertrages</a>. Bei der Ausgestaltung der AVB sind die Gesellschaften an das <a href="versicherungsvertragsgesetz">Bundesgesetz über den Versicherungsvertrag</a> (VVG) gebunden. Dieses Gesetz enthält gewisse Bestimmungen, die durch Vertragsabrede überhaupt nicht, andere, die nicht zuungunsten des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> oder Anspruchsberechtigten abgeändert werden dürfen. So sind die AVB vielfach der Niederschlag gesetzlicher Bestimmungen. Zudem unterliegen die AVB der Genehmigung durch die <a href="aufsichtsbehorde">Aufsichtsbehörde</a>, dem <a href="#invalid-reference-69">Bundesamt für Privatversicherungen</a> (BPV). Durch diese strenge Kontrolle sind die Interessen des <a href="versicherter">Versicherten</a> in jedem Fall bestens gewährleistet. Da die AVB zum Vertragsinhalt gehören, müssen sie vorgängig oder spätestens vor Einreichung des <a href="antrag">Antrages</a> dem Versicherungsnehmer abgegeben werden. Nachdem dies meistens in separater Form geschieht, lassen sich die Gesellschaften Empfang und Annahme der AVB durch den Versicherungsnehmer im Antragsformular bestätigen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(377, 1, 'Generalpolice', 'generalpolice', NULL, 'Vertragsart der <a href="transportversicherung">Transportversicherung</a>, die für zahlreiche Transporte von Gütern unterschiedlichster Art gilt. Dabei muss jede Warensendung beim <a href="versicherer">Versicherer</a>einzeln angemeldet werden. Dieser verbucht die <a href="pramie">Prämie</a>, ohne dass jedes Mal eine neue <a href="versicherungspolice">Versicherungspolice</a> ausgestellt wird. Der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> erhält dann monatlich die Rechnung für den aufgelaufenen Prämienbetrag.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(378, 1, 'Allg. Technische Anlagen-Versicherung (ATA)', 'allg-technische-anlagen-versicherung-ata', NULL, '<a href="technische-versicherungen">Technische Versicherung</a>, die bestimmte technische Anlagen wie z. B. Leuchtreklamen oder Gemeinschaftsantennen versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(379, 1, 'Bruchschaden', 'bruchschaden', NULL, 'Von einem Bruchschaden spricht man, wenn der <a href="schaden">Schaden</a> an einem Glas, Spiegel oder glasähnlichen Material nicht nur oberflächlich, sondern durchgängig ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(380, 1, 'Glasbruchversicherung', 'glasbruchversicherung', NULL, 'Die Glasbruchversicherung ist Teil der <a href="hausratversicherung">Hausratversicherung.</a> Sie versichert <a href="bruchschaden">Bruchschäden</a> an Mobiliar- und/oder <a href="gebaudeverglasung">Gebäudeverglasung.</a> Darin sind in der Regel auch Lavabos, Bidets oder Closets eingeschlossen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(381, 1, 'Gebäudeverglasung', 'gebaudeverglasung', NULL, 'Fenster, Isolierverglasung, Türen, Glasbausteine, etc. zählen zur Gebäudeverglasung. Lavabos, Bidets, Closets werden häufig mit der Gebäudeverglasung mitversichert.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(382, 1, 'Gesamtberatung', 'gesamtberatung', NULL, 'Gesamtberatung in der Lebensversicherung muss den ganzen Bereich der Personenversicherung umfassen. Es genügt nicht mehr, eine «einfache Lebensversicherung» zu erwerben. Vielmehr soll die Lebensversicherung mithelfen, die finanziellen Probleme, die der Einkommensausfall bei vorübergehender <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a>, der vorzeitige Tod oder Rücktritt aus dem aktiven Erwerbsleben mit sich bringen, zu lösen und die damit verbundenen <a href="kosten">Kosten</a> abzudecken. Diese globale Sicherung erfolgt in der Schweiz nach dem <a href="drei-saulenkonzept">Drei-Säulen-Konzept</a> durch das Gemeinwesen (<a href="ahv-1">AHV</a> / <a href="iv">IV</a>) den Betrieb (<a href="berufliche-vorsorge">berufliche Vorsorge</a>)-, Unfallversicherung, Krankenversicherung) und die private <a href="vorsorge">Vorsorge</a> (Lebensversicherung, Krankenkasse/Krankenversicherung, Unfallversicherung). Alle diese Leistungen sind nicht a priori genau aufeinander abgestimmt. Um den Prämienfranken optimal einzusetzen, braucht es daher eine sorgfältige Analyse des bestehenden <a href="versicherungsschutz">Versicherungsschutzes</a>, eine genaue Abklärung des Vorsorgebedarfs und die Ausarbeitung einer Lösung, die sowohl den Wunschvorstellungen als auch den finanziellen Möglichkeiten Rechnung trägt. Die Beratung durch den Lebensversicherungsfachmann ist unentgeltlich und in jedem Fall zu empfehlen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(383, 1, 'Goldene Regel', 'goldene-regel', NULL, 'Die Goldene Regel ist erfüllt, wenn während längerer Zeit die Steigerung der Löhne in Prozenten gleich hoch ist wie der <a href="technischer-zins">technische Zinsfuss</a>. Ist die Goldene Regel erfüllt, so wird das Vorsorgeziel des <a href="bvg-1">BVG</a> erfüllt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(384, 1, 'Waren', 'waren', NULL, 'Sachen, die für den Verkauf oder Verbrauch bestimmt sind. Versicherte Gegenstände der <a href="geschaftsversicherung">Geschäftsversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(385, 1, 'Staatsanleihen', 'staatsanleihen', NULL, '<a href="obligationen">Obligationen</a>, die von Staaten zur Finanzierung ihres Staatshaushaltes emittiert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(386, 1, 'Grüne Karte', 'grune-karte', NULL, '<a href="versicherungsnachweis">Versicherungsnachweis</a> für die <a href="motorfahrzeug-haftpflichtversicherung">Motorfahrzeug-Haftpflichtversicherung</a> eines Motorfahrzeughalters. Die grüne Karte wird in ganz Europa anerkannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(387, 1, 'Grobe Fahrlässigkeit (Grobfahrlässigkeit)', 'grobe-fahrlassigkeit-grobfahrlassigkeit', NULL, 'Grobfahrlässig handelt, wer eine elementare Vorsichtspflicht verletzt, deren Beachtung sich jedem verständigen Menschen in der gleichen Lage aufdrängt. Wird durch grobe Fahrlässigkeit ein <a href="schaden">Schaden</a> herbeigeführt, so kann die <a href="versicherungsleistungen">Versicherungsleistung</a> reduziert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(388, 1, 'Bruttoprämie', 'bruttopramie', NULL, 'Bruttoprämie = <a href="nettopramie">Nettoprämie</a> plus Kostenzuschläge.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(389, 1, 'Bruttorendite', 'bruttorendite', NULL, 'Bei <a href="immobilienfonds-liegenschaftsfonds">Immobilienfonds</a> die Mieteinnahmen in Prozent des <a href="verkehrswert-immobilienfonds">Verkehrswerts</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(390, 1, 'Kollektivversicherung', 'kollektivversicherung', NULL, '1. <br />Mehrere Gegenstände (Sachen, Vermögen oder Personen) werden durch einen Vertrag versichert.<br />\r<br />2.<br />Mehrere <a href="versicherter">Versicherte</a> oder mehrere <a href="versicherer">Versicherer</a> sind an einem <a href="versicherungsvertrag">Versicherungsvertrag</a> beteiligt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(391, 1, 'Firmen-Kollektiv-Versicherungen', 'firmen-kollektiv-versicherungen', NULL, 'Versicherungen, mit denen ein Unternehmen sein Personal gegen bestimmte <a href="risiko">Risiken</a> bei einer Versicherungsgesellschaft kollektiv versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(392, 1, 'Gruppenversicherung', 'gruppenversicherung', NULL, 'Planmässige Versicherung für eine generell umschriebene Gruppe. Die Abdeckung aller <a href="bvg-1">BVG</a>-Leistungen mit einer Gruppenversicherung ist möglich.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(393, 1, 'Sicherheitsfonds', 'sicherheitsfonds', NULL, 'Der Sicherheitsfonds ist eine nationale Einrichtung in der Rechtsform einer Stiftung. Er hat die Aufgabe, Zuschüsse an <a href="vorsorgeeinrichtung">Vorsorgeeinrichtungen</a> auszurichten, die eine ungünstige Alterstruktur aufweisen. Ausserdem stellt er die gesetzlichen Leistungen von zahlungsunfähig gewordenen Vorsorgeeinrichtungen sicher. Der Sicherheitsfonds wird von den Vorsorgeeinrichtungen finanziert. Massgebend für den Finanzierungsanteil ist die Summe der <a href="koordinierter-lohn">koordinierten Löhne</a>aller <a href="versicherter">Versicherten</a>, die für die Altersleistungen Beiträge zu entrichten haben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(394, 1, 'Nachversicherungs-Garantie', 'nachversicherungs-garantie', NULL, 'Verschiedene Versicherer bieten ihren Kunden ohne Prämienzuschlag eine Nachversicherungs-Garantie (auch Garantie der Versicherungsfähigkeit genannt) an. Der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> kann dabei in bestimmten Zeitabständen oder bei in den <a href="avb">AVB</a> festgehaltenen Ereignissen wie Heirat, Geburt, Aufnahme einer selbständigen Erwerbstätigkeit die <a href="versicherungsleistungen">Versicherungsleistung</a> ohne <a href="gesundheitsprufung">Gesundheitsprüfung</a> erhöhen lassen. Einschränkungen können für den Fall bestehen, dass das versicherte Ereignis kurz nach der Erhöhung eintritt. Ist der Versicherte erwerbsunfähig, werden keine Leistungen auf dem erhöhten Teil gewährt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(395, 1, 'Garantiesumme', 'garantiesumme', NULL, 'Finanzielle Begrenzung des versicherten <a href="vermogensbedarf">Vermögensbedarfs</a> eines <a href="versicherter">Versicherten</a> im Rahmen einer <a href="vermogensversicherung">Vermögensversicherung</a>. Die Garantiesumme wird in der <a href="police">Police</a> als Maximalbetrag ausgewiesen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(396, 1, 'Beistand', 'beistand', NULL, 'Ein Beistand wird bestellt, wenn einer Person für bestimmte Rechtsgeschäfte oder zur Vermögensverwaltung eine Hilfsperson zur Seite gestellt werden muss. Der verbeiständigten Person verbleibt indessen die volle Handlungsfähigkeit.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(397, 1, 'Vormund', 'vormund', NULL, 'Geeignete, amtlich bestellte Person, die die gesetzliche Vertretung einer aus bestimmten, im Gesetz genannten Gründen entmündigten oder bevormundeten Person übernimmt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(398, 1, 'Gästeeffekten', 'gasteeffekten', NULL, 'Persönliche Gegenstände, die ein Gast in den Haushalt des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> mitbringt, werden unter dem Begriff der "Gästeeffekten" zusammengefasst. (Kleider, Schuhe, Toilettenartikel, Sportartikel u.ä.).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(399, 1, 'GUSTAVO', 'gustavo', NULL, 'Anfangsbuchstaben der Kantone ohne <a href="kantonale-gebaudeversicherung">Kantonale</a> <a href="gebaudeversicherung">Gebäudeversicherung</a>. Unterschieden werden Kantone, in denen die Gebäudeversicherung für die <a href="feuerversicherung">Feuerversicherung</a> freiwillig ist (Appenzell Innerrhoden, Genf, Tessin und Valais) oder obligatorisch (Uri, Schwyz und Obwalden).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(400, 1, 'Hagel', 'hagel', NULL, '<a href="elementargefahr">Elementargefahr</a>, bei der Schäden durch Hagelkörner entstehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(401, 1, 'Manipulationsversicherung', 'manipulationsversicherung', NULL, '<a href="transportversicherung">Transportversicherung</a> für Unternehmen, die schwere Maschinen oder Maschinenteile (z. B. Turbinen, Schiffsmotoren) auf ihrem Werksgelände verschieben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(402, 1, 'Familienhaupt', 'familienhaupt', NULL, 'Siehe <a href="haftpflicht-des-familienhaupts">Haftpflicht des Familienhaupts</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(403, 1, 'Haftpflicht des Familienhaupts', 'haftpflicht-des-familienhaupts', NULL, '<a href="kausalhaftung">Kausalhaftung</a> der Eltern oder anderer Aufsichtspersonen für den <a href="schaden">Schaden</a>, den Kinder wegen mangelnder Aufsicht bei Drittpersonen anrichten. Es handelt sich um einen Fall der <a href="ausservertragliche-haftpflicht">ausservertraglichen Haftpflicht</a> (<a href="milde-kausalhaftung">milde Kausalhaftung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(404, 1, 'Krankenversicherung', 'krankenversicherung', NULL, 'Siehe <a href="soziale-krankenversicherung">soziale Krankenversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(405, 1, 'Krankenpflegeversicherung', 'krankenpflegeversicherung', NULL, 'Siehe <a href="obligatorische-krankenpflegeversicherung">Obligatorische Krankenpflegeversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(406, 1, 'Wärmetauscher', 'warmetauscher', NULL, 'Ein Wärmetauscher ist Bestandteil einer Wärmepumpe. Diese gibt z. B. die Erdwärme oder Sonnenwärme mittels einer Übertragungsflüssigkeit an einen Wasserkreislauf ab, der die Wärme in das Haus trägt. Aus diesem Kreislauf kann austretende Flüssigkeit einen Wasserschaden verursachen, der von der Wasserversicherung - z.T. mit Prämienzuschlag - entschädigt wird.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(407, 1, 'Hedge Funds', 'hedge-funds-1', NULL, 'Entgegen ihrem Namen (englisch to hedge = absichern) sind Absicherungsgeschäfte keineswegs der Hauptzweck solcher Fonds. Mit dem Ziel der Erwirtschaftung absoluter Erträge werden vielmehr Anlagen getätigt, die herkömmlichen Fonds nicht erlaubt sind (Spekulieren à la baisse, <a href="leerverkauf">Leerverkäufe</a> von Wertpapieren, Einsatz von <a href="derivate">Derivaten</a>, Finanzierung der Anlagen durch Kredite). Dadurch können Hedge Funds unabhängig von der Marktsituation positive <a href="performance">Renditen</a> erzielen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(408, 1, 'Hedging', 'hedging', NULL, 'Das Absichern von Anlagen gegen Verluste.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(409, 1, 'Absicherungskosten', 'absicherungskosten', NULL, 'Kosten, die entstehen, wenn ein <a href="portefeuille">Portefeuille</a> mittels dynamischer oder statischer Absicherung gegen Kursverluste geschützt werden soll. Bei Portefeuilles mit dynamischer Absicherung entstehen primär Ankaufs- und Verkaufskosten bei der Verschiebung von Geldmitteln zwischen dem <a href="core-portfolio-1">Core Portfolio</a> und dem <a href="opportunity-portfolio-1">Opportunity Portfolio</a>. Bei einer statischen Absicherung entstehen die Kosten beim Kauf von derivativen Instrumenten, beispielsweise <a href="optionen">Put-Optionen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(410, 1, 'Erben', 'erben', NULL, 'Als Erben werden diejenigen Personen bezeichnet, an die das Vermögen einer verstorbenen Person übergeht. Es kann zwischen den gesetzlichen Erben (in der Regel Verwandte bis zu einem bestimmten Grad) und den eingesetzten (testamentarischen) Erben unterschieden werden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(411, 1, 'Pflichtteil', 'pflichtteil', NULL, 'Derjenige Teil des Vermögens, über das der Erblasser testamentarisch nicht verfügen kann. Bestimmte gesetzliche Erben werden speziell geschützt und erhalten in jedem Fall einen Erbanteil.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(412, 1, 'Heredität', 'hereditat', NULL, 'Erblichkeit der zu versichernden Person, um das Gesundheitsrisiko abzuschätzen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(413, 1, 'Hochwasser', 'hochwasser', NULL, 'Von Hochwasser spricht man, wenn Wasser als Folge von übermässigen Niederschlägen in unverhältnismässigen Mengen auftritt, sich aber innerhalb der ihm von der Natur oder dem Menschen gezogenen Grenzen bewegt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(414, 1, 'High Watermark', 'high-watermark-1', NULL, 'Kommt im Zusammenhang mit erfolgsabhängigen Gebühren (<a href="performance-fee-1">Performance Fees</a>) zur Anwendung. Der Fondsmanager berechnet seine Gewinnbeteiligung auf der Basis der Wertsteigerung über den letzten Höchststand des <a href="nettoinventarwert">Nettoinventarwertes</a> hinaus. Dies hat zur Folge, dass die Performance Fee erst anfällt, nachdem eventuelle Verluste wieder vollständig aufgeholt wurden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(415, 1, 'High-Yield Funds', 'high-yield-funds-1', NULL, '<a href="obligationenfonds">Obligationenfonds</a>, die in Papieren von Schuldnern geringerer <a href="bonitat-fonds">Bonität</a> investieren. Solche Titel bieten höhere Zinsen; gleichzeitig ist aber auch das Ausfallrisiko - die Gefahr, dass ein Schuldner die Zinsen nicht bezahlt oder die Rückzahlung ausfällt - grösser.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(416, 1, 'Historic Pricing', 'historic-pricing-1', NULL, 'Abrechnungsmethode im Fondsgeschäft. Der <a href="anleger">Anleger</a> kennt im Zeitpunkt der Zeichnung/Rückgabe den <a href="nettoinventarwert">Nettoinventarwert</a> des Fonds. Gegenteil: <a href="forward-pricing-1">Forward Pricing</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(417, 1, 'HMO-Modell', 'hmo-modell-1', NULL, 'HMO = Health Maintenance Organization (wörtl.: Gesundheitserhaltungsorganisation)<br />\r<br />Kostensparendes <a href="versicherungsmodell">Versicherungsmodell</a>, bei dem der Versicherte aus einer Liste seiner Krankenkasse einen Arzt oder eine Praxisgemeinschaft auswählt und sich verpflichtet, im Krankheitsfall immer zuerst diese(n) aufzusuchen. Der behandelnde Arzt bzw. die behandelnde Praxis erhält für die eingeschriebenen HMO-Patienten eine monatliche Fallpauschale.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(418, 1, 'Hohlglas', 'hohlglas', NULL, 'Geblasene oder gegossene Gläser, z.B. Trinkgläser, Vasen, Beleuchtungskörper, etc.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(419, 1, 'Einbruchdiebstahl', 'einbruchdiebstahl', NULL, 'Einbruchdiebstahl ist eine Art des <a href="diebstahl">Diebstahls.</a> Dabei dringt der Täter gewaltsam in ein Gebäude oder in den Raum eines Gebäudes ein, oder er bricht darin ein Behältnis auf.<br />Dem Einbruchdiebstahl gleichgestellt ist Diebstahl durch Aufschliessen mit den richtigen Schlüsseln oder Codes, sofern sich der Täter diese durch Einbruchdiebstahl oder durch <a href="beraubung">Beraubung</a> angeeignet hat.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(420, 1, 'Haushaltversicherung', 'haushaltversicherung', NULL, 'Sammelbegriff für Versicherungen des Privathaushalts. Umfasst die <a href="hausratversicherung">Hausratversicherung</a>, die <a href="privathaftpflichtversicherung">Privathaftpflichtversicherung</a>, die <a href="wertsachenversicherung">Wertsachenversicherung</a> und die Privat<a href="rechtsschutzversicherung">rechtsschutzversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(421, 1, 'Hausrat', 'hausrat', NULL, 'Alle beweglichen, dem privaten Gebrauch dienenden Gegenstände des Haushalts, die nicht Gebäudebestandteil oder bauliche Einrichtungen sind, gelten als Hausrat. Zum Hausrat gehören das Mobiliar, Kleidung, <a href="gasteeffekten">Gästeeffekten</a>, Sportgeräte etc.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(422, 1, 'Inventarblatt für Hausrat', 'inventarblatt-fur-hausrat', NULL, 'Ein Formular, auf welchem sämtliche Hausratgegenstände einzeln oder in Gruppen aufgeführt werden. Es dient zur korrekten Ermittlung der <a href="versicherungssumme">Versicherungssumme.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(423, 1, 'Hausratindex', 'hausratindex', NULL, 'Der Hausratindex beziffert die Teuerung der Hausratgegenstände. Er wird jährlich zum 30. September vom Sachversicherungsverband (SSV) berechnet. Die <a href="automatische-summenanpassung">automatische Summenanpassung</a> orientiert sich am Hausratindex.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(424, 1, 'Hausratversicherung', 'hausratversicherung', NULL, 'Die Hausratversicherung zählt zu den <a href="sachversicherung">Sachversicherungen.</a> Sie übernimmt <a href="schaden">Schäden</a>, die durch <a href="feuer">Feuer</a>, <a href="wasser-schaden">Wasser</a>, <a href="diebstahl">Diebstahl</a> oder <a href="glasbruchversicherung">Glasbruch</a> am <a href="hausrat">Hausrat</a> entstehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(425, 1, 'VR', 'vr-1', NULL, 'Abkürzung für "<a href="versicherer">Versicherer</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(426, 1, 'VS', 'vs-1', NULL, 'Abkürzung für "<a href="versicherungssumme">Versicherungssumme</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(427, 1, 'Zum Gebrauch entwenden', 'zum-gebrauch-entwenden', NULL, 'Entwenden bedeutet, eine Sache mit List oder Gewalt gegen den Willen des Eigentümers wegnehmen.<br />Wenn diese Sache, nur für eine beschränkte Zeit verwendet wird, ohne sie behalten zu wollen, wird von Gebrauch gesprochen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(428, 1, 'Unbewegliche Sache', 'unbewegliche-sache', NULL, 'Siehe <a href="immobilie">Imobilie.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(429, 1, 'Implosion', 'implosion-1', NULL, 'Eine Implosion ist eine schlagartige Zertrümmerung eines Hohlkörpers durch äusseren Überdruck (z.B. bei Bildschirmröhren von Fernsehgeräten oder Computern).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(430, 1, 'Vorschüssig', 'vorschussig', NULL, 'Vorschüssig heisst, dass eine Leistung vor Beginn einer Zahlungperiode fällig ist.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(431, 1, 'Nachschüssig', 'nachschussig', NULL, 'Nachschüssig heisst, dass eine Leistung am Ende einer Zahlungsperiode fällig wird.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(432, 1, 'Betrieb', 'betrieb', NULL, 'Ein <a href="motorfahrzeug">Motorfahrzeug</a> ist in Betrieb, wenn es sich im Verkehr befindet und/oder sich seine besonderen Eigenschaften (Motor, Kraft, Masse, Geschwindigkeit und eventuell Licht und Lärm) auswirken.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(433, 1, 'Arbeitsunfähigkeit', 'arbeitsunfahigkeit', NULL, 'Arbeitsunfähig ist eine Person, die nach ärztlichem Ermessen wegen ihres Gesundheitsschadens im bisherigen Beruf oder im bisherigen Arbeitsbereich nicht mehr tätig sein kann. Arbeitsunfähigkeit ist nicht gleichbedeutend mit <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(434, 1, 'Versicherungsbeginn', 'versicherungsbeginn', NULL, 'Der Zeitpunkt, mit welchem der <a href="versicherungsschutz">Versicherungsschutz</a> wirksam wird. Er kann mit dem Vertragsbeginn zusammenfallen oder durch Vereinbarung später einsetzen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(435, 1, 'Ertrag', 'ertrag', NULL, 'Zinsen und <a href="dividende">Dividenden</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(436, 1, 'Renditeliegenschaft', 'renditeliegenschaft', NULL, 'Immeuble qui n''est pas construit ou acheté par le propriétaire pour son propre usage, mais en vue d''un placement.', '2016-08-29 10:21:09', '2016-08-29 10:22:12'),
(437, 1, 'Einkommenssplitting', 'einkommenssplitting', NULL, 'Siehe <a href="splitting-einkommenssplitting">Splitting.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(438, 1, 'Incoterms', 'incoterms', NULL, 'Abk. für: International Commercial Terms. <br />Lieferklauseln der Internationalen Handelskammer in Paris (ICC, International Chamber of Commerce). Die Incoterms regeln die Aufgaben-, Kosten- und Risikoteilung im internationalen Handel.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(439, 1, 'Gefahrserhöhung', 'gefahrserhohung', NULL, 'Nachträgliche Änderung der bei Vertragsabschluss bestehenden Ausprägung von <a href="gefahrstatsachen">Gefahrstatsachen</a>, die zu einer Verschärfung des Gefahrszustandes führt, d.h. den Eintritt der versicherten Gefahr oder eine Vergösserung des Schadens wahrscheinlicher macht (Kommentar VVG - Fuhrer, Basel, 2001, N 17 zu Vorbem. Art. 28-30).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(440, 1, 'Scharfe Kausalhaftung', 'scharfe-kausalhaftung', NULL, 'Siehe <a href="kausalhaftung">Kausalhaftung.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(441, 1, 'Integritätsentschädigung', 'integritatsentschadigung', NULL, 'Die Integritätsentschädigung stellt eine Geldleistung an einen Behinderten dar. Sie will damit einen gewissen Ausgleich bieten für körperliche Schmerzen, Leid, verminderte Lebensfreude, Beeinträchtigung des Lebensgenusses und ähnliche Ursachen seelischen Unbehagens.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(442, 1, 'Schadenversicherung', 'schadenversicherung', NULL, 'Schadenversicherung liegt vor, wenn eine Vermögenseinbusse Voraussetzung und Kriterium für die Bemessung  der Leistungspflicht des <a href="versicherer">Versicherers</a> ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(443, 1, 'Haftzeit', 'haftzeit', NULL, 'Begrenzung der Versicherungsleistungen der <a href="betriebsunterbrechungsversicherung">Betriebsunterbrechungsversicherung</a> auf einen bestimmten Zeitraum.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(444, 1, 'Index', 'index-2', NULL, 'Statistische Kennziffer, die aufgrund einer in der Vergangenheit bestimmten Basisgrösse periodisch (z.B. stündlich, täglich, wöchentlich etc.) neu berechnet wird und welche in ihrer zeitlichen Veränderung eine Tendenz widerspiegelt <a href="aktienindex">Aktienindex</a>, <a href="dow-jones-index-1">Dow Jones Index</a>, <a href="swiss-performance-index">Swiss Performance Index.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(445, 1, 'Index (Fonds)', 'index-fonds', NULL, 'Kennzahl der Entwicklung der Kurse an einem oder mehreren Märkten. Der bekannteste und zugleich älteste Aktienindex ist der <a href="dow-jones-index-1">Dow Jones</a>. Indices erlauben es, die <a href="performance">Performance</a> eines Fonds, der auf einem bestimmten Markt investiert ist, mit der Entwicklung dieses Marktes zu vergleichen. Vergleiche auch <a href="benchmark">Benchmark</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(446, 1, 'Indexfonds', 'indexfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in ihrer Titelselektion und Gewichtung einen im Voraus gewählten Börsenindex möglichst genau nachbilden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(447, 1, 'Indirekte Amortisation', 'indirekte-amortisation', NULL, 'Methode zur Amortisation von Hypotheken. Statt direkter Amortisation der Hypothek mittels laufend erbrachten Teilrückzahlungen wird eine Spar-/Risikoversicherung geäufnet und die Hypothek mit Ablauf des Versicherungsvertrags bzw. mit der Auszahlung des Erlebensfallkapitals in einem Betrag amortisiert. Die steuerlichen Vor- oder Nachteile dieser Methode sind umstritten.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(448, 1, 'Indirekter Schaden', 'indirekter-schaden', NULL, 'Folge<a href="schaden">schaden</a> an einer versicherten Sache.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(449, 1, 'Mittelbarer Schaden', 'mittelbarer-schaden', NULL, 'Reine <a href="vermogensschaden">Vermögensschäden</a>, die als Folge eines <a href="sachschaden">Sachschadens</a> entstehen (Folgekosten).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(450, 1, 'Individuelles Konto (IK)', 'individuelles-konto-ik', NULL, 'Konto, das die AHV-<a href="ausgleichskasse">Ausgleichskasse</a> für jeden <a href="versicherter">Versicherten</a> führt und auf dem die jeweiligen Jahreseinkommen vermerkt sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(451, 1, 'Einzelkapital-Versicherung', 'einzelkapital-versicherung', NULL, 'Lebensversicherung nach Einzeltarif. Sie bildet während der <a href="vertragsdauer">Vertragsdauer</a> durch den Sparteil der Versicherungsprämie ein <a href="deckungskapital">Deckungskapital</a>, das während der Vertragsdauer verzinst und spätestens bei Ablauf der Versicherung fällig wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(452, 1, 'Einzelversicherung (Private Unfallversicherung)', 'einzelversicherung-private-unfallversicherung', NULL, 'Versicherung einer einzelnen Person im Rahmen der privaten <a href="unfallversicherung">Unfallversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(453, 1, 'Einzelversicherung (Sachversicherung)', 'einzelversicherung-sachversicherung', NULL, 'Versicherung eines einzelnen Objektes (z.B. ein Bild oder ein Haus)', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(454, 1, 'Einzellebensversicherung', 'einzellebensversicherung', NULL, '<a href="lebensversicherung">Lebensversicherung</a> für Einzelpersonen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(455, 1, 'Einzelpolice', 'einzelpolice', NULL, 'Vertragsart der <a href="transportversicherung">Transportversicherung</a>, bei der ein bestimmter Einzeltransport versichert wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(456, 1, 'Betriebsgefahr', 'betriebsgefahr', NULL, 'Die Gefahr aus dem <a href="betrieb">Betrieb</a> eines Motorfahrzeugs besteht im wesentlichen darin, dass durch den Motor Bewegung einer grossen Masse ermöglicht werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(457, 1, 'Bankinternes Sondervermögen', 'bankinternes-sondervermogen', NULL, 'Vermögen, das von einem eng begrenzten Anlegerkreis zur gemeinsamen Kapitalanlage aufgebracht und nach dem Grundsatz der Risikoverteilung verwaltet wird. Anders als bei <a href="anlagefonds">Anlagefonds</a> wird für Bankinterne Sondervermögen keine öffentliche Werbung betrieben. Auch Inhouse Funds genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(458, 1, 'Geschädigte', 'geschadigte', NULL, 'Der Geschädigte ist einer der Beteiligten im Dreieck <a href="versicherer">Versicherer</a> - <a href="versicherter">Versicherter</a>- Geschädigter.<br />Geschädigter ist derjenige, der einen <a href="schaden">Schaden</a> erlitten hat.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(459, 1, 'Insolvenz', 'insolvenz', NULL, 'Zahlungsunfähigkeit.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(460, 1, 'Insolvenzentschädigung', 'insolvenzentschadigung', NULL, 'Leistung der Arbeitslosenversicherung (<a href="arbeitslosen-und-insolvenzversicherung">ALV</a>) beim Ausfall des Erwerbseinkommens wegen Zahlungsunfähigkeit des Arbeitgebers.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(461, 1, 'Insolvenzschutz', 'insolvenzschutz', NULL, 'Schutz der <a href="versicherter">Versicherten</a> vor der Zahlungsunfähigkeit des <a href="versicherer">Versicherers</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(462, 1, 'Sicherheitsinstitut', 'sicherheitsinstitut', NULL, 'Schweizerisches Institut zur Förderung der Sicherheit (Sicherheitsinstitut), vormals BVD - Brandverhütungsdienst. Beratungsstelle für Gewerbe und Industrie. Das Sicherheitsinstitut berät und unterstützt Betriebe in Fragen wie Brandverhütung, Arbeitssicherheit. Das Sicherheitsintitut wird durch die Schweizerische Versicherungswirtschaft getragen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(463, 1, 'Versicherung', 'versicherung', NULL, 'siehe <a href="versicherer">Versicherer.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(464, 1, 'Versicherungsgeschäft', 'versicherungsgeschaft', NULL, 'Teil der Erfolgsrechnung, in dem das Versicherungsgeschäft erfasst ist. Man spricht auch von der versicherungstechnischen Erfolgsrechnung. Erfasst werden die Prämienanteile, die für die Schaden- / Leistungszahlungen sowie die Verwaltungs- und Aquisitionskosten verwendet werden. Die Erträge und Aufwände aus den Kapitalanlagen sind im <a href="finanzgeschaft">Finanzgeschäft</a> erfasst.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(465, 1, 'Versicherungsnachweis', 'versicherungsnachweis', NULL, 'Der Versicherungsnachweis (Versicherungsbescheinigung) beweist, dass für das betreffende <a href="fahrzeug">Fahrzeug</a> eine <a href="haftpflichtversicherung">Haftpflichtversicherung</a> existiert, die dem Gesetz genügt. Der <a href="versicherer">Versicherer</a> stellt den Versicherungnachweis aus.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(466, 1, 'Versicherungsvertrag', 'versicherungsvertrag', NULL, 'Ein zweiseitiger Vertrag zwischen einem <a href="versicherungsnehmer-in">Versicherungsnehmer</a> und einem <a href="versicherer">Versicherer</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(467, 1, 'Versicherungsschutz', 'versicherungsschutz', NULL, 'Die Frage nach dem Versicherungsschutz ist die Frage, ob und wieweit nach einem konkreten Vertrag Deckung bestehe: <br />- welche Gefahr ist in welchem Umfang versichert (sachlicher Geltungsbereich);<br /> - welche Personen sind versichert (persönlicher Geltungsbereich); <br />- für welche Länder soll der <a href="versicherungsvertrag">Versicherungsvertrag</a> gelten (örtlicher Geltungsbereich); <br />- ab wann und bis wann besteht der Versicherungsschutz nach einem bestimmten Vertrag (zeitlicher Geltungsbereich). <br />\r<br />Wenn von Versicherungsschutz oder Deckung gesprochen wird, muss stets überlegt werden, in welchem Sinn dieser technische Begriff gemeint ist. Mit der Annahme des <a href="antrag">Antrages</a> durch die Gesellschaft wird der Versicherungsschutz definitiv. Der Ablauf des Versicherungsschutzes ist für die einzelnen Leistungen in der Police festgelegt. Wird die Prämienzahlung vorzeitig eingestellt, erlischt die Deckung ganz oder teilweise gemäss den Versicherungsbestimmungen der betreffenden Versicherungsart. Der Geltungsbereich des Versicherungsschutzes erstreckt sich in der Lebensversicherung - im Gegensatz zu den meisten anderen Branchen - auf die ganze Welt; unabhängig davon, wo das versicherte Ereignis eingetreten ist. Man spricht daher von einer "Weltpolice".', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(468, 1, 'Versicherung für fremde Rechnung', 'versicherung-fur-fremde-rechnung', NULL, 'Wie die <a href="versicherung-auf-fremdes-leben">Versicherung auf fremdes Leben</a> ist auch die Versicherung für fremde Rechnung eine Fremdversicherung. Mit der Versicherung für fremde Rechnung versichert der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> fremde Objekte (Sachen, Vermögen). Zur Prämienzahlung ist der Versicherungsnehmer verpflichtet, die <a href="versicherungsleistungen">Versicherungsleistung</a> steht hingegen dem Eigentümer oder Inhaber des entsprechenden Objektes  (<a href="vvg-1">VVG</a> 17).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(469, 1, 'Versicherung zu Gunsten Dritter', 'versicherung-zu-gunsten-dritter', NULL, 'Eine <a href="personenversicherung">Personenversicherung</a>, bei welcher der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> einen <a href="begunstigte-r">Begünstigten</a> bezeichnet, dem die <a href="versicherungsleistungen">Versicherungsleistung</a> ganz oder teilweise zustehen soll.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(470, 1, 'Versicherungsmodell', 'versicherungsmodell', NULL, 'Das KVG bietet einen Spielraum für kostensparende Versiche-rungsmodelle. Verbreitet sind das <a href="hmo-modell-1">HMO-Modell</a>, das <a href="hausarzt-modell">Hausarzt-Modell</a>, das <a href="light-modell">Light-Modell</a> und das <a href="telemed-modell-1">Telemed-Modell</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(471, 1, 'Versicherungsobligatorium (Hausrat)', 'versicherungsobligatorium-hausrat', NULL, 'Verschiedene Kantone schreiben für den <a href="hausrat">Hausrat</a> eine Versicherungspflicht bei <a href="feuer">Feuer</a> und <a href="elementargefahr">Elementargefahren</a> vor.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(472, 1, 'Tierversicherung', 'tierversicherung', NULL, 'Tiere können u. a. gegen Unfall- und Krankheitskosten versichert werden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(473, 1, 'Vermögensversicherung', 'vermogensversicherung', NULL, 'Neben den <a href="sachversicherung">Sachversicherungen</a> und den <a href="personenversicherung">Personenversicherungen</a> gibt es die Gruppe der Vermögensversicherungen. Sie übernehmen Vermögensverluste und -einbussen infolge eines versicherten Ereignisses (z.B.: <a href="haftpflichtversicherung">Haftpflichtversicherung</a>, <a href="rechtsschutzversicherung">Rechtsschutzversicherung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(474, 1, 'Gebäudeversicherung', 'gebaudeversicherung', NULL, 'Die Gebäudeversicherung kommt für <a href="schaden">Schäden</a> an Gebäuden und Gebäudebestandteilen auf, die durch <a href="feuer">Feuer</a>, <a href="wasser-schaden">Wasser</a> und Glasbruch entstehen. In der Gebäudeversicherung sind auch zur Wohnung gehörende Einrichtungsgegenstände mitversichert (Einbauschränke, Öfen, Herde, Bäder, Fensterläden, etc.).<br />Die Versicherung von Gebäuden gegen Feuerschäden ist in den meisten Kantonen obligatorisch. <br />Der Versicherungsschutz und die <a href="pramie">Prämien</a> sind von Kanton zu Kanton verschieden. In der Regel wird der Schaden versichert, der durch <a href="brand">Brand</a>, Blitzschlag, <a href="explosion-1">Explosionen</a>und <a href="elementarschaden">Elementarschäden</a> entsteht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(475, 1, 'Sachversicherung', 'sachversicherung', NULL, 'Sachversicherung ist wie der Begriff der "<a href="personenversicherung">Personenversicherung</a>" ein Sammelbegriff für bestimmte Versicherungsbranchen. Schäden, die durch Beschädigung, Zerstörung oder Wegnahme von <a href="fahrhabe">Fahrhabe</a> oder Gebäude entstehen, werden durch Sachversicherungen entschädigt. Zur Gruppe der Sachversicherungen gehören u.a. die <a href="hausrat">Hausrat-</a> und die <a href="gebaudeversicherung">Gebäudeversicherung.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(476, 1, 'Personenversicherung', 'personenversicherung', NULL, 'Die Personenversicherung umfasst alle Versicherungsarten, bei denen eine Person hinsichtlich Heilungskosten, vorübergehendem oder dauerndem Erwerbsausfall, Tod oder Alter versichert ist (Lebensversicherung, Krankenversicherung, Unfallversicherung).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(477, 1, 'Wertsachenversicherung', 'wertsachenversicherung', NULL, 'Die Wertsachenversicherung gehört in die Gruppe der <a href="sachversicherung">Sachversicherungen.</a> Sie übernimmt Schäden an Schmucksachen, Pelzen, Bildern, Musikinstrumenten und anderen persönlichen Wertsachen infolge von <a href="diebstahl">Diebstahl</a>, <a href="feuer">Feuer</a> und <a href="wasser-schaden">Wasser.</a> Neben den herkömmlichen <a href="risiko">Risiken</a> können in der Regel auch Beschädigungen und Zerstörung, Abhandenkommen und <a href="verlegen-verlieren">Verlieren</a> versichert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(478, 1, 'Versicherung auf ein Leben', 'versicherung-auf-ein-leben', NULL, '<a href="lebensversicherung">Lebensversicherung</a> mit einer Police, in der eine einzige Person versichert ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(479, 1, 'Versicherung auf fremdes Leben', 'versicherung-auf-fremdes-leben', NULL, 'Bei der Versicherung auf fremdes Leben schliesst der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> eine Lebensversicherung nicht auf sein eigenes Leben, sondern auf das Leben einer Drittperson ab. Versicherungsnehmer und <a href="versicherte-person">versicherte Person</a> sind somit nicht identisch, weshalb man auch von einer Fremd- oder Drittlebensversicherung spricht. Die Rechte und Pflichten aus dem <a href="versicherungsvertrag">Versicherungsvertrag</a> stehen nur dem Versicherungsnehmer, nicht aber der versicherten Person zu. Der Abschluss einer Versicherung auf fremdes Leben braucht die vorgängige schriftliche Zustimmung des versicherten Dritten, damit er gültig ist (<a href="vvg-1">VVG</a> 74).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(480, 1, 'Versicherung auf zwei Leben', 'versicherung-auf-zwei-leben', NULL, '<a href="lebensversicherung">Lebensversicherung</a> mit einer Police, in der zwei Personen versichert sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(481, 1, 'Vorsorgefall', 'vorsorgefall', NULL, 'Der Vorsorgefall entspricht dem <a href="versicherungsfall">Versicherungsfall</a> im Rahmen des BVG.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(482, 1, 'Versicherungspolice', 'versicherungspolice', NULL, 'Die Versicherungspolice ist eine Vertragsurkunde, welche die gegenseitigen Rechte und Pflichten der Parteien umschreibt. Sie dient lediglich als Beweis für den Vertragsinhalt. Insbesondere wird darin festgehalten wer <a href="versicherungsnehmer-in">Versicherungsnehmer</a>, <a href="versicherte-person">versicherte Person</a> und <a href="begunstigte-r">Begünstigter</a> ist. Das Dokument enthält ebenfalls die versicherte Leistung, deren Fälligkeit sowie die Gegenleistung des Versicherungsnehmers, die <a href="pramie">Prämie</a> und deren Fälligkeit. Die Details sind in den <a href="avb">AVB</a> sowie in den allenfalls nötigen besonderen Bestimmungen festgehalten. Sie sind ein wesentlicher Bestandteil der Versicherungspolice. Werden Leistungen fällig, so ist die <a href="police">Police</a> vorzulegen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(483, 1, 'Versicherungsbedarf', 'versicherungsbedarf', NULL, 'Entsteht, wenn die möglichen finanziellen Folgen des Eintritts einer <a href="gefahr">Gefahr</a> trotz Massnahmen zur Schadenvermeidung und Schadenverminderung so gross sind, dass eine Person sie nicht selbst tragen kann oder möchte. Die Ermittlung des Versicherungsbedarfs ist Teil des <a href="riskmanagement-rm">Riskmanagements</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(484, 1, 'Versicherungsaufsicht', 'versicherungsaufsicht', NULL, 'Die öffentlich-rechtliche Versicherungsaufsicht wird von staatlichen Behörden wahrgenommen, die für das Versicherungswesen zuständig sind (z.B. <a href="efd">EFD</a>, <a href="finma">FINMA</a>). Diese überwachen gemäss <a href="versicherungsaufsichtsgesetz-vag">Versicherungsaufsichtsgesetz (VAG)</a> die Einhaltung der einschlägigen Gesetzesvorschriften.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(485, 1, 'Versicherungsaufsichtsgesetz (VAG)', 'versicherungsaufsichtsgesetz-vag', NULL, 'Gesetz, das die Versicherungsaufsicht regelt und für alle Privatversicherer (Direktversicherer und Rückversicherer) gültig ist, die in der Schweiz tätig sind. Das VAG befasst sich primär mit der <a href="bewilligung">Bewilligung</a> zum Geschäftsbetrieb (durch das <a href="efd">EFD</a>) und der Überwachung der Geschäftstätigkeit (durch die <a href="finma">FINMA</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(486, 1, 'Versicherungswert', 'versicherungswert', NULL, 'Der Geldwert, den die versicherte Sache im Zeitpunkt des Vertragsschlusses aufweist.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(487, 1, 'Vermögensbildende Versicherung', 'vermogensbildende-versicherung', NULL, 'Vermögensbildende Versicherungen, auch kapitalbindende genannt, sind Lebensversicherungen, mit denen ein Sparprozess in irgendeiner Form verbunden ist.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(488, 1, 'Versicherung, prämienfreie', 'versicherung-pramienfreie', NULL, 'Eine prämienfreie Versicherung ist ein Vertrag, für den der Kunde keine <a href="pramie">Prämien</a> mehr bezahlt. Bei der <a href="pramienfreistellung">Prämienfreistellung</a>wird der vorhandene <a href="ruckkaufswert">Rückkaufswert</a> dazu verwendet (als <a href="einmaleinlage-einmalpramie">Einmaleinlage</a>), die <a href="versicherungssumme">Versicherungssumme</a> neu (normalerweise tiefer als zu Beginn) zu bestimmen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(489, 1, 'Versicherungsjahr', 'versicherungsjahr', NULL, 'Entspricht nicht (unbedingt) einem Kalenderjahr. Wurde eine Versicherung im April abgeschlossen, so dauert ihr Versicherungsjahr vom 1.4. des einen Jahres bis zum 31.3. des Folgejahres.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(490, 1, 'Versicherungsleistungen', 'versicherungsleistungen', NULL, 'Die vertragliche Leistung des <a href="versicherer">Versicherers</a> nach einem <a href="versicherungsfall">Versicherungsfall</a>. Sie kann eine Geldleistung oder eine Dienstleistung sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(491, 1, 'Versicherungssumme', 'versicherungssumme', NULL, 'Die in der <a href="versicherungspolice">Versicherungspolice</a>  festgehaltene Summe, die fällig wird, wenn das versicherte Ereignis eintritt. Die Versicherungssumme (VS) wird von den Parteien frei vereinbart, richtet sich aber in der <a href="sachversicherung">Sachversicherung</a> nach dem Wert der Sache.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(492, 1, 'Versicherungsfall', 'versicherungsfall', NULL, 'Ein Versicherungsfall liegt vor, wenn sich der Tatbestand erfüllt, der grundsätzlich eine Leistungspflicht des <a href="versicherer">Versicherers</a>, bzw. einen Leistungsanspruch des <a href="versicherter">Versicherten</a> entstehen lässt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(493, 1, 'Versicherter', 'versicherter', NULL, 'Der Versicherte ist der Vertragspartner des <a href="versicherer">Versicherers.</a> Er bezahlt die <a href="pramie">Prämie</a> und erhält dafür von der Versicherung die im <a href="versicherungsvertrag">Versicherungsvertrag</a> vereinbarte Gegenleistung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(494, 1, 'Versicherte Person', 'versicherte-person', NULL, 'Allgemein: <br />Die Person, deren Sachen, Vermögen oder Person Gegenstand des <a href="versicherungsvertrag">Versicherungsvertrages</a> bildet. Sie ist auch <a href="anspruchsberechtigter">anspruchsberechtigt</a>. Synonym: der Versicherte.<br />\r<br />In der Personenversicherung:<br />Die Person, auf deren Leben die Versicherung gestellt ist, oder für die eine Unfall- oder Krankendeckung vereinbart ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(495, 1, 'Gefahr', 'gefahr', NULL, 'Die versicherte Gefahr ist der Tatbestand, für welchen der <a href="versicherungsvertrag">Versicherungsvertrag</a> <a href="versicherungsschutz">Versicherungsschutz</a> gewährt (Unfall, Erreichen eines bestimmten Alters, usw.). Zur Zeit des Vertragsabschlusses muss ungewiss sein, ob sich dieser Tatbestand überhaupt je oder wann er sich verwirklichen wird. Es muss sich um ein künftiges Ereignis handeln. Die versicherte Gefahr muss mit dem versicherten Gegenstand zusammenhängen. Die versicherte Gefahr ist von den <a href="gefahrstatsachen">Gefahrstatsachen</a> zu unterscheiden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(496, 1, 'Abredeversicherung', 'abredeversicherung', NULL, 'Bei einer Abredeversicherung bietet der <a href="versicherer">Versicherer</a> dem <a href="versicherter">Versicherten</a> im Rahmen des Unfallversicherungsrechts die Möglichkeit, die Nicht-Berufsunfallversicherung durch besondere Abrede zu verlängern. Ihr Zweck ist die Weiterversicherung, wenn die obligatorische Versicherung endet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(497, 1, 'Versicherer', 'versicherer', NULL, 'Der Vertragspartner (Aktiengesellschaft oder Genossenschaft) des <a href="versicherungsfall">Versicherungsnehmers</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(498, 1, 'Absicht', 'absicht', NULL, 'Der versicherungsrechtliche Begriff Absicht umfasst nicht nur den Willen des Täters zur Verursachung des versicherten <a href="schaden">Schadens</a> (Tatwille), sondern auch seinen Willen, dass die Schadensfolgen eintreten (Erfolgswille).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(499, 1, 'Wechselschilder', 'wechselschilder', NULL, 'Mit Wechselschildern kann ein <a href="halter">Halter</a>, der zwei <a href="motorfahrzeug">Motorfahrzeuge</a> besitzt, die gleichen <a href="kontrollschilder">Kontrollschilder</a> für diese beiden Fahrzeuge verwenden. Er darf aber immer nur ein Fahrzeug verwenden, welches mit den Schildern versehen sein muss.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(500, 1, 'Zins', 'zins', NULL, 'Zins ist eine Entschädigung für die Überlassung von Kapital. Man kann auch sagen, es ist die "Miete" für das Kapital. Zins wird grundsätzlich in absoluten Beträgen, z.B. Fr. 250.-, ausgedrückt. Wegen der einfacheren Vergleichbarkeit und Berechenbarkeit wird das Verhältnis Zins/Kapital in Prozenten, im Zinssatz, ausgedrückt. Wird der Zins im Verhältnis zum Kapital ausgedrückt, dann ist dies die Rendite.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(501, 1, 'Zinsmarge', 'zinsmarge', NULL, 'Wird einer Bank Geld anvertraut, dann muss sie dem Gläubiger einen Zins bezahlen (<a href="passivgeschaft">Passivgeschäft</a>). Das gleiche Geld wird durch die Bank in Form von Krediten weiter ausgeliehen (<a href="aktivgeschaft">Aktivgeschäft</a>). Dafür verlangt sie einen Zins. Der Aktivzins ist normalerweise höher als der Passivzins, die Differenz der beiden Zinssätze wird Zinsmarge} genannt (<a href="marge">Marge</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(502, 1, 'Vorauszahlungszins', 'vorauszahlungszins', NULL, '<a href="zins">Zins</a> für auf <a href="versicherungspolice">Versicherungspolicen</a> gewährte "Darlehen". Er ist vorschüssig zu Beginn des <a href="versicherungsjahr">Versicherungsjahres</a> zu bezahlen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(503, 1, 'Zinsabschlagssteuer', 'zinsabschlagssteuer-1', NULL, 'Dal 1° gennaio 1993, data della sua introduzione, gli istituti di credito tedeschi incaricati della tenuta di depositi sono in linea di principio obbligati a prelevare il 30% dei redditi da interessi distribuiti (i dividendi soggiacciono alla tassa sui redditi da capitale) a titolo di trattenuta e a versarli a favore dell''amministrazione delle finanze. Si tratta nella fattispecie di una tassa d''incasso che può essere in parte o interamente recuperata dal contribuente tramite la propria dichiarazione dell''imposta sul reddito. Nel caso di custodia a domicilio (in particolare per gli investitori che hanno acquistato quote a contanti, ossia allo sportello), l''aliquota è fissata al 35% dei redditi distribuiti. Fanno eccezione i fondi a capitalizzazione per i quali essa rimane invariata al 30% a prescindere dalla tenuta in deposito o a domicilio delle quote.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(504, 1, 'Nachdeckung', 'nachdeckung', NULL, 'Versicherungsschutz der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> für die Gefahren Tod und <a href="invaliditat">Invalidität</a> über die Beendigung des Arbeitsverhältnisses hinaus.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(505, 1, 'Zwischengewinnbesteuerung', 'zwischengewinnbesteuerung', NULL, 'L''administration fiscale allemande ayant constaté que de nombreux investisseurs vendaient leurs parts juste avant la clôture annuelle de l''exercice des fonds afin d''échapper ainsi à l''imposition des revenus (considérés comme plus-values exonérées d''impôts), le législateur allemand a introduit l''imposition dite des bénéfices intermédiaires le 1er janvier 1994. Aux termes de celle-ci, l''imposition porte sur les intérêts cumulés par le fonds (ne concerne pas les dividendes) jusqu''à la date de vente ou de rachat des parts de fonds étrangers. La vente ou le rachat avant la date de la clôture annuelle du fonds ne permet donc plus de dégager des revenus exonérés d''impôt.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(506, 1, 'Innenverhältnis', 'innenverhaltnis', NULL, 'Rechtsbeziehung mehrerer verbundener Personen untereinander; bei der <a href="solidarhaftung">Solidarhaftung</a> der <a href="haftpflicht">Haftpflicht</a>igen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(507, 1, 'Innere Unruhe', 'innere-unruhe', NULL, 'Gewalttätigkeiten gegen Personen oder Sachen, die anlässlich von Zusammenrottung, Krawall oder Tumult begangen werden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(508, 1, 'Innerer Wert', 'innerer-wert', NULL, 'Siehe <a href="nettoinventarwert">Nettoinventarwert</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(509, 1, 'Invalidenversicherung', 'invalidenversicherung', NULL, 'Zusammen mit der <a href="ahv-1">AHV</a> stellt die Invalidenversicherung (IV) die erste Säule des <a href="drei-saulenkonzept">Drei-Säulen-Konzepts</a> dar. Wie die AHV ist auch die IV obligatorisch. Versichert sind alle in der Schweiz wohnhaften, diejenigen, die in der Schweiz eine Erwerbstätigkeit ausüben oder die im Ausland für einen schweizerischen Arbeitgeber tätig sind. Das Gesetz über die eidgenössische Invalidenversicherung trat am 1. Januar 1960 in Kraft.<br />Erster Zweck der Invalidenversicherung ist die Wiedereingliederung des invalid gewordenen Versicherten.<br />Anspruch auf Invalidenrente besteht nach erfolgtem Eingliederungsverfahren und wenn die <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a>ununterbrochen während 365 Tagen bestand. Die Höhe der Invalidenrente hängt u. a. vom Grad der Invalidität ab. Neben den einfachen und Ehepaar-Invalidenrenten richtet die IV auch Zusatzrenten für die Ehefrau sowie Kinderrenten aus.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(510, 1, 'Inventar', 'inventar', NULL, 'Monetäre Bewertung der Einrichtungen und Waren eines Unternehmens zur Bestimmung der <a href="versicherungssumme">Versicherungssumme</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(511, 1, 'Anlagewährung', 'anlagewahrung', NULL, 'Währung, in der ein <a href="anlagefonds">Anlagefonds</a> seine Anlagen tätigt. Nicht zu verwechseln mit der <a href="referenzwahrung">Referenzwährung</a> und der <a href="rechnungswahrung">Rechnungswährung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(512, 1, 'Anlagefonds', 'anlagefonds', NULL, 'Ein Anlagefonds ist ein Vermögen, das auf Grund öffentlicher Werbung von einer Vielzahl voneinander unabhängiger Anleger zur gemeinschaftlichen Kapitalanlage aufgebracht und von einer <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> nach dem Grundsatz der Risikostreuung (<a href="diversifikation-risikostreuung">Diversifikation</a>) für Rechnung der Anleger verwaltet wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(513, 1, 'Anlagehorizont', 'anlagehorizont', NULL, 'Die Zeit, für die ein <a href="anleger">Anleger</a> einen Teil seines Vermögens investieren will.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(514, 1, 'Anlageinstrumente', 'anlageinstrumente', NULL, 'Die verschiedenen Anlagekategorien, z.B. <a href="aktie">Aktien</a>, <a href="obligationen">Obligationen</a> und Geldmarktpapiere.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(515, 1, 'Anlagepolitik', 'anlagepolitik', NULL, 'La politica d''investimento stabilisce la procedura da seguire per raggiungere l''obiettivo d''investimento (selezione dei titoli, timing per l''acquisto risp. la vendita, quota di liquidità e così via).', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(516, 1, 'Anlagegrundsätze', 'anlagegrundsatze', NULL, 'Die Anlagegrundsätze charakterisieren und spezifizieren den Fonds. Der <a href="anleger">Anleger</a> erhält Informationen über die im <a href="portefeuille">Portefeuille</a> gehaltenen Wertpapiere, die <a href="anlagewahrung">Anlagewährung</a>, die geografische Reichweite der Anlagen sowie die <a href="diversifikation-risikostreuung">Risikostreuung</a> des Fonds.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(517, 1, 'Anlagevorschriften', 'anlagevorschriften', NULL, 'Anlagevorschriften sind gesetzliche Bestimmungen, die die Anlage des Vermögens der Lebensversicherungsgesellschaften und der Personalvorsorgeeinrichtungen nach den Kriterien Sicherheit, <a href="rendite">Rendite</a> und Liquidität regeln. Zu diesem Zweck kann der Gesetzgeber einen Katalog der zulässigen Anlagen aufstellen oder beispielsweise gewisse Anlagen verbieten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(518, 1, 'Anleger', 'anleger', NULL, 'Mit dem Kauf von <a href="anteil">Anteilen</a> erwirbt der Anleger das Recht auf eine seinem Anteil entsprechende Beteiligung am Vermögen und am <a href="ertrag">Ertrag</a> des Fonds.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(519, 1, 'ISIN-Nummer', 'isin-nummer', NULL, 'International Securities Identification Number. International anerkannte Wertschriftenkennziffer. In der Schweiz <a href="valorennummer">Valorennummer</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(520, 1, 'Emission', 'emission', NULL, 'Ausgabe von neuen Wertschriften.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(521, 1, 'Ausgabepreis', 'ausgabepreis', NULL, 'Preis zu dem <a href="anleger">Anleger</a> Fondsanteile zeichnen können. Er entspricht dem <a href="nettoinventarwert">Nettoinventarwert</a> eines <a href="anteil">Anteils</a>, zuzüglich der <a href="ausgabekommission">Ausgabekommission</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(522, 1, 'Emittent', 'emittent', NULL, 'Persona giuridica o ente di diritto pubblico incaricato di raccogliere capitale di terzi tramite l''emissione di titoli.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(523, 1, 'Ausgabekommission', 'ausgabekommission', NULL, 'Kommission, die von der ausgebenden Fondsgesellschaft bei der Zeichnung von <a href="anteil">Anteilen</a> dem Käufer belastet wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(524, 1, 'IV', 'iv', NULL, 'Abkürzung für <a href="invalidenversicherung">"Invalidenversicherung"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(525, 1, 'Gütergemeinschaft', 'gutergemeinschaft', NULL, 'Le régime matrimonial de la communauté de biens se compose des biens communs (fortune et revenus professionnels des époux) et des <a href="biens-propres">biens propres</a> de chacun des conjoints (effets personnels et droits à des créances pour réparation d''un tort moral). Les biens communs appartiennent indivisément aux deux époux, les biens propres reviennent à celui auquel ils appartiennent en propre.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(526, 1, 'Solidarhaftung', 'solidarhaftung', NULL, 'Solidarhaftung liegt dann vor, wenn mehrere Ersatzpflichtige für denselben <a href="schaden">Schaden</a> gemeinsam einstehen müssen. Jeder einzelne Ersatzpflichtige haftet für die ganze Entschädigung.<br />Der <a href="geschadigte">Geschädigte</a> kann sich mit seinen Forderungen an den Ersatzpflichtigen seiner Wahl halten.<br />Die Ersatzpflichtigen regeln die Aufteilung des Schadenersatzes unter sich (<a href="ruckgriff">Rückgriff</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(527, 1, 'Junk Bond Funds', 'junk-bond-funds', NULL, 'Siehe <a href="high-yield-funds-1">High-Yield Funds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(528, 1, 'Erdrutsch', 'erdrutsch', NULL, '<a href="elementargefahr">Elementargefahr</a>, bei der Schäden durch rutschende Erdmassen entstehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(529, 1, 'Large Caps', 'large-caps-1', NULL, 'Gesellschaften mit sehr grosser <a href="borsenkapitalisierung">Börsenkapitalisierung</a> im Verhältnis zum Börsenmarkt, auf dem sie präsent sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(530, 1, 'Late Stage', 'late-stage-1', NULL, 'Von Late-Stage-Finanzierungen spricht man bei reifen, schon seit einigen Jahren bestehenden Unternehmen. Solche Investitionen sind in der Regel mit höheren Anlagebeträgen verbunden, dafür aber liquider und weniger riskant; die Unternehmung kann schneller veräussert oder an die Börse gebracht werden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(531, 1, 'Gesetz der grossen Zahl', 'gesetz-der-grossen-zahl', NULL, 'Durch statistische Verarbeitung einer grossen Zahl von Personen werden Gesetzmässigkeiten im Schadenfall ersichtlich. Sie dienen als Basis für die versicherungsmathematische Berechnung des Risikoteils der <a href="pramie">Prämie</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(532, 1, 'Versicherungsvertragsgesetz', 'versicherungsvertragsgesetz', NULL, 'Bundesgesetz über den <a href="versicherungsvertrag">Versicherungsvertrag</a> vom 2. April 1908 (obligationenrechtliches Spezialgesetz).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(533, 1, 'Geleaste Sache', 'geleaste-sache', NULL, 'Siehe <a href="anvertraute-sache-geleaste-oder-gemietete">anvertraute Sache</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(534, 1, 'Leasing', 'leasing', NULL, 'Leasing ist das Vermieten von Gütern, wobei die Mietzahlungen bei einem eventuellen späteren Kauf angerechnet werden können.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(535, 1, 'Beirat', 'beirat', NULL, 'Der Beirat kann als "Vormund minderen Grades" bezeichnet werden. Er wird einer Person zum Schutz ihrer Vermögensinteressen zur Seite gestellt, deren Handlungsfähigkeit damit teilweise eingeschränkt wird. Ein Beirat ist zu bestellen, wenn eine schutzbedürftige Person ihre wirtschaftliche Existenz oder die ihrer unterstützungsberechtigten Angehörigen gefährdet.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(536, 1, 'Juristische Person', 'juristische-person', NULL, 'Une personne morale est une entité artificielle créée par le droit. Elle résulte du groupement de personnes physiques au sein d''une forme d''organisation prévue par la loi. Les sociétés détiennent en outre un capital. Les personnes morales ont des droits et des obligations, à l''instar des personnes physiques. Exemples: association, fondation, société anonyme, coopérative, Sàrl.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(537, 1, 'Rechtsverfahren', 'rechtsverfahren', NULL, 'Rechtsverfahren können einen <a href="vermogensbedarf">Vermögensbedarf</a> verursachen, der durch Leistungen einer <a href="rechtsschutzversicherung">Rechtsschutzversicherung</a> abgedeckt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(538, 1, 'Rechtsschutzversicherung', 'rechtsschutzversicherung', NULL, 'Die Rechtsschutzversicherung schützt den <a href="versicherungsnehmer-in">Versicherungsnehmer</a> gegen den <a href="schaden">Schaden</a> (finanzieller Aufwand), der aus der Inanspruchnahme einer Rechtshilfe entsteht.<br />Die Leistung der Rechtsschutzversicherung erfolgt in der Form von Dienstleistungen oder in Form einer Entschädigung.<br />Häufig wird unterschieden zwischen Privat-, Verkehrs-, oder Berufs-Rechtsschutzversicherung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(539, 1, 'Aktivgeschäft', 'aktivgeschaft', NULL, 'Bankgeschäfte, welche ihren Niederschlag auf der Aktivseite <a href="aktiven">Aktiven</a> der <a href="bilanz">Bilanz</a> finden. Insbesondere sind dies alle Arten von Kreditgewährungen wie Blankokredite, gedeckte Kredite, Hypotheken etc.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(540, 1, 'Leverage', 'leverage-1', NULL, 'Mit vergleichsweise geringem Kapitaleinsatz lassen sich mit derivativen Instrumenten grössere <a href="performance">Renditen</a> erzielen, als es bei einer Investition in den eigentlichen Basiswerten möglich ist. Dieser Effekt wird als Leverage bezeichnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(541, 1, 'Passiven', 'passiven', NULL, 'Rechte Seite der <a href="bilanz">Bilanz</a> einer Unternehmung, welche die Schuldner oder Verbindlichkeiten aufzeigt. Die Passiven zeigen auch, wie eine Unternehmung finanziert ist. Gegenstück <a href="aktiven">Aktiven.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(542, 1, 'Leibrente', 'leibrente', NULL, 'Eine Leibrente wird an den jeweiligen Fälligkeitsterminen ausbezahlt, solange die versicherte Person lebt. Der Vertrag zur Begründung einer Leibrente bedarf der Schriftlichkeit. Vielfach werden die aufgeschobenen <a href="altersrenten">Altersrenten</a> als Leibrenten, die sofort beginnenden als Altersrenten bezeichnet. In der Praxis werden beide Begriffe synonym gebraucht. Als Rentenversicherung ist sie von der Leibrente nach Obligationenrecht (OR) zu unterscheiden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(543, 1, 'Lebenserwartung', 'lebenserwartung', NULL, 'Statistisch erhobene Lebensdauer für Personen eines bestimmten Alters.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(544, 1, 'Lebensversicherung', 'lebensversicherung', NULL, 'Versicherung zum Schutz vor den finanziellen Folgen der Lebensrisiken Alter, Tod und <a href="invaliditat">Invalidität</a> bzw. <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a>. Das Angebot umfasst die <a href="einzellebensversicherung">Einzellebensversicherung</a> und die <a href="kollektivversicherung">kollektive Lebensversicherung</a> und wird v. a. im Rahmen der <a href="dritte-saule">Dritten Säule</a> genutzt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(545, 1, 'Erlebensfallversicherung', 'erlebensfallversicherung', NULL, 'Bei der Erlebensfallversicherung wird das vereinbarte Kapital fällig, wenn der <a href="versicherter">Versicherte</a> einen bestimmten Zeitpunkt erlebt. In dieser reinen Form existiert der Vertrag jedoch kaum mehr. In der Regel ist die <a href="pramienruckgewahr">Prämienrückgewähr</a>  eingeschlossen. Dadurch werden im Todesfall die einbezahlten <a href="pramie">Prämien</a> ohne Zins ganz oder teilweise an den <a href="begunstigte-r">Begünstigten</a>zurückbezahlt. Da eine Erlebensfallversicherung für die Versicherungsgesellschaft keine Risikoleistung enthält, kann auf eine <a href="gesundheitsprufung">Gesundheitsprüfung</a> verzichtet werden. Sie eignet sich daher auch für jene Fälle, in denen der Abschluss einer anderen Lebensversicherung aus Gesundheitsgründen nicht möglich ist. Die Erlebensfallversicherung kann als Form der Kapitalanlage angesehen werden, besonders wenn sie mit einer <a href="einmaleinlage-einmalpramie">Einmaleinlage</a> - Prämie abgeschlossen wird. Finanziert mit periodischen Prämien und unter Mitversicherung der <a href="pramienbefreiung">Prämienbefreiung</a> bei <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a>stellt sie ein sicheres und interessantes Mittel zur Kapitalbildung dar.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(546, 1, 'Drittlebens-Versicherung', 'drittlebens-versicherung', NULL, 'Siehe <a href="versicherung-auf-fremdes-leben">Versicherung auf fremdes Leben</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(547, 1, 'Erlebensfallversicherung mit Rückgewähr', 'erlebensfallversicherung-mit-ruckgewahr', NULL, 'Bei einem allfälligen Tod vor dem Schlussalter werden die bis dahin bezahlten <a href="pramie">Prämien</a> ohne Zins von der Versicherungsgesellschaft zurückerstattet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(548, 1, 'Erlebensfallversicherung ohne Rückgewähr', 'erlebensfallversicherung-ohne-ruckgewahr', NULL, 'Bei einem allfälligen Tod vor dem Schlussalter erlischt die Versicherung ohne jede Vergütung.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(549, 1, 'Lebenslange Rente', 'lebenslange-rente', NULL, '<a href="altersrenten">Altersrente</a> mit lebenslanger Dauer, d.h. der Versicherer zahlt die <a href="rente">Rente</a>n bis zum Tod des Versicherten (auch: <a href="leibrente">Leibrente</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(550, 1, 'Light-Modell', 'light-modell', NULL, 'Kostensparendes <a href="versicherungsmodell">Versicherungsmodell</a>, bei dem sich der Versicherte verpflichtet, im Krankheitsfall einen kostengünstigen Arzt auszusuchen, der auf der Ärzteliste seiner Krankenkasse verzeichnet ist. Je nach Kasse nimmt der Versicherte ggf. weitere Einschränkungen in Kauf (z.B. Generika statt Originalmedikamente).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(551, 1, 'Kleinmotorrad', 'kleinmotorrad', NULL, 'Kleinmotorräder sind einplätzige <a href="motorrader">Motorräder</a> ohne Seitenwagen mit einem Hubraum bis zu 50 cm3.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(552, 1, 'Mahnfrist', 'mahnfrist', NULL, 'Zeitraum, den der <a href="glaubiger">Gläubiger</a> dem Schuldner zur Bezahlung einer fälligen Forderung ansetzt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(553, 1, 'Limite / Limitierung', 'limite-limitierung', NULL, 'Eine Limitierung ist eine summenmässige Leistungsbegrenzung, die unter der festgelegten <a href="versicherungssumme">Versicherungssumme</a> liegt. Im Schadenfall wird nur bis zum festgesetzten Höchstbetrag vergütet, auch wenn die Schadensumme höher liegt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(554, 1, 'Haftungsprivileg', 'haftungsprivileg', NULL, 'Der Schädiger ist gegenüber seinen <a href="uvg">UVG</a>-versicherten Arbeitnehmern, bzw. Familienangehörigen von der <a href="haftung">Haftung</a> befreit, sofern er den <a href="schaden">Schaden</a> nicht absichtlich oder grobfahrlässig verursacht hat (ATSG 75).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(555, 1, 'Erbstamm', 'erbstamm', NULL, 'Der Stamm (häufig auch Parentel genannt) umfasst eine Person (Stammesvater bzw. -mutter) mit all ihren Nachkommen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(556, 1, 'Versicherung, gebundene', 'versicherung-gebundene', NULL, 'Die gebundene <a href="vorsorgepolice">Vorsorge-Police</a> (Säule 3a) ist steuerlich stark privilegiert, die Verfügungsgewalt des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> über einen solchen Vertrag ist allerdings auch sehr stark eingeschränkt. Siehe auch <a href="vorsorge">Vorsorge</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(557, 1, 'Gebundene Vorsorge', 'gebundene-vorsorge', NULL, 'Die gebundene Vorsorge verkörpert das steuerprivilegierte Sparen im Hinblick auf die Altersvorsorge. Sie ist gedacht zur Ergänzung der 1. und 2. Säule. Weil die zurückgelegten Mittel ausschliesslich und unwiderruflich der Vorsorge dienen müssen, wird diese Form als "gebunden" bezeichnet. Da das Sparen steuerprivilegiert ist, sind aufgrund gesetzlicher Vorschriften, Einschränkungen beim Abschluss, der Gestaltung und bei der Verfügung der Ansprüche vorhanden. Neben der Altersvorsorge lassen sich auch Tod und Invalidität individuell und bedarfsgerecht gestalten und kombinieren. Als anerkannte Vorsorgeformen gelten die gebundene Vorsorgeversicherung bei einer Versicherungsgesellschaft und die gebundene Vorsorgevereinbarung bei einer Bankstiftung.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(558, 1, 'Liquidität', 'liquiditat', NULL, 'Mit Anlagefondsanteilen ist der Anteilinhaber liquide, das heisst, er kann seine <a href="anteil">Anteile</a> in der Regel jederzeit zurückgeben. Die Fondsgesellschaften sind verpflichtet, Anteile ohne Kündigungsfristen zum jeweiligen <a href="rucknahmepreis">Rücknahmepreis</a> zurückzunehmen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(559, 1, 'Kotierung', 'kotierung', NULL, 'Admission d''un titre à la Bourse, généralement assortie de conditions.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(560, 1, 'Vorauszahlung', 'vorauszahlung', NULL, 'Siehe <a href="belehnung">Belehnung.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(561, 1, 'Darlehenswert', 'darlehenswert', NULL, 'Betrag, der im Rahmen des <a href="ruckkaufswert">Rückkaufswertes</a> als verzinsliches Darlehen bezogen werden kann.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(562, 1, 'Standortversicherung', 'standortversicherung', NULL, 'In der <a href="hausratversicherung">Hausratversicherung</a> schützt die Standortversicherung die versicherten Sachen an ihrem Standort oder am Arbeitsplatz.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(563, 1, 'Lombardkredit', 'lombardkredit', NULL, 'Bankkredit, wobei die Wertschriften des Kreditnehmers der Bank als Sicherheit verpfändet werden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(564, 1, 'Lombardpolitik', 'lombardpolitik', NULL, 'Die Lombardpolitik gehört zu den klassischen Instrumenten der Geldpolitik. Gegen Hinterlegung von erstklassigen Wertpapieren können Banken zur Überbrückung eines vorübergehenden Liquiditätsengpasses bei der Zentralbank (<a href="nationalbank">Nationalbank</a>) Geld aufnehmen. Der Zins, der für diesen <a href="lombardkredit">Lombardkredit</a> bezahlt werden muss, heisst Lombardsatz. Durch Erhöhung oder Senkung des Lombardsatzes, Beschränkung oder Erweiterung der Lombardkontingente oder Änderung der qualitativen Anforderungen an lombardfähige Wertpapiere kann die Notenbank die Liquidität der Geschäftsbanken und damit den Preis für Kredite beeinflussen, was sich beispielsweise über die Investitionstätigkeit auf die Konjunkturlage auswirken kann. Die Lomardpolitik spielt in der Schweiz eine untergeordnete Rolle. Hauptinstrument der schweizerischen Geldpolitik bildet der An- und Verkauf von Devisen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(565, 1, 'Long/Short', 'long-short-1', NULL, 'Die unter diesem Begriff zusammengefassten Strategien optimieren <a href="performance">Rendite</a> und <a href="risiko-fonds">Risiko</a> durch die Kombination von Finanzinstrumenten. Der Kauf einer Aktie, die eine Wertsteigerung erwarten lässt (= Long-Seite), wird kombiniert mit dem gleichzeitigen Verkauf einer anderen <a href="aktie">Aktie</a>, von der eine Wertsenkung erwartet wird (= Short-Seite). <a href="leerverkauf">Leerverkäufe</a> und Kreditfinanzierungen für Investments sind ausdrücklich erlaubt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(566, 1, 'Long-term Funds', 'long-term-funds-1', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in <a href="obligationen">Obligationen</a> mit einer (<a href="restlaufzeit">Rest-</a>)<a href="laufzeit">Laufzeit</a> von mindestens 5 Jahren investieren.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(567, 1, 'Schadenminderungs- und Rettungskosten', 'schadenminderungs-und-rettungskosten', NULL, '<a href="mittelbarer-schaden">Mittelbarer Schaden</a> (Folgekosten) eines <a href="schaden">Schaden</a>ereignisses, der dadurch entsteht, dass ein versicherter Gegenstand gerettet oder vor grösserem Schaden bewahrt wird. Die meisten Versicherungen decken diese Kosten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(568, 1, 'Verlust der Urteilsfähigkeit', 'verlust-der-urteilsfahigkeit', NULL, 'Vorübergehender Verlust der Urteilsfähigkeit des <a href="lenker">Lenkers</a> liegt z.B. dann vor, wenn dieser eine Herzattacke hat oder ohnmächtig wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(569, 1, 'Versorgerschäden', 'versorgerschaden', NULL, 'Ein Versorgerschaden ist derjenige Schaden, der durch die Tötung oder Verletzung eines Versorgers ( z.B.: Ehemann und -frau, Vater, Mutter) entsteht, weil die unterstützungspflichtigen Personen ihren Versorger verlieren.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(570, 1, 'Arbeitsausfall', 'arbeitsausfall', NULL, 'Eine Voraussetzung für eine <a href="arbeitslosenentschadigung">Arbeitslosenentschädigung</a>. Der Arbeitsausfall ist anrechenbar, wenn er einen Verdienstausfall zur Folge hat und mindestens zwei aufeinanderfolgende volle Arbeitstage dauert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(571, 1, 'Schadenverhütungskosten', 'schadenverhutungskosten', NULL, 'Kosten, die der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> tragen muss, wenn Drittpersonen etwas unternehmen, um einen unmittelbar drohenden, versicherten <a href="schaden">Schaden</a> zu verhindern.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(572, 1, 'Verlegen / Verlieren', 'verlegen-verlieren', NULL, 'Verlegen und Verlieren liegt dann vor, wenn der Besitzer den letzten Standort einer Sache nicht mehr nennen kann. Sachen, die durch Verlegen oder Verlieren abhanden kommen, sind in der <a href="hausratversicherung">Hausratversicherung</a> nicht versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(573, 1, 'Kapitalabfindung (BV)', 'kapitalabfindung-bv', NULL, 'Geldbetrag, der im Rahmen der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> als einmalige Altersleistung ausbezahlt wird. Dazu kommt die Abfindung für den überlebenden Ehegatten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(574, 1, 'Luxemburger Fonds', 'luxemburger-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die ihre Rechtsgrundlage im luxemburgischen Recht haben und von einer in Luxemburg domizilierten Fondsgesellschaft verwaltet werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(575, 1, 'Maschinenkaskoversicherung', 'maschinenkaskoversicherung', NULL, '<a href="technische-versicherungen">Technische Versicherung</a> und Variante der <a href="maschinenversicherung">Maschinenversicherung</a> für fahrbare Maschinen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(576, 1, 'Maschinenversicherung', 'maschinenversicherung', NULL, '<a href="technische-versicherungen">Technische Versicherung</a> für Unternehmen, die Anlagen, Maschinen, Installationen und Apparate gegen unvorhergesehene und plötzlich eintretende Beschädigungen oder Zerstörungen durch innere oder äussere Einwirkungen versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(577, 1, 'Malus', 'malus', NULL, 'Tarifliche Höhereinstufung aufgrund des individuellen schlechten Schadenverlaufes.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(578, 1, 'Verwaltungsgebühr', 'verwaltungsgebuhr', NULL, 'Entgelt zu Gunsten der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> für die Verwaltung eines <a href="anlagefonds">Anlagefonds</a>. Die Höhe der Verwaltungsgebühr wird in Prozenten beziehungsweise Zehntelprozenten auf dem <a href="fondsvermogen">Fondsvermögen</a>, oft auch in Basispunkten, ausgedrückt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(579, 1, 'Obligatorische Unfallversicherung', 'obligatorische-unfallversicherung', NULL, 'Das Bundesgesetz über die Unfallversicherung (UVG) trat am 1. Januar 1984 in Kraft.<br />Obligatorisch versichert sind die in der Schweiz beschäftigten Arbeitnehmer; in der Schweiz wohnhafte, selbständig Erwerbende und deren Familienmitglieder können sich freiwillig gemäss UVG versichern.<br />Die Leistungen des UVG werden bei Berufsunfällen, Nicht-Berufsunfällen und <a href="berufskrankheit">Berufskrankheit</a> gewährt. Dabei sieht das UVG Pflegeleistungen und Kostenvergütungen vor und richtet Taggelder und <a href="rente">Renten</a> aus.<br />Als versicherter Verdienst gilt der nach der Bundesgesetzgebung über die <a href="alters-und-hinterlassenenversicherung">Alters- und Hinterlassenenversicherung</a> (AHV) massgebende Lohn.<br />Die Unfallversicherung (UVG) wird entweder durch die Schweiz. Unfallversicherungsanstalt (SUVA) oder durch andere zugelassene <a href="versicherer">Versicherer</a> durchgeführt.<br />Die <a href="pramie">Prämie</a> für die obligatorische Versicherung trägt der Arbeitgeber; diejenige für die obligatorische Nicht-Berufsunfallversicherung darf dem Arbeitnehmer überbunden werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(580, 1, 'Zwingende Bestimmung', 'zwingende-bestimmung', NULL, 'Eine Gesetzesbestimmung, die nicht durch Parteivereinbarung abgeändert werden kann. Sie kann eine relativ (Änderung zu Gunsten des <a href="versicherter">Versicherten</a> möglich) oder absolut (keine Änderung möglich) zwingende Bestimmung sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(581, 1, 'Marge', 'marge', NULL, 'Differenz zwischen Aktiv- und Passivzinssätzen (<a href="aktiven">Aktiven</a>, <a href="passiven">Passiven</a>) der Bank sowie zwischen den An- und Verkaufspreisen von ausländischen Währungen, Devisen und Edelmetallen.<br />\r<br />Sicherheitsspanne in der Belehnung von Wertpapieren bei der Gewährung von <a href="lombardkredit">Lombardkrediten</a>. Die Marge dient als Vorsicht vor allfälligen Kursveränderungen nach unten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(582, 1, 'Börsenkapitalisierung', 'borsenkapitalisierung', NULL, 'Marktwert eines Unternehmens gemessen am Börsenkurs seiner <a href="aktie">Aktien</a> multipliziert mit der Anzahl aller ausstehender Titel. Auch Marktkapitalisierung genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(583, 1, 'Kurswert / Marktwert', 'kurswert-marktwert', NULL, 'Grundsätzlich ist der Kurswert / Marktwert einer Anleihe von zwei Einflusskategorien abhängig: <br />1. Wertpapierspezifischen Faktoren wie Zinscoupon,                      (Rest-) Laufzeit, Tilgungsbetrag, Schuldnerqualität usw. und <br />2. dem aktuellen Marktzinssatz und damit dem                fundamentalökonomischen Umfeld.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(584, 1, 'Marktrisiko', 'marktrisiko', NULL, 'Rischio legato a fattori che condizionano l''intero mercato e che non può essere ridotto o neutralizzato mediante la diversificazione di portafoglio.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(585, 1, 'Verkehrswert (Immobilienfonds)', 'verkehrswert-immobilienfonds', NULL, 'Der gegenwärtige, von unabhängigen Experten geschätzte Wert einer Liegenschaft, der bei sorgfältigem Verkauf zum Zeitpunkt der Schätzung wahrscheinlich erzielt würde. Die Schätzung wird in der Regel einmal jährlich, gemäss der von der Fondsleitung angewandten Schätzungsmethode vorgenommen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(586, 1, 'Sachschäden', 'sachschaden', NULL, 'Als Sachschaden wird der durch Beschädigung, Zerstörung oder Verlust einer Sache entstandene finanzielle <a href="schaden">Schaden</a> bezeichnet.<br />Auch bei Verletzung und Tötung von Tieren liegt ein Sachschaden vor.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(587, 1, 'Mutterschaftsentschädigung (MSE)', 'mutterschaftsentschadigung-mse', NULL, '<a href="sozialversicherung">Sozialversicherung</a>sleistung, die in der <a href="erwerbsersatzordnung">Erwerbsersatzordnung</a> (EO) geregelt wird. Die MSE wird beim Ausfall des Erwerbseinkommens selbstständig erwerbender oder angestellter Mütter in Form eines Taggelds für maximal 14 Wochen nach der Geburt des Kindes ausgezahlt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(588, 1, 'Deckungskapital', 'deckungskapital', NULL, 'Als Deckungskapital werden die zum <a href="technischer-zins">technischen Zins</a> aufgezinsten Sparteile der <a href="pramie">Prämien</a> bezeichnet. Wird der Sparteil der Prämie ohne Berücksichtigung der beim Abschluss entstehenden Kosten aufgezinst, sprechen wir vom Nettodeckungskapital. Wird zum Nettodeckungskapital eine Rückstellung für zukünftige Verwaltungskosten hinzugerechnet, ergibt sich das Inventardeckungskapital. Das Deckungskapital spielt auch bei der <a href="risikoversicherung">Risiko-Versicherung</a> eine gewisse Rolle, weil während der ganzen <a href="vertragsdauer">Vertragsdauer</a>gleichbleibende Prämien erhoben werden, mit zunehmendem Alter das Sterblichkeits- und Invaliditätsrisiko aber ansteigen. Der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> zahlt daher anfänglich zu viel, später zu wenig Risikoprämie. Um auch später der Leistungspflicht nachkommen zu können, müssen die <a href="versicherer">Versicherer</a> die für die Deckung der Risikoleistungen zuviel erhobenen Prämienanteile in Form von Deckungskapital verzinslich ansammeln. Beim <a href="ruckkauf">Rückkauf</a> einer Lebensversicherung wird vom Deckungskapital ein Teil der noch nicht getilgten Abschlusskosten für das getragene <a href="risiko">Risiko</a> abgezogen. Was verbleibt ist der <a href="ruckkaufswert">Rückkaufswert.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(589, 1, 'Laufzeit', 'laufzeit', NULL, 'Zeitspanne von der <a href="emission">Emission</a> bis zur Fälligkeit bzw. zur vorzeitigen Rückzahlung einer <a href="obligationen">Obligation</a>. Nicht zu verwechseln mit der <a href="duration">Duration</a>. Siehe auch <a href="restlaufzeit">Restlaufzeit</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(590, 1, 'BVG-Lohnmaximum', 'bvg-lohnmaximum', NULL, 'Obere Einkommensgrenze eines Arbeitnehmers, die im Rahmen der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> versichert wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(591, 1, 'UVG-Lohnmaximum', 'uvg-lohnmaximum', NULL, 'Obere Einkommensgrenze, die im Rahmen der <a href="obligatorische-unfallversicherung">obligatorischen Unfallversicherung</a> versichert wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(592, 1, 'Heilungskosten', 'heilungskosten', NULL, 'Kostenkomponente bei <a href="personenschaden">Personenschäden</a> für die Heilung einer verletzten Person; dazu gehören z.B. Behandlungskosten, Spitalkosten, Kosten für die Anschaffung von Prothesen, Brillen Apparate und anderer Hilfsmittel sowie Transport- und Rettungskosten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(593, 1, 'Gesundheitsprüfung', 'gesundheitsprufung', NULL, 'Eine Gesundheitsprüfung ist grundsätzlich immer dann erforderlich, wenn bei einem Lebensversicherungs<a href="antrag">antrag</a> Todesfall- und/oder Invaliditätsrisiken zu übernehmen sind. Beim Normalrisiko ist die <a href="pramie">Prämie</a> für eine vollständig gesunde Person berechnet worden und bedingt folgendes:<br />- Guter Gesundheitszustand<br />- Normale Berufstätigkeit<br />- Normale Sportart<br />- Normaler Aufenthaltsort<br />\r<br />Weichen der gegenwärtige Gesundheitszustand oder die erbliche Veranlagung von dieser Norm ab, oder sind Folgen einer durchgemachten Krankheit zu erwarten (erhöhtes Risiko), so müssen unter Umständen Prämienzuschläge (<a href="erschwerungen">Erschwerungen</a>) verlangt werden, um das erhöhte Risiko übernehmen zu können. Bis zu einer gewissen Versicherungssumme, die nach Gesellschaft und <a href="eintrittsalter">Eintrittsalter</a> variiert, wird die Gesundheitsprüfung durch Befragung der zu versichernden Person über ihre Gesundheitsverhältnisse durchgeführt. Diese Fragen sind ein wesentlicher Bestandteil des Lebensversicherungsantrages; von deren richtiger Beantwortung ist unter Umständen die Gültigkeit des Vertrages abhängig (<a href="anzeigepflichtverletzung">Anzeigepflichtverletzung</a>). Bei höheren <a href="versicherungssumme">Versicherungssummen</a> oder Erwerbsausfallrenten reichen diese Selbstangaben für die Beurteilung nicht mehr aus. Die Gesellschaft verlangt dann ein ärztliches Attest, allenfalls mit zusätzlichen Untersuchungen. Die Kosten für das Attest sowie die zusätzlich angeordneten Untersuchungen übernimmt die Gesellschaft. Der Arzt sendet seinen Bericht an den ärztlichen Dienst der Direktion der betreffenden Gesellschaft. Der Antragsteller entbindet den Untersuchungsarzt wie auch andere Ärzte, Spitäler und Sanatorien gegenüber der Gesellschaft vom Arztgeheimnis. Andererseits sind aber die zuständigen Organe der Gesellschaft durch strenge Vorschriften an das <a href="arztgeheimnis">Arztgeheimnis</a> gebunden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(594, 1, 'Arztgeheimnis', 'arztgeheimnis', NULL, 'Arztgeheimnis - siehe <a href="gesundheitsprufung">Gesundheitsprüfung</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(595, 1, 'Medium-term Funds', 'medium-term-funds-1', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in <a href="obligationen">Obligationen</a> mit einer (<a href="restlaufzeit">Rest-</a>)<a href="laufzeit">Laufzeit</a> von 3-5 Jahren investieren.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(596, 1, 'Kassenobligationen', 'kassenobligationen', NULL, 'Von Banken ausgegebene festverzinsliche Anlageformen mit <a href="laufzeit">Laufzeiten</a> zwischen zwei und acht Jahren. Für <a href="obligationenfonds">Obligationenfonds</a> kommen die Kassenobligationen nicht in Frage, da sie nicht zum offiziellen Börsenhandel zugelassen sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(597, 1, 'Obligatorium', 'obligatorium', NULL, 'Von Gesetzes wegen müssen Personen oder Sachen gegen bestimmte <a href="risiko">Risiken</a> versichert werden. Die Versicherung erfolgt durch staatliche Institutionen mit oder ohne Monopol oder durch private Gesellschaften (z.B.: <a href="kantonale-gebaudeversicherung">Kantonale Gebäudeversicherung</a>, <a href="feuerversicherung">Feuerversicherung</a>, etc.).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(598, 1, 'Zahlungsmodus', 'zahlungsmodus', NULL, 'Art, wie eine <a href="pramie">Prämie (Leben)</a> / <a href="#invalid-reference-254">Prämie (Nicht-Leben)</a>oder eine Rente entrichtet werden kann.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(599, 1, 'Mid Caps', 'mid-caps-1', NULL, 'Gesellschaften mit mittlerer <a href="borsenkapitalisierung">Börsenkapitalisierung</a>. Auch Nebenwerte genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(600, 1, 'Militärversicherung', 'militarversicherung', NULL, 'Die Militärversicherung (MV) beruht auf dem Bundesgesetz vom 20. September 1949 und wird durch das Bundesamt für Militärversicherung verwaltet. <br />\r<br />Die MV deckt grundsätzlich jede Gesundheitsschädigung, die während des Dienstes auftritt. Die Leistungen bestehen insbesondere in: Krankenpflege, Leistung für <a href="sachschaden">Sachschäden</a>, Krankengeld, Invalidenrenten, Hinterlassenenrenten.<br />\r<br />Versichert sind im wesentlichen alle Personen, die Militärdienst leisten und an ausserdienstlichen Schiessübungen, an Massnahmen der militärtechnischen Vorbildung oder an freiwilligen ausserdienstlichen militärischen Anlässen teilnehmen. Die private Lebensversicherung kann wegen Leistungen aus der Militärversicherung ihre eigenen Leistungen nicht kürzen; ebensowenig kann die Militärversicherung ihre Leistungen kürzen, weil eine private Lebensversicherung Leistungen erbringt.<br />\r<br />In Kriegszeiten oder bei Unruhen ist dagegen der <a href="versicherungsschutz">Versicherungsschutz</a> eingeschränkt.<br />\r<br />Führt die Schweiz selbst Krieg oder wird sie in kriegsähnliche Handlungen hineingezogen, so wird vom Kriegsbeginn an ein einmaliger Kriegsumlagebeitrag geschuldet und zwar unabhängig davon, ob der <a href="versicherter">Versicherte</a> am Krieg teilnimmt oder nicht. Nimmt der Versicherte dagegen an einem Krieg oder an kriegsähnlichen Handlungen teil, ohne dass die Schweiz selbst beteiligt ist, so ist im Fall des Todes des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> nur das auf den Todestag berechnete <a href="deckungskapital">Deckungskapital</a> geschuldet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(601, 1, 'Mindestzins (-satz)', 'mindestzins-satz', NULL, 'Vorgeschriebene Ver<a href="zins">zins</a>ung der Altersgutschriften im Rahmen der <a href="berufliche-vorsorge">beruflichen Vorsorge</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(602, 1, 'Geldmarktfonds', 'geldmarktfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in kurzfristigen, festverzinslichen Papieren (<a href="laufzeit">Laufzeit</a> unter einem Jahr) in bestimmten Währungen investieren. Auch Money Market Funds genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(603, 1, 'Geldmarktinstrumente', 'geldmarktinstrumente', NULL, 'Titoli con durate massime di un anno, negoziati sul mercato monetario. In Svizzera, gli strumenti classici di questo mercato sono cambiali nazionali, buoni e certificati della Confederazione. Per gli impegni all''estero la parte del leone spetta a Commercial Papers e Certificates of Deposit.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(604, 1, 'Geldmenge', 'geldmenge', NULL, 'Menge des in einer Volkswirtschaft vorhandenen inländischen Bar- und Buchgeldes. Durch Erhöhung oder Reduktion der Geldmenge kann die <a href="nationalbank">Nationalbank</a> die Wirtschaftspolitik steuern. Vor allem in Zeiten hoher Inflation wird die Geldmenge reduziert, um die Teuerung zu bekämpfen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(605, 1, 'Monopol', 'monopol', NULL, 'Bei der <a href="gebaudeversicherung">Gebäudeversicherung</a> schreiben die meisten Kantone für die <a href="feuerversicherung">Feuerversicherung</a> (Feuer und <a href="elementargefahr">Elementargefahr</a>) die <a href="kantonale-gebaudeversicherung">kantonale Gebäudeversicherung</a> vor. Diese hat also eine Monopolstellung. Kein Monopol kennen die <a href="gustavo">GUSTAVO</a> - Kantone. Die Kantone Nidwalden und Waadt kennen für die Hausrat- und Geschäftsversicherung ebenfalls ein Monopol.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(606, 1, 'Morbiditätstafel', 'morbiditatstafel', NULL, 'Die Morbiditätstafel liefert die statistischen Erfahrungswerte für versicherungstechnische Berechnungen im Zusammenhang mit Erkrankungen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(607, 1, 'Sterbetafel', 'sterbetafel', NULL, 'Die Sterbetafel liefert die statistischen Erfahrungswerte für alle versicherungstechnischen Berechnungen in Verbindung mit dem Todes- und Erlebensfall (Todeswahrscheinlichkeit/<a href="lebenserwartung">Lebenserwartung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(608, 1, 'Motorfahrzeug', 'motorfahrzeug', NULL, 'Motorfahrzeug im Sinne des Strassenverkehrsgesetzes ist jedes <a href="fahrzeug">Fahrzeug</a> mit eigenem <a href="antrieb">Antrieb</a>, durch den es <a href="auf-dem-erdboden">auf dem Erdboden</a> <a href="unabhangig-von-schienen">unabhängig von Schienen</a> fortbewegt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(609, 1, 'Motorfahrzeug-Halterhaftpflicht', 'motorfahrzeug-halterhaftpflicht', NULL, '<a href="kausalhaftung">Kausalhaftung</a> des Motorfahrzeug<a href="halter">halter</a>s für den <a href="schaden">Schaden</a>, der durch sein <a href="motorfahrzeug">Motorfahrzeug</a> bei Drittpersonen angerichtet wird. Es handelt sich um einen Fall der <a href="ausservertragliche-haftpflicht">ausservertraglichen Haftpflicht</a> (<a href="scharfe-kausalhaftung">scharfe Kausalhaftung</a> aufgrund der  Betriebsgefahr des Motorfahrzeuges).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(610, 1, 'Motorwagen', 'motorwagen', NULL, 'Die Motorwagen sind dadurch gekennzeichnet, dass sie vier (oder drei) Räder haben und mehr als 400 kg Leergewicht aufweisen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(611, 1, 'Motorräder', 'motorrader', NULL, 'Als Motorräder gelten mit Ausnahme der Motorfahrräder (Mofas) alle einspurigen, zweirädrigen <a href="motorfahrzeug">Motorfahrzeuge</a> sowie die dreirädrigen Motorfahrzeuge, die nicht als <a href="motorwagen">Motorwagen</a> gelten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(612, 1, 'Verkehrsrechtsschutz', 'verkehrsrechtsschutz', NULL, 'Bereich der <a href="rechtsschutzversicherung">Rechtsschutzversicherung</a>, der Rechtsstreitigkeiten im Zusammenhang mit dem Strassenverkehr versichert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(613, 1, 'Kontrollschilder', 'kontrollschilder', NULL, 'Kontrollschilder werden umgangssprachlich auch Nummernschilder oder Autonummern genannt. Sie dienen u.a. der Identifizierung des <a href="halter">Halters</a> und der obligatorischen <a href="haftpflichtversicherung">Haftpflichtversicherung.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(614, 1, 'Motorfahrzeug-Haftpflichtversicherung', 'motorfahrzeug-haftpflichtversicherung', NULL, 'Gegenstand dieser obligatorischen Versicherung ist die <a href="haftpflicht">Haftpflicht</a> des <a href="halter">Halters</a> oder <a href="lenker">Lenkers</a> eines <a href="motorfahrzeug">Motorfahrzeugs</a> für <a href="personenschaden">Personen-</a> und <a href="sachschaden">Sachschäden.</a> Grund für die Schaffung des Obligatoriums war der Schutz des Geschädigten, der nur bei Zahlungsfähigkeit des haftpflichtigen Halters gewährleistet ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(615, 1, 'Fahrnisbauten', 'fahrnisbauten', NULL, 'Unter Fahrnisbauten versteht man Bauten, die nicht als Dauereinrichtung erstellt wurden (Kaninchenstall, Bienenhäuschen, Geräteschuppen,...).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(616, 1, 'Fahrhabe', 'fahrhabe', NULL, 'Fahrhabe ist ein anderer Ausdruck für bewegliche Sachen, die nicht als Gebäudebestandteil oder bauliche Einrichtungen gelten.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(617, 1, 'bewegliche Sache', 'bewegliche-sache', NULL, 'Siehe <a href="mobilie">Mobilie.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(618, 1, 'Mobilie', 'mobilie', NULL, 'Bewegliche körperliche <a href="sache">Sache</a>(z. B. Auto, Möbel-, Schmuck- oder Kleidungsstück).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(619, 1, 'Mehrfachagent', 'mehrfachagent', NULL, 'Bestandteil des indirekten <a href="absatzkanal">Absatzkanals</a>. Person oder Organisation, die <a href="versicherung">Versicherungen</a> mehrerer <a href="versicherer">Versicherer</a> anbietet und vermittelt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(620, 1, 'Nationalbank', 'nationalbank', NULL, 'Die Schweizerische Nationalbank ist die Zentralbank. Der Bund hat ihr das ausschliessliche Recht zur Ausgabe von Banknoten gegeben. Die Hauptaufgaben sind: den Geldumlauf des Landes zu regeln, den Zahlungsverkehr zu erleichtern und eine im Gesamtinteresse des Landes dienende Kredit- und Währungspolitik zu führen (<a href="geldmenge">Geldmenge</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(621, 1, 'Nationaler Garantiefonds', 'nationaler-garantiefonds', NULL, 'Zusammenschluss der Motorfahrzeug-Haftpflicht<a href="versicherer">versicherer</a> zur Deckung von <a href="schaden">Schäden</a>, die von unbekannten oder nicht versicherten <a href="motorfahrzeug">Motorfahrzeugen</a> und Fahrrädern verursacht werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(622, 1, 'Elementarschaden', 'elementarschaden', NULL, 'Ein Elementarschaden ist ein Schaden, der durch ein <a href="elementarereignis">Elementarereignis</a> verursacht wurde.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(623, 1, 'Elementarereignis', 'elementarereignis', NULL, 'Unter die Bezeichnung "Elementarereignis" fallen: Hochwasser, Überschwemmung, Sturm, Hagel, Felssturz, Erdrutsch, Schneedruck, Lawine und Steinschlag. Schäden durch Elementarereignisse werden über die <a href="feuerversicherung">Feuerversicherung</a> vergütet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(624, 1, 'Elementargefahr', 'elementargefahr', NULL, 'Gefahr, die von der Macht der Natur ausgeht und in der privaten Sachversicherung folgende Schadenereignisse umfasst: <a href="hochwasser">Hochwasser</a>, <a href="uberschwemmung">Überschwemmung</a>, <a href="sturm">Sturm</a>, Hagel, Lawinen, <a href="schneedruck">Schneedruck</a>, Felssturz, Steinschlag und <a href="erdrutsch">Erdrutsch</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(625, 1, 'Natürliche Person', 'naturliche-person', NULL, 'Jedermann ist als natürliche Person rechtsfähig (<a href="zgb">ZGB</a> 11) und unterscheidet sich dadurch grundlegend von der <a href="juristische-person">juristischen Person</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(626, 1, 'ES', 'es-1', NULL, 'Abkürzung für <a href="elementarschaden">"Elementarschaden"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(627, 1, 'Bedarfsdeckungsverfahren', 'bedarfsdeckungsverfahren', NULL, 'Finanzierungsmethode, bei der der Versicherer die eingehenden Prämien eines Jahres zur Bezahlung der Schäden desselben Jahres und zur Bildung von Schadenreserven und Rückstellungen verwendet. Dieses Finanzierungsverfahren wird bei den meisten Privatversicherungen angewendet (mit Ausnahme der Lebensversicherung). siehe auch: <a href="kapitaldeckungsverfahren">Kapitaldeckungsverfahren</a> / <a href="umlageverfahren">Umlageverfahren</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(628, 1, 'Fahrlässigkeit', 'fahrlassigkeit', NULL, 'Unvorsichtige Verhaltensweise, bei der die vorhersehbare Schädigung einer Drittperson nicht beachtet wurde. Man unterscheidet <a href="leichte-fahrlassigkeit">leichte Fahrlässigkeit</a> und <a href="grobe-fahrlassigkeit-grobfahrlassigkeit">grobe Fahrlässigkeit</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(629, 1, 'Nettoinventarwert', 'nettoinventarwert', NULL, 'Der Nettoinventarwert eines <a href="anteil">Anteils</a> entspricht dem <a href="nettofondsvermogen">Nettofondsvermögen</a> geteilt durch die Anzahl der sich im Umlauf befindenden Anteile. Bei Wertschriften- und <a href="geldmarktfonds">Geldmarktfonds</a> ist der Nettoinventarwert, der in der Regel täglich berechnet wird, die Basis für die Abrechnung der <a href="zeichnung">Zeichnungen</a> und <a href="rucknahmen">Rücknahmen</a>. Auch Inventarwert, Anteilswert oder innerer Wert genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(630, 1, 'Nettovermögen (Immobilienfonds)', 'nettovermogen-immobilienfonds', NULL, 'Das Netto(fonds)vermögen ist die Summe der <a href="verkehrswert-immobilienfonds">Verkehrswerte</a>, abzüglich Verbindlichkeiten und der zu erwartenden Liquidationssteuern (Grundstücksgewinnsteuern).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(631, 1, 'Nettofondsvermögen', 'nettofondsvermogen', NULL, 'Das zu Verkehrswerten berechnete <a href="fondsvermogen">Fondsvermögen</a> abzüglich allfälliger Verbindlichkeiten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(632, 1, 'Nettoprämie', 'nettopramie', NULL, 'Nettoprämie = <a href="bruttopramie">Bruttoprämie</a> minus Kosten', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(633, 1, 'Barprämie', 'barpramie', NULL, 'Prämie, die dem Kunden in Rechnung gestellt wird (<a href="bruttopramie">Bruttoprämie</a> plus Zuschläge abzüglich allfälliger Rabatte).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(634, 1, 'Indifferentes Geschäft', 'indifferentes-geschaft', NULL, 'Bankgeschäfte, die keinen Niederschlag in der Bank<a href="bilanz">bilanz</a> sondern in der Gewinn- und Verlustrechnung einer Bank finden. Gegenstück sind Zinsdifferenzgeschäfte, d.h. Bankgeschäfte, bei denen die Bank entweder einen Zins erhält oder einen Zins bezahlen muss. Zu den Indifferenten Geschäften gehören der Zahlungsverkehr, das Börsen- und Depotgeschäft, der Devisen- und Edelmetallhandel etc.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(635, 1, 'Schadenfreiheitsrabatt', 'schadenfreiheitsrabatt', NULL, '<a href="pramienvergunstigung">Prämienvergünstigung</a> für Versicherungen, bei denen innerhalb einer bestimmten Versicherungsperiode (meist innerhalb eines Vertragsjahres) kein <a href="schaden">Schaden</a> gemeldet wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(636, 1, 'No-load Funds', 'no-load-funds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die weder die Ausgabe noch die Rücknahme von <a href="anteil">Anteilen</a> mit einer <a href="kommissionen">Kommission</a> belasten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(637, 1, 'Ausservertragliche Haftpflicht', 'ausservertragliche-haftpflicht', NULL, '<a href="haftung">Haftung</a> für den <a href="schaden">Schaden</a> einer Drittperson aufgrund einer Gesetzesvorschrift (unerlaubte Handlung gemäss Art. 41 OR oder ein Tatbestand der <a href="kausalhaftung">Kausalhaftung</a>). Zwischen dem Schädiger und dem Geschädigten besteht kein Vertragsverhältnis.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(638, 1, 'Anzeigepflichtverletzung', 'anzeigepflichtverletzung', NULL, 'Anzeigepflichtverletzung besteht dann, wenn der anzeigepflichtige <a href="versicherungsnehmer-in">Versicherungsnehmer</a> oder <a href="versicherte-person">versicherte Person</a> beim Abschluss der <a href="versicherung">Versicherung</a> eine erhebliche <a href="gefahrstatsachen">Gefahrstatsache</a>, die er kannte oder hätte kennen müssen, unrichtig mitgeteilt oder verschwiegen hat. Die Versicherungsgesellschaft ist in diesem Falle nicht an den <a href="versicherungsvertrag">Versicherungsvertrag</a>gebunden, wenn sie innert vier Wochen, nachdem sie von der Verletzung der Anzeigepflicht Kenntnis erhalten hat, vom Vertrag zurücktritt. Diese Bestimmung gilt auch dann, wenn die falschen Angaben im <a href="antrag">Antrag</a> von einem Dritten gemacht wurden. Mit seiner Unterschrift im Antrag bestätigt der Antragsteller, dass alle Fragen richtig beantwortet wurden. Siehe auch <a href="gesundheitsprufung">Gesundheitsprüfung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(639, 1, 'Unbeteiligte Dritte', 'unbeteiligte-dritte', NULL, 'Siehe <a href="dritte">Dritte.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(640, 1, 'Versicherung, freie', 'versicherung-freie', NULL, 'Eine freie Versicherung (Säule 3b) ist im Gegensatz zu einer <a href="versicherung-gebundene">gebundenen Versicherung</a> (Säule 3a) ein <a href="versicherungsvertrag">Vertrag</a>, der steuerlich nur mässig privilegiert ist, über den der Kunde dafür aber frei verfügen kann. Siehe auch <a href="vorsorge">Vorsorge.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(641, 1, 'Freie Vorsorge', 'freie-vorsorge', NULL, 'Als freie Vorsorge werden alle im Rahmen der Säule 3b des <a href="drei-saulenkonzept">Drei-Säulenkonzepts</a> getroffenen Massnahmen der individuellen Selbstvorsorge bezeichnet. Dazu gehören in erster Linie Lebensversicherungen, aber auch Kapitalanlagen, Erwerb von Wohneigentum usw.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(642, 1, 'Nichtberufsunfall (NBU)', 'nichtberufsunfall-nbu', NULL, '<a href="unfall">Unfälle</a>, die sich ausserhalb der Arbeit im Privatleben eines Versicherten ereignen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(643, 1, 'Nebenamtliche Tätigkeit', 'nebenamtliche-tatigkeit', NULL, 'Unbezahlte Arbeit während der Freizeit und versicherte "Gefahr" der <a href="privathaftpflichtversicherung">Privathaftpflichtversicherung</a>. Kommt es bei einer nebenamtlichen Tätigkeit zu einem Haftpflichtfall, tritt die Privathaftpflichtversicherung für <a href="schaden">Schäden</a> ein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(644, 1, 'Notes', 'notes-1', NULL, 'Am Schweizer Kapitalmarkt privat (ohne öffentliche Werbung) platzierte Wertpapiere ausländischer Schuldner.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(645, 1, 'NW', 'nw', NULL, 'Abkürzung für <a href="neuwert">"Neuwert"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(646, 1, 'Obliegenheit', 'obliegenheit', NULL, 'Unter Obliegenheiten versteht man versicherungsrechtlich die gesetzlichen oder vertraglichen Nebenpflichten der Parteien aus dem <a href="versicherungsvertrag">Versicherungsvertrag</a> (<a href="anzeigepflicht">Anzeigepflicht</a>, <a href="rettungspflicht">Rettungspflicht</a>, <a href="mitwirkungspflicht">Mitwirkungspflicht</a>, bestimmte Auflagen). Die Verletzung einer Obliegenheit hat einen Rechtsverlust zur Folge, z.B. Dahinfallen des Vertrages, Suspension der Leistungspflicht, Leistungskürzungen, Prämienerhöhung).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(647, 1, 'Lohnfortzahlungspflicht', 'lohnfortzahlungspflicht', NULL, 'Wenn ein Arbeitnehmer wegen <a href="krankheit">Krankheit</a>, <a href="unfall">Unfall</a>, Schwangerschaft oder Mutterschaft unverschuldet ausfällt, muss der Arbeitgeber dessen Lohn für eine beschränkte Zeit (abhängig vom Dienstalter) weiterhin ausbezahlen. Die Lohnfortzahlungspflicht besteht bei unbefristeten Arbeitsverhältnissen, die länger als drei Monate gedauert haben, und bei befristeten Arbeitsverhältnissen, die länger als drei Monate dauern (OR 324).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(648, 1, 'Versicherungspflicht', 'versicherungspflicht', NULL, 'Bevor ein <a href="fahrzeug">Fahrzeug</a> in den Verkehr gebracht werden kann, muss eine <a href="haftpflichtversicherung">Haftpflichtversicherung</a> abgeschlossen werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(649, 1, 'Berufunfall (BU)', 'berufunfall-bu', NULL, '<a href="unfall">Unfall</a>eines Arbeitnehmers, wenn dieser im Auftrag oder im Interesse seines Arbeitgebers arbeitet bzw. tätig ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(650, 1, 'Betriebsunfall (Maschinenversicherung)', 'betriebsunfall-maschinenversicherung', NULL, 'Ursache für die Beschädigung oder Zerstörung eines versicherten Objekts (Anlage, Maschine, Installation, Apparat). Im Rahmen der <a href="maschinenversicherung">Maschinenversicherung</a> sind alle Arten von Unfällen und Missgeschicken versichert, die sich plötzlich und unvorhergesehen ereignen (<a href="all-risk-versicherung">All-Risk-Versicherung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(651, 1, 'Berufskrankheit', 'berufskrankheit', NULL, 'Als Berufskrankheit gilt eine Krankheit, die ausschliesslich oder stark überwiegend durch berufliche Tätigkeit oder anlässlich der Ausübung des Berufs durch schädigende Stoffe oder bestimmte Arbeiten verursacht wurde. Die SUVA führt eine Liste aller anerkannten Berufskrankheiten.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(652, 1, 'Freizügigkeit', 'freizugigkeit', NULL, 'Das Recht des <a href="versicherter">Versicherten</a> unter bestimmten Voraussetzungen, ohne Rücksicht auf Aufnahmebeschränkungen z.B. betreffend Alters- oder Gesundheitszustand, von einer Versicherungsunternehmung in eine andere überzutreten. (z.B. Krankenkassen, Pensionskassen beim Wechsel der Arbeitgeber).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(653, 1, 'Freizügigkeitsleistung', 'freizugigkeitsleistung', NULL, 'Freizügigkeitsleistung ist grundsätzlich das bei Ausscheiden aus der Vorsorgeeinrichtung vor Eintritt des Vorsorgefalles bestehende <a href="altersguthaben">Altersguthaben.</a> Das neue Freizügigkeitsgesetz führt den im obligatorischen Bereich bereits verwirklichten Grundsatz der vollen <a href="freizugigkeit">Freizügigkeit</a> auch für die überobligatorischen Vorsorgelösungen weiter. Die ganze Freizügigkeitsleistung (obligatorischer und überobligatorischer Teil) ist zwingend auf die neue <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> zu übertragen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(654, 1, 'Freizügigkeitspolice', 'freizugigkeitspolice', NULL, 'Kann die Freizügigkeitsleistung keiner neuen <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> überwiesen werden, so kann der Vorsorgeschutz durch Errichtung einer Freizügigkeitspolice erhalten werden. Die Freizügikeitspolice ist eine besondere, ausschliesslich und unwiderruflich der Vorsorge dienenden Kapital- oder Rentenversicherung im Rahmen der 2. Säule einschliesslich allfälliger Zusatzversicherungen für den Todes- oder Invaliditätsfall. Ein Mindestzinssatz ist garantiert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(655, 1, 'Vorsorgeeinrichtung', 'vorsorgeeinrichtung', NULL, 'Das <a href="bvg-1">BVG</a> verpflichtet die Arbeitgeber, die obligatorisch zu versichernde Arbeitnehmer beschäftigen, einen verselbständigten Vorsorgeträger, eine sogenannte Vorsorgeeinrichtung, zu schaffen - gewöhnlich Pensionskassen genannt - oder sich einer bestehenden Vorsorgeeinrichtung anzuschliessen. Dahinter steckt die Absicht des Gesetzgebers, das Vorsorgevermögen im Falle eines Konkurses des Arbeitgebers zu schützen und somit für die Personalvorsorge zu sichern. Die Vorsorgeeinrichtung muss in das Register für berufliche Vorsorge eingetragen sein und die Rechtsform einer Stiftung, Genossenschaft oder einer Einrichtung des öffentlichen Rechts aufweisen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(656, 1, 'Berufliche Vorsorge', 'berufliche-vorsorge', NULL, 'Das Bundesgesetz über die berufliche Alters-, Hinterlassenen- und Invalidenvorsorge (BVG) regelt als obligatorische 2. Säule des <a href="drei-saulenkonzept">Drei-Säulen-Konzepts</a> die berufliche Vorsorge für Arbeitnehmer.<br />Die berufliche Vorsorge soll Betagten, Hinterlassenen und Invaliden, zusammen mit den Leistungen der <a href="ahv-1">AHV</a>/ <a href="iv">IV</a>, die Fortsetzung der gewohnten Lebenshaltung in angemessener Weise ermöglichen.<br />Versichert sind alle AHV-pflichtigen Arbeitnehmer ab einem bestimmten minimalen AHV-Lohn pro Arbeitgeber.<br />Die wichtigsten Leistungen des BVG: Ausrichtung von Alters-, Witwen- und Waisenrenten sowie Invalidenrenten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(657, 1, 'Aussenversicherung', 'aussenversicherung', NULL, 'In der <a href="hausratversicherung">Hausratversicherung</a> schützt die Aussenversicherung die versicherten Sachen, die sich vorübergehend ausserhalb des Standorts befinden. Der Schutz besteht in der Regel auf der ganzen Welt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(658, 1, 'Offshore Funds', 'offshore-funds', NULL, '<a href="anlagefonds">Anlagefonds</a>, deren Rechtsdomizil sich in einem Land befindet, das Steuerfreiheit oder Steuervorteile bietet (z.B. Bahamas, Bermudas). In der Schweiz werden nur Offshore Funds aus Ländern zum öffentlichen Vertrieb zugelassen, die über eine mit der Schweiz vergleichbare Fondsaufsicht verfügen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(659, 1, 'Alters- und Hinterlassenenversicherung', 'alters-und-hinterlassenenversicherung', NULL, 'Die Eidgenössische Alters- und Hinterlassenenversicherung (AHV) ist seit 1948 in Kraft. Sie wurde mehrmals revidiert. Die AHV stellt zusammen mit der <a href="iv">IV</a> die sogenannte 1. Säule des <a href="drei-saulenkonzept">Drei-Säulen-Konzepts</a> dar.<br />Sie dient der Existenzsicherung. Die AHV gehört zu den obligatorischen Versicherungen. Obligatorisch versichert sind alle, die in der Schweiz wohnen oder arbeiten. Die AHV entrichtet Alters- und Hinterlassenenrenten, sowie Hilflosenentschädigungen und Hilfsmittel an Altersrentner.<br />Anspruchsberechtigt sind alle <a href="versicherter">Versicherten</a> und deren Hinterlassenen. Für Ausländer gelten Sonderbestimmungen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(660, 1, 'Altersrenten', 'altersrenten', NULL, 'Altersrenten oder auch Leibrenten genannt  werden in jährlichen, halbjährlichen, vierteljährlichen oder monatlichen Raten ausbezahlt und zwar so lange, als die versicherte Person lebt. Bei Altersrenten mit Rückgewähr werden beim Tod der versicherten Person die einbezahlten <a href="pramie">Prämien</a>, abzüglich der bereits bezogenen Renten, ohne Zins, an die im Vertrag Begünstigten zurückbezahlt. Ist die Altersrente ohne <a href="ruckgewahr">Rückgewähr</a> abgeschlossen, verfallen beim Tod der versicherten Person die einbezahlten Prämien. Dafür ist die versicherte Rente entsprechend grösser. Die Leistungsdauer kann je nach Produktekonstellation temporär oder lebenslang erfolgen. Bei der lebenslänglichen Zahlung sind die Leistungen geschuldet solange der Versicherte lebt, auch wenn die einbezahlten Prämien bereits durch die Rentenzahlungen aufgebraucht sind. Eine Altersrente kann als "Pension" für Selbständigerwerbende, aber auch als Ergänzung zur beruflichen Vorsorge von Unselbständigen angesehen werden. Allfällige Lücken in der <a href="ahv-1">AHV</a> können mit einer privaten Altersrente geschlossen werden. Oft ist die Ehefrau gegenüber dem Mann bei dessen Tod nach der Pensionierung schlechter gestellt, mit der Altersrente auf das Leben der Frau kann dies korrigiert werden. Eine privat finanzierte Altersrente geniesst nicht unwesentliche <a href="steuerprivileg">Steuerprivilegien</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(661, 1, 'Ombudsman / Ombudsstelle', 'ombudsman-ombudsstelle', NULL, 'Unabhängige Persönlichkeit oder unabhängige Stelle bzw. Organisation (Ombudsstelle), die Beschwerden entgegennimmt und Streitigkeiten zwischen zwei Vertragspartnern schlichtet.<br />Im Bereich der Versicherung gibt es den "Ombudsman der <a href="privatversicherung">Privatversicherung</a> und der Suva" sowie den "Ombudsman der <a href="soziale-krankenversicherung">sozialen Krankenversicherung</a>". <br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(662, 1, 'Auf dem Erdboden', 'auf-dem-erdboden', NULL, 'Auf dem Erdboden bewegt sich das <a href="motorfahrzeug">Motorfahrzeug</a> im Unterschied zum Luft- und Wasserfahrzeug.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(663, 1, 'Online-Vertrieb', 'online-vertrieb', NULL, 'Bestandteil des direkten <a href="absatzkanal">Absatzkanals</a>. Beinhaltet die Kontaktaufnahme mit Kunden und den Verkauf von <a href="versicherung">Versicherungen</a> per Internet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(664, 1, 'Offener Fonds', 'offener-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a> mit variablem Kapital, der laufend neue <a href="anteil">Anteile</a> ausgeben kann, der aber auch verpflichtet ist, ausgegebene Anteile auf Verlangen zum <a href="nettoinventarwert">Nettoinventarwert</a> zurückzunehmen. Die schweizerischen Anlagefonds entsprechen diesem Fondstyp.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(665, 1, 'Betriebsrisiko', 'betriebsrisiko', NULL, '<a href="gefahr">Gefahr</a>, die durch die <a href="betriebshaftpflichtversicherung">Betriebshaftpflichtversicherung</a> versichert wird. Es handelt sich um die <a href="haftpflicht">Haftpflicht</a> eines Unternehmens gegenüber Dritten im Zusammenhang mit Schäden, die Mitarbeiter bei der Verrichtung ihrer Tätigkeit anrichten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(666, 1, 'Opportunity Portfolio', 'opportunity-portfolio-1', NULL, 'Das Opportunity Portfolio ist der Teil des <a href="portefeuille">Portefeuilles</a> eines (dynamischen) <a href="absicherungsfonds">Absicherungsfonds</a>, mit dem eine Partizipation an der Entwicklung der internationalen Finanzmärkte erzielt werden soll. Die Anlagen erfolgen in der Regel in <a href="obligationen">Obligationen</a> in Fremdwährung sowie in <a href="aktie">Aktien</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(667, 1, 'Dispositive Bestimmung', 'dispositive-bestimmung', NULL, 'Règle de droit privé qui ne s''applique que si les parties n''en ont pas décidé autrement.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(668, 1, 'Optionen', 'optionen', NULL, 'Der Käufer einer Option erwirbt das Recht - nicht aber die Verpflichtung - innerhalb einer bestimmten Zeit, eine feste Anzahl eines bestimmten <a href="basiswert">Basiswertes</a> zu einem im Voraus fixierten Ausübungspreis zu kaufen (Call-Option) oder zu verkaufen (Put-Option). Für dieses Recht zahlt er dem Verkäufer der Option eine Optionsprämie (Optionspreis).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(669, 1, 'Orderpapier', 'orderpapier', NULL, 'Durch <a href="indossament">Indossament</a> übertragbares Wertpapier: entweder geborenes Orderpapier, wie Check und Wechsel oder Wertpapier, das erst durch Beifügung der Orderklausel oder an Order zum Orderpapier wird. Bei Blankoindossierung entspricht das Orderpapier praktisch dem Inhaberpapier.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(670, 1, 'Leichte Fahrlässigkeit', 'leichte-fahrlassigkeit', NULL, 'Jede Fahrlässigkeit, die keine <a href="grobe-fahrlassigkeit-grobfahrlassigkeit">grobe</a> ist. Die <a href="versicherungsleistungen">Versicherungsleistung</a> darf nicht gekürzt werden (<a href="vvg-1">VVG</a> 14).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(671, 1, 'Übrige Fonds', 'ubrige-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die weder <a href="effektenfonds">Effektenfonds</a> noch <a href="immobilienfonds-liegenschaftsfonds">Immobilienfonds</a> sind. Sie investieren in Anlagen, die nur beschränkt marktgängig sind, meist höheren Kursschwankungen unterliegen, eine begrenzte Risikoverteilung aufweisen oder deren Bewertung erschwert ist (z.B. Anlagen in Edelmetallen, Commodities, Optionen, Terminkontrakten und in Anteilen anderer Anlagefonds sowie in anderen Rechten).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(672, 1, 'Übrige Fonds mit besonderem Risiko', 'ubrige-fonds-mit-besonderem-risiko', NULL, '<a href="anlagefonds">Anlagefonds</a>, die im Unterschied zur Kategorie der <a href="ubrige-fonds">übrigen Fonds</a> zusätzlich ein besonderes Risiko aufweisen, das nicht mit dem Risiko von <a href="effektenfonds">Effektenfonds</a> vergleichbar ist. Auf diesen Umstand ist im Fondsnamen sowie im <a href="prospekt-fondsreglement">Prospekt</a> und in der Werbung speziell hinzuweisen. Die Anteile solcher Fonds dürfen nur auf Grund eines schriftlichen Vertrags verkauft werden, in welchem auf das besondere Risiko hingewiesen wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(673, 1, 'Überversicherung', 'uberversicherung', NULL, 'Überversicherung liegt vor, wenn die <a href="versicherungssumme">Versicherungssumme</a> höher ist als der Wert des versicherten Gegenstandes, der <a href="versicherungswert">Versicherungswert</a> (<a href="vvg-1">VVG</a> 51). Sie bringt dem <a href="versicherter">Versicherten</a> keinen Nutzen, kostet ihn aber <a href="pramie">Prämien</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(674, 1, 'PAK', 'pak', NULL, 'Abkürzung für<a href="pramienanpassungsklausel-pak">"Prämienanpassungsklausel"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(675, 1, 'Parikurs', 'parikurs', NULL, 'Kurs, der bei Wertpapieren dem <a href="nennwert">Nennwert</a>, bei Devisen der <a href="wahrungsparitat">Währungsparität</a>entspricht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(676, 1, 'Nennwert', 'nennwert', NULL, 'Der auf Wertpapieren angegebene (nominelle) Forderungsbetrag im Unterschied zum effektiven <a href="kurswert-marktwert">Kurswert / Marktwert</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(677, 1, 'Parität', 'paritat', NULL, 'Parität bedeutet in der beruflichen <a href="vorsorge">Vorsorge</a>, dass sich die leitenden Organe der Personalvorsorgestiftung aus der gleichen Zahl von Vertretern des Arbeitgebers und der Arbeitnehmer unabhängig von ihren Beiträgen zusammensetzen müssen. Die Parität gilt für alle registrierten privatrechtlichen Personalvorsorgestiftungen, ungeachtet dessen, ob sie nur die gemäss <a href="bvg-1">BVG</a> obligatorischen oder überobligatorischen Leistungen erbringen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(678, 1, 'Währungsparität', 'wahrungsparitat', NULL, 'Offiziell festgesetztes Wertverhältnis einer Währung gegenüber anderen Währungen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(679, 1, 'Teilschaden', 'teilschaden', NULL, '<a href="schaden">Schaden</a>, bei dem eine Reparatur der versicherten Sache weniger teuer ist als der versicherte Wert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(680, 1, 'Prämienfreier Teil', 'pramienfreier-teil', NULL, 'Die <a href="versicherungssumme">Versicherungssumme</a> kann aus einem prämienpflichtigen und einem prämienfreien Teil bestehen. Für den prämienfreien Teil muss der Kunde keine Prämien bezahlen. Entweder ist der prämienfreie Teil zu Beginn der Versicherung mittels einer <a href="einmaleinlage-einmalpramie">Einmaleinlage</a> oder während der Laufzeit infolge einer Vertragsänderung (z.B. Herabsetzung der Versicherungssumme) errichtet worden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(681, 1, 'Teilrückkauf', 'teilruckkauf', NULL, 'Änderung am Vertrag, wobei nicht der ganze <a href="ruckkaufswert">Rückkaufswert</a>, sondern nur ein Teil davon, ein sogenannter Teilrückkaufswert, zur Auszahlung gelangt. Durch die Verminderung der Substanz der Versicherung (Rückkaufswert, <a href="deckungskapital">Deckungskapital</a>) werden die versicherten Leistungen herabgesetzt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(682, 1, 'Teilwertversicherung', 'teilwertversicherung', NULL, 'Alternative zur <a href="vollwertversicherung">Vollwertversicherung</a> bei einer <a href="sachversicherung">Sachversicherung</a>, um eine Leistungsreduktion wegen <a href="unterversicherung-uv">Unterversicherung</a> auszuschliessen. Dabei wird die <a href="versicherungssumme">Versicherungssumme</a> auf einen prozentualen Anteil des Vollwerts festgesetzt, der im <a href="schaden">Schaden</a>fall ausbezahlt wird. Eine Teilwertversicherung wird vereinbart, wenn ein <a href="totalschaden">Totalschaden</a> höchstwahrscheinlich nie eintreten wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(683, 1, 'Teilverzicht', 'teilverzicht', NULL, 'Herabsetzung der <a href="versicherungsleistungen">versicherten Leistungen</a>, ohne dass dabei ein Teilrückkaufswert frei wird. Bei Hauptversicherungen, die noch keinen <a href="ruckkaufswert">Rückkaufswert</a> aufweisen. Dies ist in der Regel der Fall, solange die <a href="pramie">Prämien</a> für die Mindestprämienzahlungsdauer noch nicht fällig geworden und bezahlt sind. Bei der Herabsetzung von Zusatzversicherungen spricht man ebenfalls von einem Teilverzicht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(684, 1, 'Partizipationsschein', 'partizipationsschein', NULL, 'Aktien&auml;hnliches Wertpapier, beinhaltet im Gegensatz zur Aktie aber keine Mitgliedschaftsrechte, sondern lediglich Verm&ouml;gens- und gewisse Informationsrechte. Dient zur Beschaffung von Grundkapital. Der Mindestnominalwert betr&auml;gt Fr. 10.-. Hat aufgrund des revidierten Aktienrechts an Bedeutung verloren.', '2016-08-29 10:21:09', '2016-08-31 10:39:18'),
(685, 1, 'Passiver Rechtsschutz', 'passiver-rechtsschutz', NULL, 'Abwehr von <a href="haftpflicht">Haftpflicht</a>anspr&uuml;chen, die <a href="performance">unrechtm&auml;ssig </a>an den <a href="inventarwert">Versicherten</a> gestellt werden.', '2016-08-29 10:21:09', '2016-09-20 14:24:27'),
(686, 1, 'Einlösungsstelle', 'einlosungsstelle', NULL, 'Siehe <a href="zahlstelle">Zahlstelle</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(687, 1, 'Zahlstelle', 'zahlstelle', NULL, 'Die vom <a href="emittent">Emittenten</a> ausdrücklich beauftragte Stelle bzw. Bank, welche für alle laufenden Transaktionen zuständig ist, die sich für die Besitzer der betreffenden Wertschriften ergeben, wie z.B. die Einlösung der fälligen Dividenden- und Zinscoupons. Auch Einlösungsstelle genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(688, 1, 'Payout-Ratio (Immobilienfonds)', 'payout-ratio-immobilienfonds', NULL, 'Drückt das Verhältnis zwischen <a href="cashflow">Cashflow</a> und ausgeschüttetem <a href="ertrag">Ertrag</a> aus. Die Relation zeigt an, wie viel von dem im <a href="rechnungsjahr">Geschäftsjahr</a> erarbeiteten Mitteln effektiv ausgeschüttet wurde.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(689, 1, 'Altersleistung', 'altersleistung', NULL, 'Leistungen der <a href="alters-und-hinterlassenenversicherung">AHV</a>, der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> und der <a href="uberobligatorium-bv">überobligatorischen</a> Versicherung, die bei Erreichen des <a href="rente">Rente</a>nalters vom <a href="versicherer">Versicherer</a> erbracht werden. Dazu gehören eine<a href="altersrenten">Altersrente</a>, Kinderrente, Hilflosenentschädigung sowie Beiträge für Hilfsmittel.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(690, 1, 'Überschussrente', 'uberschussrente', NULL, 'Rente, die nur aus Überschüssen finanziert wird. Die Überschussrente erhöht die vertragliche Rente und wird zusammen mit dieser ausbezahlt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(691, 1, 'Ausgleichskasse', 'ausgleichskasse', NULL, 'Derjenige Teil der Verwaltung einer Sozialversicherungseinrichtung, der die Beiträge einzieht und die <a href="versicherungsleistungen">Versicherungsleistungen</a> auszahlt. Im engeren Sinn versteht man darunter die AHV-Ausgleichskasse.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(692, 1, 'Vorsorgereglement', 'vorsorgereglement', NULL, 'Das Vorsorgereglement regelt die <a href="berufliche-vorsorge">berufliche Vorsorge</a> der <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> und hält die Rechte und Pflichten aller Beteiligten fest. Es wird vom zuständigen Organ der Vorsorgeeinrichtung erlassen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(693, 1, 'Vorsorgeausweis', 'vorsorgeausweis', NULL, 'Der Vorsorgeausweis ist ein Informationsdokument für die <a href="versicherte-person">versicherte Person</a> und enthält Angaben über persönliche Ansprüche und Verpflichtungen sowie deren konkrete Höhe z.B. Pensionskassenausweis, AHV-Ausweis.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(694, 1, 'Sofort beginnende Rente', 'sofort-beginnende-rente', NULL, 'Bei einer privaten <a href="altersrenten">Altersrenten</a>versicherung (<a href="leibrente">Leibrente</a>) mit sofortigem Beginn übergibt der Versicherungsnehmer das notwendige <a href="rente">Rente</a>nkapital in Form einer <a href="einmaleinlage-einmalpramie">Einmaleinlage</a> und die Rentenleistung des Versicherers setzt sofort nach dem Vertragsabschluss ein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(695, 1, 'Rente', 'rente', NULL, 'Als Rente bezeichnet man eine periodisch wiederkehrende Leistung. Sie kann zeitlich begrenzt sein, z.B. <a href="todesfall-zeitrente">Todesfall-Zeitrente</a>, Erwerbsausfallsrente. Sie kann auch auf die Lebenszeit der <a href="versicherte-person">versicherten Person</a> geschuldet sein: <a href="altersrenten">Altersrente</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(696, 1, 'Anrechenbarer Lohn', 'anrechenbarer-lohn', NULL, 'Der dem Vorsorgeplan zugrundeliegende effektiv versicherte Lohn. Siehe<a href="koordinierter-lohn">koordinierter Lohn.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(697, 1, 'Performance', 'performance', NULL, 'Prozentuale Veränderung des Werts einer Investition zuzüglich eventuell aufgelaufener Erträge und korrigiert um allfällige Ein- und Auszahlungen während einer bestimmten Messperiode. Aussagekräftige Performancevergleiche sind bei <a href="anlagefonds">Anlagefonds</a> nur über längere Zeiträume möglich. Sinnvoll sind Vergleiche über drei, fünf und zehn Jahre.<br />\r<br />Gesamtertrag einer Anlage in Prozenten des Marktwertes zu Beginn der Messperiode. Die Performance setzt sich zusammen aus Kursveränderungen und reinvestierten Erträgen (z.B. Dividendenausschüttungen bei <a href="aktie">Aktien</a> und Couponzahlungen bei <a href="obligationen">Obligationen</a>). Ausgewiesen wird die Performance meistens in der Rechnungswährung des Fonds. Bei ausschüttenden Fonds erfolgt die Berechnung der Performance in der Annahme einer Wiederanlage der <a href="ausschuttung">Ausschüttungen</a>. Die Gesamt- oder absolute Performance über 5 Jahre (GP5) berücksichtigt den Zinseszinseffekt. Sie kann durch die nachfolgend dargestellte Multiplikation der 5 letzten jährlichen Performance (JP1 bis JP5) berechnet werden: GP5 = (JP1+1) x (JP2+1) x (JP3+1) x (JP4+1) x (JP5+1) - 1 (alle Performancewerte in %). Die annualisierte oder durchschnittliche jährliche Performance (AP5) wird als geometrisches und nicht als arithmetisches Mittel wiedergebeben und mit folgender Formel berechnet :AP5 = [1+GP5]1/5 -1. So ist zum Beispiel bei einer Gesamtperformance von 20% über 5 Jahre die annualisierte Performance 3,71%.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(698, 1, 'Performance Fee', 'performance-fee-1', NULL, 'Bei nicht-klassischen <a href="anlagefonds">Anlagefonds</a> wie <a href="hedge-funds-1">Hedge Funds</a> wird dem <a href="anleger">Anleger</a> oft neben der herkömmlichen Verwaltungsgebühr noch eine zusätzliche erfolgsabhängige Gebühr in Form eines prozentualen Anteils der jährlichen Wertsteigerung des Fonds belastet. Dabei wird meist eine <a href="high-watermark-1">Hochwassermarke (High Watermark)</a> gesetzt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(699, 1, 'Versicherungsperiode', 'versicherungsperiode', NULL, 'Die Zeitspanne, nach welcher die <a href="pramie">Versicherungsprämie</a> berechnet wird. Sie beträgt im Zweifel ein Jahr (<a href="vvg-1">VVG</a> 19).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(700, 1, 'Karenzzeit', 'karenzzeit', NULL, 'Als Karenzzeit bezeichnet man jene Zeit, während welcher die Gesellschaft in einem Ereignisfall ab <a href="vertragsbeginn">Vertragsbeginn</a> keine Leistungen zu erbringen hat. Die Karenzzeit kommt nur einmal zum Zuge, nämlich ab Vertragsbeginn für die vereinbarte Dauer. Sie ist nicht zu verwechseln mit der <a href="wartefrist">Wartefrist.</a> Karenzzeiten kennt man in der Lebensversicherung kaum mehr (ausser z.B. Selbsttötung).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(701, 1, 'Ewige Anleihe', 'ewige-anleihe', NULL, '<a href="obligationen">Obligationen</a> ohne Fälligkeitstermin. Ewige oder unendliche Anleihen leisten zwar regelmässig Zinszahlungen, tilgen den Nominalbetrag aber nie; um sein Kapital zurückzuerhalten, muss ein <a href="anleger">Anleger</a> solche Obligationen an der Börse verkaufen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(702, 1, 'Personenschäden', 'personenschaden', NULL, 'Personenschäden umfassen alle durch Körperverletzung oder Tötung eines Menschen verursachten materiellen Einbussen (Bsp.: Arzt- und Heilungskosten, Invaliditätsentschädigungen, <a href="versorgerschaden">Versorgerschäden</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(703, 1, 'Eigengut', 'eigengut', NULL, 'Eigengut sind von Gesetzes wegen (Art. 198 <a href="zgb">ZGB</a>):<br />- Gegenstände zum ausschliesslich persönlichen Gebrauch <br />- Vermögenswerte, die einem Ehegatten schon vor der Ehe oder sonstwie unentgeltlich zufallen<br />- Genugtuungsansprüche<br />- Ersatzanschaffungen für Eigengut', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(704, 1, 'Einfacher Diebstahl', 'einfacher-diebstahl', NULL, 'Unter einfachem Diebstahl versteht man die unrechtmässige Wegnahme einer Sache ohne Anwendung von Gewalt (weder an Personen (<a href="beraubung">Beraubung</a>) noch an Sachen (<a href="einbruchdiebstahl">Einbruch</a>)). Bei einfachem Diebstahl stehen dem Täter keine grösseren Hindernisse im Weg (z.B.: die Balkontüre oder ein Fenster stehen offen, der Schlüssel liegt unter der Fussmatte).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(705, 1, 'VN', 'vn-2', NULL, 'Abkürzung für "<a href="versicherungsnehmer-in">Versicherungsnehmer</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(706, 1, 'Taschen- / Trickdiebstahl', 'taschen-trickdiebstahl', NULL, '<a href="diebstahl">Diebstahl</a>, bei denen der Täter durch Können, List, Trick oder Täuschung vom eigentlichen Diebstahlvorgang ablenkt, wird Trick- oder Taschendiebstahl genannt. Er wird wie <a href="einfacher-diebstahl">einfacher Diebstahl</a> behandelt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(707, 1, 'Säule 3a', 'saule-3a', NULL, 'Teil der <a href="private-vorsorge">privaten Vorsorge</a> des schweizerischen <a href="sozialversicherung">Sozialversicherung</a>ssystems (<a href="dritte-saule">Dritte Säule</a>), der die <a href="gebundene-vorsorge">gebundene Vorsorge</a> beinhaltet. Hier nimmt der Versicherte gesetzliche Auflagen in Kauf, dafür profitiert er von einem <a href="steuerprivileg">Steuerprivileg</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(708, 1, 'Säule 3b', 'saule-3b', NULL, 'Teil der <a href="private-vorsorge">privaten Vorsorge</a> des schweizerischen <a href="sozialversicherung">Sozialversicherung</a>ssystems (<a href="dritte-saule">Dritte Säule</a>), der die <a href="freie-vorsorge">freie Vorsorge</a> beinhaltet. Hier hat der Versicherte keine gesetzlichen Auflagen, dafür sind seine <a href="steuerprivileg">Steuerprivilegien</a> eingeschränkt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(709, 1, 'Versicherungsort', 'versicherungsort', NULL, 'Sachen sind an dem /den in der <a href="police">Police</a> bezeichneten Ort/Orten versichert. <a href="hausrat">Hausrat</a> kann sich auch ausserhalb des bezeichneten Standortes befinden, in der Regel jedoch höchstens für ein bis zwei Jahre (je nach <a href="versicherungsvertrag">Versicherungsvertrag</a>). Dies ist wichtig bei Militärdienst, Reisen etc. Der Leistungsumfang ist dabei jedoch beschränkt (<a href="limite-limitierung">Limiten</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(710, 1, 'Anlagerisiko', 'anlagerisiko', NULL, 'Risiko, das durch die <a href="betriebshaftpflichtversicherung">Betriebshaftpflichtversicherung</a> versichert wird. Es handelt sich um die <a href="haftpflicht">Haftpflicht</a> eines Unternehmens gegenüber Dritten im Zusammenhang mit ganz oder teilweise selber genutzten Gebäuden, Räumlichkeiten, Betriebsanlagen und Grundstücken.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(711, 1, 'Police', 'police', NULL, 'Die Police ist eine private Urkunde über Rechte und Pflichten der Parteien, die der <a href="versicherer">Versicherer</a> dem <a href="versicherungsnehmer-in">Versicherungsnehmer</a> auszuhändigen hat (<a href="vvg-1">VVG</a> 11). Die Police dient dem Beweis des Abschlusses und des Inhalts (VVG 12) eines <a href="versicherungsvertrag">Versicherungsvertrages</a>. Sie ist nicht Voraussetzung für das Zustandekommen der Versicherung, sondern vielmehr ein Ausfluss daraus. Sie ist kein Wertpapier, wird jedoch hinsichtlich der Kraftloserklärung diesen gleichgestellt (VVG 13).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(712, 1, 'Policendarlehen', 'policendarlehen', NULL, 'Kreditaufnahme durch Belehnung des Sparkapitals einer <a href="lebensversicherung">Lebensversicherung</a> der <a href="saule-3b">Säule 3b</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(713, 1, 'Belehnung', 'belehnung', NULL, 'Bezug einer Vorauszahlung zu Lasten der <a href="versicherungsleistungen">Versicherungsleistungen</a> im Rahmen des <a href="ruckkaufswert">Rückkaufswertes.</a> Diese Vorauszahlung wird jährlich <a href="vorschussig">vorschüssig</a> verzinst, muss aber nicht zwingend zurückbezahlt werden. Wird sie es nicht, so zieht man die Belehnung/den Vorbezug/die Vorauszahlung bei Fälligkeit der Leistung vom frei werdenden Betrag ab.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(714, 1, 'Policenverpfändung', 'policenverpfandung', NULL, 'Eine <a href="lebensversicherung">Lebensversicherung</a> kann verpfändet werden (z. B. als Sicherheit für einen Kredit). Bei Fälligkeit der Versicherungsleistungen (z. B. im Todesfall) erhält der Pfandgläubiger (z. B. die Bank) das Vermögen. Eine Police der <a href="gebundene-vorsorge">gebundenen Vorsorge</a> (<a href="saule-3a">Säule 3a</a>) darf nur für <a href="wohneigentum-selbst-genutzt">selbst genutztes Wohneigentum</a> verpfändet werden. Bei einer Police der <a href="freie-vorsorge">freien Vorsorge</a> (<a href="saule-3b">Säule 3b</a>) gibt es bezüglich der Verpfändung keine Einschränkungen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(715, 1, 'Versicherungsnehmer/in', 'versicherungsnehmer-in', NULL, 'Der Vertragspartner (einzeln oder kollektiv) des <a href="versicherer">Versicherers</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(716, 1, 'Pool', 'pool', NULL, 'Um die Versicherungskapazität für besondere Risiken zu vergrössern, schliessen sich mehrere <a href="versicherer">Versicherer</a> in einem sogenannten Pool zusammen, an dem sie mit einer bestimmten Quote beteiligt sind. Überschüsse und Verluste werden auf die Mitglieder des Pools nach Massgabe ihrer Quoten verteilt (Schweizer Pool für die Versicherung von Nuklearrisiken, Schweizer Pool für die Luftfahrtversicherungen, Schweizer Elementarschaden-Pool, Pool Schweizerischer Lebensversicherungsgesellschaften für <a href="freizugigkeitspolice">Freizügigkeitspolicen</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(717, 1, 'Portfolio', 'portfolio-1', NULL, 'Siehe <a href="portefeuille">Portefeuille</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(718, 1, 'Portefeuille', 'portefeuille', NULL, 'Per i fondi d''investimento, composizione del patrimonio di un fondo.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(719, 1, 'Portfolio Manager (Fondsmanager)', 'portfolio-manager-fondsmanager', NULL, 'Anlagespezialisten, die das <a href="fondsvermogen">Fondsvermögen</a> der <a href="anlagefonds">Anlagefonds</a> verwalten. Sie entscheiden innerhalb der festgelegten Anlagegrundsätze, welche Werte zu kaufen bzw. zu verkaufen sind. Auch Asset Manager genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(720, 1, 'Portfoliotheorie', 'portfoliotheorie', NULL, 'Eine zentrale Erkenntnis der modernen Portfoliotheorie betrifft den Zusammenhang zwischen <a href="risiko-fonds">Risiko</a> und <a href="ertrag">Ertrag</a>. Ein höherer Ertrag kann zwar grundsätzlich nur mit einem grösseren Risiko "erkauft" werden. Mit einer breiten Streuung der Anlagen (<a href="diversifikation-risikostreuung">Diversifikation</a>) kann das Verhältnis zwischen Risiko und Ertrag indessen optimiert werden. Auf diese Weise lässt sich mit einem gleich grossen Risiko ein höherer Ertrag erwirtschaften bzw. ein gleich hoher Ertrag mit einem geringeren Risiko erzielen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(721, 1, 'Halter', 'halter', NULL, 'Nach Strassenverkehrsgesetz (SVG) gilt derjenige als Halter, welcher tatsächlich über das <a href="motorfahrzeug">Motorfahrzeug</a> verfügt und dieses in seinem Interesse und auf seine Kosten benützt.<br />In der Praxis gilt derjenige als Halter, dessen Name im Fahrzeugausweis eingetragen ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(722, 1, 'Konkursprivileg', 'konkursprivileg', NULL, 'Gemäss <a href="vvg-1">VVG</a> erlischt die Begünstigung mit der Pfändung des Versicherungsanspruches und mit der Konkurseröffnung über den Versicherungsnehmer. Sie lebt wieder auf, wenn die Pfändung dahinfällt oder der Konkurs widerrufen wird. Eine wichtige Ausnahme bildet das Konkursprivileg. Das heisst, dass die Lebensversicherung nicht in die Konkursmasse fällt, wenn die Begünstigung an erster Stelle auf den Ehegatten oder die Kinder lautet. Auf diese Weise kann der Versicherungsschutz für die Familie erhalten werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(723, 1, 'Prämie', 'pramie', NULL, 'Die Prämie ist der Preis, den der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> dafür entrichtet, dass der <a href="verschuldenshaftung">Versicherer</a> im Schadenfall die vereinbarten Leistungen erbringt. Die Prämie wird in der Regel nach Versicherungsperioden, im Zweifel jeweils ein Jahr, bemessen, auch wenn andere Zahlungsmodalitäten, z. B. monatliche Zahlung, <a href="einmaleinlage-einmalpramie">Einmaleinlage</a>, vereinbart werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(724, 1, 'Agio', 'agio-1', NULL, 'Das Agio ist die prozentuale Differenz zwischen dem aktuellen Börsenkurs und dem ausschüttungsbereinigten <a href="inventarwert">Inventarwert</a> zuzüglich letzte <a href="ausschuttung">Ausschüttung</a> (anteilsmässig).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(725, 1, 'Prämien (Fonds)', 'pramien-fonds', NULL, 'Meist in Prozent ausgedrückter Wert, um den z.B. der Ausgabekurs eines Wertpapiers über einem bestimmten Referenzpreis, in der Regel dem Nennwert, liegt. Unter Prämie versteht man aber auch den Betrag (oft auch in Prozenten ausgedrückt), den der Käufer für ein entsprechendes Recht (Optionspreis, <a href="optionen">Option</a>, Bezugsrecht) über den rechnerischen Wert hinaus zu bezahlen bereit ist. Bei den zu Absicherungszwecken eingesetzten derivativen Instrumenten (Put-Optionen) der Preis, den der Optionskäufer für das Recht bezahlt, den <a href="basiswert">Basiswert</a> zu verkaufen. Bei den für die Limited Risk Funds eingesetzten Put-Optionen steigt die Prämie mit zunehmender <a href="risiko-fonds">Volatilität</a> des Marktes.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(726, 1, 'Prämienanpassungsklausel (PAK)', 'pramienanpassungsklausel-pak', NULL, 'Die Parteien können - durch entsprechende Klauseln in den <a href="avb">AVB</a> - vereinbaren, dass der <a href="versicherer">Versicherer</a> <a href="pramie">Prämien</a> oder <a href="selbstbehalt">Selbstbehalte</a> während der laufenden <a href="vertragsdauer">Vertragsdauer</a> anpassen kann. Dem <a href="versicherungsnehmer-in">Versicherungsnehmer</a> ist gleichzeitig im Fall einer solchen Vertragsanpassung ein angemessenes Kündigungsrecht einzuräumen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(727, 1, 'Prämiendepot (oder Prämienkonto)', 'pramiendepot-oder-pramienkonto', NULL, 'Das Prämiendepot ist ein von der Versicherungsgesellschaft spesenfrei geführtes verzinsliches Konto, das zur Finanzierung von künftigen <a href="pramie">Prämien</a> eines Lebensversicherungsvertrages dient. Die Prämien werden jeweils bei Fälligkeit abgebucht. Reicht das Depot nicht zur Deckung der Prämien bis zum Ablauf der Prämienzahlungsdauer, so muss die laufende Prämienzahlung wieder aufgenommen oder das Depot erhöht werden. Der Saldo des Depots darf nicht die Höhe der zukünftigen Prämie übersteigen. Je nach Gesellschaft wird zwischen einem unwiderruflichen und einem widerruflichen Prämiendepot unterschieden. Der Vorteil des unwiderruflichen Prämienkontos ist, dass ein höherer Zins gewährt wird, jedoch können nur in bestimmten Fällen Rückzüge getätigt werden. Zudem unterstehen die Zinsen nicht der Verrechnungssteuer. Der erwirtschaftete Zins muss als Einkommen versteuert werden und der Saldo muss im Vermögen deklariert werden. Beim widerruflichen Prämiendepot kann der Prämiendepotinhaber in jedem Fall Rückzüge tätigen und das Prämiendepot kündigen. Der Zinssatz ist jedoch bedeutend tiefer als beim unwiderruflichen. Auf dem Zins muss die Verrechnungssteuer abgezogen werden. Ansonsten ist die Besteuerung gleich wie beim unwiderruflichen Prämiendepot. Die Steuerämter haben jederzeit die Möglichkeit, seit dem Bundesgerichtsentscheid vom 09.12.1994, von den Versicherunsgesellschaften die Offenlegung aller Prämienkontoinhaber zu verlangen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(728, 1, 'Prämienkonto', 'pramienkonto', NULL, 'siehe<a href="pramiendepot-oder-pramienkonto">Prämiendepot</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(729, 1, 'Prämienvergünstigung', 'pramienvergunstigung', NULL, 'Jeder Versicherte der <a href="obligatorische-krankenpflegeversicherung">obligatorischen Krankenpflegeversicherung</a> kann seine Prämienbelastung senken, indem er die <a href="franchise">Franchise</a> erhöht oder ein kostengünstiges <a href="versicherungsmodell">Versicherungsmodell</a> wählt. Versicherte in bescheidenen wirtschaftlichen Verhältnissen können zudem <a href="pramienverbilligung">Prämienverbilligungen</a> durch staatliche Zuschüsse verlangen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(730, 1, 'Prämienzahler', 'pramienzahler', NULL, 'Als Prämienzahler wird diejenige natürliche oder juristische Person bezeichnet, welche dem <a href="versicherer">Versicherer</a> die <a href="pramie">Prämien</a> bezahlt. In der Regel ist der Prämienzahler identisch mit dem <a href="versicherungsnehmer-in">Versicherungsnehmer</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(731, 1, 'Prämienzahlung', 'pramienzahlung', NULL, 'Beitragspflicht des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> zur Deckung der versicherten <a href="gefahr">Gefahr</a>. Die Höhe der Prämienzahlung hängt von der Höhe des Risikos und der <a href="versicherungsdauer">Versicherungsdauer</a> ab.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(732, 1, 'Prämienbefreiung', 'pramienbefreiung', NULL, 'Die versicherte Person wird von der Prämienzahlungspflicht entbunden, wenn der Versicherte während der Versicherungsdauer arbeits- bzw. erwerbsunfähig wird. Die Versicherungsgesellschaft übernimmt die weiteren Prämienzahlungen entsprechend dem Grad der <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a> der versicherten Person. Die Versicherung läuft unverändert weiter. Die Prämienbefreiung im Todesfall bei einer Versicherung auf mehrere Leben erlaubt den mitversicherten Personen die prämienfreie Weiterführung des Vertrages.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(733, 1, 'Prämienfreistellung', 'pramienfreistellung', NULL, 'Durch die Prämienfreistellung einer Versicherung wird die <a href="versicherungssumme">Versicherungssumme</a> reduziert und alle Zusatzversicherungen erlöschen. Technisch wird der im Zeitpunkt der Prämienfreistellung vorhandene <a href="ruckkaufswert">Rückkaufswert</a> als <a href="einmaleinlage-einmalpramie">Einmaleinlage</a> für eine Versicherung mit den vorhandenen technischen Daten verwendet. Aus dieser Berechnung resultiert die prämienfreie, reduzierte Versicherungssumme, welche der Kunde per Ablauf (je nach Tarif auch beim Tode) erhält. Der Kunde zahlt vom Moment der Prämienfreistellung an keine Prämien mehr für seinen Vertrag. Ausgelöst wird die Prämienfreistellung durch Kundenwunsch oder bei Nichtbezahlung der Prämien nach erfolgter Mahnung. Voraussetzung: Die Versicherung muss rückkaufsfähig sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(734, 1, 'Prämienverbilligung', 'pramienverbilligung', NULL, 'Möglichkeit der <a href="pramienvergunstigung">Prämienvergünstigung</a> in der <a href="#invalid-reference-0">obligatorischen Krankenpflegeversicherung</a>. Nach dem KVG können Versicherte in bescheidenen wirtschaftlichen Verhältnissen Prämienverbilligungen durch Zuschüsse beantragen. Dabei legt der Kanton die Einkommensgrenze sowie die zuständige Behörde fest.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(735, 1, 'Prämienrückgewähr', 'pramienruckgewahr', NULL, 'Die Prämienrückgewähr trifft man vor allem bei den Altersrenten an. Sie bedeutet, dass beim Tode der versicherten Person die einbezahlten <a href="pramie">Prämien</a> bzw. <a href="einmaleinlage-einmalpramie">Einmalprämien</a> (ohne Zins) abzüglich allenfalls bereits bezogener <a href="rente">Renten</a>, an die im Vertrag begünstigte Person (Begünstigung) zurückbezahlt werden. Prämienrückgewähr kennt man aber auch in der Erlebensfall-Versicherung. Beim Tod der versicherten Person werden die bis dahin einbezahlten Prämien, ebenfalls ohne Zins, zurückerstattet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(736, 1, 'Prämiensatz', 'pramiensatz', NULL, '<a href="pramie">Prämie</a>, die für die Leistung Fr. 1.- bezahlt werden muss.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(737, 1, 'Zeitwert', 'zeitwert', NULL, 'Als Zeitwert (ZW) wird der Betrag bezeichnet, der für die Neuanschaffung oder die Wiederherstellung einer gleichartigen Sache erforderlich ist, abzüglich der <a href="wertminderung">Wertminderung</a> infolge von Abnützung, Alter oder anderen Gründen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(738, 1, 'Bauherr', 'bauherr', NULL, 'Eigentümer eines entstehenden Bauwerks.', '2016-08-29 10:21:09', '2016-08-29 10:21:55');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(739, 1, 'Bauherren-Haftpflichtversicherung', 'bauherren-haftpflichtversicherung', NULL, '<a href="vermogensversicherung">Vermögensversicherung</a>, die einen Bauherrn (Werkeigentümer) vor <a href="haftpflicht">Haftpflicht</a>ansprüchen Dritter schützt, die im Zusammenhang mit dem entstehenden Bauwerk stehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(740, 1, 'Privatbankier', 'privatbankier', NULL, 'Ein Privatbankier ist ein Unternehmer des privaten Bankgewerbes, der mit eigenem Kapital, unbeschränkter Haftung seines Gesamtvermögens (Geschäfts- und Privatvermögen) und alleiniger Entscheidungsbefugnis (ohne übergeordnete Organe wie Generalversammlung oder Verwaltungsrat) Bankgeschäfte betreibt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(741, 1, 'Private Equity', 'private-equity-1', NULL, 'Ein Start-up-Unternehmen oder ein schon agierendes Jungunternehmen benötigen Kapital, das über Investoren und Private Equity Funds gesucht wird. Da es sich meistens um noch nicht an der Börse kotierte Unternehmen handelt, spricht man von privaten Märkten, in denen investiert wird.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(742, 1, 'Privatversicherung', 'privatversicherung', NULL, 'Fanno parte dell''assicurazione privata (assicuratori privati) le imprese d''assicurazione organizzate giuridicamente a titolo privato. Si parla di assicurazione di<a href="diritto-pubblico">diritto pubblico</a> in caso di assicuratori organizzati giuridicamente in base al diritto pubblico.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(743, 1, 'Privatrecht', 'privatrecht', NULL, 'Teil der Rechtsordnung, die das Verhältnis zwischen privaten Rechtssubjekten (Bürger, Privatunternehmen) untereinander regelt. Bei Rechtsverstössen muss der betroffene Bürger bzw. das betroffene Unternehmen selbst aktiv werden und klagen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(744, 1, 'Privatrechtsschutz', 'privatrechtsschutz', NULL, 'Bereich der <a href="rechtsschutzversicherung">Rechtsschutzversicherung</a>, der Rechtsstreitigkeiten versichert, mit denen Privatpersonen konfrontiert werden. Ausgenommen davon ist der <a href="verkehrsrechtsschutz">Verkehrsrechtsschutz</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(745, 1, 'Private Vorsorge', 'private-vorsorge', NULL, 'Siehe <a href="dritte-saule">Dritte Säule</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(746, 1, 'Privathaftpflichtversicherung', 'privathaftpflichtversicherung', NULL, 'Die Privathaftpflichtversicherung schützt den <a href="versicherter">Versicherten</a> gegen Ansprüche, die aufgrund gesetzlicher Haftpflichtbestimmungen gegen ihn erhoben werden. Geschützt ist der Versicherte als Privatperson, als Familienvater, als Hundehalter, als Wohnungsmieter, Wehrmann und Sportausübender usw. Ausgeschlossen sind <a href="schaden">Schäden</a>, die der Versicherte als <a href="lenker">Lenker</a> oder <a href="halter">Halter</a> eines <a href="motorfahrzeug">Motorfahrzeuges</a> oder im Rahmen einer beruflichen Tätigkeit verursacht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(747, 1, 'Privatgebrauch', 'privatgebrauch', NULL, 'Nutzung von <a href="sache">Sachen</a> für private Zwecke.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(748, 1, 'Erbschaftsprivileg', 'erbschaftsprivileg', NULL, 'Vgl. <a href="erbvorteile">Erbvorteile</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(749, 1, 'Prozess', 'prozess', NULL, 'Siehe <a href="geschaftsprozess">Geschäftsprozess</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(750, 1, 'Produktentwicklung', 'produktentwicklung', NULL, 'Teilprozess des <a href="leistungsprozess">Leistungsprozesses</a>, der die Weiterentwicklung bestehender oder die Entwicklung neuer Versicherungsprodukte zum Ziel hat.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(751, 1, 'Produktehaftpflicht', 'produktehaftpflicht', NULL, 'Haftung des Herstellers, Importeurs oder Händlers für <a href="schaden">Schaden</a>, der durch mangelhafte Produkte angerichtet wird (Mangelfolgeschaden). Es handelt sich um einen Fall der <a href="ausservertragliche-haftpflicht">ausservertraglichen Haftpflicht</a> (<a href="milde-kausalhaftung">milde</a> <a href="kausalhaftung">Kausalhaftung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(752, 1, 'Produktrisiko', 'produktrisiko', NULL, '<a href="gefahr">Gefahr</a>, die durch die <a href="betriebshaftpflichtversicherung">Betriebshaftpflichtversicherung</a> versichert wird. Es handelt sich um die <a href="haftpflicht">Haftpflicht</a> eines Unternehmens gegenüber Dritten im Zusammenhang mit Mangelfolgeschäden. Abgedeckt sind also Folgeschäden, die durch die Herstellung oder den Vertrieb eines fehlerhaften Produkts entstehen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(753, 1, 'Prozessrisiko', 'prozessrisiko', NULL, 'Kostenrisiko für Konflikte, die in einem Gerichtsprozess beurteilt werden. Wenn ein Versicherter seine Ansprüche vor Gericht durchsetzen oder abwehren will, werden die dadurch entstehenden Kosten (Anwalts- und Gerichtskosten, Aufwandsentschädigungen) durch eine <a href="rechtsschutzversicherung">Rechtsschutzversicherung</a> gedeckt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(754, 1, 'Leistungsprozess', 'leistungsprozess', NULL, 'Zu den Leistungsprozessen gehören die Geschäftsprozesse eines Unternehmens, die direkt mit dem Produkt / der Dienstleistung zusammenhängen.<br />In der Privatversicherungsbranche sind das v.a. die <a href="produktentwicklung">Produktentwicklung</a>, <a href="beratung-und-verkauf">Beratung / Verkauf</a>, <a href="underwriting-und-riskmanagement">Underwriting / Riskmanagement</a>, Bestandsverwaltung / Zahlungsverkehr und Schadendienst.<br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(755, 1, 'Berufshaftpflichtversicherung', 'berufshaftpflichtversicherung', NULL, '<a href="betriebshaftpflichtversicherung">Betriebshaftpflichtversicherung</a>für bestimmte Berufsgruppen (Selbstständige, Freiberufler) mit besonderen Schutzbedürfnissen (z. B. Ärzte, Architekten, Ingenieure, Anwälte, Treuhänder).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(756, 1, 'Berufsutensilien', 'berufsutensilien', NULL, 'In der <a href="hausratversicherung">Hausratversicherung</a> sind häufig auch Gegenstände miteingeschlossen, die dem versicherten Personenkreis gehören, aber beruflich genutzt werden (Werkzeug, Computer, Arbeitskleidung, etc.).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(757, 1, 'Gewinn', 'gewinn', NULL, 'siehe <a href="uberschussbeteiligung">Überschussbeteiligung</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(758, 1, 'Veränderungsverbot', 'veranderungsverbot', NULL, 'Bevor der Schaden ermittelt ist, darf der aus dem <a href="versicherungsvertrag">Versicherungsvertrag</a> <a href="anspruchsberechtigter">Anspruchsberechtigte</a> ohne Zustimmung des <a href="versicherer">Versicherers</a> an den beschädigten Gegenständen keine Veränderung vornehmen, welche die Feststellung der Ursache oder des Schadens erschweren oder verunmöglichen (<a href="vvg-1">VVG</a> 68).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(759, 1, 'Wohneigentumsförderung (BV)', 'wohneigentumsforderung-bv', NULL, 'Das <a href="altersguthaben">Altersguthaben</a> der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> kann (mit gewissen Einschränkungen) zum Erwerb von <a href="wohneigentum-selbst-genutzt">Wohneigentum</a> verwendet werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(760, 1, 'Sache', 'sache', NULL, 'Beweglicher (<a href="mobilie">Mobilie</a>) oder unbeweglicher (<a href="immobilie">Immobilie</a>) körperlicher Gegenstand.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(761, 1, 'Verlustschein', 'verlustschein', NULL, 'Amtlicher Ausweis für den <a href="glaubiger">Gläubiger</a> für den im Betreibungsverfahren nicht gedeckten Teil seiner Forderung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(762, 1, 'Antrag', 'antrag', NULL, 'Antrag ist die Willenserklärung, mit welcher der Antragsteller verbindlich seinen Willen mitteilt, einen Vertrag abschliessen zu wollen, so dass es für das Zustandekommen des Vertrages nur noch der zustimmenden Gegenerklärung (der Annahme) des Empfängers bedarf. Der Antrag für den Abschluss eines <a href="versicherungsvertrag">Versicherungsvertrages</a> geht normalerweise vom Versicherungsinteressenten (dem zukünftigen <a href="versicherungsnehmer-in">Versicherungsnehmer</a>) aus. Er muss alle objektiv wesentlichen Vertragspunkte (versicherte Gefahr, versicherter Gegenstand, <a href="versicherungsleistungen">Versicherungsleistung</a>, <a href="pramie">Prämie</a>, Beginn und Dauer der Versicherung) und allenfalls weitere Punkte, die eine Partei als wesentlich bezeichnet hat, enthalten. Üblicherweise wird bei der Antragstellung ein Antragschein verwendet. Die <a href="avb">AVB</a> sind entweder darin enthalten oder es wird auf sie verwiesen. Sie müssen im letzteren Fall dem Antragsteller vor Einreichung des Antrages übergeben worden sein, ansonsten der Antrag unverbindlich ist. Der Antragsteller bleibt von der Absendung an 14 Tage an den Antrag gebunden (ist eine ärztliche Untersuchung notwendig, beträgt die Frist 4 Wochen). Innerhalb dieser Frist kann der <a href="versicherer">Versicherer</a> die <a href="annahme">Annahme</a> erklären, was den Vertragsschluss bewirkt. Trifft die Annahmeerklärung (üblicherweise geschieht dies durch Zustellung der <a href="police">Police</a> oder Rechnungstellung für die erste Prämie) verspätet oder mit Änderungen in Bezug auf wesentliche Vertragspunkte beim Antragsteller ein, so stellt dies einen neuen Antrag des Versicherers an den zukünftigen Versicherungsnehmer dar, den dieser anzunehmen hat, damit der Vertrag zustande kommt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(763, 1, 'Antrieb', 'antrieb', NULL, 'Mit eigenem Antrieb (Explosionsmotor, elektrisch oder solar) bedeutet, dass das <a href="fahrzeug">Fahrzeug</a> durch eine eigene Kraftquelle und nicht durch fremde, menschliche, tierische, motorische oder natürliche Kräfte gezogen, geschoben oder sonstwie bewegt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(764, 1, 'Prospekt / Fondsreglement', 'prospekt-fondsreglement', NULL, 'In Prospekt und Fondsreglement sind die Rechte und Pflichten der Vertragsparteien verankert. Insbesondere enthalten sie die Richtlinien für die <a href="anlagepolitik">Anlagepolitik</a>, für die Verwendung der Erträge und für die Kosten, die dem Fonds resp. dem <a href="anleger">Anleger</a> belastet werden dürfen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(765, 1, 'Übervorteilungsschutz', 'ubervorteilungsschutz', NULL, 'Tutela degli <a href="assicurato">assicurati</a> dal potere degli specialisti.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(766, 1, 'Deckungszusage, provisorische', 'deckungszusage-provisorische', NULL, 'Provisorische oder vorläufige Deckungszusage liegt vor, wenn der <a href="versicherer">Versicherer</a> <a href="versicherungsschutz">Versicherungsschutz</a> gewährt, bevor er die Annahme des gestellten Versicherungsantrages erklärt. In der Regel hat die vorläufige Deckung den Umfang des gestellten <a href="antrag">Antrages</a>. Sie wird regelmässig befristet und fällt normalerweise dahin, wenn der Versicherer den Antrag annimmt oder ablehnt. Die vorläufige Deckung stellt nach herrschender Lehre einen selbständigen <a href="versicherungsvertrag">Versicherungsvertrag</a> dar, der an keine Form gebunden ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(767, 1, 'Öffentliches Recht', 'offentliches-recht', NULL, 'Teil der Rechtsordnung, die das Verhältnis zwischen den Trägern der Staatsgewalt (Behörden) und privaten Rechtssubjekten (Bürger, Privatunternehmen) regelt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(768, 1, 'Put-Optionen', 'put-optionen', NULL, 'Siehe <a href="optionen">Optionen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(769, 1, 'ZW', 'zw', NULL, 'Abkürzung für "<a href="zeitwert">Zeitwert</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(770, 1, 'Offerte', 'offerte', NULL, 'Offerte ist gleichbedeutend wie Antrag im Rechtssinne. Für Weiteres siehe unter <a href="antrag">Antrag</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(771, 1, 'Raiffeisenbank', 'raiffeisenbank', NULL, 'Die Darlehens- und Raiffeisenkassen, wie sie von der Bankenstatistik genannt werden, machen von der Bilanzsumme nur rund 2 bis 3 % aller von der Bankenstatistik erfassten Institute aus. Es handelt sich um gemeinnützige und nach ihrem Gründer, Friedrich Wilhelm Raiffeisen, benannte dorfweise organisierte Spar- und Kreditinstitute. Sie sammeln das am Ort aufkommende Geld, um es zu möglichst günstigen Bedingungen an Kreditsuchende des Ortes wieder auszuleihen. Die Raiffeisenbank sind somit Selbsthilfeorganisationen auf genossenschaftlicher Basis.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(772, 1, 'Umwandlungssatz', 'umwandlungssatz', NULL, 'Prozentsatz, dessen Höhe vom Bundesrat aufgrund der durchschnittlichen Lebenserwartung festgelegt wird. Der Umwandlungssatz dient zur Berechnung der <a href="altersrenten">Altersrente</a> aufgrund des im Rücktrittsalters vorhandenen <a href="altersguthaben">Altersguthabens</a> (Altersguthaben multipliziert mit dem Umwandlungssatz = jährliche Altersrente). Er wird ebenfalls verwendet für die Berechnung der Invalidenrenten nach <a href="bvg-1">BVG.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(773, 1, 'Rating', 'rating-1', NULL, 'Die Bewertung der <a href="bonitat-fonds">Bonität</a> von Schuldnern durch spezielle Rating-Agenturen wie Standard & Poor´s oder Moody''s.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(774, 1, 'Wiederinkraftsetzung', 'wiederinkraftsetzung', NULL, 'Reaktivierung eines ausser Kraft stehenden Vertrages in voller oder reduzierter Höhe.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(775, 1, 'Immobilie', 'immobilie', NULL, 'Unbewegliche <a href="sache">Sache</a> (z. B. unbebautes oder bebautes Grundstück, Eigentumswohnung).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(776, 1, 'Immobilienfonds (Liegenschaftsfonds)', 'immobilienfonds-liegenschaftsfonds', NULL, 'Ein Vermögen, das auf Grund öffentlicher Werbung von einer Vielzahl grosser und kleiner <a href="anleger">Anleger</a> zum Zweck der gemeinschaftlichen Kapitalanlage aufgebracht und in Immobilien investiert wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(777, 1, 'Realwert', 'realwert', NULL, 'Nachkalkulation des aktuellen Substanzwerts einer Liegenschaft, d.h. der Kosten für die Neuerrichtung des Objekts im analogen Standard unter Berücksichtigung der zwischenzeitlich eingetretenen Altersentwertung und des <a href="landwert">Landwerts</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(778, 1, 'Verwertbarkeit (bei Konkurs/Pfändung)', 'verwertbarkeit-bei-konkurs-pfandung', NULL, 'Bei Zahlungsunfähigkeit des Kunden kann der <a href="ruckkaufswert">Rückkaufswert</a> einer Lebensversicherung nach vorgängiger <a href="pfandung">Pfändung</a> bzw. im Rahmen des Konkursverfahrens verwertet werden. Das Rückkaufsbegehren stellt das Betreibungs- bzw. das Konkursamt. Mit dem Rückkaufswert werden die Forderungen des/der <a href="glaubiger">Gläubiger/s</a> ganz oder teilweise befriedigt. Eine Verwertung kann aber nur erfolgen, wenn der Vertrag nicht durch das <a href="konkursprivileg">Konkursprivileg</a> geschützt ist oder wenn es sich nicht um eine gebundene <a href="vorsorgepolice">Vorsorge-Police</a> handelt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(779, 1, 'Gegenseitige übereinstimm. Willensäusserung', 'gegenseitige-ubereinstimm-willensausserung', NULL, 'Ein <a href="antrag">Antrag</a> zum Vertragsschluss, der die wesentlichen Vertragspunkte beinhaltet, und eine deckungsgleiche Annahmeerklärung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(780, 1, 'Wagnis (NBU)', 'wagnis-nbu', NULL, 'Private Aktivität, durch die sich ein Versicherter freiwillig einer besonders grossen Unfallgefahr aussetzt und die zur Kürzung oder Verweigerung der Leistungen der <a href="obligatorische-unfallversicherung">obligatorischen Unfallversicherung führt</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(781, 1, 'Regress', 'regress', NULL, 'Die Frage nach dem Regressrecht stellt sich, wenn zwei oder mehrere Personen, meist aus verschiedenen Rechtsgründen, für denselben <a href="schaden">Schaden</a> ersatzpflichtig werden. Dies ist beispielsweise der Fall, wenn eine Drittperson dem <a href="versicherter">Versicherten</a> einen Schaden zufügt, für den der <a href="versicherer">Versicherer</a> aufgrund des <a href="versicherungsvertrag">Versicherungsvertrages</a> aufzukommen hat. Es stellt sich die Frage, unter welchen Voraussetzungen der Versicherer, soweit er dem Geschädigten den Schaden ersetzt hat, die geleistete Zahlung vom Schadenverursacher zurückfordern kann. Die Regressregeln richten sich nach <a href="vvg-1">VVG</a> 72 (<a href="subrogation-1">Subrogation</a>) und <a href="or-1">OR</a> 51. Hat der Schadenverursacher den Schaden schuldhaft aus unerlaubter Handlung herbeigeführt, steht dem Versicherer ein Regressrecht zu (VVG 72). Hat der Schadenverursacher gegenüber dem Geschädigten wie der Versicherer einzig aus Vertrag einzustehen, ist ein Regress des Versicherers nur im Falle von <a href="grobe-fahrlassigkeit-grobfahrlassigkeit">Grobfahrlässigkeit</a>möglich (OR 51). Der Personensummenversicherung, für welche das <a href="kumulation">Kumulationsprinzip</a> gilt, ist der Regress fremd.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(782, 1, 'Rückgriff', 'ruckgriff', NULL, 'Rückgriff (oder <a href="regress">Regress</a>) bedeutet Rückforderung von einem anderen Ersatzpflichtigen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(783, 1, 'Rücknahmen', 'rucknahmen', NULL, 'Rückgabe von Fondsanteilen an die Fondsgesellschaft.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(784, 1, 'Rücknahmekommission', 'rucknahmekommission', NULL, 'Kommission, die von der Fondsgesellschaft bei der Rücknahme von <a href="anteil">Anteilen</a> vom <a href="anleger">Anleger</a> erhoben wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(785, 1, 'Rückgabe von Anteilen / Börsenhandel', 'ruckgabe-von-anteilen-borsenhandel', NULL, '<a href="anteil">Anteile</a> von <a href="immobilienfonds-liegenschaftsfonds">Immobilienfonds</a> können täglich an der Börse gehandelt werden oder der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> auf Ende eines <a href="rechnungsjahr">Rechnungsjahres</a> unter Einhaltung einer 12 monatigen Kündigungsfrist gekündigt werden. Die Rückzahlung erfolgt zwei Monate nach Ablauf der Kündigungsfrist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(786, 1, 'Rücknahmepreis', 'rucknahmepreis', NULL, 'Preis, zu dem ein <a href="anlagefonds">Anlagefonds</a> verpflichtet ist, <a href="anteil">Anteile</a> zu Lasten des <a href="fondsvermogen">Fondsvermögens</a> zurückzunehmen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(787, 1, 'Rücknahmepreis (Immobilienfonds)', 'rucknahmepreis-immobilienfonds', NULL, 'Entspricht dem Preis, zu dem <a href="anteil">Anteile</a> von der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> unter Einhaltung der gesetzlichen Kündigungsfrist zurückgenommen werden müssen (<a href="inventarwert">Inventarwert</a> abzüglich einer Kommission gemäss Reglement).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(788, 1, 'Einredenausschluss', 'einredenausschluss', NULL, 'Der <a href="versicherer">Versicherer</a> darf gegenüber dem <a href="geschadigte">Geschädigten</a> keine Deckungseinschränkung anführen (z.B. Alkoholklausel), welche zu einer Reduktion des Schadenersatzes führen würden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(789, 1, 'Referenzwährung', 'referenzwahrung', NULL, 'Die Währung, in welcher ein <a href="anleger">Anleger</a> normalerweise denkt, rechnet und seine Verbindlichkeiten erfüllt. Bei einem <a href="anlagefonds">Anlagefonds</a> die Währung, in der die Wertentwicklung gemessen wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(790, 1, 'Referenzindex', 'referenzindex', NULL, 'Siehe <a href="benchmark">Benchmark</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(791, 1, 'Rückgewähr', 'ruckgewahr', NULL, 'Rückgewähr siehe <a href="pramienruckgewahr">Prämienrückgewähr</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(792, 1, 'Leistungsverweigerung/ -kürzung (UV)', 'leistungsverweigerung-kurzung-uv', NULL, 'Die Leistungen der <a href="obligatorische-unfallversicherung">obligatorischen Unfallversicherung</a> können in bestimmten Fällen verweigert oder gekürzt werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(793, 1, 'Regionalbank', 'regionalbank', NULL, 'Die Regionalbanken und Sparkassen gehören zu den ältesten Banken in der Schweiz. Das Ziel war die sichere und zinstragende Anlage der Ersparnisse. Auch heute versteht man unter einer Sparkasse ein Geldinstitut, dessen Fremdgelder vollständig oder doch überwiegend aus Spareinlagen bestehen. Im <a href="aktivgeschaft">Aktivgeschäft</a> legen die Sparkassen die ihnen anvertrauten Gelder vorwiegend in Hypotheken an. Die Regionalbanken waren Banken, die mehr oder weniger alle Bankgeschäfte, sowohl die Zinsdifferenz- als auch<a href="indifferentes-geschaft">Indifferenten Geschäfte</a>, tätigen, aber ein grösseres Einzugsgebiet aufweisen. Sie sind in ihrer Region stark verankert und zählen traditionell das Handwerk, das Gewerbe, die Industrie, die Landwirtschaft sowie Private zu ihren Kunden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(794, 1, 'Regionales Arbeitsvermittlungszentrum (RAV)', 'regionales-arbeitsvermittlungszentrum-rav', NULL, 'Öffentliches Amt für die Vermittlung von Arbeitsstellen in der Region. Die Zusammenarbeit mit den Beratern des RAV ist eine Voraussetzung für eine <a href="arbeitslosenentschadigung">Arbeitslosenentschädigung</a>. Das RAV kann Stellenbewerbungen verlangen und die Teilnahme an Informationsveranstaltungen sowie Massnahmen zur Verbesserung der Arbeitsmarktfähigkeit vorschreiben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(795, 1, 'Regionenfonds', 'regionenfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die ihre Investitionen in einer bestimmten geografischen Region (z.B. Skandinavien) oder einem besonderen Wirtschaftsraum (z.B. Euroland) tätigen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(796, 1, 'Namenaktie', 'namenaktie', NULL, 'Aktie, die auf den Namen lautet und deren Eigentümer oder Nutzniesser im Aktienregister der betreffenden Gesellschaft eingetragen ist. Dies erschwert den Handel mit solchen Titeln. Namenaktien können aber trotzdem an den Effektenbörsen kotiert und gehandelt werden. Gegensatz: <a href="inhaberaktie">Inhaberaktie.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(797, 1, 'Eingliederungsmassnahmen', 'eingliederungsmassnahmen', NULL, 'Individuelle Massnahmen im Rahmen der <a href="invalidenversicherung">Invalidenversicherung</a>, um invalide Personen in den Arbeitsprozess zu integrieren. Dazu gehören berufliche, schulische und medizinische Massnahmen zur Wiedererlangung der Erwerbsfähigkeit, Taggelder während dieser Massnahmen und unterstützende Hilfsmittel, die im Berufs- oder Privatleben benötigt werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(798, 1, 'Rückversicherung', 'ruckversicherung', NULL, 'Bei der Rückversicherung geht es darum, das versicherte <a href="risiko">Risiko</a> in einem bestimmten Verhältnis auf Erst- und Rückversicherer zu verteilen. Der <a href="versicherer">Versicherer</a>, der den Vertrag mit dem Publikum abschliesst, wird als Erstversicherer bezeichnet. Wenn er einen Teil seines Geschäftes an den Rückversicherer abgibt, handelt er als Zedent; der das Risiko übernehmende Rückversicherer ist der Zessionar. Denjenigen Teil des Risikos, den der Erstversicherer selbst trägt, nennt man Eigenbehalt, denjenigen Teil, den er weitergibt, Zession oder zweites Risiko.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(799, 1, 'Wiederanlage', 'wiederanlage', NULL, 'Die Möglichkeit, die <a href="ausschuttung">Ausschüttung</a> im gleichen Fonds wiederanzulegen. Einzelne Fonds gewähren dem Anleger bei der Wiederanlage seiner Jahresausschüttung einen speziellen Wiederanlagerabatt auf dem Ausgabepreis. Bei den meisten Fonds schweizerischen Rechts ist dies der Fall.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(800, 1, 'Thesaurierender Fonds', 'thesaurierender-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, der seine Erträge laufend wiederanlegt und nicht an die <a href="anleger">Anleger</a> ausschüttet. Gegenteil: <a href="ausschuttender-fonds">Ausschüttender Fonds</a>. Vergleiche auch <a href="tranche">Tranche</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(801, 1, 'Thesaurierung', 'thesaurierung', NULL, 'Laufende Wiederanlage der erwirtschafteten Erträge im selben Fonds.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(802, 1, 'Aussenverhältnis', 'aussenverhaltnis', NULL, 'Rechtsbeziehung mehrerer verbundener Personen zu aussen stehenden Dritten; bei der <a href="solidarhaftung">Solidarhaftung</a> der <a href="haftpflicht">Haftpflicht</a>igen zum Geschädigten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(803, 1, 'Relative Value', 'relative-value-1', NULL, 'Ein Finanzinstrument kann relativ zu einem anderen Finanzinstrument falsch bewertet sein. Bei dieser Strategie nutzt der <a href="portfolio-manager-fondsmanager">Portfoliomanager</a> Preisunterschiede bei verschiedenen Finanzinstrumenten und an verschiedenen Börsenplätzen aus (<a href="arbitrage-1">Arbitrage</a>). Allerdings muss der Preisunterschied so gross sein, dass trotz Kosten oder Währungsumrechnungen ein Gewinn anfällt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(804, 1, 'Gemietete Sache', 'gemietete-sache', NULL, 'Siehe <a href="anvertraute-sache-geleaste-oder-gemietete">anvertraute Sache</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(805, 1, 'Verzicht', 'verzicht', NULL, 'Aufgeben eines Rechts oder eines Anspruchs.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(806, 1, 'Verzinsung der Altersguthaben', 'verzinsung-der-altersguthaben', NULL, 'Gemäss Art. 15 Abs. 2 <a href="bvg-1">BVG</a> legt der Bundesrat den Mindestzinssatz auf <a href="altersguthaben">Altersguthaben</a> fest. Er beträgt gegenwärtig (Stand 2005) 2.5 Prozent (Art. 12 BVV 2).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(807, 1, 'Wiederbeschaffungskosten', 'wiederbeschaffungskosten', NULL, '<a href="mittelbarer-schaden">Mittelbarer Schaden</a> (Folgekosten) eines <a href="schaden">Schaden</a>ereignisses in der <a href="hausratversicherung">Hausratversicherung</a>, der dadurch entsteht, dass versicherte Gegenstände wiederbeschafft werden müssen (z. B. Ausweise, Kreditkarten, Abonnemente, Flugtickets). Die Hausratversicherung übernimmt diese Kosten bis zum vorgesehenen Betrag.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(808, 1, 'Ersatzwert', 'ersatzwert', NULL, 'Der Ersatzwert (EW) ist der Wert einer Sache zum Zeitpunkt des Schadenereignisses. Es ist der Wert, den die Versicherung ihrer Entschädigungsberechnung zu Grunde legt. Der Ersatzwert ist je nach Vertragsbedingungen <a href="zeitwert">Zeit-</a> oder <a href="neuwert">Neuwert.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(809, 1, 'Ersatzfahrzeug', 'ersatzfahrzeug', NULL, 'Ein Ersatzfahrzeug ist ein Ersatz für ein <a href="motorfahrzeug">Motorfahrzeug</a> gleicher Kategorie, welches für eine befristete Zeit an dessen Stelle in den Verkehr gesetzt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(810, 1, 'Rechenschaftsbericht', 'rechenschaftsbericht', NULL, 'Siehe <a href="jahresbericht-halbjahresbericht">Jahresbericht</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(811, 1, 'Schadenmeldung (Schadenanzeige)', 'schadenmeldung-schadenanzeige', NULL, 'Mit der Schadenmeldung bringt der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> dem <a href="versicherer">Versicherer</a> ein versichertes Ereignis zur Kenntnis. Der <a href="versicherungsvertrag">Versicherungsvertrag</a> kann vorsehen, dass die Anzeige schriftlich erfolgen muss.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(812, 1, 'Vertreter (Fonds)', 'vertreter-fonds', NULL, 'Natürliche oder juristische Person mit Sitz in der Schweiz, die <a href="anlagefonds">Anlagefonds</a> ausländischen Rechts, die in der Schweiz zum öffentlichen Vertrieb zugelassen sind, in der Schweiz gegenüber <a href="anleger">Anlegern</a> und <a href="aufsichtsbehorde-fonds">Aufsichtsbehörde</a> vertritt. Für die Ausübung einer Vertreterfunktion bedarf es der vorgängigen Bewilligung durch die <a href="#invalid-reference-533">Eidgenössische Bankenkommission</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(813, 1, 'Vertreter, gesetzlicher', 'vertreter-gesetzlicher', NULL, 'Diejenige Person, die für eine handlungsunfähige Person (Unmündige, Bevormundete, Entmündigte) die Handlungen vornehmen kann bzw. muss, die letztere zur Begründung von Rechten und Pflichten benötigt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(814, 1, 'Vorbehalt', 'vorbehalt', NULL, 'Speziell in der Personenversicherung kann ein erhöhtes Risiko wegen vorbestandener Krankheit/Unfälle oder schlechtem Gesundheitszustand im Zeitpunkt des Abschlusses zu einem Vorbehalt führen; als Vorbehalt wird eine Einschränkung im <a href="versicherungsschutz">Versicherungsschutz</a> bezeichnet. Als Alternative zum Vorbehalt kann allenfalls eine Mehrprämie vereinbart werden. Siehe auch <a href="erschwerungen">Erschwerungen</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(815, 1, 'Haftung', 'haftung', NULL, 'Haftung bedeutet, einstehen müssen für den, einem <a href="dritte">Dritten</a> zugefügten, <a href="schaden">Schaden.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(816, 1, 'Haftpflichtige', 'haftpflichtige', NULL, 'Der Haftpflichtige ist derjenige, der für den einem <a href="dritte">Dritten</a> zugefügten <a href="schaden">Schaden</a> einstehen muss.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(817, 1, 'Haftbare', 'haftbare', NULL, 'siehe <a href="haftpflichtige">Haftpflichtige</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(818, 1, 'Ressourceneffizienz', 'ressourceneffizienz', NULL, 'Una produzione efficiente sotto il profilo delle risorse utilizza meno risorse naturali e/o meno inquinanti preservando così l''ambiente e maturando al contempo risparmi finanziari.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(819, 1, 'Altersgutschriften', 'altersgutschriften', NULL, 'Die Altersgutschriften sind jener Teil des Vorsorgeaufwandes, welcher angespart wird. Sparbeiträge leisten sowohl die Arbeitnehmer als auch der Arbeitgeber. Die Altersgutschriften gemäss <a href="bvg-1">BVG</a> werden in Prozenten des versicherten Lohnes jährlich berechnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(820, 1, 'Rendite', 'rendite', NULL, 'Siehe <a href="performance">Performance</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(821, 1, 'Pfandrecht', 'pfandrecht', NULL, 'Ein Pfandrecht ist ein Sicherungsmittel für eine Forderung. [Beispiel: Hypothekarvertrag = Kreditvertrag, der durch eine Liegenschaft (Pfand) gesichert ist.] Wird diese Forderung nicht erfüllt, kann sich der <a href="glaubiger">Gläubiger</a> am Wert des Pfandes schadlos halten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(822, 1, 'Rücknahmepflicht', 'rucknahmepflicht', NULL, 'Siehe <a href="kundigung-fonds">Kündigung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(823, 1, 'Direktes Forderungsrecht', 'direktes-forderungsrecht', NULL, 'Sofern (nach <a href="svg">SVG</a>) eine Versicherungspflicht besteht, kann der <a href="geschadigte">Geschädigte</a> direkt vom <a href="versicherer">Versicherer</a> den vom Schädiger geschuldeten Schadenersatz verlangen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(824, 1, 'Anspruchsberechtigter', 'anspruchsberechtigter', NULL, 'Person, der ein Versicherungsanspruch kraft eigenen oder abgeleiteten Rechtes im Zeitpunkt des Eintritts des versicherten Ereignisses zusteht (<a href="versicherungsnehmer-in">Versicherungsnehmer</a>, mitversicherte Personen in der <a href="haftpflichtversicherung">Haftplichtversicherung</a> und in der kollektiven Personenversicherung, <a href="begunstigte-r">Begünstigte</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(825, 1, 'Risiko', 'risiko', NULL, 'Als Risiko bezeichnet man die Möglichkeit des Eintrittes eines bestimmten Ereignisses, das einen <a href="schaden">Schaden</a>verursacht oder einen Bedarf auslöst. Der Eintritt dieses Ereignisses ist dabei ungewiss und zufällig. Wenn die Wahrscheinlichkeit des Eintreffens des versicherten Ereignisses und die Schadenhöhe von den an der Versicherung beteiligten Personen nicht beeinflusst werden können, sprechen wir von einem objektiven Risiko. Die Gefahren, die sich jedoch aus dem Verhalten der an der Versicherung beteiligten Personen ergeben, sowie besonders auch der Umstand, dass der Eintritt des Versicherungsfalles und die Höhe der Leistung vom Interesse der beteiligten Personen an dieser Leistung beeinflusst werden können, werden als subjektive Risiken bezeichnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(826, 1, 'Risiko (Fonds)', 'risiko-fonds', NULL, 'In der <a href="portfoliotheorie">Portfoliotheorie</a> wird das Risiko einer Anlage mit der Höhe der Ertragsschwankungen (Volatilität) gemessen. Risiko und Ertrag stehen in direktem Zusammenhang: Die Portfoliotheorie nach Markowitz unterstellt, dass ein höherer Ertrag nur mit einem grösseren Risiko erkauft werden kann.<br />\r<br />In der Portfoliotheorie wird das Risiko meist als Standardabweichung der <a href="performance">Performance</a>werte definiert. Die Standardabweichung ist ein statistisches Mass für die Streuung um den Mittelwert der Performancewerte über den Beobachtungszeitraum.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(827, 1, 'Risikofähigkeit', 'risikofahigkeit', NULL, 'Die Fähigkeit eines <a href="anleger">Anlegers</a>, über eine bestimmte Zeit sein Geld (Kapital) zu binden, ohne dabei in finanzielle Schwierigkeiten zu geraten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(828, 1, 'Gefahrengemeinschaft', 'gefahrengemeinschaft', NULL, 'Gruppe von Personen oder Sachen, die einem gleichartigen <a href="risiko">Risiko</a> ausgesetzt sind. Sie schliessen sich zusammen, um einander Deckung zu geben - nach dem Prinzip "einer für alle, alle für einen".', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(829, 1, 'Provisorische Deckung', 'provisorische-deckung', NULL, 'siehe <a href="deckungszusage-provisorische">Deckungszusage, vorläufige</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(830, 1, 'Risikoprüfung', 'risikoprufung', NULL, 'Prüfung der deklarierten Risiken und allfälliger zusätzlicher Erhebungen wie <a href="gesundheitsprufung">medizinische Untersuchungen</a>, um zu entscheiden, ob ein <a href="antrag">Antrag</a> überhaupt, und wenn ja, ob zu normalen oder zu erschwerten Bedingungen, angenommen werden kann.  Die <a href="bindungsfrist">Bindungsfrist</a> des Antragstellers gibt dem <a href="versicherer">Versicherer</a> Zeit für diese Prüfung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(831, 1, 'Gefahrstatsachen', 'gefahrstatsachen', NULL, 'Gefahrstatsachen sind Tatsachen oder Umstände, welche auf die <a href="gefahr">Gefahr</a> einen Einfluss haben und deren Erfassung dem <a href="versicherer">Versicherer</a> erlauben, die Wahrscheinlichkeit, dass sich ein <a href="versicherungsfall">Versicherungsfall</a> ereignet, konkret für den einzelnen <a href="versicherter">Versicherten</a> abzuschätzen. Man spricht von objektiven Gefahrstatsachen, wenn sie den versicherten Gegenstand betreffen (z.B. Hubraum eines Autos); von subjektiven Gefahrstatsachen, wenn sie mit der Person des Versicherten zusammenhängen (Gesundheit des Autohalters und -lenkers); von wandelbaren Gefahrstatsachen, wenn sie sich ändern können; von unwandelbaren Gefahrstatsachen, wenn sie ein für allemal feststehen (Geburtsjahr einer Person).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(832, 1, 'Risikoversicherung', 'risikoversicherung', NULL, 'Bei einer Risikoversicherung wird der <a href="versicherer">Versicherer</a> überhaupt nicht leistungspflichtig, wenn sich das versicherte <a href="risiko">Risiko</a> (Todesfall oder <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a>) während der <a href="vertragsdauer">Vertragsdauer</a> nicht verwirklicht hat. Im Gegensatz zu einer <a href="erlebensfallversicherung">Erlebensfall-Versicherung</a>, ist sie nicht mit einem Sparvorgang verbunden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(833, 1, 'Risikolebensversicherung', 'risikolebensversicherung', NULL, 'Form der <a href="lebensversicherung">Lebensversicherung</a>, die die Gefahren Tod oder <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a> (wegen <a href="invaliditat">Invalidität</a>) versichert. Entsprechend unterscheidet man zwischen <a href="todesfallrisiko-versicherung">Todesfallrisikoversicherung</a> und <a href="erwerbsausfallversicherung">Erwerbsunfähigkeitsversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(834, 1, 'Riskmanagement (RM)', 'riskmanagement-rm', NULL, 'Systematische Erfassung und Bewertung von Risiken sowie Ableitung von führungsmässigen und organisatorischen Konsequenzen. Einzelne Versicherer bieten Riskmanagement als Dienstleistung an. <br />Ein <a href="risiko">Risiko</a> kann man vermeiden (Schadenvermeidung), vermindern (Schadenverminderung), auf Dritte abwälzen (z.B. durch <a href="versicherung">Versicherung</a>) oder selbst tragen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(835, 1, 'Risikoprämie', 'risikopramie', NULL, 'Prämienbestandteil, der das versicherungstechnische Risiko abdeckt.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(836, 1, 'Risikoprämie (BV)', 'risikopramie-bv', NULL, 'Prämienbestandteil, der bei der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> für die Risiken Tod und Invalidität erhoben wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(837, 1, 'Risikoneigung', 'risikoneigung', NULL, 'Die Höhe möglicher Kursschwankungen, die ein <a href="anleger">Anleger</a> einzugehen gewillt ist, um ein bestimmtes Anlageziel zu erreichen. Die Risikoneigung bzw. das Sicherheitsbedürfnis eines Anlegers ist mitentscheidend für die Wahl eines <a href="anlagefonds">Anlagefonds</a>. Je höher die Risikoneigung, desto grösser darf der Aktien- und Fremdwährungsanteil sein.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(838, 1, 'Assistanceversicherung', 'assistanceversicherung', NULL, 'Freiwillige <a href="versicherung">Versicherung</a> für Motorfahrzeughalter, bei der der <a href="versicherer">Versicherer</a> bestimmte Folgekosten bei Unfällen, Pannen oder Schäden am <a href="motorfahrzeug">Motorfahrzeug</a> übernimmt, die eine Weiterfahrt verunmöglichen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(839, 1, 'Beraubung', 'beraubung', NULL, 'Beraubung ist eine Art des <a href="diebstahl">Diebstahls.</a> Dabei wird unter Androhung oder Anwendung von Gewalt gegen den <a href="versicherungsnehmer-in">Versicherungsnehmer</a> oder mit ihm in Hausgemeinschaft lebenden Personen (je nach <a href="avb">AVB</a>) ein Diebstahl begangen. Als Beraubung gilt auch Diebstahl, der bei Unfähigkeit zum Widerstand infolge Tod, Ohnmacht oder Unfall des Opfers begangen wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(840, 1, 'EW', 'ew', NULL, 'Abkürzung für <a href="ersatzwert">"Ersatzwert"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(841, 1, 'Sabotage (Maschinenversicherung)', 'sabotage-maschinenversicherung', NULL, 'Mutwillige Beschädigung oder Zerstörung eines versicherten Objekts (Anlage, Maschine, Installation, Apparat). Wird im Rahmen der <a href="maschinenversicherung">Maschinenversicherung</a> versichert (<a href="all-risk-versicherung">All-Risk-Versicherung</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(842, 1, 'Lohn', 'lohn', NULL, 'Siehe <a href="ahv-lohn">AHV-Lohn</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(843, 1, 'Vertriebsträger (Ausgabestelle)', 'vertriebstrager-ausgabestelle', NULL, 'Partenaire d''un promoteur de fonds de placement, qui vend ses produits à des clients et qui les leur rachète.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(844, 1, 'Beratung und Verkauf', 'beratung-und-verkauf', NULL, 'Teilprozess des <a href="leistungsprozess">Leistungsprozesses</a>, der die Massnahmen des Versicherers umfasst, um seine Versicherungsprodukte abzusetzen. <br />Stellt man den Servicegedanken in den Vordergrund, erfolgt die Kundenbindung über das <a href="sicherungsteam">Sicherungsteam</a>. <br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(845, 1, 'Absatzkanal', 'absatzkanal', NULL, 'Beim direkten Absatzkanal werden Versicherungen vom <a href="versicherer">Versicherer</a> unmittelbar an Endkunden verkauft (z.B. über Call-Center, Internet, Direct-Mail). Beim indirekten Absatzkanal werden Versicherungen über unabhängige Partner (Vermittler, <a href="makler">Makler</a>, <a href="agentur">Agenten</a>) an Endkunden verkauft.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(846, 1, 'Verkaufsgespräch', 'verkaufsgesprach', NULL, 'Kundengespräch zur Befriedigung eines Sicherheitsbedürfnisses bzw. zur Lösung eines Sicherheitsproblems.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(847, 1, 'Verkaufsprospekt', 'verkaufsprospekt', NULL, 'Siehe <a href="prospekt-fondsreglement">Prospekt</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(848, 1, 'Umsatzpolice', 'umsatzpolice', NULL, 'Vertragsart der <a href="transportversicherung">Transportversicherung</a> für Unternehmen, die regelmässig gleichartige Güter auf den gleichen Transportwegen und mit den gleichen Transportitteln transportieren. Dabei muss der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> die einzelnen Warensendungen nicht anmelden. Massgebend für die Berechnung der <a href="pramie">Prämien</a> ist der gesamte Warenwert der Transporte während einer bestimmten Zeitdauer.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(849, 1, 'Vorsorgepolice', 'vorsorgepolice', NULL, 'Die Vorsorgepolice ist ein breitgefächertes Angebot der Lebensversicherung im Rahmen der steuerbegünstigten, gebundenen Vorsorge.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(850, 1, 'Vorsorge', 'vorsorge', NULL, '1. Freie Vorsorge:<br />Unter freier Vorsorge verstehen wir alle Vorkehren, die im Hinblick auf Vorsorge getroffen werden. Dazu gehören in erster Linie Lebensversicherungen, aber auch Kapitalanlagen, Erwerb von Wohneigentum usw.<br />\r<br />2. Gebundene Vorsorge:<br />In der Schweiz steuerpflichtige Erwerbstätige haben die Möglichkeit, mit zusätzlichen steuerlichen Erleichterungen Selbstvorsorge zu betreiben. Die zurückgelegten Mittel müssen jedoch ausschliesslich und unwiderruflich der Vorsorge dienen. Den steuerlichen Vergünstigungen stehen andererseits einschränkende Vorschriften bezüglich Abschluss, Gestaltung und Verfügung gegenüber. Die gebundene Vorsorge kann sowohl über die gebundene Lebensversicherung wie auch über Banksparpläne getroffen werden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(851, 1, 'Sparprämie', 'sparpramie', NULL, 'Teil der gesamten Prämie bei kapitalbildenden Versicherungen, welcher mit den Zinsen den jährlichen Zuwachs des <a href="deckungskapital">Deckungskapitals</a> bestimmt. <br />Bei dieser Art von Versicherungen hat der <a href="versicherer">Versicherer</a> grundsätzlich selbst dann eine Leistung zu erbringen, wenn der <a href="versicherungsvertrag">Versicherungsvertrag</a> vorzeitig aufgelöst wird, d.h. bevor ein <a href="versicherungsfall">Versicherungsfall</a> eingetreten ist und sofern die Mindestprämienzahlungsdauer eingehalten wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(852, 1, 'BB', 'bb', NULL, 'Abkürzung für "<a href="besondere-bedingungen-bb">Besondere Bedingungen</a>" (zu den AVB)', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(853, 1, 'Skala 44', 'skala-44', NULL, 'Bei einer vollständigen Beitragsdauer für die <a href="alters-und-hinterlassenenversicherung">Alters- und Hinterlassenenversicherung</a> (AHV)  wird eine Vollrente ausbezahlt. Die Höhe dieser Vollrente kann der Skala 44 entnommen werden. Bei Beitragslücken kommt eine Teilrente nach den Skalen 1 bis 43 zur Auszahlung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(854, 1, 'Anwendungsbereich', 'anwendungsbereich', NULL, 'Der Anwendungsbereich entspricht den festgelegten Grenzen, innerhalb welcher sich eine <a href="versicherung">Versicherung</a> bewegen darf. Diese Grenzen können sich pro Versicherung beziehen auf:<br />- die minimale und maximale <a href="versicherungssumme">Versicherungssumme</a>,<br />- das tiefste und höchste <a href="eintrittsalter">Eintrittsalter</a>,<br />- das tiefste und höchste <a href="endalter">Endalter</a>,<br />- die geringste und die längste <a href="versicherungsdauer">Versicherungsdauer</a> etc.<br />Unter den Anwendungsbereich fallen auch andere Bestimmungen. Beispielsweise <br />- maximale Höhe von Zusatzversicherungen zur Hauptversicherung,<br />- Möglichkeit, eine zweite, evtl. dritte Person einzuschliessen,<br />- ob der gewünschte <a href="tarif">Tarif</a> als gebundene <a href="vorsorgepolice">Vorsorgepolice</a> zulässig ist <br />etc.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(855, 1, 'Zweite Säule', 'zweite-saule', NULL, '<a href="berufliche-vorsorge">Berufliche Vorsorge</a> als zweiter Pfeiler des schweizerischen <a href="sozialversicherung">Sozialversicherung</a>ssystems. Die zweite Säule umfasst die berufliche Alters-, Hinterlassenen- und Invalidenversicherung (BV, obligatorisch für Arbeitnehmende ab einem bestimmten Einkommen) sowie die <a href="obligatorische-unfallversicherung">obligatorische Unfallversicherung</a> (UV, obligatorisch für alle Arbeitnehmenden).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(856, 1, 'Sektordurchschnitt', 'sektordurchschnitt', NULL, 'Als Sektordurchschnitt bezeichnet man die ungewichtete durchschnittliche <a href="performance">Performance</a> aller Fonds derselben Anlagekategorie. Üblicherweise werden alle Fonds derselben Anlagekategorie betrachtet, die in einem Land zum Vertrieb zugelassen sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(857, 1, 'Branchenfonds (Sektorenfonds)', 'branchenfonds-sektorenfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die ihr Vermögen ausschliesslich in Wertpapieren eines bestimmten Wirtschaftssektors anlegen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(858, 1, 'Existenzsicherung', 'existenzsicherung', NULL, 'Innerhalb des <a href="sozialversicherung">Sozialversicherung</a>ssystems übergeordnetes Ziel der staatlichen Vorsorge bzw. der <a href="erste-saule">Ersten Säule</a>: Sicherung eines menschenwürdigen Lebensstandards.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(859, 1, 'Effekten', 'effekten', NULL, 'Dies sind Wertschriften, die in einer grösseren Anzahl inhaltlich gleicher Stücke in Verkehr gesetzt werden. Die einzelnen Papiere unterscheiden sich lediglich durch eine unterschiedliche Laufnummer. Somit sind die einzelnen Papiere untereinander austauschbar (fungibel), da sie den gleichen Wert verkörpern.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(860, 1, 'Effektenfonds', 'effektenfonds', NULL, '<a href="anlagefonds">Anlagefonds</a> mit Anlagen in Wertpapieren und Wertrechten, die an einer Börse oder an einem anderen geregelten, dem Publikum offen stehenden Markt gehandelt werden. Vergleiche <a href="immobilienfonds-liegenschaftsfonds">Immobilienfonds</a> und <a href="ubrige-fonds">übrige Fonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(861, 1, 'Securities Lending', 'securities-lending-1', NULL, 'Das Ausleihen von Wertschriften gegen Entgelt und Stellung von Sicherheit. Seit 1992 ist Securities Lending auch für <a href="anlagefonds">Anlagefonds</a> zulässig.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(862, 1, 'Valorennummer', 'valorennummer', NULL, 'In der Schweiz verwendete Kennnummer von Wertpapieren, die deren Handel und Transfer erleichtert. Auf internationaler Ebene <a href="isin-nummer">ISIN-Nummer</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(863, 1, 'Titelankaufsspesen', 'titelankaufsspesen', NULL, 'Bei einigen Fonds werden auf Grund der Besonderheiten ihrer Konstruktion zusätzlich zur <a href="ausgabekommission">Ausgabekommission</a> so genannte Titelankaufspesen erhoben. Diese Kommission fliesst dem Fonds zu und dient dazu, die beim Ankauf von Wertschriften entstehenden Kosten abzudecken.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(864, 1, 'Sicherungsfonds', 'sicherungsfonds', NULL, 'Die Lebensversicherungseinrichtungen sind verpflichtet bei der Betriebsaufnahme eine Anfangsgarantie von Fr. 500''000.- zu leisten, welche in einem Sicherungsfonds geäufnet werden muss. Der Sicherungsfonds soll die Ansprüche der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> aus den abgeschlossenen Lebensversicherungen decken. Der Sollbetrag entspricht den technischen Rückstellungen einer Lebensversicherungsunternehmung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(865, 1, 'Titelrisiko', 'titelrisiko', NULL, 'Risque de fluctuation du cours d''un titre.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(866, 1, 'Arrest', 'arrest', NULL, 'Confiscation officielle de biens du débiteur dans le cadre d''une procédure particulière de la loi sur la poursuite pour dettes et la faillite.', '2016-08-29 10:21:09', '2016-08-29 10:22:13'),
(867, 1, 'Halbjahresbericht', 'halbjahresbericht', NULL, 'Siehe <a href="jahresbericht-halbjahresbericht">Jahresbericht</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(868, 1, 'Halbzwingende Bestimmung', 'halbzwingende-bestimmung', NULL, 'Gesetzliche Bestimmung, von welcher nicht zu Ungunsten des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> abgewichen werden darf.', '2016-08-29 10:21:09', '2016-08-29 10:22:18');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(869, 1, 'Teilbarkeit der Prämie', 'teilbarkeit-der-pramie', NULL, 'Der neue Grundsatz des revidierten <a href="versicherungsvertragsgesetz">Versicherungsvertragsgesetzes</a> (in Kraft Tretung voraussichtlich am 01.01.2006), wonach der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> die <a href="pramie">Prämie</a> nur für die Zeitspanne bis zur vorzeitigen Auflösung oder Beendigung des Vertrages zu bezahlen hat (revVVG 24 g).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(870, 1, 'Leistungskatalog', 'leistungskatalog', NULL, 'Der Leistungskatalog enthält alle vorgeschriebenen Leistungen der <a href="obligatorische-krankenpflegeversicherung">obligatorischen Krankenpflegeversicherung</a> und stellt eine einheitliche, qualitativ hoch stehende medizinische Grundversicherung der Schweizer Bevölkerung sicher.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(871, 1, 'SFA', 'sfa', NULL, 'Siehe <a href="swiss-fund-association-1">Swiss Fund Association</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(872, 1, 'Aktienindex', 'aktienindex', NULL, 'Kennziffer zur Beobachtung der Kursentwicklung der Aktien der Börse. Die tägliche Veränderung der Aktienkurse und somit der Kennziffer ermöglicht es, die Tendenz an der Börse zu verfolgen. In der Schweiz ist der wichtigste Aktienindex der <a href="swiss-performance-index">Swiss Performance Index</a> (SPI). Der wohl berühmteste ausländische Aktienindex ist der <a href="dow-jones-index-1">Dow Jones Index</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(873, 1, 'Sharpe Ratio', 'sharpe-ratio-1', NULL, 'Die Sharp Ratio drückt aus, mit einem wie viel höheren (bzw. niedrigerem) Ertrag ein Investor im Vergleich zum risikofreien Zinssatz (z.B. Sparzins) pro Risikoeinheit (<a href="risiko-fonds">Volatilität</a>) rechnen kann. Der risikofreie Zinssatz ändert von Währung zu Währung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(874, 1, 'Leerverkauf', 'leerverkauf', NULL, 'Verkauf von Finanzinstrumenten auf Termin, die der Verkäufer noch nicht besitzt, wobei es sein Anlageziel ist, sich bis zur Lieferung billig mit den fehlenden Titeln eindecken zu können. Das Risiko bei Leerverkäufen liegt in einer möglichen Kurssteigerung der Basistitel.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(875, 1, 'Short-term Funds', 'short-term-funds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in <a href="obligationen">Obligationen</a> mit einer (Rest-)<a href="laufzeit">Laufzeit</a> von 1 bis 3 Jahren investieren.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(876, 1, 'Kurzarbeitsentschädigung', 'kurzarbeitsentschadigung', NULL, 'Leistung der <a href="arbeitslosen-und-insolvenzversicherung">Arbeitslosenversicherung</a> (ALV) bei Reduktion des Erwerbseinkommens wegen Kurzarbeit, die vom Arbeitgeber wegen schlechten Arbeitsgangs angeordnet werden muss.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(877, 1, 'Sicaf', 'sicaf', NULL, 'Société d''investissement à capital fixe. <a href="anlagefonds">Anlagefonds</a> in Form einer Aktiengesellschaft mit fixem Kapital. Vergleiche <a href="gesellschaftstypus">Gesellschaftstypus</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(878, 1, 'Sicav', 'sicav', NULL, 'Société d''investissement à capital variable. <a href="anlagefonds">Anlagefonds</a> in Form einer Aktiengesellschaft mit variablem Kapital. Die <a href="anteil">Anteile</a> werden in Form von <a href="aktie">Aktien</a> ausgestellt. Vergleiche <a href="gesellschaftstypus">Gesellschaftstypus</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(879, 1, 'Krankheit', 'krankheit', NULL, 'Krankheit ist eine medizinisch wahrnehmbare, vom Willen des Versicherten unabhängige Störung der normalen Körperfunktion durch krankhafte Vorgänge.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(880, 1, 'Sichtguthaben', 'sichtguthaben', NULL, 'Bankguthaben, über die sofort, d.h. ohne vorherige Kündigung verfügt werden kann.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(881, 1, 'Milde Kausalhaftung', 'milde-kausalhaftung', NULL, 'Siehe <a href="kausalhaftung">Kausalhaftung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(882, 1, 'Einfache Kausalhaftung', 'einfache-kausalhaftung', NULL, 'Siehe <a href="kausalhaftung">Kausalhaftung.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(883, 1, 'Einmaleinlage / Einmalprämie', 'einmaleinlage-einmalpramie', NULL, '<a href="pramie">Prämie</a> (Leben), die einmalig zur Finanzierung der Versicherung geleistet wird, und zwar zu Beginn der Versicherung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(884, 1, 'SIFS', 'sifs', NULL, 'Abkürzung für <a href="sicherheitsinstitut">Sicherheitsinstitut</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(885, 1, 'Small Caps', 'small-caps-1', NULL, '<a href="aktie">Aktien</a> von Unternehmen mit einer Börsenkapitalisierung von in der Regel weniger als 500 Mio. CHF. Auch Nebenwerte genannt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(886, 1, 'Nebenwerte', 'nebenwerte', NULL, 'Siehe <a href="small-caps-1">Small Caps</a> und <a href="mid-caps-1">Mid Caps</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(887, 1, 'Nebenwertefonds', 'nebenwertefonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in <a href="aktie">Aktien</a> von Unternehmen mit relativ niedriger <a href="borsenkapitalisierung">Börsenkapitalisierung</a> investieren. Vergleiche <a href="small-caps-1">Small Caps</a> und <a href="mid-caps-1">Mid Caps</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(888, 1, 'Schneedruck', 'schneedruck', NULL, 'Überlast durch Schnee mit schädigender Wirkung auf  Dach oder Wände.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(889, 1, 'Schneerutsch', 'schneerutsch', NULL, 'Durch Dachlawinen oder von Bäumen fallender Schnee usw. verursachter Schaden, z.B. bei der <a href="motorfahrzeug-kaskoversicherung">Motorfahrzeugversicherung</a> am <a href="motorfahrzeug">Motorfahrzeug</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(890, 1, 'Soziale Krankenversicherung', 'soziale-krankenversicherung', NULL, '<a href="sozialversicherung">Sozialversicherung</a>, die im Krankenversicherungsgesetz (KVG) geregelt wird. Sie umfasst die <a href="obligatorische-krankenpflegeversicherung">obligatorische Krankenpflegeversicherung</a> und die freiwillige <a href="krankentaggeldversicherung-nach-kvg">Krankentaggeldversicherung</a> (nach KVG).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(891, 1, 'Sozialversicherung', 'sozialversicherung', NULL, 'Obligatorische Personenversicherungen, deren Versicherungsleistungen gesetzlich festgelegt sind. Ziel der Sozialversicherungen ist ein Basisschutz für die finanziellen Folgen der Gefahren Alter, Tod, Krankheit, Unfall, Invalidität, Arbeitslosigkeit, Dienstpflicht und Mutterschaft. Die soziale Ausgestaltung zeigt sich insbesondere im Solidaritätsgedanken und im gesetzlichen Anspruch. Versichert ist die ganze Wohnbevölkerung (z.B. AHV/IV) oder ein eingeschränkter Personenkreis (z.B. UVG, EO).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(892, 1, 'Socially Responsible Investments', 'socially-responsible-investments-1', NULL, 'Unter Socially Responsible Investments (SRI) werden Anlagen zusammengefasst, die neben traditionellen finanziellen Faktoren auch soziale und ökologische Kriterien berücksichtigen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(893, 1, 'BVB', 'bvb', NULL, 'Besondere Versicherungsbedingungen, die nicht für alle Kunden gelten und deshalb nur bei Bedarf beigefügt werden. Siehe <a href="avb">AVB</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(894, 1, 'Besondere Bedingungen (BB)', 'besondere-bedingungen-bb', NULL, 'Zusätzlich zu den <a href="allgemeine-versicherungsbedingungen">Allgemeinen Versicherungsbedingungen</a> (AVB) werden in der <a href="police">Police</a> allfällige individuelle Vereinbarungen als Besondere Bedingungen (BB) aufgeführt, die z.B. Zusatzversicherungen oder Ein- und Ausschlüsse besonderer <a href="risiko">Risiken</a> betreffen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(895, 1, 'Sondermassnahmen', 'sondermassnahmen', NULL, 'Jede <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> hat grundsätzlich eine bestimmte jährliche Summe (1 Prozent der koordinierten Löhne aller Versicherten, die Sparbeiträge zu entrichten haben) bereitzustellen. Dies sind die Mittel für die Sondermassnahmen, die insbesondere für die Verbesserung der Leistungen der <a href="eintrittsgeneration">Eintrittsgeneration</a> sowie für die Anpassung der laufenden <a href="altersrenten">Altersrenten</a> an die Teuerung zu verwenden sind.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(896, 1, 'Spezialitätenfonds', 'spezialitatenfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die sich durch einen besonderen Anlageansatz oder eine spezielle Ausrichtung auf bestimmte Länder, Branchen oder auf bestimmte <a href="anlageinstrumente">Anlageinstrumente</a> von herkömmlichen Anlagefonds unterscheiden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(897, 1, 'SPI', 'spi', NULL, 'Abkürzung für<a href="swiss-performance-index">"Swiss Performance Index"</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(898, 1, 'Splitting (Einkommenssplitting)', 'splitting-einkommenssplitting', NULL, 'Besonderheit bei der Berechnung der <a href="altersrenten">Altersrenten</a> von Ehepaaren. Zur Ermittlung des durchschnittlichen Erwerbseinkommens wird jedem Ehepartner für die gemeinsamen Ehejahre das halbe Jahreseinkommen des anderen Ehepartners gutgeschrieben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(899, 1, 'Splitting-Vorsorgelösung', 'splitting-vorsorgelosung', NULL, 'Organisationsform der überobligatorischen Versicherung in der <a href="berufliche-vorsorge">beruflichen Vorsorge</a>, bei der die BVG-Minimalleistungen und die <a href="uberobligatorium-bv">überobligatorischen</a> Leistungen getrennt versichert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(900, 1, 'Ecart', 'ecart', NULL, 'Siehe <a href="agio-1">Agio</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(901, 1, 'Reglement zur Personalvorsorge', 'reglement-zur-personalvorsorge', NULL, 'Gesetzlich vorgeschriebenes Regelwerk einer <a href="personalvorsorgeeinrichtung">Personalvorsorgeeinrichtung</a>, das Einzelheiten zur <a href="berufliche-vorsorge">beruflichen Vorsorge</a> seiner Mitarbeiter festlegt (z. B. die Höhe der Versicherungsbeiträge und -leistungen).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(902, 1, 'Personalvorsorgeeinrichtung', 'personalvorsorgeeinrichtung', NULL, 'Organisation des Arbeitgebers, die sich um die <a href="berufliche-vorsorge">berufliche Vorsorge</a> seiner Arbeitnehmer kümmert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(903, 1, 'Stempelabgabe (Umsatzabgabe)', 'stempelabgabe-umsatzabgabe', NULL, '<a href="anlagefonds">Anlagefonds</a> mit Domizil im Ausland unterliegen in der Schweiz beim Bezug aus <a href="emission">Emissionen</a> der Stempelabgabe. Die <a href="rucknahmen">Rücknahme</a> ist von der Stempelabgabe befreit. Anlagefonds nach schweizerischem Recht sind bei der Ausgabe/Rücknahme von <a href="anteil">Anteilen</a> an den Fonds von der Stempelabgabe befreit.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(904, 1, 'Kurs', 'kurs', NULL, 'Preis für <a href="anteil">Anteile</a> von <a href="anlagefonds">Anlagefonds</a>, die an der Börse kotiert sind oder ausserbörslich gehandelt werden (Immobilienfonds). Der Kurs bildet sich durch Angebot und Nachfrage auf dem Markt, während der <a href="ausgabepreis">Ausgabe-</a> und <a href="rucknahmepreis">Rücknahmepreis</a> der Anteile auf Grund des <a href="nettoinventarwert">Nettoinventarwerts</a> durch die <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> festgelegt wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(905, 1, 'Sturm', 'sturm', NULL, 'Sturm ist in der <a href="hausratversicherung">Hausratversicherung</a> Wind von mind. 75 km/h, der in der Umgebung der versicherten Sachen Bäume entwurzelt oder Gebäude abdeckt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(906, 1, 'Straight Bonds', 'straight-bonds-1', NULL, '<a href="obligationen">Obligationen</a> mit festem Zinssatz und festem Rückzahlungstermin.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(907, 1, 'Strategy Funds', 'strategy-funds-1', NULL, 'Siehe <a href="anlagestrategiefonds">Anlagestrategiefonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(908, 1, 'Strukturvertrieb', 'strukturvertrieb', NULL, 'Unabhängige Beratungsorganisation im Bereich von Finanzdienstleistungen und Bestandteil des indirekten <a href="absatzkanal">Absatzkanals</a>. Berät Kunden in Vermögens- und Finanzfragen und vermittelt Geschäfte an <a href="versicherer">Versicherer</a> oder Banken.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(909, 1, 'Subfonds', 'subfonds', NULL, 'Unterfonds oder Teilfonds bei nach dem «<a href="umbrella-fonds">Umbrella</a>»-Prinzip gestalteten Fonds. Bei <a href="anlagefonds">Anlagefonds</a> mit verschiedenen Subfonds ist der <a href="anleger">Anleger</a> nur am Vermögen und am Ertrag jenes Subfonds berechtigt, an dem er beteiligt ist. Subfonds werden häufig auch als Segmente oder Kompartimente bezeichnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(910, 1, 'Subrogation', 'subrogation-1', NULL, 'Auf den <a href="versicherer">Versicherer</a> gehen, soweit er Entschädigung geleistet hat, diejenigen <a href="anspruch">Ansprüche</a> über, welche dem <a href="versicherter">Versicherten</a> gegenüber dem Schadenverursacher aus unerlaubter Handlung zustehen, sofern letzterer den <a href="schaden">Schaden</a> schuldhaft verursacht hat (<a href="vvg-1">VVG</a> 72).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(911, 1, 'Zeichnung', 'zeichnung', NULL, 'Im Fondsgeschäft wird unter Zeichnung der Erwerb von Fondsanteilen verstanden. Gegenteil: <a href="rucknahmen">Rücknahme</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(912, 1, 'Folgeprämien', 'folgepramien', NULL, 'Alle der <a href="eintrittspramie">Eintrittsprämie</a> folgenden periodischen Prämien.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(913, 1, 'Ersatzkasse (BU)', 'ersatzkasse-bu', NULL, 'Auffangbecken der Privatversicherer, die eine berufliche <a href="obligatorische-unfallversicherung">Unfallversicherung</a> anbieten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(914, 1, 'Aufsichtsbehörde', 'aufsichtsbehorde', NULL, 'Jeder Kanton bezeichnet eine Behörde, welche alle Personalvorsorgeeinrichtungen mit Sitz in seinem Gebiet beaufsichtigt. Sie wacht darüber, dass die gesetzlichen Vorschriften eingehalten werden und führt das Register für die berufliche <a href="vorsorge">Vorsorge.</a> Die Aufsichtsbehörden unterstehen der Oberaufsicht des Bundesrates. Diese wird durch  das <a href="#invalid-reference-69">Bundesamt für Privatversicherungen</a> oder das <a href="bundesamt-fur-sozialversicherung">Bundesamt für Sozialversicherung</a>wahrgenommen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(915, 1, 'Aufsichtsbehörde (Fonds)', 'aufsichtsbehorde-fonds', NULL, 'Staatliche Aufsichtsbehörde, welche die Tätigkeit von <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitungen</a> überwacht. In der Schweiz fungiert die <a href="#invalid-reference-533">Eidgenössische Bankenkommission (EBK)</a> als Aufsichtsbehörde über das Anlagefondsgeschäft. In Luxemburg ist die <a href="commission-de-surveillance-du-sect-financier">Commission de Surveillance du Secteur Financier (CSSF)</a>, in Deutschland das Bundesaufsichtsamt für das Kreditwesen (BAKred), in Österreich das Bundesministerium für Finanzen und in Liechtenstein das Amt für Finanzdienstleistungen für die Aufsicht zuständig.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(916, 1, 'Aufsichtspflicht', 'aufsichtspflicht', NULL, 'Begründung für die verschuldensunabhängige Haftung bei einigen  <a href="milde-kausalhaftung">milden</a> <a href="kausalhaftung">Kausalhaftungen</a>. Dem Kausal<a href="haftpflicht">haftpflicht</a>igen wird eine Aufsichtspflicht auferlegt. Führt  ihre Verletzung zu einem <a href="schaden">Schaden</a> bei einem Dritten, löst das die Haftung aus. Das individuelle Verschulden spielt keine Rolle.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(917, 1, 'Ergänzungsleistungen (EL)', 'erganzungsleistungen-el', NULL, 'Leistungen der staatlichen <a href="vorsorge">Vorsorge</a> (<a href="erste-saule">Erste Säule</a>), die zum Tragen kommen, wenn <a href="alters-und-hinterlassenenversicherung">AHV</a>- oder IV-Rentenbezüger kein Existenz sicherndes Einkommen haben. Sie sind eine Ergänzung bei ungenügendem Einkommensersatz und können nur von bedürftigen Personen bezogen werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(918, 1, 'Krankenzusatzversicherung', 'krankenzusatzversicherung', NULL, 'Freiwillige private <a href="personenversicherung">Personenversicherung</a>, die den Basisschutz der <a href="soziale-krankenversicherung">sozialen Krankenversicherung</a> erweitert und im <a href="versicherungsvertragsgesetz">Versicherungsvertragsgesetz</a> (VVG) geregelt wird. Sie umfasst Ergänzungen zur <a href="obligatorische-krankenpflegeversicherung">obligatorischen Krankenpflegeversicherung</a> und eine <a href="krankentaggeldversicherung-nach-vvg">Krankentaggeldversicherung nach VVG</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(919, 1, 'Unterstützungsprozess', 'unterstutzungsprozess', NULL, 'Zu den Unterstützungsprozessen gehören die <a href="geschaftsprozess">Geschäftsprozesse</a>, die dazu beitragen, dass die Leistungsprozesse eines Unternehmens optimal erbracht werden können. <br />Die wichtigsten Bereiche sind das Management, Rechnungswesen / Controlling, IT-Services, Personal / Ausbildung.<br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(920, 1, 'Überschussbeteiligung', 'uberschussbeteiligung', NULL, 'Leistungen und <a href="pramie">Prämien</a> eines Lebensversicherungs-Vertrages sind während der ganzen <a href="versicherungsdauer">Versicherungsdauer</a> garantiert. Daher müssen die Prämien bezüglich Sterblichkeit, Erwerbsunfähigkeitsrisiko, Zinsverlauf und <a href="kosten">Kosten</a> vorsichtig berechnet werden. In der Regel verläuft aber die tatsächliche Entwicklung des <a href="risiko">Risikos</a> und des Zinsertrages besser als angenommen. Die Lebensversicherungs-Gesellschaften gewähren daher zum Ausgleich eine Überschussbeteiligung, indem die gewissermassen zuviel verlangte Prämie dem Kunden wieder zurückerstattet wird. Der Anspruch auf eine Überschussbeteiligung beginnt nach einem bis drei Versicherungsjahren. Die Beteiligung des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> an den Überschüssen der Gesellschaft kann aufgrund verschiedener Systeme erfolgen. Die Überschussanteile können wie folgt verwendet werden:<br />-Verzinsliche Ansammlung<br />-Erhöhung der Versicherungsleistung<br />-Prämienermässigung', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(921, 1, 'Summenexedenten-Rückversicherung', 'summenexedenten-ruckversicherung', NULL, 'Die Summenexzedenten-Rückversicherung ist ein Vertragstyp der proportionalen <a href="ruckversicherung">Rückversicherung</a>. <br />Der Rückversicherer beteiligt sich an den Haftungen, Prämien und Schäden der Versicherungen eines Versicherungsbestands, deren Versicherungssumme einen bestimmten Wert (das sogenannte Maximum) übersteigt. <br />Der Umfang der Beteiligung ist bei jeder Versicherung separat zu berechnen. Er bemisst sich aus dem Verhältnis zwischen dem Maximum und der <a href="versicherungssumme">Versicherungssumme</a>. <br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(922, 1, 'Rückkauf', 'ruckkauf', NULL, 'Der Rückkauf ist eine besondere Art, den <a href="versicherungsvertrag">Versicherungsvertrag</a> vor Ablauf der <a href="vertragsdauer">Vertragsdauer</a> vorzeitig aufzulösen. Das Rückkaufsbegehren des <a href="versicherungsnehmer-in">Versicherungsnehmers</a> bewirkt die sofortige Auflösung des Vertrages. Der <a href="versicherer">Versicherer</a> hat den <a href="ruckkaufswert">Rückkaufswert</a> zu ermitteln und zu entrichten. Laut Gesetz muss der Versicherer diejenigen Lebensversicherungen zurückkaufen, bei denen der Eintritt des versicherten Ereignisses gewiss ist und die Prämien wenigstens für 3 Jahre bezahlt worden sind. Die <a href="allgemeine-versicherungsbedingungen">Allgemeinen Versicherungsbedingungen</a> sind bei praktisch allen Gesellschaften kundenfreundlicher. Der Rückkauf ist meistens schon nach 1 oder 2 Jahren möglich.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(923, 1, 'Rückkaufswert', 'ruckkaufswert', NULL, 'Rückkaufswert weisen nur so genannte gemischte Versicherungen auf, die aus einer Risiko- und einer Sparkomponente bestehen und daher ein <a href="deckungskapital">Deckungskapital</a> bilden. Das Gesetz spricht von Versicherungen mit gewissem Versicherungsfall (<a href="vvg-1">VVG</a> 90.2). Von jeder für eine gemischte Versicherung bezahlten <a href="pramie">Prämie</a> legt die Gesellschaft einen Teil als Sparprämie zinstragend an. Einen anderen Teil der Prämie benötigt sie für die Risikodeckung, einen weiteren Teil für die laufenden Kosten. Die beim Vertragsabschluss entstehenden, einmaligen Abschlusskosten werden auf alle folgenden Prämien verteilt; sie sind daher erst am Ende der Prämienzahlungsdauer getilgt. Die <a href="allgemeine-versicherungsbedingungen">allgemeinen Versicherungsbedingungen</a> enthalten die Regeln, nach denen der Rückkaufswert auf dem Deckungskapital berechnet wird. Der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> hat das Recht, im Zweifelsfall die Richtigkeit der Berechnung durch das <a href="#invalid-reference-69">Bundesamt für Privatversicherungen</a> unentgeltlich überprüfen zu lassen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(924, 1, 'Todesfall-Zeitrente', 'todesfall-zeitrente', NULL, 'Die Todesfall-Zeitrente erbringt anstelle eines Kapitals (<a href="todesfallrisiko-versicherung">Todesfallrisiko-Versicherung</a>) bei Tod der <a href="versicherte-person">versicherten Person</a> vor Ablauf der vereinbarten Frist jährlich wiederkehrende <a href="rente">Renten</a> und zwar bis zum vereinbarten <a href="endtermin">Endtermin</a>. Der <a href="begunstigte-r">Begünstigte</a> erhält somit ein regelmässiges Ersatzeinkommen bis zu einem bestimmten Zeitpunkt. Stirbt er ebenfalls vor dem Endtermin der Versicherung, geht sein Anspruch an den Nächstbegünstigten (<a href="begunstigung">Begünstigung</a>) über, allenfalls an seine Erben. Die Lebensversicherungsgesellschaft muss also in jedem Fall die durch den Tod der versicherten Person fällig werdenden <a href="rente">Renten</a>bezahlen. Daher ist der "Wert" genau berechenbar und der Begünstigte hat das Recht, sofern der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> nichts anderes bestimmt hat, sich den diskontierten Wert aller zukünftigen <a href="zeitrenten">Zeitrenten</a>als Kapital auszahlen zu lassen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(925, 1, 'Deckungsunterbruch', 'deckungsunterbruch', NULL, 'Bezahlt ein Kunde seine <a href="pramie">Prämie</a> auch nach Mahnung nicht, ist sein <a href="versicherungsschutz">Versicherungsschutz</a> unterbrochen. Dieser bleibt so lange ausgesetzt, bis die Prämie bezahlt ist. Ereignet sich während des Deckungsunterbruchs ein Schadenfall, so wird dieser nicht vergütet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(926, 1, 'Stundungsfrist', 'stundungsfrist', NULL, 'Zeitraum, der zwischen <a href="glaubiger">Gläubiger</a> und Schuldner vereinbart wird und den Zweck hat, die Fälligkeit einer Forderung um eine gewisse Dauer hinauszuschieben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(927, 1, 'SUVA', 'suva-1', NULL, 'Abk. für: Schweizerische Unfallversicherungsanstalt Anbieter der <a href="obligatorische-unfallversicherung">obligatorischen Unfallversicherung</a>. Bestimmte Unternehmen (z.B. Baugewerbe, Industriebetriebe) müssen die Unfallversicherung ihrer Mitarbeiter bei der SUVA abschliessen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(928, 1, 'SVG', 'svg', NULL, 'Abkürzung für "Bundesgesetz über den Strassenverkehr" vom 12. Dezember 1958.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(929, 1, 'SVV', 'svv', NULL, 'Abkürzung für "Schweizerischer Versicherungsverband"', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(930, 1, 'Swiss Fund Association', 'swiss-fund-association-1', NULL, 'Associazione che rappresenta gli interessi degli operatori nel settore dei fondi attivi in Svizzera. Istituita nel 1992 a Basilea dall''Associazione Svizzera dei Banchieri (ex Associazione svizzera fondi di investimento).', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(931, 1, 'Swiss Performance Index', 'swiss-performance-index', NULL, 'Der Swiss-Performance-Index (SPI) wurde nach der Sitzung vom 1. Juni 1987 mit einem Stand von 1000 Punkten eingeführt. Der SPI umfasst alle an den Börsen von Zürich, Genf und Basel gehandelten Aktien, Partizipationsscheine und Genussscheine sowie die Titel der Nebenbörsen von insgesamt 269 Gesellschaften (Ende 1991). Diese mehr als 450 Titel sind nach ihrer Börsenkapitalisierung gewichtet. Im Gegensatz zu den meisten anderen Aktienindizes werden hier die Dividendenabgänge dem Index zugeschlagen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(932, 1, 'Erwerbsersatzordnung', 'erwerbsersatzordnung', NULL, 'Die Erwerbsersatzordnung (EO, seit 1. Januar 1953 in Kraft) regelt die Erwerbsausfallentschädigung an Wehr- und Zivilschutzpflichtige. <br />Anspruch auf Entschädigung haben insbesondere Personen, die in der schweizerischen Armee oder im Zivilschutz Dienst leisten. Die EO gewährt folgende Entschädigungen: Haushaltungsentschädigung, Entschädigung für Alleinstehende, Kinderzulagen, Unterstützungszulagen und Betriebszulagen.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(933, 1, 'Tarif', 'tarif', NULL, 'Als Tarif bezeichnet der Versicherungsfachmann die einzelnen Versicherungsarten. Tarif heisst aber auch die Zusammenfassung der <a href="pramie">Prämien</a> (Leben), gegliedert nach <a href="eintrittsalter">Eintrittsalter</a> und <a href="vertragsdauer">Vertragsdauer</a> oder Endalter für die einzelnen Versicherungsarten. Sie werden ihrerseits im Tarifbuch zusammengefasst.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(934, 1, 'Tarifalter', 'tarifalter', NULL, 'Das Tarifalter ist das versicherungstechnische <a href="eintrittsalter">Eintrittsalter</a> des <a href="versicherter">Versicherten</a> im Zeitpunkt des Vertragsabschlusses.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(935, 1, 'Steuervorteil', 'steuervorteil', NULL, 'Siehe <a href="steuerabzug">Steuerabzug.</a><br />Siehe <a href="steuerprivileg">Steuerprivileg.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(936, 1, 'Quellensteuer', 'quellensteuer', NULL, 'Steuer, die das Steuerobjekt unmittelbar beim Entstehen (an der Quelle) erfasst, in der Schweiz zum Beispiel die <a href="verrechnungssteuer-fonds">Verrechnungssteuer</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(937, 1, 'Steuerprivileg', 'steuerprivileg', NULL, 'Steuervorteil, der z.B. aus dem Abschluss einer Lebensversicherung resultiert. Der Staat gewährt für Vorsorgelösungen, die bestimmten gesetzlichen Bestimmungen entsprechen, steuerliche Vorzüge. Man unterscheidet punkto Privilegierung die Säulen 3a (<a href="versicherung-gebundene">Versicherung, gebundene</a>) und 3b (<a href="versicherung-freie">Versicherung, freie</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(938, 1, 'Steuervergünstigung', 'steuervergunstigung', NULL, 'Siehe <a href="steuerabzug">Steuerabzug.</a><br />Siehe <a href="steuerprivileg">Steuerprivileg.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(939, 1, 'Steuerabzug', 'steuerabzug', NULL, 'Die <a href="pramie">Prämien</a> für eine Lebensversicherung der Säule 3b (<a href="versicherung-freie">Versicherung, freie</a>) können zusammen mit den Prämien für andere Versicherungen (z.B. für die Krankenkasse) bis zu einem gewissen Betrag vom Einkommen abgezogen werden (Pauschalabzug). Die Prämien für eine gebundene <a href="vorsorgepolice">Vorsorge-Police</a>können vollständig vom Einkommen abgezogen werden, sofern die maximalen Abzugslimiten nicht überschritten werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(940, 1, 'Technische Versicherungen', 'technische-versicherungen', NULL, 'In der technischen Versicherung geht es um technische Anlagen. Darunter versteht man Maschinen, Anlagen und Apparate aller Art. Der Deckungsbereich ist weit gefasst. Soweit die <a href="avb">AVB</a> keine ausdrücklichen Ausschlüsse vorsehen, sind alle Beschädigungen und Zerstörungen gedeckt, die plötzlich und unvorhergesehen eintreten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(941, 1, 'Technischer Zins', 'technischer-zins', NULL, 'Der technische Zins ist der garantierte Mindestzinssatz während der ganzen <a href="vertragsdauer">Vertragsdauer</a>, mit dem der Sparteil der <a href="pramie">Prämie</a> bei vermögensbildenden Versicherungen aufgezinst wird. Wird mehr als der technische Zins erwirtschaftet, so wird dies in Form einer nicht garantierten <a href="uberschussbeteiligung">Überschussbeteiligung</a> dem <a href="versicherungsnehmer-in">Versicherungsnehmer</a> gutgeschrieben.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(942, 1, 'Technischer Zinssatz', 'technischer-zinssatz', NULL, 'Zinssatz, der den versicherungsmathematischen Berechnungen zugrunde liegt. Er muss so angelegt werden, dass der effektiv erzielbare <a href="zins">Zins</a> möglichst nie kleiner ist.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(943, 1, 'Telemed-Modell', 'telemed-modell-1', NULL, 'Kostensparendes <a href="versicherungsmodell">Versicherungsmodell</a>, bei dem sich der Versicherte verpflichtet, im Krankheitsfall immer zuerst eine Hotline seiner Krankenkasse anzurufen, bevor er einen Arzt aufsucht. Am Telefon wird der Versicherte von Fachpersonen so beraten, dass ggf. unnötige Arztbesuche vermieden werden können.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(944, 1, 'Telefonverkauf', 'telefonverkauf', NULL, 'Bestandteil des direkten <a href="absatzkanal">Absatzkanals</a>. Beinhaltet die Kontaktaufnahme mit Kunden und den Verkauf von <a href="versicherung">Versicherungen</a> per Telefon.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(945, 1, 'Lebenshaltungskosten', 'lebenshaltungskosten', NULL, '<a href="mittelbarer-schaden">Mittelbarer Schaden</a> (Folgekosten) eines <a href="schaden">Schaden</a>ereignisses in der <a href="hausratversicherung">Hausratversicherung</a>, der dadurch entsteht, dass der Versicherte das eigene Zuhause nicht nutzen kann (z. B. nach einem Feuer, Wassereinbruch oder Sturm). Die Hausratversicherung deckt die Mehrausgaben für die Lebenshaltung (z.B. für ein Hotelzimmer und auswärtige Verpflegung) bis zum vorgesehenen Betrag.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(946, 1, 'Zeitrenten', 'zeitrenten', NULL, 'Als Zeitrenten werden für eine bestimmte Zeit vereinbarte, regelmässige Rentenzahlungen bezeichnet; im Gegensatz zu lebenslänglichen <a href="rente">Renten.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(947, 1, 'Temporäre Leibrenten', 'temporare-leibrenten', NULL, 'Diese Versicherungsform (auch zeitliche Leibrente genannt) gewährleistet die Auszahlung einer zum voraus festgelegten Anzahl jährlicher Renten, unter Voraussetzung, dass der <a href="versicherter">Versicherte</a> lebt. Die Rentenzahlungen enden unmittelbar mit dem Tode des Versicherten, bzw. nach Auszahlung der festgelegten Anzahl jährlicher <a href="rente">Renten</a>. Die Finanzierung dieser Versicherung kann über periodische <a href="pramie">Prämien</a> oder durch eine <a href="einmaleinlage-einmalpramie">Einmaleinlage</a> erfolgen. Die temporäre Leibrenten-Versicherung interessiert Personen, die sich nur für den Erlebensfall, und dies für einen begrenzten Zeitabschnitt, ein Einkommen sichern wollen. (Beispiel: finanzielle Brücke zwischen einer vorzeitigen Pensionierung und der Fälligkeit der <a href="ahv-1">AHV</a>-Leistungen.)', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(948, 1, 'Temporäre Rente', 'temporare-rente', NULL, 'Private <a href="altersrenten">Altersrente</a> mit begrenzter Dauer, d.h. der Versicherer zahlt die <a href="rente">Renten</a> nur bis zum Ablauf der vereinbarten <a href="versicherungsdauer">Versicherungsdauer</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(949, 1, 'Endtermin', 'endtermin', NULL, 'Als Endtermin bezeichnet man den Zeitpunkt, in dem der Lebens<a href="versicherungsvertrag">versicherungsvertrag</a> abläuft.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(950, 1, 'Terminversicherung', 'terminversicherung', NULL, 'Bei der Terminversicherung wird die vereinbarte <a href="versicherungssumme">Versicherungssumme</a> auch bei vorzeitigem Tod des <a href="versicherter">Versicherten</a> erst zu dem im voraus bestimmten Zeitpunkt ausbezahlt. Die Terminversicherung ist eine vermögensbildende Versicherung. Sie hat daher grundsätzlich auch einen <a href="ruckkaufswert">Rückkaufswert</a>. Diese Versicherungsart wird daher auch Versicherung auf festen Termin genannt oder in Anlehnung an den französischen Ausdruck, Termefix-Versicherung. Stirbt die versicherte Person während der Vertragsdauer, entfallen die für die restliche Dauer noch geschuldeten Prämien.<br />Trotzdem wird aber die vereinbarte Versicherungssumme im festgesetzten Zeitpunkt voll ausbezahlt. Mit dieser Versicherung können planmässig auf einen im voraus festgesetzten Zeitpunkt Mittel bereitgestellt werden. Sie eignet sich daher besonders in der Kombination mit der Kinderversicherung (Ausbildungs- und Aussteuerversicherung).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(951, 1, 'Restlaufzeit', 'restlaufzeit', NULL, 'Verbleibende <a href="laufzeit">Laufzeit</a> einer <a href="obligationen">Obligation</a> vom gegenwärtigen Zeitpunkt bis zur Endfälligkeit bzw. zur vorzeitigen Rückzahlung. Nicht zu verwechseln mit der <a href="duration">Duration</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(952, 1, 'Kündigung', 'kundigung', NULL, 'Befugnis der Parteien, unter Einhaltung einer Frist den <a href="versicherungsvertrag">Versicherungsvertrag</a> durch einseitige Willenserklärung auf einen bestimmten Zeitpunkt hin aufzulösen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(953, 1, 'Kündigung (Fonds)', 'kundigung-fonds', NULL, 'Die Möglichkeit eines <a href="emittent">Emittenten</a>, seine <a href="obligationen">Anleihe</a> vor Ablauf der <a href="laufzeit">Laufzeit</a> zurückzubezahlen.<br />\r<br />Im Anlagefondsgeschäft der Widerruf des <a href="kollektivanlagevertrag">Kollektivanlagevertrags</a> durch den <a href="anleger">Anleger</a>. Der <a href="rucknahmepreis">Rücknahmepreis</a> ist grundsätzlich sofort auszuzahlen. Für <a href="immobilienfonds-liegenschaftsfonds">Immobilienfonds</a> besteht eine Sonderregelung, siehe <a href="ruckgabe-von-anteilen-borsenhandel">Rückgabe von Anteilen / Börsenhandel</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(954, 1, 'Austrittsleistung (BV)', 'austrittsleistung-bv', NULL, '<a href="altersguthaben">Altersguthaben</a> der <a href="berufliche-vorsorge">beruflichen Vorsorge</a>, das einer versicherten Person bei Stellenaufgabe gutgeschrieben oder mitgegeben wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(955, 1, 'Aussetzen der Versicherung', 'aussetzen-der-versicherung', NULL, 'Das Aussetzen der <a href="versicherung">Versicherung</a> findet dann statt, wenn ein <a href="halter">Halter</a> Fahrzeugausweis und <a href="kontrollschilder">Kontrollschilder</a>bei den Behörden hinterlegt, weil er das Fahrzeug nicht benötigt, oder wenn er die <a href="pramie">Prämie</a> nicht bezahlt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(956, 1, 'Aufhören der Versicherung', 'aufhoren-der-versicherung', NULL, 'Die <a href="versicherung">Versicherung</a> hört auf, wenn der <a href="halter">Halter</a> sein <a href="motorfahrzeug">Motorfahrzeug</a> verkauft und der Käufer eine andere Versicherung abschliesst.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(957, 1, 'Versichertes Gebiet bei Umzug', 'versichertes-gebiet-bei-umzug', NULL, 'Als "versichertes Gebiet" werden während des Umzuges die Schweiz, das Fürstentum Liechtenstein und die Enklaven Büsingen und Campione verstanden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(958, 1, 'Verfügung: Letztwillige Verfügung mit Vollmacht', 'verfugung-letztwillige-verfugung-mit-vollmacht', NULL, 'Bei <a href="drittlebens-versicherung">Drittlebensversicherungen</a> kann der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> regeln, wer nach seinem Tod neuer Versicherungsnehmer wird. Durch den Tod des Versicherungsnehmers wird keine <a href="versicherungsleistungen">Versicherungsleistung</a> fällig, und der Vertrag erlischt nicht. Die Regelung über den neuen Versicherungsnehmer erfolgt in einer letztwilligen Verfügung. Die dazugehörige Vollmacht ermächtigt den <a href="versicherer">Versicherer</a>, sofort nach Kenntnisnahme des Todes den Übergang des Versicherungsnehmers durchzuführen. Fehlt eine letztwillige Verfügung, so fällt der <a href="versicherungsvertrag">Versicherungsvertrag</a> in die Erbmasse des Verstorbenen. Alle vorhandenen <a href="erben">Erben</a> werden gemeinsam neue Versicherungsnehmer.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(959, 1, 'Diebstahl', 'diebstahl', NULL, 'Diebstahl ist eines der vier <a href="risiko">Risiken</a>, die in der <a href="hausratversicherung">Hausratversicherung</a> eingeschlossen sind. In der Diebstahlversicherung werden Schäden durch <a href="einbruchdiebstahl">Einbruchdiebstahl</a>, <a href="beraubung">Beraubung</a>, <a href="entreissdiebstahl">Entreissdiebstahl</a>, <a href="taschen-trickdiebstahl">Taschen- und Trickdiebstahl</a> sowie <a href="einfacher-diebstahl">einfachen Diebstahl</a>versichert.<br />Juristisch wird Diebstahl folgendermassen definiert: "Wer jemandem eine fremde, bewegliche Sache wegnimmt, um sich oder einen anderen damit unrechtmässig zu bereichern, begeht Diebstahl."', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(960, 1, 'Strolch', 'strolch', NULL, 'Der Strolch zeichnet sich dadurch aus, dass er ein Motorfahrzeug <a href="zum-gebrauch-entwenden">zum Gebrauch entwendet.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(961, 1, 'Themenfonds', 'themenfonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, die in Werten investieren, welche einem bestimmten Kriterium gerecht werden (Umweltfreundlichkeit, ethisches Engagement, usw.) oder welche alle die gleiche Eigenschaft aufweisen (z.B. Unternehmen, die eine Restrukturierung durchlaufen).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(962, 1, 'Theoret.Verfallrendite(netto)/Oblig. u. Geldm.fond', 'theoret-verfallrendite-netto-oblig-u-geldm-fond', NULL, 'Die bei <a href="obligationenfonds">Obligationen-</a> und <a href="geldmarktfonds">Geldmarktfonds</a> ausgewiesene theoretische Verfallrendite ist eine für die nächsten 12 Monate erwartete Rendite auf der Basis des aktuellen Fondsportefeuilles. Sie ist eine Schätzung und entspricht dem gewichteten Durchschnitt der Renditen auf Verfall aller Einzelanlagen des Fonds. Dabei werden die bei der Verwaltung des Fonds anfallenden Kosten berücksichtigt und von der Bruttorendite auf Verfall abgezogen. Bei Änderungen der Portefeuillestruktur und Schwankungen des Zinsniveaus verändert sich auch die theoretische Verfallrendite. Es kann daher nicht von der Verfallrendite eines Fonds auf eine allfällige <a href="ausschuttung">Ausschüttung</a> geschlossen werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(963, 1, 'Theoret. Verfallrendite (brutto) / Anlagestrat.fonds', 'theoret-verfallrendite-brutto-anlagestrat-fonds', NULL, 'Im Gegensatz zu den <a href="obligationenfonds">Obligationen-</a> und <a href="geldmarktfonds">Geldmarktfonds</a> wird die theoretische Verfallrendite hier aus Gründen der Vergleichbarkeit brutto ausgewiesen, da die <a href="anlagestrategiefonds">Anlagestrategiefonds</a> neben unterschiedlich hohen Anteilen an verzinslichen Anlagen auch unterschiedliche Kommissionsstrukturen aufweisen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(964, 1, 'Dritte', 'dritte', NULL, 'Der Dritte ist der <a href="geschadigte">Geschädigte</a>, der im <a href="svg">SVG</a> durch die <a href="kausalhaftung">Kausalhaftung</a> oder <a href="verschuldenshaftung">Verschuldenshaftung</a> geschützt werden soll. Der Dritte ist z.B. ein Fussgänger, eine Fahrradfahrerin, ein Mitfahrer, ein anderer Autohalter oder der Besitzer eines Gartenzauns.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(965, 1, 'Dritte Säule', 'dritte-saule', NULL, '<a href="private-vorsorge">Private Vorsorge</a> als dritter Pfeiler des schweizerischen <a href="sozialversicherung">Sozialversicherung</a>ssystems. Die dritte Säule ist freiwillig und umfasst die <a href="gebundene-vorsorge">gebundene Vorsorge</a> (<a href="saule-3a">Säule 3a</a>) sowie die freie <a href="freie-vorsorge">Vorsorge</a> (<a href="saule-3b">Säule 3b</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(966, 1, 'Haftpflicht', 'haftpflicht', NULL, 'Unter Haftpflicht versteht man die Pflicht, für einen <a href="schaden">Schaden</a>, den man einem anderen widerrechtlich zugefügt hat, einstehen zu müssen.<br />Wir haften aus verschiedenen Gründen; weil wir schuldhaft gehandelt oder weil wir eine <a href="sorgfaltspflicht">Sorgfaltspflicht</a> verletzt haben. Besonders streng wird die Haftpflicht nach dem Strassenverkehrsgesetz, die <a href="motorfahrzeug-haftpflichtversicherung">Motorfahrzeug-Haftpflichtversicherung</a> ausgelegt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(967, 1, 'Haftpflichtversicherung', 'haftpflichtversicherung', NULL, 'Die Haftpflichtversicherung übernimmt den Vermögensverlust, den der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> dadurch erlitten hat, dass er berechtigte <a href="anspruch">Ansprüche</a> <a href="dritte">Dritter</a> befriedigen muss. Sie lehnt auch unberechtigte Ansprüche für den Versicherungsnehmer ab (Rechtsschutzfunktion).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(968, 1, 'Dritteigentum', 'dritteigentum', NULL, '<a href="sache">Sachen</a>, die Dritten (z. B. den Angestellten oder Kunden eines Unternehmens) gehören. Versicherte Gegenstände der <a href="geschaftsversicherung">Geschäftsversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(969, 1, 'Festgeld', 'festgeld', NULL, 'Auf Termin, d.h. auf einen bestimmten festen Verfalltag bei einer Bank angelegtes kurz- oder mittelfristiges Guthaben, das eine höhere Verzinsung abwirft als z.B. ein <a href="sichtguthaben">Sichtguthaben.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(970, 1, 'Grobes Verschulden', 'grobes-verschulden', NULL, 'siehe <a href="grobe-fahrlassigkeit-grobfahrlassigkeit">Grobfahrlässigkeit.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(971, 1, 'Verschuldenshaftung', 'verschuldenshaftung', NULL, 'Als Verschuldenshaftung bezeichnet man diejenige Art der Haftung, bei welcher man für einen selbst verschuldeten <a href="schaden">Schaden</a> einstehen muss. Vorausgesetzt wird immer ein persönliches Verschulden. Im Obligationenrecht heisst es: "Wer einem anderen widerrechtlich Schaden zufügt, sei es mit Absicht, sei es aus Fahrlässigkeit, wird ihm zum Ersatze verpflichtet."', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(972, 1, 'Totalschaden', 'totalschaden', NULL, '<a href="schaden">Schaden</a>, bei dem eine Reparatur der versicherten Sache teurer ist als der versicherte Wert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(973, 1, 'Gesamtrisiko', 'gesamtrisiko', NULL, 'Siehe <a href="risiko-fonds">Risiko</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(974, 1, 'Tracking Error', 'tracking-error-1', NULL, 'Mass für die Abweichung der Fondsrendite zur Rendite des <a href="benchmark">Benchmarks</a> über einen bestimmten Beobachtungszeitraum. Ausgedrückt in Prozent. Der Tracking Error ist umso kleiner, je passiver der <a href="anlagefonds">Anlagefonds</a> verwaltet wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(975, 1, 'Arbeitgeber-Verbandsversicherung', 'arbeitgeber-verbandsversicherung', NULL, '<a href="versicherung">Versicherung</a>, die ein Verband für die ihm angeschlossenen Unternehmen bei einer Versicherungsgesellschaft abschliesst und die zum Zweck hat, das Personal der Verbandsmitglieder gegen bestimmte <a href="risiko">Risiken</a> zu versichern.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(976, 1, 'Trading Opportunistic', 'trading-opportunistic-1', NULL, 'Unter diesem Begriff werden Strategien zusammengefasst, die situationsabhängig in einer Vielzahl von Finanzinstrumenten, Sektoren und Märkten investieren, je nach Möglichkeit, die sich den Managern bietet. Macro Hedge Funds versuchen globale Profitchancen durch makroökonomische Analysen zu erkennen und auszunützen. Wegen der Verschiedenheit der Strategien sind auch die <a href="korrelation">Korrelationen</a> zu den Märkten unterschiedlich.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(977, 1, 'Tranche', 'tranche', NULL, 'Ein Fonds resp. <a href="subfonds">Subfonds</a> kann in mehrere Tranchen, d.h. separate Valoren aufgeteilt werden, die sich durch einen oder mehrere Faktoren von einander unterscheiden. Beispiele: Ausschüttungspraxis, Konditionen, Fondswährung. Hingegen sind alle Tranchen eines Fonds/Subfonds immer in das selbe Portfolio investiert.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(978, 1, 'Transportversicherung', 'transportversicherung', NULL, 'Die Transportversicherung deckt Güter gegen Risiken während des Transports. <br />Sie umfasst zwei Hauptzweige: <br />Bei der Transportversicherung / Warenversicherung geht es um das transportierte Gut selbst. Bei der <a href="verkehrshaftungsversicherung">Verkehrshaftungsversicherung</a> geht es um den Spediteur und den Frachtführer. <br />Daneben gehören einige weitere  Versicherungen zu den Transportversicherungen: <a href="valorenversicherung">Valorenversicherung</a>, <a href="werkverkehrsversicherung">Werkverkehrsversicherung</a>, die <a href="manipulationsversicherung">Manipulationsversicherung</a> sowie die Kaskoversicherung für Transportmittel. <br />Es gibt drei Varianten der Transportversicherung: die Eingeschränkte Versicherung, die erweiterte Versicherung und die Versicherung für alle Risiken (<a href="all-risk-versicherung">All Risk</a>). <br />In der Transportversicherung gibt es vier verschiedene Vertragsarten: <a href="manipulationsversicherung">Einzelpolice</a>, <a href="einzelpolice">Generalpolice</a>, <a href="generalpolice">Pauschalpolice</a> und <a href="umsatzpolice">Umsatzpolice</a>.<br />', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(979, 1, 'Werkverkehrsversicherung', 'werkverkehrsversicherung', NULL, '<a href="transportversicherung">Transportversicherung</a> für Unternehmen, die eigene Waren mit eigenen <a href="fahrzeug">Fahrzeugen</a> in der Schweiz transportieren.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(980, 1, 'Drei-Säulenkonzept', 'drei-saulenkonzept', NULL, 'System der schweizerischen Alters-, Hinterlassenen- und Invalidenvorsorge. Dieses System ist seit 1972 in der Bundesverfassung (Art. 111) verankert und ruht auf drei Säulen. Die erste Säule bildet die <a href="ahv-1">AHV</a> / <a href="iv">IV</a>, eine allgemeine, die Existenz sichernde staatliche Versicherung. Die zweite Säule wird durch die <a href="berufliche-vorsorge">berufliche Vorsorge</a> gebildet. Sie hat zusammen mit der ersten Säule zum Ziel, die Fortführung der gewohnten Lebenshaltung zu gewährleisten. Die dritte Säule ist die freiwillige Selbstvorsorge. Sie ergänzt die erste und die zweite Säule entsprechend den persönlichen Bedürfnissen. Die dritte Säule wird unterteilt in die <a href="freie-vorsorge">freie (3b)</a> und in die <a href="gebundene-vorsorge">gebundene (3a) Vorsorge</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(981, 1, 'UV', 'uv-1', NULL, 'Abkürzung für "<a href="unterversicherung-uv">Unterversicherung</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(982, 1, 'Umbrella-Fonds', 'umbrella-fonds', NULL, '<a href="anlagefonds">Anlagefonds</a>, der aus mehreren <a href="subfonds">Subfonds</a> zusammengesetzt ist. Die Gesamtheit der Subfonds bildet eine juristische Einheit, so dass nur für den Umbrella ein Zulassungsverfahren nötig ist. Die Subfonds sind im selben <a href="prospekt-fondsreglement">Fondsreglement und Prospekt</a>geregelt. Nach der erstmaligen Zulassung können weitere Subfonds geschaffen werden. Nicht zu verwechseln mit <a href="fund-of-funds-1">Fund of Funds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(983, 1, 'Unterversicherung (UV)', 'unterversicherung-uv', NULL, 'Unterversicherung liegt vor, wenn die <a href="versicherungssumme">Versicherungssumme</a> tiefer liegt als der Wert des versicherten Gegenstandes im Zeitpunkt des Schadens (<a href="ersatzwert">Ersatzwert</a>). Der <a href="versicherer">Versicherer</a> kann seine Leistung im Teilschaden nach der so genannten Proportionalregel (Verhältnis, in dem die Versicherungssumme zum Ersatzwert steht) kürzen. Im Totalschaden ist die Leistung auf die Versicherungssumme beschränkt. Die Proportionalregel kann durch Vereinbarung (Erstrisikodeckung) ausgeschaltet werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(984, 1, 'Basiswert', 'basiswert', NULL, 'Finanzwert (Wertpapier, Währung, Index, Rohstoff usw.), der einer <a href="optionen">Option</a> oder einem <a href="futures">Future</a> zu Grunde liegt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(985, 1, 'Underwriting und Riskmanagement', 'underwriting-und-riskmanagement', NULL, 'Teilprozess des <a href="leistungsprozess">Leistungsprozesses</a>, bei dem es um den Entscheid über den Abschluss von <a href="versicherungsvertrag">Versicherungsverträgen</a> geht und um den Umgang des Versicherers mit den <a href="risiko">Risiken</a> seines Versicherungsbestands (Riskmanagement).', '2016-08-29 10:21:09', '2016-08-29 10:22:18');
INSERT INTO `contrexx_module_topics_entries` (`id`, `active`, `name`, `slug`, `href`, `description`, `created`, `updated`) VALUES
(986, 1, 'Arbeitslosen- und Insolvenzversicherung', 'arbeitslosen-und-insolvenzversicherung', NULL, 'Obligatorisch versichert durch die Arbeitslosen- und Insolvenzversicherung (AIV) sind alle Arbeitnehmerinnen und Arbeitnehmer.<br />Die AIV soll den Arbeitnehmer wenigstens teilweise für den Einkommensverlust entschädigen, den er durch eine nicht selbstverschuldete Arbeitslosigkeit (Stellenverlust, Kurzarbeit, schlechtes Wetter) oder wegen Zahlungsunfähigkeit des Arbeitgebers erleidet.<br />Die Entschädigung des Arbeitslosen besteht in der Bezahlung eines Taggeldes. An der Durchführung der AIV sind die Arbeitgeber, die <a href="ahv-1">AHV</a> und die Ausgleichsstelle der Arbeitslosenversicherung beteiligt. <br />Zur Verhütung und Bekämpfung der Arbeitslosigkeit leistet die AIV zudem finanzielle Beiträge an Massnahmen zur Umschulung, Weiterbildung und Eingliederung und an <a href="versicherter">Versicherte</a>, die ausserhalb ihres Wohnorts Arbeit annehmen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(987, 1, 'Arbeitslosenentschädigung', 'arbeitslosenentschadigung', NULL, 'Leistung der<a href="arbeitslosen-und-insolvenzversicherung">Arbeitslosenversicherung (ALV)</a> beim Ausfall des Erwerbseinkommens wegen unverschuldeter Arbeitslosigkeit.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(988, 1, 'Anteil', 'anteil', NULL, 'Forderung des einzelnen <a href="anleger">Anlegers</a> gegenüber der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> auf verhältnismässige Beteiligung an Vermögen und <a href="ertrag">Ertrag</a> des <a href="anlagefonds">Anlagefonds</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(989, 1, 'Anteilschein', 'anteilschein', NULL, 'Wertpapiermässige Verbriefung des <a href="anteil">Anteils</a> an einem <a href="anlagefonds">Anlagefonds</a> in einer Urkunde. Meistens werden die Anteile an einem Fonds jedoch buchmässig geführt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(990, 1, 'Widerrrechtlichkeit', 'widerrrechtlichkeit', NULL, '<a href="ausservertragliche-haftpflicht">Ausservertragliche Haftpflicht</a>: Unerlaubter Eingriff in die Persönlichkeit, in das Eigentum oder in den Besitz einer Person.<br /><a href="vertragliche-haftpflicht">Vertragliche Haftpflicht</a>: Verletzung der mit einem Vertrag eingegangenen Pflichten.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(991, 1, 'Unterstützung', 'unterstutzung', NULL, 'Massgebliche Unterstützung. Eine Person kann im Rahmen der 2. Säule oder der Säule 3a begünstigt werden, wenn sie von der verstorbenen (versicherten) Person massgeblich unterstützt worden ist. Dies kann in der Regel dann angenommen werden, wenn die Unterstützung mehr als 50 % des Unterhalts der begünstigten Person ausmachte und sie länger als 5 Jahre dauerte und im Zeitpunkt des Todes noch andauerte. Unterhalb dieser Grenzen ist im Einzelfall eine sachgerechte Lösung zu finden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(992, 1, 'Nutzfeuer', 'nutzfeuer', NULL, 'Ein Nutzfeuer ist ein Feuer, das an seinem dafür vorgesehenen Ort entsteht und einem beabsichtigten Zweck dient (z.B. Feuer im Kamin, Feuer im Gartengrill, Feuer auf dem Gasherd, Feuer im Heizungskessel). Das Gegenteil zum Nutzfeuer ist das <a href="schadenfeuer">Schadenfeuer.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(993, 1, 'UVG-Zusatzversicherung', 'uvg-zusatzversicherung', NULL, 'Erweiterung des <a href="obligatorische-unfallversicherung">obligatorischen Unfallschutzes</a> nach UVG um zusätzliche Leistungen, die von Privatversicherern angeboten werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(994, 1, 'UVG', 'uvg', NULL, 'Abkürzung für "Bundesgesetz über die Unfallversicherung" vom 20. März 1981', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(995, 1, 'Geldwerte', 'geldwerte', NULL, 'Unter Geldwerten wird nicht nur Bargeld verstanden, sondern auch Wertpapiere, Sparhefte, Edelmetalle (unverarbeitet), Münzen und Medaillen, lose Edelsteine und Perlen.<br />Geldwerte sind meistens summenmässig limitiert versichert.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(996, 1, 'Valorenversicherung', 'valorenversicherung', NULL, '<a href="transportversicherung">Transportversicherung</a> für Valoren (Wertpapiere, Edelmetalle, Banknoten, Münzgeld etc.).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(997, 1, 'Neuwert', 'neuwert', NULL, 'Wird eine Versicherung zum Neuwert (NW) abgeschlossen, so entspricht die Leistung der Versicherung in einem Schadenfall dem aktuellen Marktpreis.<br />Die <a href="hausratversicherung">Hausratversicherung</a> wird in der Regel zum Neuwert abgeschlossen, deshalb ist eine aktuelle Anpassung der <a href="versicherungssumme">Versicherungssumme</a> bei Neuanschaffungen notwendig. <br />Bei gewissen Versicherungen wird die Versicherungssumme an die Entwicklung bestimmter <a href="hausratindex">Indizes</a> angepasst bzw. von Zeit zu Zeit automatisch erhöht.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(998, 1, 'Wertschöpfungskette', 'wertschopfungskette', NULL, 'Siehe <a href="geschaftsprozess">Geschäftsprozess</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(999, 1, 'Landwert', 'landwert', NULL, 'Valore di un terreno in base alle possibilità di sfruttamento economico. Il valore del terreno dipende da un lato dall''ubicazione e dalle caratteristiche del sottosuolo, dall''altro dalle possibilità tecniche e giuridiche di edificazione.', '2016-08-29 10:21:09', '2016-08-29 10:22:15'),
(1000, 1, 'Vandalismus', 'vandalismus', NULL, 'Vandalismus ist die mutwillige Zerstörung von Gegenständen, meist aus Wut oder Aggressivität. Werden Hausratgegenstände und Gebäudeteile bei einem <a href="einbruchdiebstahl">Einbruchdiebstahl</a> zerstört, sind diese in der Regel über die Diebstahlversicherung versichert (Folgeschaden).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1001, 1, 'Variabel verzinsliche Obligationen', 'variabel-verzinsliche-obligationen', NULL, '<a href="obligationen">Obligationen</a>, deren Zins viertel- oder halbjährlich mit der Entwicklung des jeweiligen Referenzzinssatzes ändert. Ein häufiger Referenzzinssatz ist der Libor (London Interbank Offered Rate).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1002, 1, 'Fahrzeug', 'fahrzeug', NULL, 'Fahrzeug ist jedes Ding, das fährt. Darunter fallen Motor- und andere Fahrzeuge (z.B. Fahrräder).', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(1003, 1, 'Fehlerhafte Beschaffenheit', 'fehlerhafte-beschaffenheit', NULL, 'Fehlerhafte Beschaffenheit eines <a href="fahrzeug">Fahrzeugs</a> liegt z.B. dann vor, wenn eine Handbremse nicht mehr funktionstüchtig ist oder wenn Öl ausfliesst.<br />Führt eine fehlerhafte Beschaffenheit zu einem <a href="schaden">Schaden</a>, so haftet der Halter auch ohne <a href="verschulden">Verschulden.</a> Es handelt sich hier um eine (milde) <a href="kausalhaftung">Kausalhaftung.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1004, 1, 'Flottenversicherung', 'flottenversicherung', NULL, '<a href="versicherung">Versicherung</a>, bei der die ganze <a href="motorfahrzeug">Motorfahrzeug</a>flotte eines Halters (meist mehr als 10 Motorfahrzeuge eines Unternehmens oder der öffentlichen Hand) in einer <a href="police">Police</a> zusammengefasst wird.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1005, 1, 'Venture Capital', 'venture-capital-1', NULL, 'Dieser Begriff bezeichnet die Bereitstellung von Eigenkapital inklusive umfassender Managementunterstützung für junge Unternehmen mit überdurchschnittlichen Wachstumsaussichten. Das Kapital bleibt in der Regel fünf bis zehn Jahre in der Firma gebunden.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(1006, 1, 'Freizügigkeitskonto', 'freizugigkeitskonto', NULL, 'Zweckgebundenes Bankkonto, auf das die <a href="austrittsleistung-bv">Austrittsleistung</a> der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> einer versicherten Person fliesst, wenn diese nicht sofort eine neue Stelle antritt.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1007, 1, 'Altersguthaben', 'altersguthaben', NULL, 'Im Rahmen der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> wird jedem <a href="versicherter">Versicherten</a> ein bestimmter Prozentsatz seines <a href="koordinierter-lohn">koordinierten Lohnes</a> gutgeschrieben, sog. <a href="altersgutschriften">Altersgutschriften</a>, die das Altersguthaben bilden. Das Altersguthaben setzt sich zusammen aus den individuellen Altersgutschriften, den eingebrachten <a href="freizugigkeitsleistung">Freizügigkeitsleistungen</a> und den allfälligen Einlagen sowie auf diesen Beträgen vergüteten Zinsen. Für die Verzinsung ist der vom Bundesrat festgelegte Mindestzinssatz massgebend.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1008, 1, 'Volatilität', 'volatilitat', NULL, 'Siehe <a href="risiko-fonds">Risiko</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1009, 1, 'VVG', 'vvg-1', NULL, 'Abkürzung für "<a href="versicherungsvertragsgesetz">Versicherungsvertragsgesetz</a>"', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1010, 1, 'Karenzfrist', 'karenzfrist', NULL, 'Bei der <a href="betriebsunterbrechungsversicherung">Betriebsunterbrechungsversicherung</a> die Zeitdauer ab Beginn der Betriebsunterbrechung, für die der Versicherte die Ausfälle aus der Betriebsunterbrechung selbsttragen muss.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1011, 1, 'Wartefrist', 'wartefrist', NULL, 'Die Wartefrist ist diejenige Zeit, die zwischen dem Eintritt des <a href="versicherungsfall">Versicherungsfalles</a> und dem Beginn der Leistungspflicht der Gesellschaft liegt. Sie gilt bei den Erwerbsausfallrenten und bei den Taggeldern der Lebensversicherung. Die Wartefristen erlauben eine Koordination mit anderen Versicherungsträgern, die das gleiche <a href="risiko">Risiko</a> ebenfalls decken. Auch kann damit der Lohnzahlungspflicht durch den Arbeitgeber Rechnung getragen werden. Nicht verwechselt werden darf die Wartezeit mit der <a href="karenzzeit">Karenzzeit.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1012, 1, 'Kassenschrank', 'kassenschrank', NULL, 'Verschliessbarer Behälter, in welchem vor allem <a href="geldwerte">Geldwerte</a> oder Schmuck aufbewahrt wird. Entspricht dieser bestimmten Anforderungen, können Werte mit einer höheren <a href="versicherungssumme">Versicherungssumme</a> versichert werden. Die <a href="pramie">Prämie</a> wird in Abhängigkeit von der Beschaffenheit des Kassenschrankes berechnet.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1013, 1, 'Optionsanleihen', 'optionsanleihen', NULL, '<a href="obligationen">Obligationen</a>, die mit einem Optionsschein ausgestattet sind. Der Optionsschein berechtigt während der Optionsfrist und zu einem im Voraus fixierten Preis zum Bezug einer bestimmten Anzahl <a href="aktie">Aktien</a> des betreffenden Unternehmens. Die Obligation läuft auch nach Ausübung der <a href="optionen">Option</a> bis zum Verfalldatum weiter.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1014, 1, 'Wasser(schaden)', 'wasser-schaden', NULL, 'Die Wasserversicherung übernimmt <a href="schaden">Schäden</a>, die durch Wasser aus Wasserleitungsanlagen und daran angeschlossenen Einrichtungen und Apparaten entstehen. Schäden durch Frost, Grundwasser und Rückstau der Kanalisation sind ebenfalls versichert.<br />Die Wasserversicherung kann sowohl für <a href="fahrhabe">Fahrhabe</a> (Hausrat, Geschäftsinventar) als auch für Gebäude abgeschlossen werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1015, 1, 'Wasser', 'wasser', NULL, 'Versicherbare Grundgefahr der <a href="sachversicherung">Sachversicherung</a>.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1016, 1, 'Güterrechtliche Auseinandersetzung', 'guterrechtliche-auseinandersetzung', NULL, 'Die güterrechtliche Auseinandersetzung erfolgt im Zeitpunkt der Beendigung der Ehe, sei dies durch Scheidung oder Tod eines Ehepartners oder bei Wechsel des Güterbestandes. In diesem Moment hat die Verteilung des vorhandenen Vermögens der Ehepartner in diesem Zeitpunkt unter Berücksichtigung des anwendbaren Güterstandes (ordentlicher Güterstand der <a href="errungenschaft">Errungenschaftsbeteiligung</a>, <a href="gutergemeinschaft">Gütergemeinschaft</a> und Gütertrennung) und allfälliger Eheverträge zu erfolgen.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1017, 1, 'Rücktritt', 'rucktritt', NULL, 'Der Rücktritt vom Vertrag ist nicht zu verwechseln mit der <a href="kundigung">Kündigung</a>. Eine Kündigung löst ein Vertragsverhältnis stets für die Zukunft auf, während der Rücktritt das Vertragsverhältnis rückwirkend auf einen bestimmten Zeitpunkt zum Erlöschen bringt. Der Rücktritt hat zur Folge, dass der <a href="versicherer">Versicherer</a> Leistungen, welche er nach Vorliegen des Rücktrittsgrundes erbracht hat, zurückgefordern kann. Beispiele: Rücktritt infolge <a href="anzeigepflichtverletzung">Anzeigepflichtverletzung</a> oder betrügerischer Anspruchsbegründung.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1018, 1, 'Verrechnungssteuer', 'verrechnungssteuer', NULL, 'Steuer auf Kapitalerträgen (<a href="zins">Zinsen</a>, Dividenden: 35%), <a href="rente">Renten</a> (15%) und Kapitalleistungen aus Versicherung (8%). Sie ist vom Leistenden (Bank, Versicherung) direkt an den Fiskus abzuliefern. Wenn der Empfänger die erhaltene Leistung in der Steuererklärung deklariert, wird die erhobene Verrechnungssteuer zurückerstattet. Von diesem Prinzip (Erhebung der Steuer bei Auszahlung der Leistung und Rückerstattung bei Deklaration der Leistung) wird bei Versicherungsleistungen abgewichen. Ein Abzug der Verrechnungssteuer bei der Auszahlung der Leistung erfolgt nur dann, wenn die Ausrichtung der Leistung gemäss ausdrücklicher Anweisung des Leistungsempfängers nicht dem Fiskus (Eidgenössische Steuerverwaltung) gemeldet werden darf.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1019, 1, 'Verrechnungssteuer (Fonds)', 'verrechnungssteuer-fonds', NULL, 'Vom Bund erhobene <a href="quellensteuer">Quellensteuer</a> von 35% auf inländischen Kapitalerträgen, die unter anderem bei Ertragsausschüttungen von schweizerischen <a href="anlagefonds">Anlagefonds</a> erhoben wird. Die Abzüge können, je nach Herkunft der Erträge und Domizil des <a href="anleger">Anlegers</a>, ganz oder teilweise zurückgefordert werden.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1020, 1, 'Unabhängig von Schienen', 'unabhangig-von-schienen', NULL, 'Unabhängig von Schienen ist das <a href="motorfahrzeug">Motorfahrzeug</a> im Unterschied zu Eisenbahn und Tram. Schienenfahrzeuge sind deshalb keine Motorfahrzeuge nach <a href="svg">SVG.</a>', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1021, 1, 'Rendite', 'rendite', NULL, 'Masszahl ausgedrückt in Prozenten für den Ertrag während einer bestimmten Zeitperiode.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(1022, 1, 'Zinsstrukturkurve', 'zinsstrukturkurve', NULL, 'Die so genannte Zinsstrukturkurve stellt den Zusammenhang zwischen den <a href="laufzeit">Laufzeiten</a> der im Markt gehandelten <a href="obligationen">Obligationen</a> und ihrer Rendite auf Verfall dar. Die Zinsstrukturkurve wird allgemein in drei Segmente aufgeteilt: in ein kurzes und langes Ende sowie in ein mittleres Segment. Die Form dieser Kurve gibt Rückschlüsse auf den gegenwärtigen Zustand des Obligationenmarktes. Im Normalfall steigt die Kurve für längere Laufzeiten an: Der Anleger kommt in den Genuss einer höheren Rendite dafür, dass er sein Geld auf längere Sicht investiert. Sind die Renditen im kürzeren Laufzeitenbereich höher als "am langen Ende", so spricht man von einer inversen Zinsstruktur. Über die Positionierung seines Portefeuilles an der Zinsstrukturkurve steuert der Fondsmanager das Zinsrisiko des Fonds (siehe <a href="duration">Duration</a>).', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1023, 1, 'Ausschüttungsrendite (Barrendite)', 'ausschuttungsrendite-barrendite', NULL, 'Der ausgeschüttete Ertrag im Verhältnis zum aktuellen Börsenkurs entspricht der Ausschüttungsrendite.', '2016-08-29 10:21:09', '2016-08-29 10:21:55'),
(1024, 1, 'Zero Bonds', 'zero-bonds', NULL, '<a href="obligationen">Obligationen</a> ohne Verzinsung. Anstelle einer Verzinsung erhält der Käufer eines Zero Bonds den Titel zu einem Diskont. Die Rückzahlung erfolgt zu 100%.', '2016-08-29 10:21:09', '2016-08-29 10:22:18'),
(1025, 1, '<tag> de', 'tag-de', 'http://tv.psico.ch/dev/', '', '2016-09-20 09:25:57', '2016-09-20 09:53:15');
