(500, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '125', 'Notification to the insurer by the policyholder or insured person in the event of a claim.'),
(501, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '126', 'Claims processing'),
(502, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '126', 'claims-processing'),
(503, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '126', NULL),
(504, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '126', 'Service that includes competent, unbureaucratic advice and support for the insured client in the event of claims. Financial consequences of claims are assumed as a core service.'),
(505, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '127', 'Claims ratio'),
(506, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '127', 'claims-ratio'),
(507, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '127', NULL),
(508, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '127', 'A ratio that measures an insurer''s claims payments in relation to its premiums earned (gross or net).'),
(509, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '128', 'Claims service'),
(510, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '128', 'claims-service'),
(511, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '128', NULL),
(512, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '128', 'Tasks that arise when clients report claims. This includes, in particular, reviewing claims reports, calculating the amounts of damages and the provision of insurance benefits.'),
(513, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '129', 'Client retention team'),
(514, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '129', 'client-retention-team'),
(515, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '129', NULL),
(516, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '129', 'Implementation of the service concept within the private insurance sector. Client advisors and clients form a team that has the goal of guaranteeing insurance coverage that is customised to optimally meet the needs of individual clients at all times.'),
(517, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '130', 'Closed-end fund'),
(518, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '130', 'closed-end-fund'),
(519, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '130', NULL),
(520, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '130', 'An <a href="investment-funds">investment fund</a> in the form of a company (normally a stock company) with fixed capital. A closed-end fund is not obliged to redeem issued <a href="unit">units</a> at the request of the unitholder. Units of this type of investment fund may not be sold publicly under Swiss law. As opposed to an <a href="open-end-fund">open-end fund</a>.'),
(521, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '131', 'CO'),
(522, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '131', 'co'),
(523, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '131', NULL),
(524, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '131', 'Abbreviation "Code of Obligations"'),
(525, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '132', 'Collection'),
(526, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '132', 'collection'),
(527, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '132', NULL),
(528, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '132', 'Presentation of a debt for payment.'),
(529, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '133', 'Collective investment contract'),
(530, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '133', 'collective-investment-contract'),
(531, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '133', NULL),
(532, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '133', 'The collective investment contract is the legal basis for the investment fund business in Switzerland. This agreement is concluded between the <a href="fund-management-company">management company</a>, the <a href="custodian-bank">custodian bank</a> and the <a href="investor">investor</a>. It is the legal basis for the management of the <a href="investment-funds">investment fund</a> by the management company on the one hand and for the participation of the investors in the assets of the investment fund on the other. The collective investment contract is embodied by the <a href="prospectus-fund-regulations">fund regulations</a>.'),
(533, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '134', 'Combined ratio (CR)'),
(534, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '134', 'combined-ratio-cr'),
(535, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '134', NULL),
(536, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '134', 'A figure that relates the<a href="claims-ratio">claims ratio</a> to the <a href="#invalid-reference-0">expense ratio</a>.'),
(537, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '135', 'Commencement of a contract'),
(538, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '135', 'commencement-of-a-contract'),
