(540, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '135', 'The point in time at which the contract is concluded on the basis of the acceptance of a binding <a href="proposal-or-application">application</a>.'),
(541, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '136', 'Commercial insurance'),
(542, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '136', 'commercial-insurance'),
(543, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '136', NULL),
(544, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '136', 'Commercial insurance includes all <a href="insurance-contract">insurance contracts</a> covering <a href="damages">damages</a> caused by <a href="fire-2">fire</a>, <a href="water-damage">water</a>, broken glass and housebreaking as well as <a href="third-party-liability-civil-liability">third-party cover</a> and the closing down of operations in industrial, commercial and service companies.'),
(545, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '137', 'Commercial legal protection insurance'),
(546, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '137', 'commercial-legal-protection-insurance'),
(547, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '137', NULL),
(548, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '137', 'A type of <a href="legal-protection-insurance-1">legal protection insurance</a> that insures against legal disputes faced by companies or self-employed individuals.'),
