(553, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '139', 'Commission de Surveillance du Sect. Financier'),
(554, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '139', 'commission-de-surveillance-du-sect-financier'),
(555, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '139', NULL),
