(559, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '140', NULL),
(560, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '140', '<a href="issuing-commission">Issuing commission</a> and <a href="redemption-commission">redemption commission</a>. Fee charged on the <a href="subscription">subscription</a> or <a href="redemption">redemption</a> of fund units.'),
(561, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '141', 'Commitee'),
