(1775, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '444', NULL),
(1776, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '444', 'Basic statistical indicator providing periodical (daily, hourly, etc.) comparisons of the ratio between the present value of a thing and a pre-defined basic value of the same thing and hence revealing variations and tendencies. See also under <a href="share-index">share index</a>, <a href="dow-jones-index">Dow Jones index</a>, <a href="swiss-performance-index">Swiss Performance Index</a>.'),
(1777, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '445', 'Index (funds)'),
(1778, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '445', 'index-funds'),
(1779, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '445', NULL),
(1780, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '445', 'Indicator of performance on one or more markets. The oldest and best-known stock market index is the <a href="dow-jones-index">Dow Jones</a>. Indexes make it possible to compare the <a href="performance">performance</a> of a fund which is invested in a specific market with the development of this market. See also <a href="benchmark">benchmark</a>.'),
(1781, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '446', 'Index fund'),
(1782, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '446', 'index-fund'),
