(3511, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '878', NULL),
(3512, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '878', 'Société d''investissement à capital variable. An <a href="investment-funds">investment fund</a> in the form of a joint-stock company with variable capital. The fund''s <a href="unit">units</a> are issued in the form of <a href="equities">equities</a>. See <a href="corporate-form">corporate form</a>.'),
(3513, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '879', 'Sickness'),
