(3549, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '888', 'Snow pressure'),
(3550, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '888', 'snow-pressure'),
