(3603, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '901', NULL),
(3604, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '901', 'Legally required regulations to be drawn up by a staff pension scheme that define the particulars of a company''s employee''s occupational pension provision (e.g. the amount of insurance contributions and benefits).'),
(3605, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '902', 'Staff pension scheme'),
(3606, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '902', 'staff-pension-scheme'),
(3607, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '902', NULL),
(3608, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '902', 'Organisation that an employer uses to deal with its employees'' occupational pensions.'),
(3609, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '903', 'Stamp duty (Securities turnover tax)'),
(3610, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '903', 'stamp-duty-securities-turnover-tax'),
(3611, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '903', NULL),
(3612, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '903', 'Subscriptions to <a href="investment-funds">investment funds</a> domiciled abroad are subject to stamp duty in Switzerland. <a href="redemption">Redemptions</a> are exempt from stamp duty. The <a href="issue">issue</a> and redemption of <a href="unit">units</a> of Swiss-based investment funds are not subject to stamp duty.'),
(3613, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '904', 'Stock exchange price'),
(3614, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '904', 'stock-exchange-price'),
(3615, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '904', NULL),
(3616, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '904', 'The price of <a href="unit">units</a> of <a href="investment-funds">investment funds</a> which are listed on the stock exchange or traded over the counter (real estate funds). The price is governed by supply and demand in the market, whereas the <a href="issue-price">issue</a> and <a href="redemption-price">redemption prices</a> of the units are determined by the <a href="fund-management-company">management company</a> on the basis of the <a href="net-asset-value">net asset value</a>.'),
(3617, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '905', 'Storm'),
(3618, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '905', 'storm-1'),
(3619, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '905', NULL),
(3620, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '905', 'In <a href="household-inventory-insurance">household inventory insurance</a>, a storm is defined when windspeed exceeds 75 km/hour and the wind uproots trees or blows roofs off buildings in the area around the insured object.'),
(3621, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '906', 'Straight bonds'),
(3622, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '906', 'straight-bonds'),
(3623, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '906', NULL),
(3624, 'en', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '906', '<a href="bonds">Bonds</a> with a fixed coupon and fixed redemption date.'),
