(4599, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '126', NULL),
(4600, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '126', 'Prestation de services garantissant aux assurés assistance et traitement rapide, compétent et non bureaucratique des cas de sinistre. La prise en charge du préjudice financier constitue le noyau de la prestation.'),
(4601, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '127', 'Taux des sinistres'),
