(5529, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '359', 'Fraction'),
(5530, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '359', 'fraction'),
(5531, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '359', NULL),
(5532, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '359', 'Dans le domaine des fonds de placement, il s''agit de la fraction d''une part de fonds.'),
(5533, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '360', 'Quote-part'),
