(5895, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '450', NULL),
(5896, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '450', 'Compte tenu par la Caisse de compensation pour chaque assuré sur lequel sont enregistrés tous ses revenus annuels.'),
(5897, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '451', 'Assurance individuelle de capital'),
(5898, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '451', 'assurance-individuelle-de-capital'),
(5899, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '451', NULL),
(5900, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '451', 'Assurance-vie selon le tarif individuel. Au cours de la <a href="duree-du-contrat-duree-contractuelle">durée du contrat</a>, la part d''épargne de la prime, y compris les intérêts, forme la <a href="reserve-mathematique">réserve mathématique</a> qui pourra être versée au plus tard à l''expiration du contrat.'),
(5901, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '452', 'Assurance individuelle (Ass. accidents privée)'),
(5902, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '452', 'assurance-individuelle-ass-accidents-privee'),
(5903, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '452', NULL),
(5904, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '452', 'Couverture d''une seule personne dans le cadre de l''assurance accidents privée.'),
(5905, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '453', 'Assurance individuelle (Assurance de choses)'),
(5906, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '453', 'assurance-individuelle-assurance-de-choses'),
(5907, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '453', NULL),
(5908, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '453', 'Couverture d''une seule chose (p. ex. un tableau ou une maison).'),
(5909, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '454', 'Assurance vie individuelle'),
(5910, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '454', 'assurance-vie-individuelle'),
(5911, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '454', NULL),
(5912, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '454', 'Assurance vie conclue pour une personne.'),
(5913, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '455', 'Police au voyage'),
(5914, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '455', 'police-au-voyage'),
(5915, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '455', NULL),
(5916, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '455', 'Type de contrat dans l''assurance transport dont la particularité est de n''assurer qu''un seul transport.'),
(5917, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '456', 'Risque/danger inhérent'),
(5918, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '456', 'risque-danger-inherent'),
(5919, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '456', NULL),
(5920, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '456', 'Le risque (danger) représenté par un véhicule automobile <a href="utilisation">en cours d''utilisation</a> provient, pour l''essentiel, de l''importance de la masse mise en mouvement par le moteur.'),
(5921, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '457', 'Portefeuille collectif interne de banque (PCIB)'),
(5922, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '457', 'portefeuille-collectif-interne-de-banque-pcib'),
(5923, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '457', NULL),
(5924, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '457', 'Patrimoine géré séparément par la banque, pour lequel il est interdit de faire de la publictié.'),
(5925, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '458', 'Lésé'),
(5926, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '458', 'lese'),
(5927, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '458', NULL),
(5928, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '458', 'Le lésé est un des partenaires du triangle <a href="assureur">assureur</a> - <a href="assure">assuré</a> - lésé. <br />Le lésé est celui qui a subi un <a href="dommages">dommage</a>.'),
(5929, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '459', 'Insolvabilité'),
(5930, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '459', 'insolvabilite'),
