(7067, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '743', NULL),
(7068, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '743', 'Partie de l''ordre juridique réglant les relations entre sujets de droit privé (citoyens, entreprises privées). En cas de violation. il appartient au lésé ou à la partie lésée d''intenter une action en justice.'),
(7069, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '744', 'Protection de droit privé'),
(7070, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '744', 'protection-de-droit-prive'),
(7071, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '744', NULL),
(7072, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '744', 'Domaine de l''assurance de protection juridique couvrant les litiges entre particuliers, à l''exception de ceux qui se rapportent à la circulation.'),
(7073, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '745', 'Prévoyance privée'),
(7074, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '745', 'prevoyance-privee'),
