(7076, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '745', 'Voir troisième pilier.'),
(7077, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '746', 'Assurance responsabilité civile privée'),
(7078, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '746', 'assurance-responsabilite-civile-privee'),
(7079, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '746', NULL),
(7080, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '746', 'L''assurance responsabilité civile privée protège l''<a href="assure">assuré</a> contre les prétentions que l''on fait valoir contre lui en vertu des dispositions légales en matière de responsabilité civile.<br />L''assuré est couvert en tant que personne privée, en tant que père de famille, propriétaire de chien, locataire d''appartement, soldat et pratiquant un sport, etc. Sont exclus les <a href="dommages">dommages</a> causés par l''assuré en tant que <a href="conducteur">conducteur</a> ou <a href="detenteur">détenteur</a> d''un <a href="vehicule-automobile">véhicule automobile</a> ou dans le cadre d''une activité professionnelle.'),
(7081, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '747', 'Usage privé'),
(7082, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '747', 'usage-prive'),
(7083, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '747', NULL),
(7084, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '747', 'Utilisation d''une chose dans un but privé.'),
(7085, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '748', 'Privilège en droit successoral'),
(7086, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '748', 'privilege-en-droit-successoral'),
(7087, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '748', NULL),
(7088, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '748', 'Voir Avantages en droit successoral.'),
(7089, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '749', 'Processus'),
(7090, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '749', 'processus'),
