(7092, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '749', 'Voir <a href="#invalid-reference-1158">processus de travail</a>.'),
(7093, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '750', 'Développement des produits'),
(7094, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '750', 'developpement-des-produits'),
(7095, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '750', NULL),
(7096, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '750', 'Partie du processus de prestation visant à développer les produits existants ou à en créer de nouveaux.'),
(7097, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '751', 'RC produits'),
(7098, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '751', 'rc-produits'),
(7099, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '751', NULL),
(7100, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '751', 'Responsabilité civile du fait des produits. Les dommages dus à un produit défectueux engage la responsabilité du fabricant, de l''importateur et du commerçant (dommage consécutif au défaut). Il s''agit d''une responsabilité extracontractuelle (responsabilité causale simple).'),
(7101, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '752', 'Risque du produit'),
(7102, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '752', 'risque-du-produit'),
(7103, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '752', NULL),
(7104, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '752', 'Risque couvert par l''assurance RC d''entreprise. Il s''agit de la responsabilité civile d''une entreprise envers les tiers pour les dommages consécutifs au défaut du produit, c.-à-d. qui sont la conséquence d''un vice de fabrication ou de la vente d''un produit défectueux.'),
(7105, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '753', 'Risque du procès'),
(7106, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '753', 'risque-du-proces'),
(7107, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '753', NULL),
(7108, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '753', 'Risque de devoir supporter les frais d''un litige porté devant les tribunaux. Les frais encourus par un assuré qui intente une action en justice pour faire valoir ses droits ou pour se défendre (frais d''avocat, de justice, indemnité pour frais) sont couverts par l''assurance de protection juridique.'),
(7109, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '754', 'Processus de prestation'),
(7110, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '754', 'processus-de-prestation'),
(7111, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '754', NULL);
INSERT INTO `contrexx_translations` (`id`, `locale`, `object_class`, `field`, `foreign_key`, `content`) VALUES
(7112, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '754', 'Succession des activités d''une entreprise directement liées au processus de développement des produits et des services.<br />Dans la branche de l''assurance privée, il s''agit surtout des processus suivants : développement des produits, conseil et vente, souscription et gestion des risques, gestion du portefeuille et trafic des paiements ainsi que service des sinistres.<br />'),
(7113, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '755', 'Assurance RC professionnelle'),
(7114, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '755', 'assurance-rc-professionnelle'),
(7115, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '755', NULL),
(7116, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '755', 'Assurance de responsabilité civile couvrant certaines catégories professionnelles (indépendants, professions libérales) qui nécessitent une protection particulière (p. ex. médecins, architectes , ingénieurs, avocats, agents fiduciaires).'),
(7117, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '756', 'Instruments professionnels'),
(7118, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '756', 'instruments-professionnels'),
(7119, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '756', NULL),
(7120, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '756', 'L''assurance de l''<a href="inventaire-du-menage">inventaire du ménage</a> couvre aussi fréquemment des objets appartenant aux personnes assurées qui les utilisent dans leur profession (outils, ordinateurs, vêtements professionnels, etc.).'),
(7121, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '757', 'Bénéfices'),
(7122, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '757', 'benefices'),
(7123, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '757', NULL),
(7124, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '757', 'Voir sous <a href="participation-aux-excedents">Participation aux excédents</a>'),
(7125, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '758', 'Interdiction de changements'),
(7126, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '758', 'interdiction-de-changements'),
(7127, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '758', NULL),
(7128, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '758', 'Tant que le dommage n''a pas été évalué, l''<a href="ayant-droit">ayant droit</a> défini dans le <a href="contrat-d-assurance">contrat d''assurance</a> ne doit, sans le consentement de l''<a href="assureur">assureur</a>, apporter aux choses endommagées aucun changement qui pourrait rendre plus difficile ou impossible la détermination du sinistre ou des causes de celui-ci (art. 68 <a href="lca-1">LCA</a>).'),
(7129, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '759', 'Encouragement à la propriété (PP)'),
(7130, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '759', 'encouragement-a-la-propriete-pp'),
(7131, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '759', NULL),
(7132, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '759', 'L''<a href="avoir-de-vieillesse">avoir de vieillesse</a> de la <a href="prevoyance-professionnelle">prévoyance professionnelle</a> peut être utilisé, sous réserve de certaines restrictions, pour financer l''acquisition d''un logement pour ses propres besoins.'),
(7133, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '760', 'Chose'),
(7134, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '760', 'chose'),
(7135, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '760', NULL),
(7136, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '760', 'Objet corporel transportable (<a href="#invalid-reference-1215">mobilier</a>) ou non transportable (<a href="#invalid-reference-1173">immobilier</a>).'),
(7137, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '761', 'Acte de défaut de biens'),
(7138, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '761', 'acte-de-defaut-de-biens'),
(7139, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '761', NULL),
(7140, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '761', 'Document officiel établi à l''intention du <a href="creancier">créancier</a> pour la partie de sa créance non couverte dans le cadre de la procédure de poursuite.'),
(7141, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '762', 'Proposition'),
(7142, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '762', 'proposition'),
(7143, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '762', NULL),
(7144, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '762', 'La proposition est une déclaration de volonté par laquelle le proposant s''engage de manière ferme à conclure un contrat, de sorte qu''il ne manque à sa conclusion plus que la déclaration réciproque de l''autre partie (acceptation). La proposition de conclure un <a href="contrat-d-assurance">contrat d''assurance</a> émane normalement de la personne intéressée par l''assurance (le futur <a href="preneur-d-assurance">preneur d''assurance</a>) et doit renfermer tous les points objectifs essentiels du contrat (risque assuré, objet assuré, <a href="prestations-d-assurance">prestation d''assurance</a>, <a href="prime">prime</a>, début et durée de l''assurance) ainsi que les éventuels autres points que l''une des parties a désignés comme essentiels. Elle est généralement établie sur un formulaire spécial, qui peut soit contenir directement les <a href="cga">CGA</a>, soit s''y référer. Dans le second cas, le texte des conditions générales d''assurance doivent être remises au proposant avant que la proposition ne soit transmise à l''assureur, faute de quoi celle-ci perd son caractère obligatoire. Le proposant reste lié par sa proposition pendant 14 jours à compter de la date d''expédition (si un examen médical est nécessaire, ce délai est de quatre semaines). Pendant ce laps de temps, <br />l''<a href="assureur">assureur</a> peut déclarer accepter la proposition, à la suite de quoi le contrat est réputé conclu. Si le proposant reçoit la déclaration d''<a href="acceptation">acceptation</a> (qui se fait d''ordinaire par la remise à l''intéressé de la <a href="police">police</a> ou de la facture pour la première prime) après le délai valable ou accompagnée de modifications de points essentiels du contrat, le document ainsi modifié constitue à son tour une proposition que soumet l''assureur au futur preneur d''assurance <br />et que ce dernier doit accepter pour que le contrat soit conclu.'),
(7145, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '763', 'Propulsion'),
(7146, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '763', 'propulsion'),
(7147, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '763', NULL),
(7148, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '763', 'Propulsion autonome (par moteur à explosion, électrique ou solaire) signifie que le <a href="vehicule-1">véhicule</a> se déplace par ses propres moyens et n''est pas tiré, poussé ou déplacé d''une manière quelconque par des moyens extérieurs ou étrangers humains, animaux, mécaniques ou naturels.'),
(7149, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '764', 'Prospectus / règlement du fonds'),
(7150, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '764', 'prospectus-reglement-du-fonds'),
(7151, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '764', NULL),
(7152, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '764', 'Le prospectus et le règlement du fonds définissent les droits et les devoirs des cocontractants Ils contiennent notamment les directives concernant la <a href="politique-de-placement">politique de placement</a>, l''affectation des produits et les coûts pouvant être imputés au fonds ou aux <a href="investisseur">investisseurs</a>.'),
(7153, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '765', 'Protection contre l''exploitation de l''infériorité'),
(7154, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '765', 'protection-contre-l-exploitation-de-l-inferiorite'),
(7155, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '765', NULL),
(7156, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '765', 'Protection des <a href="assure">assurés</a> contre le pouvoir des spécialistes tirant profit de leur inexpérience.<br />'),
(7157, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '766', 'Couverture provisoire du risque'),
(7158, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '766', 'couverture-provisoire-du-risque'),
(7159, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '766', NULL),
(7160, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '766', 'Il y a couverture provisoire du risque lorsque l''<a href="assureur">assureur</a> accorde une <a href="couverture-d-assurance">couverture d''assurance</a> avant d''avoir accepté la <a href="proposition">proposition</a>. En règle générale, l''étendue de la couverture provisoire correspond à celle définie dans la proposition. Elle est régulièrement limitée dans le temps et s''éteint généralement avec l''acceptation ou le refus de la proposition par l''assureur. Selon la doctrine dominante, la couverture provisoire du risque constitue un <a href="contrat-d-assurance">contrat d''assurance</a> indépendant qui n''est pas lié à une forme particulière.'),
(7161, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '767', 'Droit public'),
(7162, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '767', 'droit-public'),
(7163, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '767', NULL),
(7164, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '767', 'Partie du droit régissant les rapports entre les organes étatiques (les autorités) et les particuliers, sujets de droit (citoyens, entreprises privées).'),
(7165, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '768', 'Option put'),
(7166, 'fr', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '768', 'option-put'),
