(8948, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '189', 'Vi è diritto alla <a href="prestazioni-assicurate">prestazione assicurativa</a> soltanto se il sinistro è coperto o il rispettivo <a href="rischio">rischio</a> è assicurato.'),
(8949, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '190', 'Responsabilità causale aggravata'),
(8950, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '190', 'responsabilita-causale-aggravata'),
