(9907, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '429', NULL),
(9908, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '429', 'L''implosione è una violenta frantumazione di un corpo cavo in seguito a sovrappressione esterna (p. es. tubi catodici di televisori o di computer).'),
(9909, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '430', 'Anticipazione della prestazione'),
