(9919, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '432', NULL),
(9920, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '432', 'Un <a href="veicolo-a-motore">veicolo a motore</a> è in esercizio quando si trova in circolazione e/o fa funzionare le sue specifiche caratteristiche (motore, forza, massa, velocità ed eventualmente luci e rumore).'),
(9921, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '433', 'Incapacità al lavoro (incapacità lavorativa)'),
(9922, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '433', 'incapacita-al-lavoro-incapacita-lavorativa'),
(9923, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '433', NULL),
(9924, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '433', 'È considerata incapacità al lavoro qualsiasi incapacità, tota-le o parziale, derivante da un danno alla salute fisica, mentale o psichica, di compiere un lavoro ragionevolmente esigibile nella professione o nel campo d''attività di una persona. <br /><a href="incapacita-al-lavoro-incapacita-lavorativa">L''incapacità al lavoro</a> (lavorativa) e l''<a href="incapacita-di-guadagno">incapacità al guadagno (lucrativa)</a> non sono la stessa cosa.<br />'),
(9925, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '434', 'Inizio dell''assicurazione'),
(9926, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '434', 'inizio-dell-assicurazione'),
(9927, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '434', NULL),
(9928, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '434', 'Momento in cui ha inizio la <a href="copertura-assicurativa">copertura assicurativa</a>. Tale data può coincidere con l''inizio del contratto oppure, se concordato, essere posticipata.'),
(9929, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '435', 'Redditi'),
