(9935, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '436', NULL),
(9936, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '436', 'Immobile eretto o acquistato non per scopi abitativi privati, bensì come forma di investimento di capitale.'),
