(10015, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '456', NULL),
(10016, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '456', 'Il rischio d''<a href="esercizio">esercizio</a> di un veicolo a motore consiste fondamentalmente nel fatto che il motore rende possibile lo spostamento di una grossa massa.'),
(10017, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '457', 'Portafoglio collettivo interno della banca'),
(10018, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '457', 'portafoglio-collettivo-interno-della-banca'),
(10019, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '457', NULL),
(10020, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '457', 'Portafoglio d''investimento interno alla banca e simile a un <a href="fondo-d-investimento">fondo</a>, le cui quote sono offerte esclusivamente ai clienti del servizio di gestione patrimoniale della banca. Per questo non possono essere rivolti inviti al pubblico.'),
(10021, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '458', 'Parte lesa'),
(10022, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '458', 'parte-lesa'),
(10023, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '458', NULL),
(10024, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '458', 'La parte lesa è un elemento del triangolo <a href="assicuratore">assicuratore</a> - <a href="assicurato">assicurato</a> - parte lesa.<br />Per parte lesa si intende colui che ha subìto un <a href="danno">danno</a>.'),
(10025, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '459', 'Insolvenza'),
