(10272, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '520', 'Emissione di nuovi titoli.'),
(10273, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '521', 'Prezzo di emissione'),
(10274, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '521', 'prezzo-di-emissione'),
(10275, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '521', NULL),
(10276, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '521', 'Prezzo al quale gli <a href="investitore">investitori</a> possono sottoscrivere le quote di un fondo; equivale al <a href="valore-netto-d-inventario">valore netto d''inventario</a> di una <a href="quota">quota</a> maggiorato della <a href="commissione-di-emissione">commissione di emissione</a>.'),
(10277, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '522', 'Emittente'),
(10278, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '522', 'emittente'),
(10279, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '522', NULL),
(10280, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '522', 'Persona giuridica o ente di diritto pubblico incaricato di raccogliere capitale di terzi tramite l''emissione di titoli.'),
(10281, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '523', 'Commissione di emissione'),
