(10284, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '523', 'Commissione prelevata dalla società di gestione emittente all''acquirente di <a href="quota">quote</a> di un fondo all''atto della sottoscrizione.'),
(10285, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '524', 'AI'),
(10286, 'it', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '524', 'ai-1'),
