INSERT INTO `contrexx_translations` (`id`, `locale`, `object_class`, `field`, `foreign_key`, `content`) VALUES
(12432, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '36', 'Ausnützung örtlicher oder internationaler Bewertungsunterschiede gleicher Wertpapiere oder verwandter Werte, wobei diese auf dem Markt mit dem niedrigeren Preis gekauft und am Markt mit dem höheren Preis verkauft werden.'),
(12433, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '37', 'Architekt'),
(12434, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '37', 'architekt'),
(12435, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '37', NULL),
(12436, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '37', 'Planer und Zeichner eines entstehenden Bauwerks (meist im Hochbau).'),
(12437, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '38', 'Verzug'),
(12438, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '38', 'verzug'),
(12439, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '38', NULL),
(12440, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '38', 'Der Zustand, in welchem sich der säumige Schuldner nach erfolgter <a href="mahnung">Mahnung</a> und unbenutzter Nachfrist befindet.'),
(12441, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '39', 'Asset Allocation'),
(12442, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '39', 'asset-allocation-1'),
(12443, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '39', NULL),
(12444, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '39', 'Je nach Fonds die Verteilung der Anlagen auf die verschiedenen <a href="anlageinstrumente">Anlageinstrumente</a> (Geldmarktpapiere, Obligationen, Aktien und Immobilien), Branchen, Märkte und Währungen.<br />\r<br />Strukturierung eines <a href="portefeuille">Portfolios</a> nach Kriterien wie Währung, Anlageinstrumente oder Strategie.'),
(12445, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '40', 'Anlagestrategiefonds'),
(12446, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '40', 'anlagestrategiefonds'),
(12447, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '40', NULL),
(12448, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '40', '<a href="anlagefonds">Anlagefonds</a>, welche die offiziellen Anlagestrategien der jeweiligen Anbieter für die verschiedenen Risikoklassen nachbilden. Anlagestrategiefonds investieren weltweit in verschiedenen <a href="anlageinstrumente">Anlageinstrumenten</a>, je nach Risikoklasse werden <a href="aktie">Aktien</a> beziehungsweise <a href="obligationen">Obligationen</a> übergewichtet. Anlagestrategiefonds werden auch Asset Allocation Funds, Strategy Funds, Portfoliofonds, Anlagezielfonds, Vermögensverwaltungsfonds oder gemischte Fonds genannt.'),
(12449, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '41', 'Inventarwert'),
(12450, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '41', 'inventarwert'),
(12451, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '41', NULL),
(12452, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '41', 'Das <a href="nettovermogen-immobilienfonds">Nettovermögen</a> eines Fonds geteilt durch die Anzahl der sich im Umlauf befindlichen <a href="anteil">Anteile</a>.'),
(12453, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '42', 'Aktiven'),
(12454, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '42', 'aktiven'),
(12455, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '42', NULL),
(12456, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '42', 'In der <a href="bilanz">Bilanz</a> auf der linken Seite aufgeführte Guthaben oder Vermögenswerte einer Unternehmung. Gegenstück <a href="passiven">Passiven</a>.'),
(12457, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '43', 'Anrechnungswert'),
(12458, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '43', 'anrechnungswert'),
(12459, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '43', NULL),
(12460, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '43', '¶"Innerer Wert" einer Police an einem bestimmten Stichtag nach Abzug der für das <a href="risiko">Risiko</a> und Verwaltung getragenen <a href="kosten">Kosten.</a>'),
(12461, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '44', 'Abtretung'),
(12462, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '44', 'abtretung'),
(12463, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '44', NULL),
(12464, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '44', 'Auch Zession genannt. Die Abtretung ist ein (schriftlicher) Vertrag zwischen altem und neuem <a href="glaubiger">Gläubiger</a>, die, wenn sie wirksam ist, zum Gläubigerwechsel führt. An Stelle des bisherigen Gläubigers tritt ein anderer. Es kann auch von einem Wechsel der Versicherungsnehmerschaft gesprochen werden. Bei einer Abtretung gehen die Rechte und Pflichten an den neuen Versicherungsnehmer (Dritten) über.'),
(12465, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '45', 'Kontrollstelle'),
(12466, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '45', 'kontrollstelle'),
(12467, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '45', NULL),
(12468, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '45', 'Organ, welches die jährliche Prüfung der Geschäftsführung, des Rechnungswesens und der Vermögensanlage einer Vorsorgeeinrichtung prüft; sie muss unabhängig von der <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> und dem Arbeitgeber sein.'),
(12469, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '46', 'Revisionsstelle'),
(12470, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '46', 'revisionsstelle'),
(12471, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '46', NULL),
(12472, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '46', 'Von der <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> und der <a href="depotbank">Depotbank</a> unabhängige Instanz. Sie überprüft regelmässig die Einhaltung der gesetzlichen Vorschriften. Revisionsstellen müssen durch die <a href="aufsichtsbehorde-fonds">Aufsichtsbehörde</a>, in der Schweiz die <a href="#invalid-reference-533">Eidgenössische Bankenkommission</a>, anerkannt sein.'),
(12473, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '47', 'Anerkannter Experte'),
(12474, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '47', 'anerkannter-experte'),
(12475, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '47', NULL),
(12476, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '47', 'Person, die das Eidg. Diplom als Pensionsversicherungsexperte besitzt oder durch das <a href="bundesamt-fur-sozialversicherung">Bundesamt für Sozialversicherung</a> (BSV) als solcher anerkannt worden ist sowie eine juristische Person, die solche Experten beschäftigt. Der Experte prüft die Sicherheit der <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> insbesondere bezüglich der Leistungen und deren Finanzierung.'),
(12477, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '48', 'Automatische Summenanpassung'),
(12478, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '48', 'automatische-summenanpassung'),
(12479, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '48', NULL),
(12480, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '48', 'Die automatische Summenanpassung bezieht die Preissteigerung in die <a href="versicherungssumme">Versicherungssumme</a> und die <a href="pramie">Prämie</a> mit ein. Sie orientiert sich an der  Entwicklung des <a href="hausratindex">Hausratindexes</a> und passt sich jährlich an die Preisentwicklung an. Die automatische Summenanpassung wird dem Kunden als Schutz gegen <a href="unterversicherung-uv">Unterversicherung</a> empfohlen.'),
(12481, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '49', 'Hilfspersonenhaftung'),
(12482, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '49', 'hilfspersonenhaftung'),
(12483, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '49', NULL),
(12484, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '49', '<a href="kausalhaftung">Kausalhaftung</a> des Unternehmers / des Unternehmens für den <a href="schaden">Schaden</a>, die Hilfspersonen (Mitarbeitende) bei der Erfüllung vertraglicher Pflichten beim Vertragspartner anrichten. Es handelt sich um eine <a href="vertragliche-haftpflicht">vertragliche Haftpflicht</a>.'),
(12485, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '50', 'AVIG'),
(12486, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '50', 'avig'),
(12487, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '50', NULL),
(12488, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '50', 'Abkürzung für "Bundesgesetz über die obligatorische <a href="arbeitslosen-und-insolvenzversicherung">Arbeitslosenversicherung und die Insolvenzentschädigung"</a>vom 25. Juni 1982 (SR 837.1).'),
(12489, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '51', 'Innendienst'),
(12490, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '51', 'innendienst'),
(12491, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '51', NULL),
(12492, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '51', 'Aufgaben im Leistungsprozess, die nicht vom <a href="aussendienst">Aussendienst</a> wahrgenommen werden (z.B. Produktentwicklung, Underwriting / <a href="riskmanagement-rm">Riskmanagement</a>, Bestandsverwaltung, Zahlungsverkehr, <a href="schadendienst">Schadendienst</a>), sowie die Unterstützungsprozesse (z.B. Rechnungswesen / Controlling, Personalwesen, Ausbildung, IT-Services)'),
(12493, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '52', 'Schlechtwetterentschädigung'),
(12494, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '52', 'schlechtwetterentschadigung'),
(12495, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '52', NULL),
(12496, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '52', 'Leistung der Arbeitslosenversicherung (<a href="arbeitslosen-und-insolvenzversicherung">ALV</a>) bei Reduktion des Erwerbseinkommens wegen schlechten Wetters für bestimmte Branchen (z. B. für das Baugewerbe).'),
(12497, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '53', 'Entreissdiebstahl'),
(12498, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '53', 'entreissdiebstahl'),
(12499, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '53', NULL),
(12500, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '53', 'Entreissdiebstahl liegt vor, wenn der Täter dem Opfer Sachen, die dieses auf sich trägt oder in der Hand hält, gewaltsam entreisst. Entreissdiebstahl kann entweder als <a href="beraubung">Beraubung</a> (Anwendung von Gewalt), als <a href="einfacher-diebstahl">einfacher Diebstahl</a>oder als separates <a href="risiko">Risiko</a> definiert werden.'),
(12501, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '54', 'Bilanz'),
(12502, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '54', 'bilanz'),
(12503, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '54', NULL),
(12504, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '54', 'Periodische Gegenüberstellung sämtlicher <a href="aktiven">Aktiven</a> und <a href="passiven">Passiven</a> auf ein bestimmtes Datum hin (meist der 31.12.). Die Bilanz ist stets ausgeglichen, d.h. die Summe der Aktiven entspricht immer der Summe der Passiven. Die Erstellung einer Jahresbilanz ist für gewisse Gesellschaftsformen im Schweizerischen Obligationenrecht (OR) zwingend vorgeschrieben. Für Banken bestehen darüber hinaus noch besonders weitgehende Vorschriften, die im Bankengesetz geregelt sind.'),
(12505, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '55', 'Bankenerklärung (Affidavit)'),
(12506, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '55', 'bankenerklarung-affidavit'),
(12507, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '55', NULL),
(12508, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '55', 'Bei <a href="anlagefonds">Anlagefonds</a> schweizerischen Rechts wird bei Ertragsausschüttungen (Zinsen/<a href="dividende">Dividenden</a>) grundsätzlich die eidgenössische <a href="verrechnungssteuer-fonds">Verrechnungssteuer</a> von 35% abgezogen. Für <a href="anleger">Anleger</a> mit Domizil im Ausland werden die Ertragssausschüttungen ohne Verrechnungssteuerabzug vorgenommen, sofern die ausgeschütteten Erträge zu mindestens 80% aus dem Ausland stammen und eine Bank bestätigt (Bankenerklärung), dass die <a href="anteil">Anteile</a> bei ihr im Depot liegen.'),
(12509, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '56', 'Kassenobligation'),
(12510, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '56', 'kassenobligation'),
(12511, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '56', NULL),
(12512, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '56', 'Je nach Bedarf laufend zur Ausgabe gelangende mittelfristige Schuldverschreibungen der Banken, in runden Beträgen, meistens mit einer Laufzeit von zwei bist acht Jahren.'),
(12513, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '57', 'Konkurs'),
(12514, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '57', 'konkurs'),
(12515, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '57', NULL),
(12516, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '57', 'Zwangsmässige Totalliquidation des Vermögens des Schuldners zur Befriedigung der Forderungen der <a href="glaubiger">Gläubiger</a>.'),
(12517, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '58', 'Inhaberaktie'),
(12518, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '58', 'inhaberaktie'),
(12519, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '58', NULL),
(12520, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '58', 'Aktie, die auf den (nicht namentlich bezeichneten) Inhaber lautet. Gegensatz: <a href="namenaktie">Namenaktie</a>'),
(12521, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '59', 'Benchmark'),
(12522, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '59', 'benchmark'),
(12523, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '59', NULL),
(12524, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '59', '<a href="index-fonds">Index</a>, der einem <a href="anlagefonds">Anlagefonds</a> als Vergleichsbasis für die <a href="performance">Performance</a> dient. Auch Referenzindex oder Vergleichsindex genannt.'),
(12525, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '60', 'Begünstigte/r'),
(12526, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '60', 'begunstigte-r'),
(12527, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '60', NULL),
(12528, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '60', 'Der Begünstigte, der sowohl eine <a href="naturliche-person">natürliche</a> als auch eine <a href="juristische-person">juristische Person</a> sein kann, hat einen anwartschaftlichen Anspruch auf die <a href="versicherungsleistungen">Versicherungsleistung</a>. Er wird vom <a href="versicherungsnehmer-in">Versicherungsnehmer</a> bestimmt und kann entweder für die ganze oder anteilige <a href="versicherungssumme">Versicherungssumme</a> begünstigt werden.'),
(12529, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '61', 'Begünstigung'),
(12530, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '61', 'begunstigung'),
(12531, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '61', NULL),
(12532, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '61', 'Der Versicherungsnehmer kann in einer sogenannter Begünstigung bestimmen, wer nach Eintritt des versicherten Ereignisses in den Genuss der ganzen oder teilweisen Versicherungsleistung gelangen soll. Eine Begünstigung im Versicherungsvertrag ist also eine Verfügung über die Versicherungsleistung. Eine Begünstigung ist nur in der <a href="personenversicherung">Personenversicherung</a> möglich. Die Begünstigung ist grundsätzlich unabhängig vom Erbrecht. Der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> ist befugt, einen <a href="dritte">Dritten</a> als Begünstigten zu bezeichnen (Art. 76 Abs. 1 <a href="vvg-1">VVG</a>). Der Begünstigte wird dadurch aber keineswegs Vertragspartei. Er schuldet deshalb auch keine <a href="pramie">Prämien</a>, hat aber im <a href="versicherungsfall">Versicherungsfall</a> verschiedene Obliegenheiten zu erfüllen, da er Anspruchsberechtigter ist. Der Versicherungsnehmer kann die Begünstigung, die er ausgesprochen hat, jederzeit widerrufen, sofern er auf das Widerrufsrecht nicht verzichtet hat (Art. 77 Abs. 2 VVG). Begünstigt werden können sowohl <a href="naturliche-person">natürliche</a> wie <a href="juristische-person">juristische Personen</a> oder beliebige Institutionen. Die Begünstigung wird normalerweise bereits im <a href="antrag">Antrag</a> festgelegt und von dort in die <a href="police">Police</a> übernommen. Oft enthalten auch die <a href="allgemeine-versicherungsbedingungen">Allgemeinen Versicherungsbedingungen</a> eine Begünstigungsregel, die gilt, falls nichts anderes bestimmt wird. Besteht keine Begünstigung, so fällt beim Tod des Versicherten die <a href="versicherungsleistungen">Versicherungsleistung</a> in die Erbmasse. Da die Begünstigung entscheidend ist für die Leistung im Ereignisfall und zu den besonderen rechtlichen Privilegien der Lebensversicherung im Bereich der Familien-<a href="vorsorge">Vorsorge</a> gehört, lohnt es sich, diese mit dem Lebensversicherungsfachmann offen und gründlich zu besprechen und von Zeit zu Zeit zu überprüfen, ob sie noch den Vorstellungen und Bedürfnissen im gegenwärtigen Zeitpunkt entspricht. In der beruflichen Vorsorge und in der gebundenen Selbstvorsorge wie auch bei <a href="freizugigkeitspolice">Freizügigkeitspolicen</a> ist die Begünstigung gesetzlich nur beschränkt möglich.'),
(12533, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '62', 'Erbvorteile'),
(12534, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '62', 'erbvorteile'),
(12535, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '62', NULL),
(12536, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '62', 'Bei Tod einer<a href="versicherte-person">versicherten Person</a> fallen die <a href="versicherungsleistungen">Versicherungsleistungen</a>nicht in die Erbmasse, sondern grundsätzlich dem oder den <a href="begunstigte-r">Begünstigten</a> zu. Vorbehalten bleibt die Herabsetzungsklage bei Pflichtteilsverletzung. In diesem Fall wird der im Zeitpunkt des Todes bestehende <a href="ruckkaufswert">Rückkaufswert</a>zur Berechnung der <a href="pflichtteil">Pflichtteile</a> herangezogen. Sind erbberechtigte Nachkommen, ein Ehegatte, Eltern, Grosseltern oder Geschwister die Begünstigten, haben sie Anspruch auf die <a href="versicherungsleistungen">Versicherungsleistungen</a>, auch wenn sie die Erbschaft, z.B. weil diese überschuldet ist, ausschlagen.'),
(12537, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '63', 'Best-of-breed'),
(12538, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '63', 'best-of-breed'),
(12539, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '63', NULL),
(12540, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '63', 'Fachlich ausgewiesene Investoren im <a href="private-equity-1">Private-Equity</a> und <a href="hedge-funds-1">Hedge-Fund</a>-Bereich.'),
(12541, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '64', 'Beta'),
(12542, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '64', 'beta-1'),
(12543, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '64', NULL),
(12544, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '64', 'Ein Risikomass, das angibt, wie sensitiv eine Anlage, z.B. ein Fonds, auf Marktbewegungen reagiert, die durch die jeweilige <a href="benchmark">Benchmark</a> repräsentiert werden. Ein Beta von 1,2 besagt beispielsweise, dass man beim Fonds eine Wertveränderung von 12% erwarten kann, wenn man für den Markt eine Veränderung von 10% prognostiziert. Der Zusammenhang beruht auf historischen Beobachtungen und gilt nur approximativ. Er ist umso enger, je höher die <a href="korrelation">Korrelation</a> zwischen der Benchmark und dem <a href="anlagefonds">Anlagefonds</a> ist.'),
(12545, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '65', 'Sperrtage'),
(12546, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '65', 'sperrtage'),
(12547, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '65', NULL),
(12548, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '65', 'Die Versicherungsleistung der <a href="arbeitslosenentschadigung">Arbeitslosenentschädigung</a> besteht aus Taggeldern. Der Anspruch auf Arbeitslosentaggeld kann bei einem Verschulden des Versicherten sistiert werden. Je nach Schwere des Verschuldens können die Behörden des Arbeitsamts bis zu 60 Sperrtage aussprechen.'),
(12549, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '66', 'Prämiensperrdepot'),
(12550, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '66', 'pramiensperrdepot'),
(12551, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '66', NULL),
(12552, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '66', 'siehe <a href="pramiendepot-oder-pramienkonto">Prämiendepot</a>'),
(12553, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '67', 'Blue Chips'),
(12554, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '67', 'blue-chips-1'),
(12555, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '67', NULL),
(12556, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '67', 'Bezeichnung für <a href="aktie">Aktien</a> führender Unternehmen mit erstklassiger <a href="bonitat-fonds">Bonität</a>, hoher <a href="borsenkapitalisierung">Börsenkapitalisierung</a>, hoher Ertragskraft und solider Finanzstruktur.'),
(12557, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '68', 'Stiftungsrat'),
(12558, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '68', 'stiftungsrat'),
(12559, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '68', NULL),
(12560, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '68', 'Oberstes Verwaltungsorgan der Stiftung. Im Rahmen von Gesetz und Stiftungsurkunde entscheidet er frei und endgültig. Er trägt auch die Verantwortung für die Stiftung, insbesondere für die Anlage des Stiftungsvermögens.'),
(12561, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '69', 'Unfallähnliche Körperschädigung (UV)'),
(12562, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '69', 'unfallahnliche-korperschadigung-uv'),
(12563, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '69', NULL),
(12564, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '69', 'Körperlicher Schaden, der nicht auf einen <a href="unfall">Unfall</a> (im Sinne von: ungewöhnliche Einwirkung eines äusseren Faktors) zurückzuführen ist, aber durch die <a href="obligatorische-unfallversicherung">obligatorische Unfallversicherung</a>nach UVG versichert ist.'),
(12565, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '70', 'Obligationenfonds'),
(12566, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '70', 'obligationenfonds'),
(12567, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '70', NULL),
(12568, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '70', '<a href="anlagefonds">Anlagefonds</a>, die in <a href="obligationen">Obligationen</a> und anderen fest- oder variabel verzinslichen Papiere investieren. Obligationenfonds sind in der Regel auf eine bestimmte Referenz- und Anlagewährung ausgerichtet. Auch Bond Funds genannt.'),
(12569, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '71', 'Obligationen'),
(12570, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '71', 'obligationen'),
(12571, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '71', NULL),
(12572, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '71', 'Schuldverpflichtungen mit festem, gelegentlich auch variablem Zinssatz und in der Regel festem Fälligkeits- und Rückzahlungstermin. Synonym für Anleihen, Renten, Bonds und Schuldverschreibungen. Häufigste <a href="emittent">Emittenten</a> sind grosse Unternehmen, staatliche Organisationen wie Bund und Kantone, öffentlich-rechtliche Institutionen sowie internationale Organisationen wie die Weltbank oder der Internationale Währungsfonds.'),
(12573, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '72', 'Not leidende Obligationen'),
(12574, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '72', 'not-leidende-obligationen'),
(12575, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '72', NULL),
(12576, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '72', '<a href="obligationen">Obligationen</a>, deren Zinszahlungen oder Rückzahlungen nicht fristgerecht erfolgen.'),
(12577, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '73', 'Bonus'),
(12578, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '73', 'bonus'),
(12579, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '73', NULL),
(12580, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '73', 'Bonus ist eine Form von <a href="uberschussbeteiligung">Überschussbeteiligung</a>; die Überschussanteile werden beim Bonussystem zur Erhöhung der <a href="versicherungsleistungen">Versicherungsleistung</a> verwendet.'),
(12581, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '74', 'Rentenbonus'),
(12582, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '74', 'rentenbonus'),
(12583, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '74', NULL),
(12584, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '74', 'Bei aufgeschobenen Renten sammelt sich während der Aufschubszeit nach dem ersten Versicherungsjahr der sogenannte Rentenbonus an, der dann (nach der Aufschubszeit) in Form einer Bonusrente (zusammen mit der Grundrente und der Überschussrente) ausbezahlt wird.'),
(12585, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '75', 'Bonussystem'),
(12586, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '75', 'bonussystem'),
(12587, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '75', NULL),
(12588, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '75', 'Das Bonussystem ist vor allem in der <a href="motorfahrzeug-haftpflichtversicherung">Motorfahrzeug-Haftpflichtversicherung</a> sowie in der <a href="motorfahrzeug-kaskoversicherung">Motorfahrzeug-Vollkaskoversicherung</a> bekannt. <br />Dieses System erlaubt es, die <a href="pramie">Prämie</a> zu reduzieren. Wird die <a href="police">Police</a> durch keinen Schadenfall belastet, so wird dem Halter ein <a href="bonus">Bonus</a> (Rabatt) gewährt. <br />Schon nach einem unfallfreien Jahr setzt das Bonussystem ein.'),
(12589, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '76', 'Buchgewinn / Buchverlust'),
(12590, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '76', 'buchgewinn-buchverlust'),
(12591, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '76', NULL),
(12592, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '76', 'Der theoretische Gewinn oder Verlust auf einer Kapitalanlage auf Grund ihrer Wertsteigerung bzw. ihres Wertverlustes solange die Anlage nicht verkauft wird.'),
(12593, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '77', 'Fremdfinanzierung'),
(12594, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '77', 'fremdfinanzierung'),
(12595, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '77', NULL),
(12596, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '77', 'Entspricht den Verbindlichkeiten des <a href="anlagefonds">Fonds</a> vor Liquidationssteuern in Prozent des Gesamtfondsvermögens.'),
(12597, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '78', 'Anleihen'),
(12598, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '78', 'anleihen'),
(12599, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '78', NULL),
(12600, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '78', 'Siehe <a href="obligationen">Obligationen</a>.'),
(12601, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '79', 'Branche'),
(12602, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '79', 'branche'),
(12603, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '79', NULL),
(12604, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '79', 'Als Branche werden die Versicherungszweige bezeichnet, die je ein spezielles <a href="risiko">Risiko</a> einschliessen, wie z. B. die Unfallversicherung oder in der Hausratversicherung die Branchen <a href="feuer">Feuer</a>, <a href="wasser-schaden">Wasser</a>, <a href="diebstahl">Diebstahl</a>, <a href="glasbruchversicherung">Glasbruch.</a>'),
(12605, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '80', 'Makler'),
(12606, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '80', 'makler'),
(12607, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '80', NULL),
(12608, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '80', 'Unabhängiger Partner und Bestandteil des indirekten <a href="absatzkanal">Absatzkanals</a>. Er ist tätig im Auftrag von Kunden und erhält als Gegenleistung vom <a href="versicherer">Versicherer</a> eine Provision auf Abschlüsse und/oder die Dauer des <a href="versicherung">Versicherung</a>svertrags. Einzelne Makler arbeiten auf Honorarbasis, d.h. sie werden vom Kunden direkt entschädigt.'),
(12609, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '81', 'Courtage'),
(12610, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '81', 'courtage'),
(12611, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '81', NULL),
(12612, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '81', 'Entschädigung der Börsenagenten für den Kauf oder Verkauf von Wertpapieren an der Börse. Die Courtagen sind nicht mehr einheitlich geregelt; jede Bank ist grundsätzlich bei deren Festlegung frei.'),
(12613, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '82', 'BSV'),
(12614, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '82', 'bsv'),
(12615, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '82', NULL),
(12616, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '82', 'Abkürzung für "<a href="bundesamt-fur-sozialversicherung">Bundesamt für Sozialversicherung</a>".'),
(12617, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '83', 'Gebäude'),
(12618, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '83', 'gebaude'),
(12619, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '83', NULL),
(12620, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '83', 'Unbewegliches Gebilde mit Dach und benutzbarem Raum, das einer Bautätigkeit entspringt und dauerhaft eingerichtet wird.'),
(12621, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '84', 'Bauliche Einrichtung'),
(12622, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '84', 'bauliche-einrichtung'),
(12623, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '84', NULL),
(12624, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '84', 'Bauelemente, die mit einem <a href="gebaude">Gebäude</a> weniger eng verbunden sind als die <a href="bestandteil">Bestandteile</a> und deshalb ohne Beschädigung des Gebäudes entfernt werden können (z. B. Fenster, Türen, Toilette).'),
(12625, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '85', 'Gebäudeversicherungsanstalt'),
(12626, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '85', 'gebaudeversicherungsanstalt'),
(12627, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '85', NULL),
(12628, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '85', 'Siehe <a href="kantonale-gebaudeversicherung">Kantonale Gebäudeversicherung</a>'),
(12629, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '86', 'Gebäudehaftpflichtversicherung'),
(12630, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '86', 'gebaudehaftpflichtversicherung'),
(12631, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '86', NULL),
(12632, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '86', '<a href="vermogensversicherung">Vermögensversicherung</a>. Schützt den Eigentümer eines Gebäudes vor berechtigten Schadenersatzansprüchen von Dritten, die durch das Gebäude geschädigt werden (v.a. <a href="werkeigentumerhaftpflicht">Werkeigentümerhaftpflicht</a>).'),
(12633, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '87', 'Gebäudesachversicherung'),
(12634, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '87', 'gebaudesachversicherung'),
(12635, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '87', NULL),
(12636, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '87', 'Siehe <a href="gebaudeversicherung">Gebäudeversicherung</a>.'),
(12637, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '88', 'Beweislast'),
(12638, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '88', 'beweislast'),
(12639, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '88', NULL),
(12640, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '88', 'Wo das Gesetz es nicht anders bestimmt, hat derjenige das Vorhandensein einer behaupteten Tatsache zu beweisen, der aus ihr Rechte ableitet; er hat die Beweislast.'),
(12641, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '89', 'Betriebsunterbrechungsversicherung'),
(12642, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '89', 'betriebsunterbrechungsversicherung'),
(12643, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '89', NULL),
(12644, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '89', 'Die Betriebsunterbrechungsversicherung deckt Ertragsausfälle eines Betriebes, welche durch einen versicherten Schaden (Feuer, Wasser, Einbruch) entstehen. Es handelt sich eigentlich um eine <a href="vermogensversicherung">Vermögensversicherung</a>, sie wird aber in der Regel mit einer Geschäfts-Sachversicherung kombiniert.'),
(12645, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '90', 'Geschäftherrenhaftpflicht'),
(12646, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '90', 'geschaftherrenhaftpflicht'),
(12647, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '90', NULL),
(12648, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '90', '<a href="kausalhaftung">Kausalhaftung</a> des Unternehmers / des Unternehmens für den <a href="schaden">Schaden</a>, den die Arbeitnehmenden oder andere Hilfspersonen in Ausübung ihrer Tätigkeit bei Drittpersonen anrichten. Es handelt sich um eine <a href="ausservertragliche-haftpflicht">ausservertragliche Haftpflicht</a> (<a href="milde-kausalhaftung">milde</a> <a href="kausalhaftung">Kausalhaftung</a>).'),
(12649, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '91', 'Geschäftsplan'),
(12650, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '91', 'geschaftsplan'),
(12651, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '91', NULL),
(12652, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '91', 'Gibt Auskunft über die geplanten Aktivitäten des <a href="versicherer">Versicherers</a>. Muss als Bestandteil des Verfahrens zur Erteilung der <a href="bewilligung">Bewilligung</a> beim <a href="efd">EFD</a> eingereicht werden.'),
(12653, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '92', 'Geschäftsprozess'),
(12654, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '92', 'geschaftsprozess'),
(12655, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '92', NULL),
(12656, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '92', 'Geschäftsprozesse sind Arbeitsabläufe oder Abfolgen von Tätigkeiten, die notwendig sind, um ein bestimmtes Resultat zu erreichen. Man unterscheidet <a href="leistungsprozess">Leistungs</a>- und <a href="unterstutzungsprozess">Unterstützungsprozesse</a>.'),
(12657, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '93', 'Betriebshaftpflichtversicherung'),
(12658, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '93', 'betriebshaftpflichtversicherung'),
(12659, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '93', NULL),
(12660, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '93', 'Versichert ist die <a href="haftpflicht">Haftpflicht</a>, die sich aus dem Betrieb eines Unternehmens aufgrund rechtlicher Bestimmungen ergibt (Anlagenrisiko, Betriebsrisiko, Produktrisiko).<br />Versichert sind Betriebsinhaber und deren Mitarbeiter.'),
(12661, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '94', 'Geschäftsjahr'),
(12662, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '94', 'geschaftsjahr'),
(12663, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '94', NULL),
(12664, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '94', 'Siehe <a href="rechnungsjahr">Rechnungsjahr</a>.'),
(12665, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '95', 'Buy-out'),
(12666, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '95', 'buy-out'),
(12667, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '95', NULL),
(12668, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '95', 'Damit werden Übernahmen von Unternehmen bezeichnet. Die bekannten Formen sind das Management Buy-Out (MBO), die Firmen- oder Bereichsübernahme durch das Management und das Leveraged Buy-Out (LBO), bei dem neue Investoren die Firmenmehrheit unter Verwendung von Fremdkapital übernehmen. Buy-Outs werden oft auch für Nachfolgeregelungen in Familienunternehmen eingesetzt.'),
(12669, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '96', 'BV'),
(12670, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '96', 'bv-1'),
(12671, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '96', NULL),
(12672, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '96', 'Abkürzung für die Bundesverfassung.'),
(12673, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '97', 'BVG'),
(12674, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '97', 'bvg-1'),
(12675, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '97', NULL),
(12676, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '97', 'Abkürzung für  "Bundesgesetz über die berufliche Alters-, Hinterlassenen- und Invalidenvorsorge" vom 25. Juni 1982). Siehe auch <a href="berufliche-vorsorge">berufliche Vorsorge.</a>'),
(12677, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '98', 'Call-Optionen'),
