INSERT INTO `contrexx_translations` (`id`, `locale`, `object_class`, `field`, `foreign_key`, `content`) VALUES
(12747, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '115', NULL),
(12748, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '115', 'Kausalhaftung heisst, dass eine <a href="haftung">Haftung</a> auch ohne <a href="verschulden">Verschulden</a> besteht.<br />Das Haftpflichtrecht unterscheidet die milde (einfache) Kausalhaftung von der scharfen Kausalhaftung (Gefährdungshaftung).'),
(12749, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '116', 'Kausalzusammenhang'),
(12750, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '116', 'kausalzusammenhang'),
(12751, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '116', NULL),
(12752, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '116', 'Siehe <a href="adaquater-kausalzusammenhang">adäquater Kausalzusammenhang</a>.'),
(12753, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '117', 'ZGB'),
(12754, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '117', 'zgb'),
