(12756, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '117', 'Abkürzung für "Zivilgesetzbuch".'),
(12757, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '118', 'Zentrale Ausgleichsstelle AHV'),
(12758, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '118', 'zentrale-ausgleichsstelle-ahv'),
(12759, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '118', NULL),
(12760, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '118', 'Stelle, die das zentrale Versichertenregister führt und die letzte <a href="ausgleichskasse">Ausgleichskasse</a> im Versicherungsfall bei der Zusammenstellung der <a href="individuelles-konto-ik">individuellen Konten</a> eines <a href="versicherter">Versicherten</a> unterstützt.'),
(12761, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '119', 'Zertifikat'),
(12762, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '119', 'zertifikat'),
(12763, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '119', NULL),
(12764, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '119', 'Sammeltitel für einen oder mehrere Anlagefonds-Anteile.'),
(12765, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '120', 'Kursveränderung / Kapitalveränderung'),
