(14369, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '521', 'Ausgabepreis'),
(14370, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '521', 'ausgabepreis'),
(14371, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '521', NULL),
(14372, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '521', 'Preis zu dem <a href="anleger">Anleger</a> Fondsanteile zeichnen können. Er entspricht dem <a href="nettoinventarwert">Nettoinventarwert</a> eines <a href="anteil">Anteils</a>, zuzüglich der <a href="ausgabekommission">Ausgabekommission</a>.'),
(14373, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '522', 'Emittent'),
