(15449, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '791', 'Rückgewähr'),
(15450, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '791', 'ruckgewahr'),
(15451, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '791', NULL),
(15452, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '791', 'Rückgewähr siehe <a href="pramienruckgewahr">Prämienrückgewähr</a>'),
(15453, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '792', 'Leistungsverweigerung/ -kürzung (UV)'),
(15454, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '792', 'leistungsverweigerung-kurzung-uv'),
(15455, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '792', NULL),
(15456, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '792', 'Die Leistungen der <a href="obligatorische-unfallversicherung">obligatorischen Unfallversicherung</a> können in bestimmten Fällen verweigert oder gekürzt werden.'),
(15457, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '793', 'Regionalbank'),
(15458, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '793', 'regionalbank'),
(15459, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '793', NULL),
(15460, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '793', 'Die Regionalbanken und Sparkassen gehören zu den ältesten Banken in der Schweiz. Das Ziel war die sichere und zinstragende Anlage der Ersparnisse. Auch heute versteht man unter einer Sparkasse ein Geldinstitut, dessen Fremdgelder vollständig oder doch überwiegend aus Spareinlagen bestehen. Im <a href="aktivgeschaft">Aktivgeschäft</a> legen die Sparkassen die ihnen anvertrauten Gelder vorwiegend in Hypotheken an. Die Regionalbanken waren Banken, die mehr oder weniger alle Bankgeschäfte, sowohl die Zinsdifferenz- als auch<a href="indifferentes-geschaft">Indifferenten Geschäfte</a>, tätigen, aber ein grösseres Einzugsgebiet aufweisen. Sie sind in ihrer Region stark verankert und zählen traditionell das Handwerk, das Gewerbe, die Industrie, die Landwirtschaft sowie Private zu ihren Kunden.'),
(15461, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '794', 'Regionales Arbeitsvermittlungszentrum (RAV)'),
(15462, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '794', 'regionales-arbeitsvermittlungszentrum-rav'),
(15463, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '794', NULL),
(15464, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '794', 'Öffentliches Amt für die Vermittlung von Arbeitsstellen in der Region. Die Zusammenarbeit mit den Beratern des RAV ist eine Voraussetzung für eine <a href="arbeitslosenentschadigung">Arbeitslosenentschädigung</a>. Das RAV kann Stellenbewerbungen verlangen und die Teilnahme an Informationsveranstaltungen sowie Massnahmen zur Verbesserung der Arbeitsmarktfähigkeit vorschreiben.'),
(15465, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '795', 'Regionenfonds'),
(15466, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '795', 'regionenfonds'),
(15467, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '795', NULL),
(15468, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '795', '<a href="anlagefonds">Anlagefonds</a>, die ihre Investitionen in einer bestimmten geografischen Region (z.B. Skandinavien) oder einem besonderen Wirtschaftsraum (z.B. Euroland) tätigen.'),
(15469, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '796', 'Namenaktie'),
(15470, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '796', 'namenaktie'),
(15471, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '796', NULL),
(15472, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '796', 'Aktie, die auf den Namen lautet und deren Eigentümer oder Nutzniesser im Aktienregister der betreffenden Gesellschaft eingetragen ist. Dies erschwert den Handel mit solchen Titeln. Namenaktien können aber trotzdem an den Effektenbörsen kotiert und gehandelt werden. Gegensatz: <a href="inhaberaktie">Inhaberaktie.</a>'),
(15473, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '797', 'Eingliederungsmassnahmen'),
(15474, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '797', 'eingliederungsmassnahmen'),
(15475, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '797', NULL),
(15476, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '797', 'Individuelle Massnahmen im Rahmen der <a href="invalidenversicherung">Invalidenversicherung</a>, um invalide Personen in den Arbeitsprozess zu integrieren. Dazu gehören berufliche, schulische und medizinische Massnahmen zur Wiedererlangung der Erwerbsfähigkeit, Taggelder während dieser Massnahmen und unterstützende Hilfsmittel, die im Berufs- oder Privatleben benötigt werden.'),
(15477, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '798', 'Rückversicherung'),
(15478, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '798', 'ruckversicherung'),
(15479, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '798', NULL),
(15480, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '798', 'Bei der Rückversicherung geht es darum, das versicherte <a href="risiko">Risiko</a> in einem bestimmten Verhältnis auf Erst- und Rückversicherer zu verteilen. Der <a href="versicherer">Versicherer</a>, der den Vertrag mit dem Publikum abschliesst, wird als Erstversicherer bezeichnet. Wenn er einen Teil seines Geschäftes an den Rückversicherer abgibt, handelt er als Zedent; der das Risiko übernehmende Rückversicherer ist der Zessionar. Denjenigen Teil des Risikos, den der Erstversicherer selbst trägt, nennt man Eigenbehalt, denjenigen Teil, den er weitergibt, Zession oder zweites Risiko.'),
(15481, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '799', 'Wiederanlage');
INSERT INTO `contrexx_translations` (`id`, `locale`, `object_class`, `field`, `foreign_key`, `content`) VALUES
(15482, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '799', 'wiederanlage'),
(15483, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '799', NULL),
(15484, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '799', 'Die Möglichkeit, die <a href="ausschuttung">Ausschüttung</a> im gleichen Fonds wiederanzulegen. Einzelne Fonds gewähren dem Anleger bei der Wiederanlage seiner Jahresausschüttung einen speziellen Wiederanlagerabatt auf dem Ausgabepreis. Bei den meisten Fonds schweizerischen Rechts ist dies der Fall.'),
(15485, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '800', 'Thesaurierender Fonds'),
(15486, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '800', 'thesaurierender-fonds'),
(15487, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '800', NULL),
(15488, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '800', '<a href="anlagefonds">Anlagefonds</a>, der seine Erträge laufend wiederanlegt und nicht an die <a href="anleger">Anleger</a> ausschüttet. Gegenteil: <a href="ausschuttender-fonds">Ausschüttender Fonds</a>. Vergleiche auch <a href="tranche">Tranche</a>.'),
(15489, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '801', 'Thesaurierung'),
(15490, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '801', 'thesaurierung'),
(15491, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '801', NULL),
(15492, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '801', 'Laufende Wiederanlage der erwirtschafteten Erträge im selben Fonds.'),
(15493, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '802', 'Aussenverhältnis'),
(15494, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '802', 'aussenverhaltnis'),
(15495, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '802', NULL),
(15496, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '802', 'Rechtsbeziehung mehrerer verbundener Personen zu aussen stehenden Dritten; bei der <a href="solidarhaftung">Solidarhaftung</a> der <a href="haftpflicht">Haftpflicht</a>igen zum Geschädigten.'),
(15497, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '803', 'Relative Value'),
(15498, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '803', 'relative-value-1'),
(15499, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '803', NULL),
(15500, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '803', 'Ein Finanzinstrument kann relativ zu einem anderen Finanzinstrument falsch bewertet sein. Bei dieser Strategie nutzt der <a href="portfolio-manager-fondsmanager">Portfoliomanager</a> Preisunterschiede bei verschiedenen Finanzinstrumenten und an verschiedenen Börsenplätzen aus (<a href="arbitrage-1">Arbitrage</a>). Allerdings muss der Preisunterschied so gross sein, dass trotz Kosten oder Währungsumrechnungen ein Gewinn anfällt.'),
(15501, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '804', 'Gemietete Sache'),
(15502, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '804', 'gemietete-sache'),
(15503, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '804', NULL),
(15504, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '804', 'Siehe <a href="anvertraute-sache-geleaste-oder-gemietete">anvertraute Sache</a>.'),
(15505, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '805', 'Verzicht'),
(15506, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '805', 'verzicht'),
(15507, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '805', NULL),
(15508, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '805', 'Aufgeben eines Rechts oder eines Anspruchs.'),
(15509, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '806', 'Verzinsung der Altersguthaben'),
(15510, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '806', 'verzinsung-der-altersguthaben'),
(15511, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '806', NULL),
(15512, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '806', 'Gemäss Art. 15 Abs. 2 <a href="bvg-1">BVG</a> legt der Bundesrat den Mindestzinssatz auf <a href="altersguthaben">Altersguthaben</a> fest. Er beträgt gegenwärtig (Stand 2005) 2.5 Prozent (Art. 12 BVV 2).'),
(15513, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '807', 'Wiederbeschaffungskosten'),
(15514, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '807', 'wiederbeschaffungskosten'),
(15515, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '807', NULL),
(15516, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '807', '<a href="mittelbarer-schaden">Mittelbarer Schaden</a> (Folgekosten) eines <a href="schaden">Schaden</a>ereignisses in der <a href="hausratversicherung">Hausratversicherung</a>, der dadurch entsteht, dass versicherte Gegenstände wiederbeschafft werden müssen (z. B. Ausweise, Kreditkarten, Abonnemente, Flugtickets). Die Hausratversicherung übernimmt diese Kosten bis zum vorgesehenen Betrag.'),
(15517, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '808', 'Ersatzwert'),
(15518, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '808', 'ersatzwert'),
(15519, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '808', NULL),
(15520, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '808', 'Der Ersatzwert (EW) ist der Wert einer Sache zum Zeitpunkt des Schadenereignisses. Es ist der Wert, den die Versicherung ihrer Entschädigungsberechnung zu Grunde legt. Der Ersatzwert ist je nach Vertragsbedingungen <a href="zeitwert">Zeit-</a> oder <a href="neuwert">Neuwert.</a>'),
(15521, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '809', 'Ersatzfahrzeug'),
(15522, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '809', 'ersatzfahrzeug'),
(15523, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '809', NULL),
(15524, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '809', 'Ein Ersatzfahrzeug ist ein Ersatz für ein <a href="motorfahrzeug">Motorfahrzeug</a> gleicher Kategorie, welches für eine befristete Zeit an dessen Stelle in den Verkehr gesetzt wird.'),
(15525, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '810', 'Rechenschaftsbericht'),
(15526, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '810', 'rechenschaftsbericht'),
(15527, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '810', NULL),
(15528, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '810', 'Siehe <a href="jahresbericht-halbjahresbericht">Jahresbericht</a>.'),
(15529, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '811', 'Schadenmeldung (Schadenanzeige)'),
(15530, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '811', 'schadenmeldung-schadenanzeige'),
(15531, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '811', NULL),
(15532, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '811', 'Mit der Schadenmeldung bringt der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> dem <a href="versicherer">Versicherer</a> ein versichertes Ereignis zur Kenntnis. Der <a href="versicherungsvertrag">Versicherungsvertrag</a> kann vorsehen, dass die Anzeige schriftlich erfolgen muss.'),
(15533, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '812', 'Vertreter (Fonds)'),
(15534, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '812', 'vertreter-fonds'),
(15535, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '812', NULL),
(15536, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '812', 'Natürliche oder juristische Person mit Sitz in der Schweiz, die <a href="anlagefonds">Anlagefonds</a> ausländischen Rechts, die in der Schweiz zum öffentlichen Vertrieb zugelassen sind, in der Schweiz gegenüber <a href="anleger">Anlegern</a> und <a href="aufsichtsbehorde-fonds">Aufsichtsbehörde</a> vertritt. Für die Ausübung einer Vertreterfunktion bedarf es der vorgängigen Bewilligung durch die <a href="#invalid-reference-533">Eidgenössische Bankenkommission</a>.'),
(15537, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '813', 'Vertreter, gesetzlicher'),
(15538, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '813', 'vertreter-gesetzlicher'),
(15539, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '813', NULL),
(15540, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '813', 'Diejenige Person, die für eine handlungsunfähige Person (Unmündige, Bevormundete, Entmündigte) die Handlungen vornehmen kann bzw. muss, die letztere zur Begründung von Rechten und Pflichten benötigt.'),
(15541, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '814', 'Vorbehalt'),
(15542, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '814', 'vorbehalt'),
(15543, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '814', NULL),
(15544, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '814', 'Speziell in der Personenversicherung kann ein erhöhtes Risiko wegen vorbestandener Krankheit/Unfälle oder schlechtem Gesundheitszustand im Zeitpunkt des Abschlusses zu einem Vorbehalt führen; als Vorbehalt wird eine Einschränkung im <a href="versicherungsschutz">Versicherungsschutz</a> bezeichnet. Als Alternative zum Vorbehalt kann allenfalls eine Mehrprämie vereinbart werden. Siehe auch <a href="erschwerungen">Erschwerungen</a>.'),
(15545, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '815', 'Haftung'),
(15546, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '815', 'haftung'),
(15547, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '815', NULL),
(15548, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '815', 'Haftung bedeutet, einstehen müssen für den, einem <a href="dritte">Dritten</a> zugefügten, <a href="schaden">Schaden.</a>'),
(15549, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '816', 'Haftpflichtige'),
(15550, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '816', 'haftpflichtige'),
(15551, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '816', NULL),
(15552, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '816', 'Der Haftpflichtige ist derjenige, der für den einem <a href="dritte">Dritten</a> zugefügten <a href="schaden">Schaden</a> einstehen muss.'),
(15553, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '817', 'Haftbare'),
(15554, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '817', 'haftbare'),
(15555, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '817', NULL),
(15556, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '817', 'siehe <a href="haftpflichtige">Haftpflichtige</a>.'),
(15557, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '818', 'Ressourceneffizienz'),
(15558, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '818', 'ressourceneffizienz'),
(15559, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '818', NULL),
(15560, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '818', 'Eine ressourceneffiziente Produktion verwendet weniger und/oder umweltfreundlichere Ressourcen. Dies schont die Umwelt und bietet finanzielle Einsparungen.'),
(15561, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '819', 'Altersgutschriften'),
(15562, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '819', 'altersgutschriften'),
(15563, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '819', NULL),
(15564, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '819', 'Die Altersgutschriften sind jener Teil des Vorsorgeaufwandes, welcher angespart wird. Sparbeiträge leisten sowohl die Arbeitnehmer als auch der Arbeitgeber. Die Altersgutschriften gemäss <a href="bvg-1">BVG</a> werden in Prozenten des versicherten Lohnes jährlich berechnet.'),
(15565, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '820', 'Rendite'),
(15566, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '820', 'rendite'),
(15567, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '820', NULL),
(15568, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '820', 'Siehe <a href="performance">Performance</a>.'),
(15569, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '821', 'Pfandrecht'),
(15570, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '821', 'pfandrecht'),
(15571, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '821', NULL),
(15572, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '821', 'Ein Pfandrecht ist ein Sicherungsmittel für eine Forderung. [Beispiel: Hypothekarvertrag = Kreditvertrag, der durch eine Liegenschaft (Pfand) gesichert ist.] Wird diese Forderung nicht erfüllt, kann sich der <a href="glaubiger">Gläubiger</a> am Wert des Pfandes schadlos halten.'),
(15573, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '822', 'Rücknahmepflicht'),
(15574, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '822', 'rucknahmepflicht'),
(15575, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '822', NULL),
(15576, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '822', 'Siehe <a href="kundigung-fonds">Kündigung</a>.'),
(15577, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '823', 'Direktes Forderungsrecht'),
(15578, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '823', 'direktes-forderungsrecht'),
(15579, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '823', NULL),
(15580, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '823', 'Sofern (nach <a href="svg">SVG</a>) eine Versicherungspflicht besteht, kann der <a href="geschadigte">Geschädigte</a> direkt vom <a href="versicherer">Versicherer</a> den vom Schädiger geschuldeten Schadenersatz verlangen.'),
(15581, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '824', 'Anspruchsberechtigter'),
(15582, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '824', 'anspruchsberechtigter'),
(15583, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '824', NULL),
(15584, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '824', 'Person, der ein Versicherungsanspruch kraft eigenen oder abgeleiteten Rechtes im Zeitpunkt des Eintritts des versicherten Ereignisses zusteht (<a href="versicherungsnehmer-in">Versicherungsnehmer</a>, mitversicherte Personen in der <a href="haftpflichtversicherung">Haftplichtversicherung</a> und in der kollektiven Personenversicherung, <a href="begunstigte-r">Begünstigte</a>).'),
(15585, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '825', 'Risiko'),
(15586, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '825', 'risiko'),
(15587, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '825', NULL),
(15588, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '825', 'Als Risiko bezeichnet man die Möglichkeit des Eintrittes eines bestimmten Ereignisses, das einen <a href="schaden">Schaden</a>verursacht oder einen Bedarf auslöst. Der Eintritt dieses Ereignisses ist dabei ungewiss und zufällig. Wenn die Wahrscheinlichkeit des Eintreffens des versicherten Ereignisses und die Schadenhöhe von den an der Versicherung beteiligten Personen nicht beeinflusst werden können, sprechen wir von einem objektiven Risiko. Die Gefahren, die sich jedoch aus dem Verhalten der an der Versicherung beteiligten Personen ergeben, sowie besonders auch der Umstand, dass der Eintritt des Versicherungsfalles und die Höhe der Leistung vom Interesse der beteiligten Personen an dieser Leistung beeinflusst werden können, werden als subjektive Risiken bezeichnet.'),
(15589, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '826', 'Risiko (Fonds)'),
(15590, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '826', 'risiko-fonds'),
(15591, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '826', NULL),
(15592, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '826', 'In der <a href="portfoliotheorie">Portfoliotheorie</a> wird das Risiko einer Anlage mit der Höhe der Ertragsschwankungen (Volatilität) gemessen. Risiko und Ertrag stehen in direktem Zusammenhang: Die Portfoliotheorie nach Markowitz unterstellt, dass ein höherer Ertrag nur mit einem grösseren Risiko erkauft werden kann.<br />\r<br />In der Portfoliotheorie wird das Risiko meist als Standardabweichung der <a href="performance">Performance</a>werte definiert. Die Standardabweichung ist ein statistisches Mass für die Streuung um den Mittelwert der Performancewerte über den Beobachtungszeitraum.'),
(15593, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '827', 'Risikofähigkeit'),
(15594, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '827', 'risikofahigkeit'),
(15595, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '827', NULL),
(15596, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '827', 'Die Fähigkeit eines <a href="anleger">Anlegers</a>, über eine bestimmte Zeit sein Geld (Kapital) zu binden, ohne dabei in finanzielle Schwierigkeiten zu geraten.'),
(15597, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '828', 'Gefahrengemeinschaft'),
(15598, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '828', 'gefahrengemeinschaft'),
(15599, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '828', NULL),
(15600, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '828', 'Gruppe von Personen oder Sachen, die einem gleichartigen <a href="risiko">Risiko</a> ausgesetzt sind. Sie schliessen sich zusammen, um einander Deckung zu geben - nach dem Prinzip "einer für alle, alle für einen".'),
(15601, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '829', 'Provisorische Deckung'),
(15602, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '829', 'provisorische-deckung'),
(15603, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '829', NULL),
(15604, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '829', 'siehe <a href="deckungszusage-provisorische">Deckungszusage, vorläufige</a>'),
(15605, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '830', 'Risikoprüfung'),
(15606, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '830', 'risikoprufung'),
(15607, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '830', NULL),
(15608, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '830', 'Prüfung der deklarierten Risiken und allfälliger zusätzlicher Erhebungen wie <a href="gesundheitsprufung">medizinische Untersuchungen</a>, um zu entscheiden, ob ein <a href="antrag">Antrag</a> überhaupt, und wenn ja, ob zu normalen oder zu erschwerten Bedingungen, angenommen werden kann.  Die <a href="bindungsfrist">Bindungsfrist</a> des Antragstellers gibt dem <a href="versicherer">Versicherer</a> Zeit für diese Prüfung.'),
(15609, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '831', 'Gefahrstatsachen'),
(15610, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '831', 'gefahrstatsachen'),
(15611, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '831', NULL),
(15612, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '831', 'Gefahrstatsachen sind Tatsachen oder Umstände, welche auf die <a href="gefahr">Gefahr</a> einen Einfluss haben und deren Erfassung dem <a href="versicherer">Versicherer</a> erlauben, die Wahrscheinlichkeit, dass sich ein <a href="versicherungsfall">Versicherungsfall</a> ereignet, konkret für den einzelnen <a href="versicherter">Versicherten</a> abzuschätzen. Man spricht von objektiven Gefahrstatsachen, wenn sie den versicherten Gegenstand betreffen (z.B. Hubraum eines Autos); von subjektiven Gefahrstatsachen, wenn sie mit der Person des Versicherten zusammenhängen (Gesundheit des Autohalters und -lenkers); von wandelbaren Gefahrstatsachen, wenn sie sich ändern können; von unwandelbaren Gefahrstatsachen, wenn sie ein für allemal feststehen (Geburtsjahr einer Person).'),
(15613, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '832', 'Risikoversicherung'),
(15614, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '832', 'risikoversicherung'),
(15615, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '832', NULL),
(15616, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '832', 'Bei einer Risikoversicherung wird der <a href="versicherer">Versicherer</a> überhaupt nicht leistungspflichtig, wenn sich das versicherte <a href="risiko">Risiko</a> (Todesfall oder <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a>) während der <a href="vertragsdauer">Vertragsdauer</a> nicht verwirklicht hat. Im Gegensatz zu einer <a href="erlebensfallversicherung">Erlebensfall-Versicherung</a>, ist sie nicht mit einem Sparvorgang verbunden.'),
(15617, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '833', 'Risikolebensversicherung'),
(15618, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '833', 'risikolebensversicherung'),
(15619, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '833', NULL),
(15620, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '833', 'Form der <a href="lebensversicherung">Lebensversicherung</a>, die die Gefahren Tod oder <a href="erwerbsunfahigkeit">Erwerbsunfähigkeit</a> (wegen <a href="invaliditat">Invalidität</a>) versichert. Entsprechend unterscheidet man zwischen <a href="todesfallrisiko-versicherung">Todesfallrisikoversicherung</a> und <a href="erwerbsausfallversicherung">Erwerbsunfähigkeitsversicherung</a>.'),
(15621, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '834', 'Riskmanagement (RM)'),
(15622, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '834', 'riskmanagement-rm'),
(15623, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '834', NULL),
(15624, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '834', 'Systematische Erfassung und Bewertung von Risiken sowie Ableitung von führungsmässigen und organisatorischen Konsequenzen. Einzelne Versicherer bieten Riskmanagement als Dienstleistung an. <br />Ein <a href="risiko">Risiko</a> kann man vermeiden (Schadenvermeidung), vermindern (Schadenverminderung), auf Dritte abwälzen (z.B. durch <a href="versicherung">Versicherung</a>) oder selbst tragen.'),
(15625, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '835', 'Risikoprämie'),
(15626, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '835', 'risikopramie'),
(15627, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '835', NULL),
(15628, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '835', 'Prämienbestandteil, der das versicherungstechnische Risiko abdeckt.'),
(15629, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '836', 'Risikoprämie (BV)'),
(15630, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '836', 'risikopramie-bv'),
(15631, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '836', NULL),
(15632, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '836', 'Prämienbestandteil, der bei der <a href="berufliche-vorsorge">beruflichen Vorsorge</a> für die Risiken Tod und Invalidität erhoben wird.'),
(15633, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '837', 'Risikoneigung'),
(15634, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '837', 'risikoneigung'),
(15635, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '837', NULL),
(15636, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '837', 'Die Höhe möglicher Kursschwankungen, die ein <a href="anleger">Anleger</a> einzugehen gewillt ist, um ein bestimmtes Anlageziel zu erreichen. Die Risikoneigung bzw. das Sicherheitsbedürfnis eines Anlegers ist mitentscheidend für die Wahl eines <a href="anlagefonds">Anlagefonds</a>. Je höher die Risikoneigung, desto grösser darf der Aktien- und Fremdwährungsanteil sein.'),
(15637, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '838', 'Assistanceversicherung'),
(15638, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '838', 'assistanceversicherung'),
(15639, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '838', NULL),
(15640, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '838', 'Freiwillige <a href="versicherung">Versicherung</a> für Motorfahrzeughalter, bei der der <a href="versicherer">Versicherer</a> bestimmte Folgekosten bei Unfällen, Pannen oder Schäden am <a href="motorfahrzeug">Motorfahrzeug</a> übernimmt, die eine Weiterfahrt verunmöglichen.'),
(15641, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '839', 'Beraubung'),
(15642, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '839', 'beraubung'),
(15643, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '839', NULL),
(15644, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '839', 'Beraubung ist eine Art des <a href="diebstahl">Diebstahls.</a> Dabei wird unter Androhung oder Anwendung von Gewalt gegen den <a href="versicherungsnehmer-in">Versicherungsnehmer</a> oder mit ihm in Hausgemeinschaft lebenden Personen (je nach <a href="avb">AVB</a>) ein Diebstahl begangen. Als Beraubung gilt auch Diebstahl, der bei Unfähigkeit zum Widerstand infolge Tod, Ohnmacht oder Unfall des Opfers begangen wird.'),
(15645, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '840', 'EW'),
(15646, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '840', 'ew'),
(15647, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '840', NULL),
(15648, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '840', 'Abkürzung für <a href="ersatzwert">"Ersatzwert"</a>'),
(15649, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '841', 'Sabotage (Maschinenversicherung)'),
(15650, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '841', 'sabotage-maschinenversicherung'),
(15651, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '841', NULL),
(15652, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '841', 'Mutwillige Beschädigung oder Zerstörung eines versicherten Objekts (Anlage, Maschine, Installation, Apparat). Wird im Rahmen der <a href="maschinenversicherung">Maschinenversicherung</a> versichert (<a href="all-risk-versicherung">All-Risk-Versicherung</a>).'),
(15653, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '842', 'Lohn'),
(15654, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '842', 'lohn'),
(15655, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '842', NULL),
(15656, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '842', 'Siehe <a href="ahv-lohn">AHV-Lohn</a>.'),
(15657, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '843', 'Vertriebsträger (Ausgabestelle)'),
(15658, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '843', 'vertriebstrager-ausgabestelle'),
(15659, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '843', NULL),
(15660, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '843', 'Partnerunternehmen eines Fondsanbieters, das dessen Produkte an Kunden verkauft und von diesen zurücknimmt.'),
(15661, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '844', 'Beratung und Verkauf'),
(15662, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '844', 'beratung-und-verkauf'),
(15663, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '844', NULL),
(15664, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '844', 'Teilprozess des <a href="leistungsprozess">Leistungsprozesses</a>, der die Massnahmen des Versicherers umfasst, um seine Versicherungsprodukte abzusetzen. <br />Stellt man den Servicegedanken in den Vordergrund, erfolgt die Kundenbindung über das <a href="sicherungsteam">Sicherungsteam</a>. <br />'),
(15665, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '845', 'Absatzkanal'),
(15666, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '845', 'absatzkanal'),
(15667, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '845', NULL),
(15668, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '845', 'Beim direkten Absatzkanal werden Versicherungen vom <a href="versicherer">Versicherer</a> unmittelbar an Endkunden verkauft (z.B. über Call-Center, Internet, Direct-Mail). Beim indirekten Absatzkanal werden Versicherungen über unabhängige Partner (Vermittler, <a href="makler">Makler</a>, <a href="agentur">Agenten</a>) an Endkunden verkauft.'),
(15669, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '846', 'Verkaufsgespräch'),
(15670, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '846', 'verkaufsgesprach'),
(15671, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '846', NULL),
(15672, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '846', 'Kundengespräch zur Befriedigung eines Sicherheitsbedürfnisses bzw. zur Lösung eines Sicherheitsproblems.'),
(15673, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '847', 'Verkaufsprospekt'),
(15674, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '847', 'verkaufsprospekt'),
(15675, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '847', NULL),
(15676, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '847', 'Siehe <a href="prospekt-fondsreglement">Prospekt</a>.'),
(15677, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '848', 'Umsatzpolice'),
(15678, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '848', 'umsatzpolice'),
(15679, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '848', NULL),
(15680, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '848', 'Vertragsart der <a href="transportversicherung">Transportversicherung</a> für Unternehmen, die regelmässig gleichartige Güter auf den gleichen Transportwegen und mit den gleichen Transportitteln transportieren. Dabei muss der <a href="versicherungsnehmer-in">Versicherungsnehmer</a> die einzelnen Warensendungen nicht anmelden. Massgebend für die Berechnung der <a href="pramie">Prämien</a> ist der gesamte Warenwert der Transporte während einer bestimmten Zeitdauer.'),
(15681, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '849', 'Vorsorgepolice'),
(15682, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '849', 'vorsorgepolice'),
(15683, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '849', NULL),
(15684, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '849', 'Die Vorsorgepolice ist ein breitgefächertes Angebot der Lebensversicherung im Rahmen der steuerbegünstigten, gebundenen Vorsorge.'),
(15685, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '850', 'Vorsorge'),
(15686, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '850', 'vorsorge'),
(15687, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '850', NULL),
(15688, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '850', '1. Freie Vorsorge:<br />Unter freier Vorsorge verstehen wir alle Vorkehren, die im Hinblick auf Vorsorge getroffen werden. Dazu gehören in erster Linie Lebensversicherungen, aber auch Kapitalanlagen, Erwerb von Wohneigentum usw.<br />\r<br />2. Gebundene Vorsorge:<br />In der Schweiz steuerpflichtige Erwerbstätige haben die Möglichkeit, mit zusätzlichen steuerlichen Erleichterungen Selbstvorsorge zu betreiben. Die zurückgelegten Mittel müssen jedoch ausschliesslich und unwiderruflich der Vorsorge dienen. Den steuerlichen Vergünstigungen stehen andererseits einschränkende Vorschriften bezüglich Abschluss, Gestaltung und Verfügung gegenüber. Die gebundene Vorsorge kann sowohl über die gebundene Lebensversicherung wie auch über Banksparpläne getroffen werden.'),
(15689, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '851', 'Sparprämie'),
(15690, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '851', 'sparpramie'),
(15691, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '851', NULL),
(15692, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '851', 'Teil der gesamten Prämie bei kapitalbildenden Versicherungen, welcher mit den Zinsen den jährlichen Zuwachs des <a href="deckungskapital">Deckungskapitals</a> bestimmt. <br />Bei dieser Art von Versicherungen hat der <a href="versicherer">Versicherer</a> grundsätzlich selbst dann eine Leistung zu erbringen, wenn der <a href="versicherungsvertrag">Versicherungsvertrag</a> vorzeitig aufgelöst wird, d.h. bevor ein <a href="versicherungsfall">Versicherungsfall</a> eingetreten ist und sofern die Mindestprämienzahlungsdauer eingehalten wird.'),
(15693, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '852', 'BB'),
(15694, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '852', 'bb'),
(15695, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '852', NULL),
(15696, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '852', 'Abkürzung für "<a href="besondere-bedingungen-bb">Besondere Bedingungen</a>" (zu den AVB)'),
(15697, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '853', 'Skala 44'),
(15698, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '853', 'skala-44'),
(15699, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '853', NULL),
(15700, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '853', 'Bei einer vollständigen Beitragsdauer für die <a href="alters-und-hinterlassenenversicherung">Alters- und Hinterlassenenversicherung</a> (AHV)  wird eine Vollrente ausbezahlt. Die Höhe dieser Vollrente kann der Skala 44 entnommen werden. Bei Beitragslücken kommt eine Teilrente nach den Skalen 1 bis 43 zur Auszahlung.'),
(15701, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '854', 'Anwendungsbereich'),
(15702, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '854', 'anwendungsbereich'),
(15703, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '854', NULL),
(15704, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '854', 'Der Anwendungsbereich entspricht den festgelegten Grenzen, innerhalb welcher sich eine <a href="versicherung">Versicherung</a> bewegen darf. Diese Grenzen können sich pro Versicherung beziehen auf:<br />- die minimale und maximale <a href="versicherungssumme">Versicherungssumme</a>,<br />- das tiefste und höchste <a href="eintrittsalter">Eintrittsalter</a>,<br />- das tiefste und höchste <a href="endalter">Endalter</a>,<br />- die geringste und die längste <a href="versicherungsdauer">Versicherungsdauer</a> etc.<br />Unter den Anwendungsbereich fallen auch andere Bestimmungen. Beispielsweise <br />- maximale Höhe von Zusatzversicherungen zur Hauptversicherung,<br />- Möglichkeit, eine zweite, evtl. dritte Person einzuschliessen,<br />- ob der gewünschte <a href="tarif">Tarif</a> als gebundene <a href="vorsorgepolice">Vorsorgepolice</a> zulässig ist <br />etc.'),
(15705, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '855', 'Zweite Säule'),
(15706, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '855', 'zweite-saule'),
(15707, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '855', NULL),
(15708, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '855', '<a href="berufliche-vorsorge">Berufliche Vorsorge</a> als zweiter Pfeiler des schweizerischen <a href="sozialversicherung">Sozialversicherung</a>ssystems. Die zweite Säule umfasst die berufliche Alters-, Hinterlassenen- und Invalidenversicherung (BV, obligatorisch für Arbeitnehmende ab einem bestimmten Einkommen) sowie die <a href="obligatorische-unfallversicherung">obligatorische Unfallversicherung</a> (UV, obligatorisch für alle Arbeitnehmenden).'),
(15709, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '856', 'Sektordurchschnitt'),
(15710, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '856', 'sektordurchschnitt'),
(15711, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '856', NULL),
(15712, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '856', 'Als Sektordurchschnitt bezeichnet man die ungewichtete durchschnittliche <a href="performance">Performance</a> aller Fonds derselben Anlagekategorie. Üblicherweise werden alle Fonds derselben Anlagekategorie betrachtet, die in einem Land zum Vertrieb zugelassen sind.'),
(15713, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '857', 'Branchenfonds (Sektorenfonds)'),
(15714, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '857', 'branchenfonds-sektorenfonds'),
(15715, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '857', NULL),
(15716, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '857', '<a href="anlagefonds">Anlagefonds</a>, die ihr Vermögen ausschliesslich in Wertpapieren eines bestimmten Wirtschaftssektors anlegen.'),
(15717, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '858', 'Existenzsicherung'),
