(15787, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '875', NULL),
(15788, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '875', '<a href="anlagefonds">Anlagefonds</a>, die in <a href="obligationen">Obligationen</a> mit einer (Rest-)<a href="laufzeit">Laufzeit</a> von 1 bis 3 Jahren investieren.'),
(15789, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '876', 'Kurzarbeitsentschädigung'),
