(15843, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '889', NULL),
(15844, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '889', 'Durch Dachlawinen oder von Bäumen fallender Schnee usw. verursachter Schaden, z.B. bei der <a href="motorfahrzeug-kaskoversicherung">Motorfahrzeugversicherung</a> am <a href="motorfahrzeug">Motorfahrzeug</a>.'),
(15845, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '890', 'Soziale Krankenversicherung'),
(15846, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '890', 'soziale-krankenversicherung'),
(15847, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '890', NULL),
(15848, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '890', '<a href="sozialversicherung">Sozialversicherung</a>, die im Krankenversicherungsgesetz (KVG) geregelt wird. Sie umfasst die <a href="obligatorische-krankenpflegeversicherung">obligatorische Krankenpflegeversicherung</a> und die freiwillige <a href="krankentaggeldversicherung-nach-kvg">Krankentaggeldversicherung</a> (nach KVG).'),
(15849, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '891', 'Sozialversicherung'),
(15850, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '891', 'sozialversicherung'),
(15851, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '891', NULL),
(15852, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '891', 'Obligatorische Personenversicherungen, deren Versicherungsleistungen gesetzlich festgelegt sind. Ziel der Sozialversicherungen ist ein Basisschutz für die finanziellen Folgen der Gefahren Alter, Tod, Krankheit, Unfall, Invalidität, Arbeitslosigkeit, Dienstpflicht und Mutterschaft. Die soziale Ausgestaltung zeigt sich insbesondere im Solidaritätsgedanken und im gesetzlichen Anspruch. Versichert ist die ganze Wohnbevölkerung (z.B. AHV/IV) oder ein eingeschränkter Personenkreis (z.B. UVG, EO).'),
(15853, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '892', 'Socially Responsible Investments'),
(15854, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '892', 'socially-responsible-investments-1'),
(15855, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '892', NULL),
(15856, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '892', 'Unter Socially Responsible Investments (SRI) werden Anlagen zusammengefasst, die neben traditionellen finanziellen Faktoren auch soziale und ökologische Kriterien berücksichtigen.'),
(15857, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '893', 'BVB'),
(15858, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '893', 'bvb'),
(15859, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '893', NULL),
(15860, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '893', 'Besondere Versicherungsbedingungen, die nicht für alle Kunden gelten und deshalb nur bei Bedarf beigefügt werden. Siehe <a href="avb">AVB</a>.'),
(15861, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '894', 'Besondere Bedingungen (BB)'),
(15862, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '894', 'besondere-bedingungen-bb'),
(15863, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '894', NULL),
(15864, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '894', 'Zusätzlich zu den <a href="allgemeine-versicherungsbedingungen">Allgemeinen Versicherungsbedingungen</a> (AVB) werden in der <a href="police">Police</a> allfällige individuelle Vereinbarungen als Besondere Bedingungen (BB) aufgeführt, die z.B. Zusatzversicherungen oder Ein- und Ausschlüsse besonderer <a href="risiko">Risiken</a> betreffen.'),
(15865, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '895', 'Sondermassnahmen'),
(15866, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '895', 'sondermassnahmen'),
(15867, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '895', NULL),
(15868, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '895', 'Jede <a href="vorsorgeeinrichtung">Vorsorgeeinrichtung</a> hat grundsätzlich eine bestimmte jährliche Summe (1 Prozent der koordinierten Löhne aller Versicherten, die Sparbeiträge zu entrichten haben) bereitzustellen. Dies sind die Mittel für die Sondermassnahmen, die insbesondere für die Verbesserung der Leistungen der <a href="eintrittsgeneration">Eintrittsgeneration</a> sowie für die Anpassung der laufenden <a href="altersrenten">Altersrenten</a> an die Teuerung zu verwenden sind.'),
(15869, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '896', 'Spezialitätenfonds'),
(15870, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '896', 'spezialitatenfonds'),
(15871, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '896', NULL),
(15872, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '896', '<a href="anlagefonds">Anlagefonds</a>, die sich durch einen besonderen Anlageansatz oder eine spezielle Ausrichtung auf bestimmte Länder, Branchen oder auf bestimmte <a href="anlageinstrumente">Anlageinstrumente</a> von herkömmlichen Anlagefonds unterscheiden.'),
(15873, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '897', 'SPI'),
