(15879, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '898', NULL),
(15880, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '898', 'Besonderheit bei der Berechnung der <a href="altersrenten">Altersrenten</a> von Ehepaaren. Zur Ermittlung des durchschnittlichen Erwerbseinkommens wird jedem Ehepartner für die gemeinsamen Ehejahre das halbe Jahreseinkommen des anderen Ehepartners gutgeschrieben.'),
(15881, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '899', 'Splitting-Vorsorgelösung'),
(15882, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '899', 'splitting-vorsorgelosung'),
(15883, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '899', NULL),
(15884, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '899', 'Organisationsform der überobligatorischen Versicherung in der <a href="berufliche-vorsorge">beruflichen Vorsorge</a>, bei der die BVG-Minimalleistungen und die <a href="uberobligatorium-bv">überobligatorischen</a> Leistungen getrennt versichert werden.'),
