(15889, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '901', 'Reglement zur Personalvorsorge'),
(15890, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '901', 'reglement-zur-personalvorsorge'),
(15891, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '901', NULL),
(15892, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '901', 'Gesetzlich vorgeschriebenes Regelwerk einer <a href="personalvorsorgeeinrichtung">Personalvorsorgeeinrichtung</a>, das Einzelheiten zur <a href="berufliche-vorsorge">beruflichen Vorsorge</a> seiner Mitarbeiter festlegt (z. B. die Höhe der Versicherungsbeiträge und -leistungen).'),
