(15895, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '902', NULL),
(15896, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '902', 'Organisation des Arbeitgebers, die sich um die <a href="berufliche-vorsorge">berufliche Vorsorge</a> seiner Arbeitnehmer kümmert.'),
(15897, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '903', 'Stempelabgabe (Umsatzabgabe)'),
(15898, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '903', 'stempelabgabe-umsatzabgabe'),
(15899, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '903', NULL),
(15900, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '903', '<a href="anlagefonds">Anlagefonds</a> mit Domizil im Ausland unterliegen in der Schweiz beim Bezug aus <a href="emission">Emissionen</a> der Stempelabgabe. Die <a href="rucknahmen">Rücknahme</a> ist von der Stempelabgabe befreit. Anlagefonds nach schweizerischem Recht sind bei der Ausgabe/Rücknahme von <a href="anteil">Anteilen</a> an den Fonds von der Stempelabgabe befreit.'),
(15901, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '904', 'Kurs'),
(15902, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '904', 'kurs'),
(15903, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '904', NULL),
(15904, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '904', 'Preis für <a href="anteil">Anteile</a> von <a href="anlagefonds">Anlagefonds</a>, die an der Börse kotiert sind oder ausserbörslich gehandelt werden (Immobilienfonds). Der Kurs bildet sich durch Angebot und Nachfrage auf dem Markt, während der <a href="ausgabepreis">Ausgabe-</a> und <a href="rucknahmepreis">Rücknahmepreis</a> der Anteile auf Grund des <a href="nettoinventarwert">Nettoinventarwerts</a> durch die <a href="fondsleitung-verwaltungsgesellschaft">Fondsleitung</a> festgelegt wird.'),
(15905, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'name', '905', 'Sturm'),
(15906, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'slug', '905', 'sturm'),
(15907, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'href', '905', NULL),
(15908, 'de', 'Cx\\Modules\\Topics\\Model\\Entity\\Entry', 'description', '905', 'Sturm ist in der <a href="hausratversicherung">Hausratversicherung</a> Wind von mind. 75 km/h, der in der Umgebung der versicherten Sachen Bäume entwurzelt oder Gebäude abdeckt.'),
