INSERT INTO `contrexx_content_page` (`id`, `node_id`, `nodeIdShadowed`, `lang`, `type`, `caching`, `updatedAt`, `updatedBy`, `title`, `linkTarget`, `contentTitle`, `slug`, `content`, `sourceMode`, `customContent`, `useCustomContentForAllChannels`, `applicationTemplate`, `useCustomApplicationTemplateForAllChannels`, `cssName`, `cssNavName`, `skin`, `useSkinForAllChannels`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `start`, `end`, `editingStatus`, `protection`, `frontendAccessId`, `backendAccessId`, `display`, `active`, `target`, `module`, `cmd`) VALUES (10,10,10,1,'content',0,'2009-03-06 19:42:07','system','Community',NULL,'Online-Community','Community','<p>Community ist die Kurzform von Online-Community.<br />\r\n<br />\r\n&quot;Eine <a href=\"http://de.wikipedia.org/wiki/Online-Community\" target=\"_blank\">Online-Community</a> (Netzgemeinschaft) ist eine Sonderform der Gemeinschaft; hier von Menschen, die einander via Internet begegnen und sich dort austauschen. Findet die Kommunikation in einem Sozialen Netzwerk statt, das als Plattform zum gegenseitigen Austausch von Meinungen, Eindr&uuml;cken und Erfahrungen dient (oft in Form von User Generated Content), spricht man auch von Sozialen Medien.&quot; (Quelle: <a href=\"http://de.wikipedia.org/wiki/Online-Community\" target=\"_blank\">Wikipedia</a>)</p>\r\n<div style=\"text-align: center;\"><img width=\"360\" height=\"294\" alt=\"\" src=\"images/ContentManager/Community.jpg\" /></div>\r\n<br />\r\nMit Interaktionen k&ouml;nnen die Internetbesucher einbezogen werden. Entsprechende Community-Mitglieder helfen indirekt Inhalte auf der Internetseite bereit zu stellen, damit bleibt das Community-Angebot attraktiv und bietet ausserdem den Suchmaschinen Inhalte zum indexieren.<br />\r\n<br />\r\nSogenanntes Community-Marketing ist die h&ouml;chste Stufe des Online-Marketing.<br />\r\n<br />',0,'',0,NULL,0,'',NULL,0,0,'Online-Community','Eine Online-Community (Netzgemeinschaft) ist eine Sonderform der Gemeinschaft; hier von Menschen, die einander via Internet begegnen und sich dort austauschen.','Community, Blog, Forum, Suchmaschinenmarketing, Online-Marketing','index',NULL,NULL,'',0,0,0,1,1,'',NULL,'');
INSERT INTO `contrexx_content_page` (`id`, `node_id`, `nodeIdShadowed`, `lang`, `type`, `caching`, `updatedAt`, `updatedBy`, `title`, `linkTarget`, `contentTitle`, `slug`, `content`, `sourceMode`, `customContent`, `useCustomContentForAllChannels`, `applicationTemplate`, `useCustomApplicationTemplateForAllChannels`, `cssName`, `cssNavName`, `skin`, `useSkinForAllChannels`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `start`, `end`, `editingStatus`, `protection`, `frontendAccessId`, `backendAccessId`, `display`, `active`, `target`, `module`, `cmd`) VALUES (11,10,10,2,'fallback',0,'2010-12-15 07:56:03','system','Community',NULL,'Online-Community','Community','<p>An online community ist a special form of society of humans who meet in the internet and interact with each other. If this interaction happens in a social network which acts as a plattform to exchange opinions, experiences, news and advices, it\'s called social media. (translation from <a href=\"http://de.wikipedia.org/wiki/Online-Community\" target=\"_blank\">wikipedia</a>)</p>\r\n<p style=\"text-align: center;\"><img width=\"360\" height=\"294\" src=\"images/ContentManager/Community.jpg\" alt=\"\" /></p>\r\n<p><br />\r\nLet your visitors be part of your site. Let them help you keeping your site up to date an keeping your website attractive. In addition they help you to feed the search engines with stuff to index.<br />\r\n<br />\r\nCommunity marketing is the most advanced form of online marketing.</p>\r\n<br />',0,'',0,NULL,0,'',NULL,0,0,'Online-Community','Eine Online-Community (Netzgemeinschaft) ist eine Sonderform der Gemeinschaft; hier von Menschen, die einander via Internet begegnen und sich dort austauschen.','Community, Blog, Forum, Suchmaschinenmarketing, Online-Marketing','index',NULL,NULL,'',0,0,0,1,1,'','','');
