lasttag --> 								</ul>\r\n                    </div>\r\n                    <!-- END tags --></div>\r\n                    </td>\r\n                </tr>\r\n                <tr class=\"row1\">\r\n                    <td>{TXT_CREATED}:</td>\r\n                    <td>{DATE_CREATED}</td>\r\n                </tr>\r\n                <tr class=\"row2\">\r\n                    <td>{TXT_LAST_CHANGE}:</td>\r\n                    <td>{DATE_UPDATED}</td>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n      </div>\r\n        </div>\r\n        <script type=\"text/javascript\">\r\n			/* <![CDATA[ */\r\n			\r\n			var sliderOp = baseOptions;\r\n			baseOptions.counter = \'{COUNTER}\';\r\n			slider_{ARTICLE_ID}{COUNTER} = new Slider(\"{ARTICLE_ID}\", sliderOp);\r\n			delete sliderOp;\r\n\r\n			rating_{ARTICLE_ID}{COUNTER} = new Rating({ARTICLE_ID}{COUNTER}, {AVERAGE}, { \r\n				stars : {MAX_RATING}, \r\n				starPath : \'images/modules/knowledge/\',\r\n				elemPrefix : \'rating_elem_\',\r\n				onRate : function(data, rating) {\r\n					var foo = new Ajax.Request(\'index.php?section=knowledge&act=rate\', {\r\n						method: \"post\",\r\n						parameters : {\r\n							id : data.nr,\r\n							rated : rating\r\n						}\r\n					});\r\n				},\r\n				callbackData : {nr : {ARTICLE_ID}}\r\n				});\r\n\r\n			/* ]]> */\r\n			</script></li>\r\n        <!-- END article -->	 </ul>\r\n        <!-- END articles --> <!-- END articlesection --></div>\r\n\r\n\r\n',1,'',0,NULL,0,'','',0,0,'Wissensdatenbank','Wissensdatenbank','Wissensdatenbank','index',NULL,NULL,'',0,0,0,1,1,'','Knowledge','');
