_username --><!-- BEGIN access_user_password -->\r\n            <p><label>{ACCESS_USER_PASSWORD_DESC}</label>{ACCESS_USER_PASSWORD}<br />Please enter your password (at least 6 characters)</p>\r\n            <!-- END access_user_password --><!-- BEGIN access_user_password_confirmed -->\r\n            <p><label>{ACCESS_USER_PASSWORD_CONFIRMED_DESC}</label>{ACCESS_USER_PASSWORD_CONFIRMED}<br />Please repeat your password</p>\r\n            <!-- END access_user_password_confirmed --> \r\n        </fieldset>\r\n        <fieldset><legend>Additional data</legend>	\r\n            <!-- BEGIN access_user_email -->\r\n            <p><label>{ACCESS_USER_EMAIL_DESC}</label>{ACCESS_USER_EMAIL}<br />\r\n            You have to enter a valid e-mail address, so we can send you an activation code.</p>\r\n            <!-- END access_user_email -->\r\n            <p><label>{ACCESS_USER_FRONTEND_LANGUAGE_DESC}</label>{ACCESS_USER_FRONTEND_LANGUAGE}<br /><br /></p>\r\n            <p><b>Note:</b><br />You can complete your profile after the registration process is finished.</p>\r\n        </fieldset>\r\n        <!-- BEGIN access_captcha -->\r\n        <fieldset><legend>{TXT_ACCESS_CAPTCHA}</legend>	\r\n            <p>{TXT_ACCESS_CAPTCHA_DESCRIPTION}<br /></p>\r\n            <p><label>CAPTCHA</label><img class=\"captcha\" src=\"{ACCESS_CAPTCHA_URL}\" alt=\"{ACCESS_CAPTCHA_ALT}\" />\r\n            <input id=\"accessSignUpCaptcha\" type=\"text\" name=\"accessSignUpCaptcha\" />\r\n            <input type=\"hidden\" name=\"accessSignUpCaptchaOffset\" value=\"{ACCESS_CAPTCHA_OFFSET}\" />\r\n            </p>\r\n        </fieldset> \r\n        <!-- END access_captcha -->\r\n        <!-- BEGIN access_tos -->\r\n            <fieldset><legend>{TXT_ACCESS_TOS}</legend>	\r\n                {ACCESS_TOS}\r\n            </fieldset> \r\n        <!-- END access_tos -->\r\n        <p>{ACCESS_SIGNUP_BUTTON}</p> \r\n    </form>\r\n    <!-- END access_signup_form --> 	\r\n    <!-- BEGIN access_signup_store_success -->\r\n    <div class=\"message_ok\">{ACCESS_SIGNUP_MESSAGE}</div>\r\n    <!-- END access_signup_store_success -->   	\r\n    <!-- BEGIN access_signup_store_error -->\r\n    <div class=\"message_error\">{ACCESS_SIGNUP_MESSAGE}</div>\r\n    <!-- END access_signup_store_error -->   	\r\n    <!-- BEGIN access_signup_confirm_success -->\r\n    <div class=\"message_ok\">{ACCESS_SIGNUP_MESSAGE}</div>\r\n    <!-- END access_signup_confirm_success -->   	\r\n    <!-- BEGIN access_signup_confirm_error -->\r\n    <div class=\"message_error\">{ACCESS_SIGNUP_MESSAGE}</div>\r\n    <!-- END access_signup_confirm_error -->    	\r\n    {ACCESS_JAVASCRIPT_FUNCTIONS}\r\n</div>',1,'',0,NULL,0,'register','',0,0,'Registrieren','Registrieren','Registrieren','index',NULL,NULL,'',0,0,0,0,1,'','Access','signup');
