getElementById(\'shipping_address\').style.display = disp;\r\n    }\r\n  }\r\n}\r\n// Redisplay the shipping address after the reset button has been clicked\r\nfunction shopReset()\r\n{\r\n  if (!confirm(\"{TXT_SHOP_FORM_RESET_CONFIRM}\")) {\r\n    return false;\r\n  }\r\n  document.getElementById(\'shipping_address\').style.display = \"block\";\r\n  return true;\r\n}\r\nfor each (var element in document.getElementsByTagName(\"input\")) {\r\n  element.onchange = function() { copy_address(); };\r\n}\r\nfor each (var element in document.getElementsByTagName(\"select\")) {\r\n  element.onchange = function() { copy_address(); };\r\n}\r\ncopy_address();\r\n//]]></script>\r\n',1,'content_shop.html',0,NULL,0,'','',0,0,'Kontoangaben','Kontoangaben','Kontoangaben','index',NULL,NULL,'',0,0,0,0,1,'','Shop','account');
