\r\n    				<!-- END icons -->\r\n    			<!-- </p> -->\r\n    			<p><label for=\"thread_subject\">{TXT_FORUM_SUBJECT}</label><input type=\"text\" name=\"thread_subject\" value=\"{FORUM_SUBJECT}\" /></p>\r\n    			<p><label for=\"thread_keywords\">{TXT_FORUM_KEYWORDS}</label><input onfocus=\"showToolTip(\'{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\', this, \'forumKeywordsHelp\')\" onblur=\"hideToolTip(\'forumKeywordsHelp\')\" title=\"{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\"  type=\"text\" name=\"thread_keywords\" value=\"{FORUM_KEYWORDS}\" /></p>\r\n    			<p><label for=\"forum_attachment\">{TXT_FORUM_FILE_ATTACHMENT}</label><input class=\"inputtext\" type=\"file\" value=\"{FORUM_ATTACHMENT_VALUE}\" name=\"forum_attachment\" /></p>\r\n    			<p><label for=\"thread_message\">{TXT_FORUM_MESSAGE}</label>{FORUM_MESSAGE_INPUT}</p>\r\n    			<!-- BEGIN captcha -->\r\n    			<p>\r\n    				<label for=\"captcha\">Captcha-Code</label><img src=\"{FORUM_CAPTCHA_IMAGE_URL}\" title=\"{FORUM_CAPTCHA_IMAGE_ALT}\" alt=\"{FORUM_CAPTCHA_IMAGE_ALT}\" /><br />\r\n    				<input type=\"text\" name=\"captcha\" /><input type=\"hidden\" name=\"offset\" value=\"{FORUM_CAPTCHA_OFFSET}\" />\r\n    			</p>\r\n    			<!-- END captcha -->\r\n    			<!-- BEGIN notificationRow -->\r\n    				<p><input id=\"forum_notify\" type=\"checkbox\" name=\"notification\" value=\"notify\" {FORUM_NOTIFICATION_CHECKBOX_CHECKED} /><label class=\"description\" for=\"forum_notify\">{TXT_FORUM_NOTIFY_NEW_POSTS}</label></p>\r\n    			<!-- END notificationRow -->\r\n    			<p><input type=\"reset\" value=\"{TXT_FORUM_RESET}\" />&nbsp;<input type=\"submit\" name=\"create\" value=\"{TXT_FORUM_CREATE_THREAD}\" />&nbsp;<input type=\"submit\" name=\"preview\" value=\"{TXT_FORUM_PREVIEW}\" /></p>\r\n    		</form>\r\n    	</fieldset> \r\n	</div>\r\n    <!-- END addThread -->\r\n</div>',0,'',0,NULL,0,'0',NULL,0,0,'Forum anzeigen','Forum anzeigen','Forum anzeigen','index',NULL,NULL,'',0,0,0,0,1,'','Forum','board');
