INSERT INTO `contrexx_module_calendar_event_field` (`event_id`, `lang_id`, `title`, `teaser`, `description`, `redirect`) VALUES (88,'2','Contrexx celebrates 10 years of success!',NULL,'Contrexx celebrates 10 years of success! Since 10 years Contrexx is growing and growing an growing. We had a great time developing one of the most secure and easy-to-handle web content management systems. Now its time to celebrate this success and prepare for the next WCMS milestone.','');
INSERT INTO `contrexx_module_calendar_event_field` (`event_id`, `lang_id`, `title`, `teaser`, `description`, `redirect`) VALUES (88,'1','Contrexx Software feiert 10. Jubiläum',NULL,'<h2>Geschichte</h2>\r\nDas <strong>Contrexx&reg; Open Source Web Content Management System</strong> wurde von der Unternehmung <a class=\"out\" href=\"http://www.comvation.com/\" target=\"_blank\">COMVATION AG</a> aus der Schweiz in der serverseitigen Skriptsprache PHP geschrieben, ist plattformunabh&auml;ngig und ben&ouml;tigt eine MySQL-Datenbank. Das erste Release wurde am 4. April 2005 ver&ouml;ffentlicht.\r\n\r\n<h2>&Uuml;ber Contrexx&reg;</h2>\r\n\r\n<div id=\"fe_PreviewContent\">\r\n<p>Mit <a class=\"out\" href=\"http://www.contrexx.com/\" target=\"_blank\">Contrexx&reg; Web Content Management System (WCMS)</a> werden Webseiten f&uuml;r Intranet, Extranet und Internet schnell, benutzerfreundlich und sicher erstellt. Die <a class=\"out\" href=\"http://www.contrexx.com/de/products/Leistungsumfang\" target=\"_blank\">standardisierten Module</a> von Contrexx&reg; erlauben ein rasches Umsetzen von individuellen Bed&uuml;rfnissen, wenn n&ouml;tig mit professioneller Unterst&uuml;tzung.<br />\r\nDas webbasierte System ist f&uuml;r die Verwaltung von flexiblen Internet- und Intranet L&ouml;sungen optimiert. Der modulare Aufbau und das Workflow System erm&ouml;glicht eine 100% Individualisierung der Logik und des Designs und f&ouml;rdert ein konsistentes und stetig wachsendes Wissensnetzwerk.<br />\r\n<br />\r\nDie Downloadversion beinhaltet einen Webinstaller, sowie verschiedene Module wie ein Linkverzeichnis, Podcast, Onlineschalter, Adressverzeichnis, Sitemap, Bildergalerie, Newsletter, G&auml;stebuch, RSS Feed Verzeichnis, News System, Abstimmung, Online Shop, Bannerverwaltung, Blockmodul und die M&ouml;glichkeiten f&uuml;r passwortgesch&uuml;tzte Bereiche.</p>\r\n\r\n<h2>Professioneller und innovativer Vertrieb</h2>\r\nContrexx&reg; wird weltweit &uuml;ber ein Partnernetzwerk vertrieben. Die <a class=\"out\" href=\"http://www.contrexx.com/?section=mediadir&amp;cmd=40\" target=\"_blank\">Contrexx&reg; Solutions Partner</a> sind autorisierte Partner welche ausgewiesene Kompetenzen im den Bereichen WebDesign, Grafik oder Internetmarketing besitzen. Damit erhalten Sie als Kunde professionelle Unterst&uuml;tzung mit hohen Kompetenzen aus Ihrer N&auml;he.</div>\r\n','');
INSERT INTO `contrexx_module_calendar_event_field` (`event_id`, `lang_id`, `title`, `teaser`, `description`, `redirect`) VALUES (89,'1','Veranstaltung erstellen',NULL,'Beschreiben Sie zuerst, worum es bei Ihrer Veranstaltung geht. Erg&auml;nzen Sie danach in den Tabs die weiteren Informationen.','');
INSERT INTO `contrexx_module_calendar_mail` (`id`, `title`, `content_text`, `content_html`, `recipients`, `lang_id`, `action_id`, `is_default`, `status`) VALUES (1,'[[URL]] - Einladung zu [[TITLE]]','Hallo [[FIRSTNAME]] [[LASTNAME]] \r\n\r\nSie wurden auf [[URL]] zum Event \\\"[[TITLE]]\\\" eingeladen.\r\nDetails: [[LINK_EVENT]]\r\n\r\nFolgen Sie dem unten stehenden Link um sich f&uuml;r diesen Event an- oder abzumelden.\r\nHinweis: Sollte der Link nicht funktionieren, kopieren Sie die komplette Adresse ohne Zeilenumbr&uuml;che in die Adresszeile Ihres Browsers und dr&uuml;cken Sie anschliessend \\\"Enter\\\".\r\n\r\n[[LINK_REGISTRATION]]\r\n\r\n\r\n--\r\nDiese Nachricht wurde automatisch generiert\r\n[[DATE]]','Hallo [[FIRSTNAME]] [[LASTNAME]]<br />\r\n<br />\r\nSie wurden auf <a href=\"http://[[URL]]\" title=\"[[URL]]\">[[URL]]</a> zum Event <a href=\"[[LINK_EVENT]]\" title=\"Event Details\">&quot;[[TITLE]]&quot;</a> eingeladen. <br />\r\nKlicken Sie <a href=\"[[LINK_REGISTRATION]]\" title=\"Anmeldung\">hier</a>, um sich an diesem Event an- oder abzumelden.<br />\r\n<br />\r\n<br />\r\n--<br />\r\n<em>Diese Nachricht wurde automatisch generiert</em><br />\r\n<em>[[DATE]]</em>','',1,1,1,1);
