INSERT INTO `contrexx_module_news_locale` (`news_id`, `lang_id`, `is_active`, `title`, `text`, `teaser_text`) VALUES (4,1,1,'Was ist Cloudrexx?','<h2>Was ist <a href=\"http://www.cloudrexx.com\" target=\"_blank\">Cloudrexx</a> ?</h2>\r\nCloudrexx ist eine f&uuml;hrende Schweizer Customer-Experience-Management-L&ouml;sung (CXM). Der neue Cloud-Service auf&nbsp;Basis der Website Management L&ouml;sung Conrexx ist&nbsp;optimiert f&uuml;r Unternehmen aus der Schweiz, Deutschland und &Ouml;sterreich.&nbsp;<br />\r\n&nbsp;\r\n<h2>Was ist mit&nbsp;<a href=\"http://www.cloudrexx.com\" target=\"_blank\">Cloudrexx</a> m&ouml;glich?</h2>\r\n\r\n<ul>\r\n	<li><strong>Web Content Management:&nbsp;</strong>Mit Cloudrexx erstellen und verwalten Sie Ihrer Website schnell und einfach. In der WYSIWYG-Umgebung funktioniert das Gestalten von Inhaltsseiten mit Texten, Tabellen und Bildern &auml;hnlich wie bei Word. Alle Inhaltsseiten sind in der &uuml;bersichtlichen Seitenverwaltung abgelegt. Dort besteht ausserdem die M&ouml;glichkeit, Benutzergruppen zu erstellen und Benutzerrechte zu definieren.</li>\r\n	<li><strong>Online Marketing:&nbsp;</strong>Cloudrexx bietet ein umfassendes Set an Funktionen, um ohne zus&auml;tzliche Spezialsoftware Online-Marketing &nbsp;zu betreiben. Optimieren Sie mit <span style=\"line-height: 20.7999992370605px;\">Cloudrexx&nbsp;</span>Ihre Website dank ausgefeilter Search Engine Optimization (SEO) oder machen Sie Ihre Kunden mit regelm&auml;ssigen Newslettern gezielt &nbsp;auf neue Produkte oder Aktionen aufmerksam. Die Anbindung an die Social-Media-Kan&auml;le ist ebenfalls gew&auml;hrleistet.</li>\r\n	<li><strong>E-Commerce:</strong>&nbsp;Bauen Sie Ihre Website mit <span style=\"line-height: 20.7999992370605px;\">Cloudrexx&nbsp;</span>zum Verkaufskanal aus. Die Software enth&auml;lt ein voll funktionst&uuml;chtiges Shop-Modul, in das Sie Produkteinfos und Fotos einpflegen sowie die Bezahlung abwickeln k&ouml;nnen. Per Knopfdruck k&ouml;nnen Sie die Produkte ver&ouml;ffentlichen und ab sofort zum Verkauf anbieten. Dank der Anbindung verschiedener Zahlungsanbieter k&ouml;nnen Sie praktisch alle verf&uuml;gbaren Zahlungsarten anbieten.</li>\r\n	<li><strong>CRM:</strong>&nbsp;Erh&ouml;hen Sie die Zufriedenheit Ihrer Kunden durch ein professionelles Kundenbeziehungsmanagement. Bearbeiten Sie Kundenanfragen ohne Umweg &uuml;ber ein zus&auml;tzliches Tool direkt in <span style=\"line-height: 20.7999992370605px;\">Cloudrexx&nbsp;</span>und profitieren von den Synergien zu den anderen Modulen. Bedienen Sie Ihre Zielgruppen bed&uuml;rfnisgerecht und individuell.</li>\r\n	<li><strong>Analytics:&nbsp;</strong>Wer die besten Informationen rechtzeitig, komplett und am richtigen Ort zur Hand hat, setzt sich durch. Messen Sie zum Beispiel, wer Ihre Website besucht, welches Ihre bestbesuchten Seiten sind und von wo die Besucher herkommen. Nutzen Sie diese Informationen, um Ihren Auftritt und Ihr Angebot zu optimieren. Erkennen Sie dank den &uuml;bersichtlichen Charts in <span style=\"line-height: 20.7999992370605px;\">Cloudrexx&nbsp;</span>die neusten Trends.</li>\r\n	<li><strong>Media Asset Management:</strong>&nbsp;<span style=\"line-height: 20.7999992370605px;\">Cloudrexx&nbsp;</span>beinhaltet ein System zum Verwalten aller g&auml;ngigen Dateiformate. Medienmanager und Bildmanager erlauben das nachtr&auml;gliche Bearbeiten von Bildern, das Bereitstellen von Downloads sowie ein intelligentes System zum Verschicken von Downloadlinks. Zudem laden Sie mit dem Multi-File-Uploader beliebig viele Dateien gleichzeitig in k&uuml;rzester Zeit hoch..</li>\r\n</ul>\r\n<br />\r\n<span style=\"line-height: 20.7999992370605px;\">Dank den umfangreichen Funktionalit&auml;ten bietet Cloudrexx f&uuml;r praktisch s&auml;mtliche Bed&uuml;rfnisse, die heute an ein Online-Business&nbsp;gestellt werden, eine L&ouml;sung an.</span>','Wir haben für unsere Website ein Contrexx CMS gewählt.');
INSERT INTO `contrexx_module_news_locale` (`news_id`, `lang_id`, `is_active`, `title`, `text`, `teaser_text`) VALUES (6,1,1,'Die 3 besten Gründe für einen Supportplan','Ab CHF 30.&ndash; erhalten Sie mit einem Supportplan ein Rundum-Sorglos-Paket f&uuml;r Ihre Contrexx-Website.&nbsp;W&auml;hlen Sie zwischen den Supportpl&auml;nen Basic, Business oder Enterprise aus. Wir beantworten Ihre Fragen, beheben Fehler und finden f&uuml;r jedes Problem eine L&ouml;sung. Dabei profitieren Sie gleich mehrfach.\r\n<ul>\r\n	<li>\r\n	<h2>Sie sparen bis zu CHF 110.&ndash; pro Supportfall.</h2>\r\n	</li>\r\n	<li>\r\n	<h2>Sie minimieren die garantierten Antwortzeiten</h2>\r\n	</li>\r\n	<li>\r\n	<h2>um bis zu 20 Stunden.*</h2>\r\n	</li>\r\n</ul>\r\nSie profitieren von attraktiven Zusatzleistungen.Ein Supportplan garantiert Ihnen die professionelle Unterst&uuml;tzung durch unser Support-Team innerhalb einer definierten Reaktionszeit.<br />\r\n<br />\r\n<a href=\"http://www.comvation.com/de/Support/Supportplan-Bestellung\">Einen Supportplan bestellen</a><br />\r\n<br />\r\n<a href=\"http://www.comvation.com/de/news/Newsmeldung?newsid=2462\">Im Support bedient Sie Raphael Fregien, Leiter Support.</a><br />\r\n<br />\r\n* Reaktionszeit ohne Supportplan: bis zu 24 Stunden. Mit Supportplan: 16, 12 oder 4 Stunden.','');
