INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (111,37,'<script type=\"text/javascript\">\r\n//<![CDATA[\r\n	{IMMO_DETAILS_JAVASCRIPT}\r\n	\r\nvar fields=new Array(\"name\",\"telephone\",\"email\");\r\n\r\nvar checkAllFields = function(){\r\n   for(i in fields){\r\n     if(trim(document.getElementById(fields[i]).value)==\'\'){\r\n      alert(\"{TXT_IMMO_FIELDS_REQUIRED}\"); \r\n      return false;\r\n     }\r\n   }\r\n}\r\nfunction trim(s) {\r\n  while (s.substring(0,1) == \' \') {\r\n    s = s.substring(1,s.length);\r\n  }\r\n  while (s.substring(s.length-1,s.length) == \' \') {\r\n    s = s.substring(0,s.length-1);\r\n  }\r\n  return s;\r\n}\r\n\r\n//]]>\r\n</script>\r\n<br />\r\n{TXT_IMMO_STATUS}\r\n<div id=\"immo\">\r\n<form id=\"contactForm\" onsubmit=\"return checkAllFields();\" method=\"post\" action=\"index.php?section=immo&amp;cmd=getPDF\">\r\n    <table border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td class=\"contact\"> {TXT_IMMO_NAME}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"name\" name=\"name\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_FIRSTNAME}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"firstname\" name=\"firstname\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_COMPANY}</td>\r\n                <td><input type=\"text\" value=\"\" id=\"company\" name=\"company\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_STREET}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"street\" name=\"street\" style=\"width: 300px;\"