INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (578,31,'<!-- START memberdir -->\r\n<div id=\"memberdir\">\r\n    <div class=\"search_form\">\r\n		<form action=\"index.php?section=memberdir\" method=\"get\">\r\n			<input type=\"hidden\" name=\"search\" value=\"search\" /> <input type=\"hidden\" name=\"section\" value=\"memberdir\" /> <input onclick=\"this.value=\'\'\" name=\"keyword\" value=\"{MEMBERDIR_KEYWORD}\" /> {MEMBERDIR_DROPDOWN} <input type=\"submit\" value=\"{MEMBERDIR_SEARCH}\" />\r\n		</form>\r\n	</div>\r\n	<div class=\"category_tree\"><!-- BEGIN tree --><a title=\"{TXT_OVERVIEW}\" href=\"index.php?section=memberdir\">{TXT_OVERVIEW}</a> <!-- BEGIN tree-element -->&gt; <a title=\"{MEMBERDIR_DIRNAME}\" href=\"index.php?section=memberdir&amp;id={MEMBERDIR_DIRID}\">{MEMBERDIR_DIRNAME}</a><!-- END tree-element --><!-- END tree --></div>\r\n	<h2>{MEMBERDIR_DESCRIPTION}</h2>\r\n	\r\n	<!-- BEGIN category_list -->\r\n	<script type=\"text/javascript\">\r\n	/* <![CDATA[ */\r\n		function openSubDir(obj) {\r\n			var id = obj.id.substr(10);\r\n			document.getElementById(\"block_\" + id).style.display = \"block\";\r\n			obj.src = \"images/modules/memberdir/minuslink.gif\";\r\n			obj.onclick = function() {closeSubDir(this)};\r\n		}\r\n		\r\n		function closeSubDir(obj) {\r\n			var id = obj.id.substr(10);\r\n			document.getElementById(\"block_\" + id).style.display = \"none\";\r\n			obj.src = \"images/modules/memberdir/pluslink.gif\";\r\n			obj.onclick = function() {openSubDir(this)};\r\n		}\r\n	/* ]]> */\r\n	</script>\r\n	\r\n	<div class=\"subcategories\">\r\n		<h3>{TXT_CATEGORY_TREE_DESC}</h3>\r\n		<!-- BEGIN category -->\r\n		<!-- BEGIN div-block-beginning -->\r\n		<div id=\"block_{MEMBERDIR_PARENT_ID}\" style=\"display: none; padding-left: {MEMBERDIR_PADDING_LEFT}px;\">\r\n		<!-- END div-block-beginning -->\r\n			<div class=\"directory\">\r\n				<img id=\"memberdir_{MEMBERDIR_DIR_ID}\" style=\"border: none; width: 11px; height: 11px; vertical-align:\" onclick=\"openSubDir(this);\" alt=\"Open subdir\" src=\"cadmin/images/icons/{MEMBERDIR_IMAGE_SRC}\" /> \r\n				<img src=\"images/modules/memberdir/folder.gif\" style=\"position: relative; top: 4px;\"/><a style=\"vertical-align: \" title=\"{MEMBERDIR_DIR_NAME}\" href=\"index.php?section=memberdir&amp;id={MEMBERDIR_DIR_ID}\">{MEMBERDIR_DIR_NAME}</a>\r\n			</div>\r\n			<!-- BEGIN div-block-ending -->\r\n		</div>\r\n		<!-- END div-block-ending -->\r\n		<!-- END category -->\r\n	</div>\r\n	<!-- END category_list -->\r\n	\r\n	<!-- BEGIN category_show -->\r\n	<div class=\"char_list\">{MEMBERDIR_CHAR_LIST}</div>\r\n	<div class=\"adress_list\">\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th>Name</th>\r\n					<th>Address</th>\r\n				</tr>\r\n				<!-- BEGIN memberdir_row -->\r\n				<tr class=\"row{MEMBERDIR_ROW}\">\r\n					<td valign=\"top\"><a title=\"{FIELD_NAME} {FIELD_VORNAME}\" href=\"index.php?section=memberdir&amp;mid={MEMBERDIR_ID}\"><strong>{FIELD_NAME} {FIELD_VORNAME}</strong></a></td>\r\n					<td valign=\"top\">{FIELD_ADRESSE}<br />{FIELD_PLZ} {FIELD_ORT}</td>\r\n				</tr>\r\n				<!-- END memberdir_row -->\r\n			</tbody>\r\n		</table>\r\n	</div>\r\n	<div class=\"paging\">{MEMBERDIR_PAGING}</div>\r\n	<!-- END category_show -->\r\n	\r\n	<!-- BEGIN memberdir_detail_view -->\r\n	<div class=\"detail_list\">\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr class=\"row1\">\r\n					<td valign=\"top\" width=\"50%\">{MEMBERDIR_PIC1}<br /></td>\r\n					<td  valign=\"top\" width=\"50%\">{MEMBERDIR_PIC2}<br /></td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th>Personal data</th>\r\n					<th><br /></th>\r\n				</tr>\r\n				<!-- BEGIN row -->\r\n				<tr class=\"row1\">\r\n					<td valign=\"top\" width=\"150\"><strong>{MEMBERDIR_FIELD_NAME}:</strong></td>\r\n					<td  valign=\"top\">{MEMBERDI