INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (579,10,'<div id=\"guestbook\">\r\n	<div class=\"count_entries\">{GUESTBOOK_TOTAL_ENTRIES} entries.</div>\r\n	<div class=\"add_entry\"><input type=\"button\" onClick=\"location.href=\'index.php?section=guestbook&amp;cmd=post\'\" value=\"Sign\" /></div>\r\n	<div class=\"entries\">\r\n		 <!-- BEGIN guestbook_row -->\r\n		 	<div class=\"entry {GUESTBOOK_ROWCLASS}\">\r\n				<div class=\"title\"><h3>{GUESTBOOK_NAME} {GUESTBOOK_FORENAME}</h3></div>\r\n				<div class=\"location\">{GUESTBOOK_GENDER} {GUESTBOOK_LOCATION}</div>\r\n				<div class=\"date\">{GUESTBOOK_DATE}</div>\r\n				<div class=\"message\">{GUESTBOOK_COMMENT}</div>\r\n				<div class=\"links\">{GUESTBOOK_EMAIL} {GUESTBOOK_URL}</div>\r\n			</div>\r\n		 <!-- END guestbook_row -->\r\n	</div>\r\n	<div class=\"paging\">{GUESTBOOK_PAGING}</div>\r\n</div>','Guestbook','','y',0,'on','system',70);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (580,10,'<div id=\"guestbook\">\r\n	<!-- BEGIN guestbookForm -->\r\n	<div class=\"error_message\">{ERROR}</div>\r\n	<div class=\"information\">You can sign the guestbook here.<br />All fields marked with a <font color=\"red\">*</font> must be filled.</div>\r\n	<div class=\"form\">\r\n		<form method=\"post\" action=\"index.php?section=guestbook&amp;cmd=post\" name=\"GuestbookForm\">\r\n			<p><label for=\"forename\">Forename<font color=\"red\"> *</font></label><input type=\"text\" value=\"{FORENAME}\" name=\"forename\" id=\"forename\" size=\"60\" maxlength=\"255\" /></p>\r\n			<p><label for=\"name\">Surname<font color=\"red\"> *</font></label><input type=\"text\" value=\"{NAME}\" name=\"name\" id=\"name\"  maxlength=\"255\" /></p>\r\n			<p><label for=\"comment\">Comment<font color=\"red\"> *</font></label><textarea cols=\"1\" rows=\"1\" id=\"comment\" name=\"comment\">{COMMENT}</textarea></p>\r\n			<p><label>Sex</label><input type=\"radio\" id=\"female\" name=\"malefemale\" value=\"F\" {FEMALE_CHECKED}/><label class=\"description\" for=\"female\">Female</label></p>\r\n			<p><input type=\"radio\" id=\"male\" name=\"malefemale\" value=\"M\" {MALE_CHECKED}/><label class=\"description\" for=\"male\"> Male</label></p>\r\n			<p><label for=\"location\">City<font color=\"red\"> *</font></label><input type=\"text\" value=\"{LOCATION}\" id=\"location\" name=\"location\" maxlength=\"255\" /></p>\r\n			<p><label for=\"email\">E-mail<font color=\"red\"> *</font></label><input type=\"text\" value=\"{EMAIL}\" id=\"email\" name=\"email\" maxlength=\"255\" /></p>\r\n			<p><label for=\"url\">Website</label><input type=\"text\" maxlength=\"255\" value=\"{HOMEPAGE}\" id=\"url\" name=\"url\"/></p>\r\n			<p><label for=\"captcha\">Captcha</label><img title=\"{IMAGE_ALT}\" alt=\"{IMAGE_ALT}\" src=\"{IMAGE_URL}\" /><br /><br />{TXT_CAPTCHA} - To keep Spam bots from entering unwanted advertisement, please enter the characters shown in the image into the field below.<br /><br /><input type=\"text\" id=\"captcha\" name=\"captcha\" /><input type=\"hidden\" value=\"{CAPTCHA_OFFSET}\" name=\"offset\" /> </p>\r\n			<p><input type=\"reset\" name=\"Submit\" value=\"&nbsp;Reset&nbsp;\" />&nbsp;&nbsp; <input type=\"submit\" name=\"save\" value=\"&nbsp;Save&nbsp;\" /></p>\r\n		</form>\r\n	</div>\r\n	<!-- END guestbookForm -->\r\n	<!-- BEGIN guestbookStatus -->\r\n	{GUESTBOOK_STATUS}<br />\r\n	<br />\r\n	<a href=\"index.php?section=guestbook\">Back to guestbook</a>\r\n	<!-- END guestbookStatus -->\r\n</div>\r\n\r\n','Sign Guestbook','post','y',579,'on','system',0);
