INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (594,23,'<div id=\"access\">\r\n	<!-- BEGIN access_settings -->\r\n		<div class=\"message_error\">{ACCESS_SETTINGS_MESSAGE}</div>\r\n		<div class=\"profile\">\r\n			<form method=\"post\" action=\"index.php?section=access&amp;cmd=settings_delete\">\r\n				To delete your account, please enter your password.<br /><br />\r\n				<p><label>Password</label>{ACCESS_USER_PASSWORD_INPUT}<br /><br /></p>\r\n				Your entries in forums, directories etc will NOT be deleted.<br />\r\n				If you want to delete them too, you need to do this BEFORE you delete your account.<br />\r\n				<br />\r\n				<b>NOTE: Deleting your account cannot be undone!</b><br />\r\n				<br />\r\n				<p><label>Confirm</label>{ACCESS_DELETE_ACCOUNT_BUTTON}</p>\r\n			</form>\r\n		</div>\r\n	<!-- END access_settings --> \r\n	<!-- BEGIN access_settings_done --> \r\n		<div class=\"message_ok\">{ACCESS_SETTINGS_MESSAGE}</div>\r\n	<!-- END access_settings_done --> \r\n	{ACCESS_JAVASCRIPT_FUNCTIONS}\r\n</div>','Delete account','settings_delete','y',591,'on','system',4);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (595,54,'<script language=\"javascript\">\r\nfunction showBcc() {\r\n document.getElementById(\"bcc\").style.display=\"block\";\r\ndocument.getElementById(\"show\").style.display=\"none\";\r\n}\r\nfunction validate()  {\r\n\r\nvar textarea = document.getElementsByTagName(\"textarea\")[0];\r\n       \r\n	if(textarea.value==\"\") {\r\n              alert(\"please enter the recepients name\");\r\n              return false;\r\n \r\n        }\r\n\r\n}\r\nfunction drop_down_block() {\r\n      document.getElementById(\"radio_send\").style.display=\"none\";\r\n      document.getElementById(\"multi_send\").style.display=\"block\";\r\n      document.send_message.rcpt_name.value=\"\"; \r\n}\r\nfunction multi_send_block() {\r\n      document.getElementById(\"radio_send\").style.display=\"block\";\r\n      document.getElementById(\"multi_send\").style.display=\"none\";\r\n}\r\n\r\n</script>\r\n\r\n<div id=\"u2u\">\r\n    <!-- BEGIN u2u_private_notification_message_link --> \r\n    <div class=\"message\"><a href=\"?section=u2u&amp;cmd=notification\">{TXT_PRIVATE_NOTIFICATION_LINK}</a><b>{TXT_NOTIFICATION_TOTAL_COUNT}</b></div>\r\n    <!-- END u2u_private_notification_message_link --> \r\n    \r\n    <!-- BEGIN u2u_send_confirm_error --> \r\n    <div class=\"message_error\">{U2U_SEND_MESSAGE}</div>\r\n    <!-- END u2u_send_confirm_error -->   \r\n                                  \r\n    <!-- BEGIN u2u_send_confirm_success --> \r\n    <div class=\"message_ok\">{U2U_SEND_MESSAGE}</div>\r\n    <!-- END u2u_send_confirm_success -->  \r\n    \r\n    <br style=\"clear: both;\" />\r\n    \r\n    <form action=\"index.php?section=u2u&amp;act=message\" method=\"post\" name=\"send_message\">\r\n        <fieldset>\r\n            <legend>{TXT_RECEPIENTS}</legend>\r\n            <p><label>Select recipient</label><input type=\"radio\" id=\"rcpt\" name=\"rcpt\" checked=\"checked\" onclick=\"drop_down_block();\" /><label class=\"description\">{TXT_U2U_SEND_MESSAGE_USERS}</label></p>\r\n            <p><input type=\"radio\" name=\"rcpt\" id=\"rcpt\" value=\"1\" onclick=\"multi_send_block();\" /><label class=\"description\">{TXT_U2U_SELECT_BUDDY}</label></p>\r\n            \r\n            <div style=\"display: none;\" id=\"radio_send\">\r\n                <p><label>Select a friend</label><select name=\"rcpt_name\"><option value=\"\">Please choose</option><!-- BEGIN u2u_user_drop_down --><option value=\"{TXT_U2U_OPTION_VALUE}\">{TXT_U2U_OPTION_VALUE}</option><!-- END u2u_user_drop_down --></select></p>\r\n            </div>\r\n            \r\n            <div id=\"multi_send\">\r\n                <p><label>{TXT_RECEPIENTS_USERNAME}</label><input type=\"text\" maxlength=\"60\"  value=\"{TXT_RECEPIENT}\" name=\"recipients\" /></p>\r\n                <p><label>{TXT_BCC_RECEPIENTS_USERNAME}</label><input type=\"text\" maxlength=\"60\" value=\"{TXT_BCC}\" name=\"bcc\" /></p>\r\n                <p>{TXT_MESSAGE_CONTENT}</p>\r\n            </div>\r\n        </fieldset>\r\n        \r\n        <fieldset>\r\n            <legend>Message</legend>\r\n            <p><label>{TXT_MESSAGE_TITLE}</label><input type=\"text\" maxlength=\"60\" value=\"{TXT_PRIVATE_MESSAGE_TITLE}\" name=\"title\" /></p>\r\n            {MESSAGE_INPUT}\r\n            <p><input type=\"submit\" name=\"submit\" value=\"{TXT_U2U_SUBMIT_MESSAGE}\" /><input type=\"submit\" name=\"preview\" value=\"{TXT_U2U_PREVIEW_MESSAGE}\" /></p>\r\n        </fieldset>\r\n    </form>   \r\n\r\n    <!-- BEGIN u2u_preview_message -->\r\n    <div class=\"preview\">\r\n        <fieldset>\r\n            <legend>{TXT_U2U_PREVIEW_MESSAGE}</legend>\r\n            <h2>{TXT_U2U_PREVIEW_HEADER}</h2>\r\n            {TXT_U2U_PREVIEW_SUBJECT}<br />\r\n            <br />\r\n            {TXT_U2U_PREVIEW_WEBSITE}\r\n        </fieldset>\r\n    </div>\r\n    <!-- END u2u_preview_message -->\r\n</div>','New Message','','y',0,'on','system',10);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (596,20,'{FORUM_JAVASCRIPT} \r\n{FORUM_JAVASCRIPT_GOTO}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<div class=\"latest\">\r\n		<h2>{TXT_FORUM_LATEST_ENTRIES}</h2>\r\n		<table cellspacing=\"0\">\r\n			<tr>\r\n				<th width=\"60%\">{TXT_FORUM_THREAD}</th>\r\n				<th width=\"22%\" nowrap=\"nowrap\" align=\"left\">{TXT_FORUM_THREAD_STRATER}</th>		\r\n				<th width=\"8%\" nowrap=\"nowrap\" align=\"center\">{TXT_FORUM_POST_COUNT}</th>		\r\n				<th width=\"10%\" nowrap=\"nowrap\" align=\"center\">{TXT_FORUM_THREAD_CREATE_DATE}</th>\r\n			</tr>\r\n			<!-- BEGIN latestPosts -->\r\n			<tr class=\"row{FORUM_ROWCLASS}\">\r\n				<td><b>{FORUM_THREAD}<b></td>\r\n				<td nowrap=\"nowrap\" align=\"left\" class=\"smallfont\">{FORUM_THREAD_STARTER}</td>\r\n				<td nowrap=\"nowrap\" align=\"center\">{FORUM_POST_COUNT}</td>\r\n				<td nowrap=\"nowrap\" align=\"center\">{FORUM_THREAD_CREATE_DATE}</td>\r\n			</tr>	\r\n			<!-- END latestPosts -->	\r\n		</table>\r\n	</div>\r\n	<!-- BEGIN forumMainCategory -->\r\n	<div class=\"category\">\r\n		<h2>{FORUM_MAINCATEGORY_NAME_TITLE}</h2>{FORUM_MAINCATEGORY_NAME_DESCRIPTION}\r\n		<div style=\"display: block;\" id=\"maincat_{FORUM_MAINCATEGORY_ID}\">\r\n			<table cellspacing=\"0\">\r\n				<tr>\r\n					<th width=\"50%\">{TXT_FORUM}</th>\r\n					<th width=\"49%\" nowrap=\"nowrap\">{TXT_LASTPOST}</th>		\r\n					<th width=\"1%\" nowrap=\"nowrap\" align=\"center\">{TXT_THREADS}</th>		\r\n				</tr>\r\n				<!-- BEGIN forumSubCategory -->\r\n				<tr class=\"row{FORUM_SUBCATEGORY_ROWCLASS}\">\r\n					<td valign=\"top\">\r\n						<div style=\"padding-left: {FORUM_SUBCATEGORY_SPACER}px;\" title=\"{FORUM_SUBCATEGORY_DESC}\">\r\n							<a title=\"{FORUM_SUBCATEGORY_NAME}\" href=\"index.php?section=forum&amp;cmd=board&amp;id={FORUM_SUBCATEGORY_ID}\"><b>{FORUM_SUBCATEGORY_NAME}</b></a><br />{FORUM_SUBCATEGORY_DESC}\r\n						</div>\r\n					</td>\r\n					<td nowrap=\"nowrap\" valign=\"top\">{FORUM_SUBCATEGORY_LASTPOST_TITLE} - {FORUM_SUBCATEGORY_LASTPOST_DATE}</td>\r\n					<td nowrap=\"nowrap\" align=\"center\" valign=\"top\">{FORUM_SUBCATEGORY_THREADS}</td>\r\n				</tr>	\r\n				<!-- END forumSubCategory -->\r\n			</table>\r\n		</div>\r\n	</div>\r\n	<!-- END forumMainCategory --> \r\n</div>','Forum','','y',0,'on','system',20);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (597,20,'{FORUM_JAVASCRIPT_NOTIFICATION}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<!-- BEGIN notification -->\r\n	<fieldset> \r\n        <legend>{TXT_FORUM_THREAD_NOTIFICATION}</legend>\r\n        <form id=\"frmForumNotification\" action=\"index.php?section=forum&amp;cmd=notification\" method=\"post\" onsubmit=\"SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">\r\n            <div class=\"notification\">\r\n    			<div class=\"notification_left\">\r\n    				<h3>Available forums / categories</h3>\r\n    				<select name=\"unsubscribed[]\" size=\"32\" multiple=\"multiple\">\r\n    				{FORUM_NOTIFICATION_UNSUBSCRIBED}\r\n    				</select>\r\n    				<br />\r\n    				<a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\">{TXT_SELECT_ALL}<br />\r\n    				</a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\">{TXT_DESELECT_ALL}</a><br />\r\n    			</div>\r\n    			<div class=\"notification_center\">\r\n    				<input type=\"button\" value=\"&gt;&gt;\" name=\"adduser\" onclick=\"AddToTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" /><br />\r\n    				<input type=\"button\" value=\"&lt;&lt;\" name=\"removeuser\" onclick=\"RemoveFromTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" />\r\n    			</div>\r\n    			<div class=\"notification_right\">\r\n    				<h3>Subscribed forums / categories</h3>\r\n    				<select name=\"subscribed[]\" size=\"32\" multiple=\"multiple\">\r\n    				{FORUM_NOTIFICATION_SUBSCRIBED}\r\n    				</select>\r\n    				<br />\r\n    				<a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_SELECT_ALL}<br />\r\n    				</a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_DESELECT_ALL}</a>\r\n    			</div>\r\n            </div>\r\n        </form>\r\n        <input type=\"submit\" value=\"{TXT_FORUM_NOTIFICATION_SUBMIT}\" name=\"forumNotificationSubmit\" />\r\n    </fieldset> \r\n	<!-- END notification -->\r\n</div>','Subscribe to forums / categories','notification','y',596,'on','system',0);
