INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (745,31,'<!-- START memberdir -->\r\n<div id=\"memberdir\">\r\n    <div class=\"search_form\">\r\n		<form action=\"index.php?section=memberdir\" method=\"get\">\r\n			<input type=\"hidden\" name=\"search\" value=\"search\" /> <input type=\"hidden\" name=\"section\" value=\"memberdir\" /> <input onclick=\"this.value=\'\'\" name=\"keyword\" value=\"{MEMBERDIR_KEYWORD}\" /> {MEMBERDIR_DROPDOWN} <input type=\"submit\" value=\"{MEMBERDIR_SEARCH}\" />\r\n		</form>\r\n	</div>\r\n	<div class=\"category_tree\"><!-- BEGIN tree --><a title=\"{TXT_OVERVIEW}\" href=\"index.php?section=memberdir\">{TXT_OVERVIEW}</a> <!-- BEGIN tree-element -->&gt; <a title=\"{MEMBERDIR_DIRNAME}\" href=\"index.php?section=memberdir&amp;id={MEMBERDIR_DIRID}\">{MEMBERDIR_DIRNAME}</a><!-- END tree-element --><!-- END tree --></div>\r\n	<h2>{MEMBERDIR_DESCRIPTION}</h2>\r\n	\r\n	<!-- BEGIN category_list -->\r\n	<script type=\"text/javascript\">\r\n	/* <![CDATA[ */\r\n		function openSubDir(obj) {\r\n			var id = obj.id.substr(10);\r\n			document.getElementById(\"block_\" + id).style.display = \"block\";\r\n			obj.src = \"images/modules/memberdir/minuslink.gif\";\r\n			obj.onclick = function() {closeSubDir(this)};\r\n		}\r\n		\r\n		function closeSubDir(obj) {\r\n			var id = obj.id.substr(10);\r\n			document.getElementById(\"block_\" + id).style.display = \"none\";\r\n			obj.src = \"images/modules/memberdir/pluslink.gif\";\r\n			obj.onclick = function() {openSubDir(this)};\r\n		}\r\n	/* ]]> */\r\n	</script>\r\n	\r\n	<div class=\"subcategories\">\r\n		<h3>{TXT_CATEGORY_TREE_DESC}</h3>\r\n		<!-- BEGIN category -->\r\n		<!-- BEGIN div-block-beginning -->\r\n		<div id=\"block_{MEMBERDIR_PARENT_ID}\" style=\"display: none; padding-left: {MEMBERDIR_PADDING_LEFT}px;\">\r\n		<!-- END div-block-beginning -->\r\n			<div class=\"directory\">\r\n				<img id=\