INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (867,8,'<h3>Online Integration using the RSS-Newsfeed</h3>\r\n<p><a class=\"rssfeed\" title=\"RSS Newsfeed of {NEWS_HOSTNAME}\" href=\"{NEWS_RSS_FEED_URL}\" style=\"\"><img width=\"48\" height=\"74\" border=\"0\" alt=\"The feed of the website can be requested using this link\" src=\"images/content/icons/rss_bg.gif\" style=\"margin: 5px 20px 20px; float: right;\" /></a>RSS is an electronic news format which allows users to receive updates from a website without the need of visiting the page at all.</p>\r\n<h3>Online Integration using Javascript</h3>\r\nThe feed of this website can also be displayed in your website in a very simple way. Simple put the following code into your website:<br />\r\n<br />\r\n<form action=\"\">\r\n    <textarea name=\"code\" cols=\"30\" rows=\"18\" style=\"width: 98%; font-size: 95%;\">{NEWS_RSS2JS_CODE}</textarea>     <br />\r\n    <br />\r\n    <input type=\"button\" name=\"button\" onclick=\"javascript:this.form.code.focus();this.form.code.select();\" value=\"Select all\" />\r\n</form>','News Feed','feed','n',865,'on','system',20);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (868,8,'<font color=\"#ff0000\">{NEWS_STATUS_MESSAGE}&nbsp;</font> \r\n<!-- BEGIN news_submit_form -->\r\n<form method=\"post\" action=\"index.php?section=news&amp;cmd=submit\">\r\n    <fieldset> \r\n    <legend>{TXT_NEWS_MESSAGE}</legend>\r\n    <p><label for=\"newsTitle\">{TXT_TITLE}</label><input id=\"newsTitle\" name=\"newsTitle\" value=\"{NEWS_TITLE}\" type=\"text\"/></p>\r\n    <p><label for=\"newsCat\">{TXT_CATEGORY}</label><select id=\"newsCat\" name=\"newsCat\"><!-- BEGIN news_category_menu --><option value=\"{NEWS_CATEGORY_ID}\">{NEWS_CATEGORY_TITLE}</option><!-- END news_category_menu --></select></p>\r\n    <p><label for=\"newsSource\">{TXT_EXTERNAL_SOURCE}</label><input id=\"newsSource\" name=\"newsSource\" value=\"{NEWS_SOURCE}\" type=\"text\"/></p>\r\n    <p><label for=\"newsUrl1\">{TXT_LINK} #1</label><input id=\"newsUrl1\" name=\"newsUrl1\" value=\"{NEWS_URL1}\" type=\"text\"/></p>\r\n    <p><label for=\"newsUrl2\">{TXT_LINK} #2</label><input id=\"newsUrl2\" name=\"newsUrl2\" value=\"{NEWS_URL2}\" type=\"text\"/></p>\r\n    <p><label for=\"newsTeaserText\">{TXT_NEWS_TEASER_TEXT}</label><textarea id=\"newsTeaserText\" name=\"newsTeaserText\">{NEWS_TEASER_TEXT}</textarea></p>\r\n    </fieldset> \r\n    <fieldset> \r\n    <legend>{TXT_NEWS_NEWS_CONTENT}</legend>\r\n    {TXT_NEWS_REDIRECT}<br /><br />\r\n    <p><label for=\"newsRedirect\">{TXT_NEWS_NEWS_URL}</label><input id=\"newsRedirect\" name=\"newsRedirect\" value=\"{NEWS_REDIRECT}\" type=\"text\" /></p>\r\n    {NEWS_TEXT}\r\n    <p><label>Captcha</label><img style=\"padding-top: 5px; padding-bottom: 5px;\" src=\"{IMAGE_URL}\" alt=\"{IMAGE_ALT}\" title=\"{IMAGE_ALT}\" /><br />{TXT_CAPTCHA}</p>\r\n    <p><input type=\"text\" name=\"captcha\" id=\"captcha\"/><input type=\"hidden\" name=\"offset\" value=\"{CAPTCHA_OFFSET}\" /></p>\r\n    <p><input type=\"reset\" onclick=\"FCKeditorAPI.GetInstance(\'newsText\').SetHTML(\'\')\" /> <input type=\"submit\" name=\"submitNews\" value=\"Submit\" /></p>\r\n    </fieldset> \r\n</form>\r\n<!-- END news_submit_form --> \r\n<!-- BEGIN news_submitted --> \r\n<a title=\"submit an other message\" href=\"?section=news&amp;cmd=submit\">Click here to submit an other message.</a> \r\n<!-- END news_submitted -->','Submit message','submit','y',865,'on','system',30);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (869,8,'{NEWS_TEXT}<br />\r\n<table cellspacing=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <th colspan=\"2\">Toolbox</th>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td width=\"1%\"><img width=\"16\" height=\"16\" alt=\"Print\" src=\"images/content/icons/printer-1.gif\" /></td>\r\n            <td width=\"99%\"><a href=\"{PRINT_URL}\">Drucken</a></td>\r\n        </tr>\r\n        <tr class=\"row2\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"Acrobat\" src=\"images/content/icons/page_white_acrobat.gif\" /></td>\r\n            <td><a href=\"{PDF_URL}\">PDF</a></td>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"Arrow Right\" src=\"images/content/icons/arrow_right.gif\" /></td>\r\n            <td><a href=\"index.php?section=recommend\">Weiterempfehlen</a></td>\r\n        </tr>\r\n        <tr class=\"row2\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"RSS\" src=\"images/content/icons/rss.gif\" /></td>\r\n            <td><a href=\"feed/news_headlines_de.xml\">RSS Abonnieren</a></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\n<table cellspacing=\"0\" border=\"0\" class=\"news_meta\">\r\n    <tbody>\r\n        <tr>\r\n            <td width=\"33%\">Ver&ouml;ffentlicht<br />{NEWS_DATE}</td>\r\n            <td width=\"33%\">{NEWS_SOURCE}</td>\r\n            <td width=\"33%\">{NEWS_URL}</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n</div>','Newsmeldung','details','y',865,'off','system',99);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (870,20,'{FORUM_JAVASCRIPT} \r\n{FORUM_JAVASCRIPT_GOTO}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<div class=\"latest\">\r\n		<h2>{TXT_FORUM_LATEST_ENTRIES}</h2>\r\n		<table cellspacing=\"0\">\r\n			<tr>\r\n				<th width=\"60%\">{TXT_FORUM_THREAD}</th>\r\n				<th width=\"22%\" nowrap=\"nowrap\" align=\"left\">{TXT_FORUM_THREAD_STRATER}</th>		\r\n				<th width=\"8%\" nowrap=\"nowrap\" align=\"center\">{TXT_FORUM_POST_COUNT}</th>		\r\n				<th width=\"10%\" nowrap=\"nowrap\" align=\"center\">{TXT_FORUM_THREAD_CREATE_DATE}</th>\r\n			</tr>\r\n			<!-- BEGIN latestPosts -->\r\n			<tr class=\"row{FORUM_ROWCLASS}\">\r\n				<td><b>{FORUM_THREAD}<b></td>\r\n				<td nowrap=\"nowrap\" align=\"left\" class=\"smallfont\">{FORUM_THREAD_STARTER}</td>\r\n				<td nowrap=\"nowrap\" align=\"center\">{FORUM_POST_COUNT}</td>\r\n				<td nowrap=\"nowrap\" align=\"center\">{FORUM_THREAD_CREATE_DATE}</td>\r\n			</tr>	\r\n			<!-- END latestPosts -->	\r\n		</table>\r\n	</div>\r\n	<!-- BEGIN forumMainCategory -->\r\n	<div class=\"category\">\r\n		<h2>{FORUM_MAINCATEGORY_NAME_TITLE}</h2>{FORUM_MAINCATEGORY_NAME_DESCRIPTION}\r\n		<div style=\"display: block;\" id=\"maincat_{FORUM_MAINCATEGORY_ID}\">\r\n			<table cellspacing=\"0\">\r\n				<tr>\r\n					<th width=\"50%\">{TXT_FORUM}</th>\r\n					<th width=\"49%\" nowrap=\"nowrap\">{TXT_LASTPOST}</th>		\r\n					<th width=\"1%\" nowrap=\"nowrap\" align=\"center\">{TXT_THREADS}</th>		\r\n				</tr>\r\n				<!-- BEGIN forumSubCategory -->\r\n				<tr class=\"row{FORUM_SUBCATEGORY_ROWCLASS}\">\r\n					<td valign=\"top\">\r\n						<div style=\"padding-left: {FORUM_SUBCATEGORY_SPACER}px;\" title=\"{FORUM_SUBCATEGORY_DESC}\">\r\n							<a title=\"{FORUM_SUBCATEGORY_NAME}\" href=\"index.php?section=forum&amp;cmd=board&amp;id={FORUM_SUBCATEGORY_ID}\"><b>{FORUM_SUBCATEGORY_NAME}</b></a><br />{FORUM_SUBCATEGORY_DESC}\r\n						</div>\r\n					</td>\r\n					<td nowrap=\"nowrap\" valign=\"top\">{FORUM_SUBCATEGORY_LASTPOST_TITLE} - {FORUM_SUBCATEGORY_LASTPOST_DATE}</td>\r\n					<td nowrap=\"nowrap\" align=\"center\" valign=\"top\">{FORUM_SUBCATEGORY_THREADS}</td>\r\n				</tr>	\r\n				<!-- END forumSubCategory -->\r\n			</table>\r\n		</div>\r\n	</div>\r\n	<!-- END forumMainCategory --> \r\n</div>','Forum','','y',0,'on','system',20);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (871,20,'{FORUM_JAVASCRIPT_NOTIFICATION}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<!-- BEGIN notification -->\r\n	<fieldset> \r\n        <legend>{TXT_FORUM_THREAD_NOTIFICATION}</legend>\r\n        <form id=\"frmForumNotification\" action=\"index.php?section=forum&amp;cmd=notification\" method=\"post\" onsubmit=\"SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">\r\n            <div class=\"notification\">\r\n    			<div class=\"notification_left\">\r\n    				<h3>Verf&uuml;gbare Foren/Kategorien</h3>\r\n    				<select name=\"unsubscribed[]\" size=\"32\" multiple=\"multiple\">\r\n    				{FORUM_NOTIFICATION_UNSUBSCRIBED}\r\n    				</select>\r\n    				<br />\r\n    				<a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\">{TXT_SELECT_ALL}<br />\r\n    				</a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\">{TXT_DESELECT_ALL}</a><br />\r\n    			</div>\r\n    			<div class=\"notification_center\">\r\n    				<input type=\"button\" value=\"&gt;&gt;\" name=\"adduser\" onclick=\"AddToTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" /><br />\r\n    				<input type=\"button\" value=\"&lt;&lt;\" name=\"removeuser\" onclick=\"RemoveFromTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" />\r\n    			</div>\r\n    			<div class=\"notification_right\">\r\n    				<h3>Abonnierte Foren/Kategorien</h3>\r\n    				<select name=\"subscribed[]\" size=\"32\" multiple=\"multiple\">\r\n    				{FORUM_NOTIFICATION_SUBSCRIBED}\r\n    				</select>\r\n    				<br />\r\n    				<a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_SELECT_ALL}<br />\r\n    				</a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_DESELECT_ALL}</a>\r\n    			</div>\r\n            </div>\r\n        </form>\r\n        <input type=\"submit\" value=\"{TXT_FORUM_NOTIFICATION_SUBMIT}\" name=\"forumNotificationSubmit\" />\r\n    </fieldset> \r\n	<!-- END notification -->\r\n</div>','Abonnierte Foren/Kategorien','notification','y',870,'on','system',0);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (872,20,'Eine <strong>Tag Cloud</strong> ist eine Methode zur Informationsvisualisierung, bei der eine Liste aus Schlagworten zweidimensional alphabetisch sortiert angezeigt wird, wobei einzelne unterschiedlich gewichtete Worte gr&ouml;sser oder auf andere Weise hervorgehoben dargestellt werden. Wortwolken werden zunehmend beim gemeinschaftlichen Indexieren und in Blogs eingesetzt. [<a target=\"_blank\" href=\"http://de.wikipedia.org/wiki/Tag_cloud\">Quelle</a>]\r\n<br /><br /><fieldset><legend>Beliebteste Tags</legend>{FORUM_TAG_HITLIST}</fieldset>\r\n<fieldset><legend>Tag Cloud</legend>{FORUM_TAG_CLOUD}</fieldset>','Tag Cloud','cloud','y',870,'on','system',1);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (873,20,'<div id=\"forum\">\r\n	<h2>Nach Aufrufe</h2>\r\n	<div class=\"div_board\">\r\n		<ol>\r\n		<!-- BEGIN mostViewed -->\r\n		<li>\r\n			<a href=\"{FORUM_MOST_VIEWED_LINK}\" title=\"{FORUM_MOST_VIEWED_SUBJECT}\">\r\n				{FORUM_MOST_VIEWED_SUBJECT} ({FORUM_MOST_VIEWED_VIEWS} mal aufgerufen)\r\n			</a><br />\r\n			Stichworte: {FORUM_MOST_VIEWED_KEYWORDS}\r\n			Inhalt: {FORUM_MOST_VIEWED_CONTENT}\r\n		</li>\r\n		<!-- END mostViewed -->\r\n		<ol>\r\n	</div>\r\n	<h2>Nach Bewertung</h2>\r\n	<div class=\"div_board\">\r\n		<ol>\r\n		<!-- BEGIN topRated -->\r\n		<li>\r\n		<a href=\"{FORUM_TOP_RATED_LINK}\" title=\"{FORUM_SUBJECT}\">\r\n			{FORUM_TOP_RATED_SUBJECT} (Bewertung: {FORUM_TOP_RATED_RATING})\r\n		</a><br />\r\n		Stichworte: {FORUM_TOP_RATED_KEYWORDS}\r\n		Inhalt: {FORUM_TOP_RATED_CONTENT}\r\n		</li>\r\n		<!-- END topRated -->\r\n		</ol>\r\n	</div>\r\n</div>','Populärste Diskussionen','toplist','y',870,'on','system',2);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (874,20,'<div id=\"forum\">\r\n	<div class=\"text\">\r\n		Hier kann in allen Beiträgen nach Stichworten gesucht werden. <br />\r\n		Wenn Sie auch den Inhalt der Diskussionen anstatt nur deren Stichworte durchsuchen wollen,\r\n		aktivieren Sie die das Kästchen neben dem Suchfeld. <br /> <br />\r\n	</div>\r\n	<!-- BEGIN forumError -->\r\n		<div class=\"message_error\">{FORUM_ERROR}</div>\r\n	<!-- END forumError -->\r\n	<div class=\"searchform\">\r\n		<form action=\"index.php\" method=\"get\">\r\n			<input type=\"hidden\" value=\"forum\" name=\"section\">\r\n			<input type=\"hidden\" value=\"searchTags\" name=\"cmd\">\r\n			<input style=\"width: 250px;\" type=\"text\" name=\"term\" value=\"{FORUM_SEARCH_TERM}\">  <input type=\"submit\" name=\"search\" value=\"Suchen\" /><br /><br />\r\n			<input type=\"checkbox\" value=\"1\" id=\"srchContent\" name=\"searchContent\" {FORUM_SEARCH_CONTENT_CHECKED} /><label for=\"srchContent\">Inhalt auch durchsuchen</label>\r\n		</form>\r\n	</div>\r\n	<div class=\"threads\">\r\n		<ul>\r\n			<!-- BEGIN threadList -->\r\n				<li style=\"list-item: none;\"><a title=\"{FORUM_THREAD_SUBJECT}\" href=\"{FORUM_THREAD_LINK}\"> {FORUM_THREAD_SUBJECT} </a><br />\r\n				Stichworte: {FORUM_THREAD_KEYWORDS} <br /> \r\n				Inhalt: {FORUM_THREAD_CONTENT}<br /><br /></li>\r\n			<!-- END threadList -->\r\n		</ul>\r\n	</div>\r\n</div>','Suche nach Tags','searchTags','y',870,'on','system',3);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (875,20,'{FORUM_JAVASCRIPT_GOTO}\r\n{FORUM_JAVASCRIPT_DELETE}\r\n{FORUM_JAVASCRIPT_INSERT_TEXT}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<!-- <div class=\"dropdown\">{FORUM_DROPDOWN}</div> -->\r\n	<div class=\"navtree\">\r\n		{FORUM_TREE}{FORUM_NAME}\r\n		<span style=\"color: rgb(255, 0, 0);\">{TXT_FORUM_ERROR}&nbsp;</span>\r\n		<span style=\"color: #006900;\">{TXT_FORUM_SUCCESS}&nbsp;</span>\r\n	</div>\r\n	<!-- BEGIN addPostAnchor -->\r\n		<div class=\"anchor\"><input onclick=\"location.href=\'#thread\';\" value=\"{TXT_FORUM_CREATE_THREAD}\" type=\"button\"></div>\r\n	<!-- END addPostAnchor -->\r\n	<div class=\"threads\">\r\n		<table cellspacing=\"0\">\r\n			<tr>\r\n				<th width=\"96%\">{TXT_THREADS_SUBJECTAUTHOR}</th>\r\n		        <th width=\"1%\" nowrap=\"nowrap\">{TXT_THREADS_LASTTOPIC}</th>\r\n		        <th width=\"1%\" nowrap=\"nowrap\" align=\"center\">{TXT_THREADS_REPLIES}</th>\r\n		        <th width=\"1%\" nowrap=\"nowrap\" align=\"center\">{TXT_THREADS_HITS}</th>\r\n				<th width=\"1%\" nowrap=\"nowrap\" align=\"right\">&nbsp;</th>\r\n			</tr>\r\n			<!-- BEGIN forumThreads -->\r\n			<tr class=\"row{FORUM_THREADS_ROWCLASS}\">\r\n				<td><strong><a href=\"index.php?section=forum&amp;cmd=thread&amp;id={FORUM_THREADS_ID}\" title=\"{FORUM_THREADS_NAME}\">{FORUM_THREADS_NAME}</a></strong><br /><div class=\"smallfont\">{FORUM_THREADS_AUTHOR}</div></td>\r\n				<td nowrap=\"nowrap\" class=\"smallfont\">{FORUM_THREADS_LASTPOST_AUTHOR}<br />{FORUM_THREADS_LASTPOST_DATE}</td>\r\n		        <td align=\"center\">{FORUM_THREADS_REPLIES}</td>\r\n				<td align=\"center\">{FORUM_THREADS_HITS}</td>\r\n				<td align=\"center\">\r\n				<!-- BEGIN deleteThread -->\r\n		        	<a onclick=\"deleteThread(\'{FORUM_FORUM_ID}\', \'{FORUM_THREADS_ID}\'); return false;\" href=