INSERT INTO `contrexx_content_page` (`id`, `node_id`, `nodeIdShadowed`, `lang`, `type`, `caching`, `updatedAt`, `updatedBy`, `title`, `linkTarget`, `contentTitle`, `slug`, `content`, `sourceMode`, `customContent`, `useCustomContentForAllChannels`, `applicationTemplate`, `useCustomApplicationTemplateForAllChannels`, `cssName`, `cssNavName`, `skin`, `useSkinForAllChannels`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `metaimage`, `start`, `end`, `editingStatus`, `protection`, `frontendAccessId`, `backendAccessId`, `display`, `active`, `target`, `module`, `cmd`) VALUES(8, 6, 6, 1, 'content', 0, '2014-09-01 22:00:00', 'system', 'Erste Schritte', '', 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'Erste-Schritte', 'In diesem Beitrag lernen Sie die ersten Schritte mit Cloudrexx&reg;. Wir w&uuml;nschen Ihnen viel Spass und Erfolg bei der Umsetzung. F&uuml;r allf&auml;llige Fragen stehen Ihnen die folgenden <a href=\"{NODE_7}\">Mittel zur Verf&uuml;gung</a>.\r\n\r\n<h2>Einf&uuml;hrung</h2>\r\nDas Backend-System ist die Administrationskonsole. In diesem Bereich k&ouml;nnen Sie Cloudrexx administrieren, neue Seiten erstellen, Inhalte anpassen und vieles mehr. Das Frontend-System ist der Internetauftritt, welche Ihre Besucher erleben. Ein Theme ist das hinterlegte WebDesign.\r\n\r\n<h2>Im Backend einloggen</h2>\r\n\r\n<p>Bitte folgen Sie dazu diesen Schritten:</p>\r\n\r\n<ul>\r\n	<li>Melden Sie sich im Cloudrexx an. Geben Sie dazu in der Adresszeile im Browser Ihre Domain ein und f&uuml;gen anschliessend &quot;cadmin&quot; hinzu. Beispielsweise &quot;http://www.musterseite.eu/cadmin&quot;</li>\r\n	<li>Danach erscheint die Anmeldemaske.</li>\r\n	<li>Geben Sie bitte die folgenden Daten ein:<br />\r\n	Sicherheitscode: Bitte wiederholen Sie den sichtbaren Code (dieser Code wird als Bild generiert und kann von Hackerwerkzeugen nicht erfasst werden)<br />\r\n	Benutzernamen: gem&auml;ss Angaben im Installer<br />\r\n	Pa&szlig;wort: gem&auml;ss Angaben im Installer</li>\r\n	<li>Empfehlung: vervollst&auml;ndigen Sie in einem ersten Schritt die Angaben in den Grundeinstellungen unter dem Navigationspunkt &quot;Administration&quot; auf der linken Seite.</li>\r\n</ul>\r\n\r\n<h2>Mit dem Content Manager arbeiten</h2>\r\n\r\n<p>Nach dem erfolgreichen Anmelden im Backend-Bereich k&ouml;nnen Sie Seiten erstellen. Um einen neuen Artikel anzulegen, w&auml;hlen Sie die Funktion &quot;Content Management - Neue Seite&quot; auf der linken Seite. Danach definieren Sie die Artikeleigenschaften, insbesondere den Linknamen, Seitentitel und die Meta Angaben des Artikels (diese Angaben sind f&uuml;r Suchamschinen relevant). Mit Hilfe des WYSIWYG-Editors k&ouml;nnen Sie die Inhalte eingeben und speichern. Im Editor stehen Ihnen die aus der Textverarbeitung bekannten Funktionalit&auml;ten zur Verf&uuml;gung. Auch die Integration von Medien und Verlinkungen erfolgt &uuml;ber den Editor. Bitte verwenden Sie in der Formatierung des Textes die Formate aus dem Editor (Beispielsweise &Uuml;berschrift 1 oder Text). Wie die &Uuml;berschrift oder ein Text im Frontend aussieht, wird im Design und Layout definiert. Ein weiterer Zusatznutzen ist, dass Suchmaschinen eine definierte &Uuml;berschrift 1 (im HTML Code entspricht dies einem H1) besser indexieren, weil &Uuml;berschirften aus der Sicht einer Suchmaschine relevanter sind.</p>\r\n\r\n<h2>Module und Themes - flexibel und erweiterbar</h2>\r\n\r\n<p>Der Aufbau von Cloudrexx basiert auf den folgenden Elementen:</p>\r\n\r\n<ul>\r\n	<li>Core System</li>\r\n	<li>Themes</li>\r\n	<li>Module</li>\r\n	<li>Inhalte</li>\r\n</ul>\r\n\r\n<p>Sogenannte Themes sind in XHTML programmiert und definieren das Erscheinungsbild der Webseite. Dabei werden im XHTML-Quelltext Platzhalter hinterlegt. Diese Platzhalter beinhalten weitere Dateien und Funktionalit&auml;ten, die sp&auml;ter auf der Website integriert werden.<br />\r\n<br />\r\nHinweis: Freie Cloudrexx Frontend-Templates k&ouml;nnen <a href=\"http://www.cloudrexx.com/templates\" target=\"_blank\">hier</a> heruntergeladen werden.</p>\r\n\r\n<p>Module sind in PHP programmiert und stellen die Funktionalit&auml;ten einer Webseite dar. Standardmodule sind die Darstellung von Texten und Navigation, Sitemap, Kontaktformulare, Suchen etc. Module bestehen aus einem Backend- und einem Frontend Bereich. Im Backendbereich kann der Administrator Einstellungen vornehmen, bspw. an welche e-Mail Adresse nach Ausf&uuml;llen eines Kontaktformulars eine Benachrichtigung versendet werden soll. Im Frontend Bereich wird die Ausgabe und Verarbeitung von Daten auf dem Onlinedienst gesteuert. Cloudrexx Templates f&uuml;hren Layouts und Module zusammen. Durch Auswahl eines Layouts im Template ist die Anzahl und Position von Platzhaltern vorbestimmt. F&uuml;r jeden dieser Container kann nun der Administrator ein Modul definieren, das dort ausgef&uuml;hrt werden soll.</p>\r\n', 0, '', NULL, NULL, NULL, '', '', 0, NULL, 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'index', NULL, NULL, NULL, '', 0, 0, 0, 1, 1, '', '', '');
INSERT INTO `contrexx_content_page` (`id`, `node_id`, `nodeIdShadowed`, `lang`, `type`, `caching`, `updatedAt`, `updatedBy`, `title`, `linkTarget`, `contentTitle`, `slug`, `content`, `sourceMode`, `customContent`, `useCustomContentForAllChannels`, `applicationTemplate`, `useCustomApplicationTemplateForAllChannels`, `cssName`, `cssNavName`, `skin`, `useSkinForAllChannels`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `metaimage`, `start`, `end`, `editingStatus`, `protection`, `frontendAccessId`, `backendAccessId`, `display`, `active`, `target`, `module`, `cmd`) VALUES(9, 6, 6, 2, 'fallback', 0, '2014-09-01 22:00:00', 'system', 'Erste Schritte', '', 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'Erste-Schritte', 'This section provides you the basic knowledge to start working with Cloudrexx&reg;. We hope you\'ll enjoin it and wish you great success in realizing your first project. If you have any questions, please have a look at the <a href=\"index.php?page=1284\">support section</a> to get an overview of where you can get help or support.<br />\r\n<br />\r\n<h2>Introduction</h2>\r\nThe backend interface aka administration console is where you manage your website like for instance create new web pages. On the other side there is the frontend interface which is the actual website resp. what your visitors see when they\'re visiting your website. The web design of your website is referred as theme.<br />\r\n<h2>Authenticate at the backend interface</h2>\r\n<p>Follow these instructions:</p>\r\n<ul>\r\n    <li>Enter the URL of your Domain in the address bar of your browser and add the suffix &quot;cadmin&quot; at the end of the URL. For instance &quot;http://www.example.com/cadmin&quot;</li>\r\n    <li>After the request has been completed you should see the login form.</li>\r\n    <li>Complete the form as follows:<br />\r\n    Fill out the security code: Retype the security code from above<br />\r\n    Username: according to your setting within the web installer<br />\r\n    Password: according to your setting within the web installer</li>\r\n    <li>Advise: as a first step you should fill out the informations in the Global Configuration section beyond the navigation point Administration on the left side.</li>\r\n</ul>\r\n<h2>Working with the Content Manager</h2>\r\n<p>After you have been successfully authenticated within the backend interface you will be able to create new web pages. To do that, select the function &quot;Content Management - New page&quot; on the left side of the interface. You can now set the desired attributes of your new page. Especially you probably wanna set the link name and page title as well as several meta attributes (those are relevant for search engines). To create the virtually content of the page you have a powerful WYSIWYG-editor on your side. The editor provides all common known features from word processing applications. As an advise, always use the provided formats of the editor when formatting headlines (for instance Heading 1 or Normal). The effective look of the headlines and text should then be defined in the theme (the design and layout of the website). This method is very important on how search engines analyze your website. Headlines which in general are based on keywords that best describe or summarize a certain page will get a higher rated by the search engine due it is marked as such one (setting the format Headline 1 on the headline will put it within a H1 HTML-tag).</p>\r\n<h2>Modules and Themes - flexible and extendable</h2>\r\n<p>The architecture of Cloudrexx is based on those elements:</p>\r\n<ul>\r\n    <li>Core System</li>\r\n    <li>Themes</li>\r\n    <li>Modules</li>\r\n    <li>Content template</li>\r\n</ul>\r\n<p>So called themes are biuld in XHTML and define the layout of the website. The output of the several features of Cloudrexx is then put into the XHTML source code using placeholders.<br />\r\n<br />\r\nHint: Check out the official <a class=\"out\" href=\"http://www.Cloudrexxthemes.com/\">repository of Cloudrexx themes</a></p>\r\n<p>The modules are developed in PHP and provide the base of the function set. The core system provides the basic features like the output of pages, the site navigation, a sitemap etc. A module consists of a backend and a frontend section. The backed is used by an administrator to manage the module and the frontend is what the visitor will see in the end when he visits that part of the website that uses the according module. Finally the content templates act as in interface between the modules and the theme. The template defines how the output of the module should be displayed within theme.</p>', 0, '', NULL, NULL, NULL, '', '', 0, NULL, 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'Ihre ersten Schritte mit dem Cloudrexx® WCMS', 'index', NULL, NULL, NULL, '', 0, 0, 0, 1, 1, '', '', '');
INSERT INTO `contrexx_content_page` (`id`, `node_id`, `nodeIdShadowed`, `lang`, `type`, `caching`, `updatedAt`, `updatedBy`, `title`, `linkTarget`, `contentTitle`, `slug`, `content`, `sourceMode`, `customContent`, `useCustomContentForAllChannels`, `applicationTemplate`, `useCustomApplicationTemplateForAllChannels`, `cssName`, `cssNavName`, `skin`, `useSkinForAllChannels`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `metaimage`, `start`, `end`, `editingStatus`, `protection`, `frontendAccessId`, `backendAccessId`, `display`, `active`, `target`, `module`, `cmd`) VALUES(10, 7, 7, 1, 'content', 0, '2014-09-01 22:00:00', 'system', 'Cloudrexx Support', '', 'Cloudrexx Support', 'Cloudrexx-Support', 'Die Erfahrung zeigt, dass Cloudrexx zuverl&auml;ssig funktioniert. Sollten Sie trotzdem Unterst&uuml;tzung ben&ouml;tigen, stehen wir Ihnen mit den folgenden Instrumenten sehr gerne zur Verf&uuml;gung.\r\n<ul>\r\n	<li><a class=\"out\" href=\"http://www.cloudrexx.com/wiki/de/\" target=\"_blank\">Dokumentation</a>: Das Dokumentationsportal f&uuml;r Cloudrexx Anwender und Entwickler.</li>\r\n	<li><a class=\"out\" href=\"http://www.cloudrexx.com/forum/forumdisplay.php?f=68\" target=\"_blank\">Cloudrexx - Forum</a>: Weiterf&uuml;hrende Unterst&uuml;tzung erhalten Sie von der Cloudrexx Community im Open Source Forum.</li>\r\n	<li><a class=\"out\" href=\"http://bugs.cloudrexx.com\" target=\"_blank\">Bugtracker</a>: Hier wird eine aktuelle Liste der bekannten Fehler, sowie der geplanten/gew&uuml;nschten Verbesserungen und Erweiterungen der Cloudrexx Software gef&uuml;hrt.</li>\r\n	<li><a class=\"out\" href=\"http://support.cloudrexx.com\" target=\"_blank\">Support - Ticket f&uuml;r Cloudrexx - Kunden</a>: Cloudrexx - Kunden k&ouml;nnen unter diesem Link ein Support-Ticket er&ouml;ffnen. Die Anfrage wird in das Ticketing-System geleitet und damit wird gew&auml;hrleistet, dass Ihre Anfrage rasch und zuverl&auml;ssig beantwortet wird. Nutzer der kostenlosen Opensource Version stehen die obigen M&ouml;glichkeiten zur Verf&uuml;gung.</li>\r\n</ul>\r\n', 0, '', NULL, NULL, NULL, '', '', 0, NULL, 'Cloudrexx Support', 'Cloudrexx Support', 'Cloudrexx Support', 'index', NULL, NULL, NULL, '', 0, 0, 0, 1, 1, '', '', '');
INSERT INTO `contrexx_content_page` (`id`, `node_id`, `nodeIdShadowed`, `lang`, `type`, `caching`, `updatedAt`, `updatedBy`, `title`, `linkTarget`, `contentTitle`, `slug`, `content`, `sourceMode`, `customContent`, `useCustomContentForAllChannels`, `applicationTemplate`, `useCustomApplicationTemplateForAllChannels`, `cssName`, `cssNavName`, `skin`, `useSkinForAllChannels`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `metaimage`, `start`, `end`, `editingStatus`, `protection`, `frontendAccessId`, `backendAccessId`, `display`, `active`, `target`, `module`, `cmd`) VALUES(11, 7, 7, 2, 'fallback', 0, '2014-09-01 22:00:00', 'system', 'Cloudrexx Support', '', 'Cloudrexx Support', 'Cloudrexx-Support', 'If you need assistance in the usage of Cloudrexx or if you have any questions please don\'t hesitate to look up one of the following sections:<br />\r\n<ul>\r\n    <li><a class=\"out\" href=\"http://www.cloudrexx.com/wiki/en/\">Documentation</a>: The wiki based documentation portal for Cloudrexx users and developers.</li>\r\n    <li><a class=\"out\" href=\"http://www.cloudrexx.com/forum/forumdisplay.php?f=69\">Cloudrexx - Forum</a>: Further support is provided by the Cloudrexx community in the open source forum.</li>\r\n    <li><a class=\"out\" href=\"http://bugs.cloudrexx.com\">Bugtracker</a>: Maintained list of current known bugs as well as planned features and improvements for Cloudrexx.</li>\r\n    <li><a class=\"out\" href=\"http://support.cloudrexx.com/index.php\">Support - Ticket for Cloudrexx - Customers</a>: Cloudrexx - Customers have to possibility to open a support-ticket. Tickets will be answered quickly and it\'s support is reliable. Users of the free opensource version can use one of the three other methods above.</li>\r\n</ul>', 0, '', NULL, NULL, NULL, '', '', 0, NULL, 'Cloudrexx Support', 'Cloudrexx Support', 'Cloudrexx Support', 'index', NULL, NULL, NULL, '', 0, 0, 0, 1, 1, '', '', '');
