INSERT INTO `contrexx_core_text` (`id`, `lang_id`, `section`, `key`, `text`) VALUES (20,1,'MultiSite','core_mail_template_message','Es freut uns sehr, dass Sie Cloudrexx nutzen. Kommen Sie zurecht? Oder benötigen Sie unsere Unterstützung? Melden Sie sich beim Cloudrexx-Support, falls etwas nicht so funktioniert, wie Sie es gerne möchten.\r\n\r\nSupport\r\n--------------------------------------\r\n\r\nAb CHF 30.– / EUR 23.– erhalten Sie mit einem Supportplan ein Rundum-Sorglos-Paket für Ihre Cloudrexx-Website.\r\n\r\nWählen Sie zwischen den Preisplänen Basic, Business oder Enterprise aus. Ein Supportplan garantiert Ihnen die professionelle Unterstützung durch unser Support-Team innerhalb einer definierten Reaktionszeit.\r\n\r\nRufen Sie uns jetzt an unter +41 33 226 60 00 oder schreiben Sie uns ein E-Mail an info@cloudrexx.com.\r\n\r\nJetzt bestellen:\r\nhttp://www.comvation.com/de/Support/Supportplan-Bestellung\r\n\r\n\r\nIhr Account\r\n--------------------------------------\r\nIhre Cloudrexx-Website:\r\nhttps://[[WEBSITE_DOMAIN]]\r\n\r\nAdministration: \r\nhttps://[[WEBSITE_DOMAIN]]/cadmin\r\n\r\n\r\nFreundliche Grüsse\r\nIhr Cloudrexx-Team');
