INSERT INTO `contrexx_core_text` (`id`, `lang_id`, `section`, `key`, `text`) VALUES (21,1,'MultiSite','core_mail_template_message','Bestimmt konnten Sie bereits einige Erfahrungen mit Cloudrexx sammeln. Was ist ihr Eindruck? Cloudrexx wird laufend weiter entwickelt und Sie können sich einbringen. Falls Sie einen Feature-Wunsch haben, schicken Sie uns ein Mail an info@cloudrexx.com. Die besten Einsendungen belohnen wir mit einem Cloudrexx-Business-Jahresabo.\r\n\r\n\r\nDie wichtigsten Informationsquellen\r\n--------------------------------------\r\n\r\nSämtliche Erweiterungen und Verbesserungen fliessen auch in Contrexx, das Download-Produkt von Cloudrexx, ein. Falls Sie einen Bug direkt melden möchten, können Sie dies im Bugtracker-Tool tun: http://bugs.contrexx.com/contrexx\r\n\r\nDie Community hilft! Anwenderfragen stellen Sie im Contrexx-Forum: http://forum.contrexx.com/forum.php\r\n\r\nBleiben Sie auf dem Laufenden mit unserem Community Newsletter. Jetzt abonnieren!\r\nhttp://www.contrexx.com/de/index.php?section=newsletter&cmd=subscribe\r\n\r\n\r\nIhr Account\r\n--------------------------------------\r\nIhre Cloudrexx-Website:\r\nhttps://[[WEBSITE_DOMAIN]]\r\n\r\nAdministration: \r\nhttps://[[WEBSITE_DOMAIN]]/cadmin\r\n\r\n\r\nFreundliche Grüsse\r\nIhr Cloudrexx-Team');
