INSERT INTO `contrexx_module_calendar_event_field` (`event_id`, `lang_id`, `title`, `teaser`, `description`, `redirect`, `place`, `place_city`, `place_country`, `org_name`, `org_city`, `org_country`) VALUES (88,'1','Cloudrexx Geburtstag',NULL,'<h2>Geschichte</h2>\r\n<strong>Cloudrexx</strong> wurde von der Unternehmung <a class="out" href="https://www.cloudrexx.com/" target="_blank">Cloudrexx AG</a> in der serverseitigen Skriptsprache PHP geschrieben, ist plattformunabh&auml;ngig und ben&ouml;tigt eine MySQL-Datenbank. Das erste Release wurde am 4. April 2005 ver&ouml;ffentlicht.\r\n\r\n<h2>&Uuml;ber Cloudrexx</h2>\r\n\r\n<div>\r\n<p>Mit <a class="out" href="https://www.cloudrexx.com/" target="_blank">Cloudrexx</a> werden Webseiten f&uuml;r Intranet, Extranet und Internet schnell, benutzerfreundlich und sicher erstellt. Die integrierten Anwendungen von Cloudrexx erlauben ein rasches Umsetzen von individuellen Bed&uuml;rfnissen, wenn n&ouml;tig mit professioneller Unterst&uuml;tzung.<br />\r\nDas webbasierte System ist f&uuml;r die Verwaltung von flexiblen Internet- und Intranet L&ouml;sungen optimiert. Der modulare Aufbau und das Workflow System erm&ouml;glicht eine 100% Individualisierung der Logik und des Designs und f&ouml;rdert ein konsistentes und stetig wachsendes Wissensnetzwerk.<br />\r\n<br />\r\nCloudrexx Download beinhaltet einen Webinstaller, sowie verschiedene Anwendungen wie ein Linkverzeichnis, Podcast, Onlineschalter, Adressverzeichnis, Sitemap, Bildergalerie, Newsletter, G&auml;stebuch, RSS Feed Verzeichnis, News System, Abstimmung, Online Shop, Blockmodul und die M&ouml;glichkeiten f&uuml;r passwortgesch&uuml;tzte Bereiche.</p>\r\n\r\n<h2>Professioneller und innovativer Vertrieb</h2>\r\nCloudrexx wird weltweit &uuml;ber ein Partnernetzwerk vertrieben. Die <a class="out" href="https://partner.cloudrexx.com/" target="_blank">Cloudrexx Solutions Partner</a> sind autorisierte Partner welche ausgewiesene Kompetenzen im den Bereichen WebDesign, Grafik oder Internetmarketing besitzen. Damit erhalten Sie als Kunde professionelle Unterst&uuml;tzung mit hohen Kompetenzen aus Ihrer N&auml;he.</div>\r\n','','','','','','','');
